"use client";

import { useState, useCallback, useEffect } from "react";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { ProviderSelection, type ProviderConfig } from "./provider-selection";
import { ChatInterface } from "./chat-interface";
import { FormPreview } from "./form-preview";
import { ConversationHistory, type Conversation } from "./conversation-history";
import { Button } from "@/components/ui/button";
import { Sparkles, ChevronDown, ChevronUp, Settings, History, ChevronLeft, ChevronRight, Eye, FileText } from "lucide-react";
import { cn } from "@/lib/utils";
import type { Message } from "./chat-interface";

// Create a client instance outside the component to avoid recreation
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 5 * 60 * 1000, // 5 minutes
      gcTime: 10 * 60 * 1000, // 10 minutes
      retry: 1,
    },
  },
});

export interface AIBuilderProps {
  className?: string;
  onFormGenerated?: (formCode: string) => void;
  onFormSubmit?: (formData: Record<string, unknown>) => void;
}

const STORAGE_KEYS = {
  PROVIDER_CONFIG: "formedible-ai-builder-provider-config",
  CONVERSATIONS: "formedible-ai-builder-conversations",
  UI_STATE: "formedible-ai-builder-ui-state",
};

function AIBuilderCore({ className, onFormGenerated, onFormSubmit }: AIBuilderProps) {
  const [providerConfig, setProviderConfig] = useState<ProviderConfig | null>(null);
  const [generatedForms, setGeneratedForms] = useState<Array<{ id: string; code: string; timestamp: Date }>>([]);
  const [currentFormIndex, setCurrentFormIndex] = useState<number>(0);
  const [isGenerating, setIsGenerating] = useState(false);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [currentConversationId, setCurrentConversationId] = useState<string | undefined>();
  const [currentMessages, setCurrentMessages] = useState<Message[]>([]);
  const [isTopSectionCollapsed, setIsTopSectionCollapsed] = useState(false);

  // Load saved data on mount
  useEffect(() => {
    try {
      // Load provider config
      const savedConfig = localStorage.getItem(STORAGE_KEYS.PROVIDER_CONFIG);
      if (savedConfig) {
        setProviderConfig(JSON.parse(savedConfig));
      }

      // Load conversations
      const savedConversations = localStorage.getItem(STORAGE_KEYS.CONVERSATIONS);
      if (savedConversations) {
        const parsedConversations = JSON.parse(savedConversations);
        setConversations(parsedConversations.map((c: any) => ({
          ...c,
          createdAt: new Date(c.createdAt),
          updatedAt: new Date(c.updatedAt),
        })));
      }

      // Load UI state
      const savedUIState = localStorage.getItem(STORAGE_KEYS.UI_STATE);
      if (savedUIState) {
        const uiState = JSON.parse(savedUIState);
        setIsTopSectionCollapsed(uiState.isTopSectionCollapsed || false);
        setCurrentConversationId(uiState.currentConversationId);
      }
    } catch (error) {
      console.warn("Failed to load saved AI Builder data:", error);
    }
  }, []);

  // Save provider config when it changes
  useEffect(() => {
    if (providerConfig) {
      try {
        localStorage.setItem(STORAGE_KEYS.PROVIDER_CONFIG, JSON.stringify(providerConfig));
      } catch (error) {
        console.warn("Failed to save provider config:", error);
      }
    }
  }, [providerConfig]);

  // Save conversations when they change
  useEffect(() => {
    try {
      localStorage.setItem(STORAGE_KEYS.CONVERSATIONS, JSON.stringify(conversations));
    } catch (error) {
      console.warn("Failed to save conversations:", error);
    }
  }, [conversations]);

  // Save UI state when it changes
  useEffect(() => {
    try {
      const uiState = {
        isTopSectionCollapsed,
        currentConversationId,
      };
      localStorage.setItem(STORAGE_KEYS.UI_STATE, JSON.stringify(uiState));
    } catch (error) {
      console.warn("Failed to save UI state:", error);
    }
  }, [isTopSectionCollapsed, currentConversationId]);

  const handleProviderConfigChange = useCallback((config: ProviderConfig | null) => {
    setProviderConfig(config);
  }, []);

  const handleFormGenerated = useCallback((formCode: string) => {
    const newForm = {
      id: `form_${Date.now()}`,
      code: formCode,
      timestamp: new Date()
    };

    setGeneratedForms(prev => {
      const updated = [...prev, newForm];
      setCurrentFormIndex(updated.length - 1); // Auto-navigate to newest form
      return updated;
    });

    onFormGenerated?.(formCode);
  }, [onFormGenerated]);

  const handleStreamingStateChange = useCallback((streaming: boolean) => {
    setIsGenerating(streaming);
  }, []);

  const handleFormSubmit = useCallback((formData: Record<string, unknown>) => {
    console.log("Form submitted:", formData);
    onFormSubmit?.(formData);
  }, [onFormSubmit]);

  const handleFormIndexChange = useCallback((index: number) => {
    setCurrentFormIndex(index);
  }, []);

  const handleDeleteForm = useCallback((index: number) => {
    setGeneratedForms(prev => {
      const updated = prev.filter((_, i) => i !== index);
      // Adjust current index if necessary
      if (index <= currentFormIndex && currentFormIndex > 0) {
        setCurrentFormIndex(currentFormIndex - 1);
      } else if (index < currentFormIndex) {
        // No change needed to currentFormIndex
      } else if (updated.length === 0) {
        setCurrentFormIndex(0);
      } else if (currentFormIndex >= updated.length) {
        setCurrentFormIndex(updated.length - 1);
      }
      return updated;
    });
  }, [currentFormIndex]);

  const handleConversationUpdate = useCallback((messages: Message[], isStreamEnd: boolean = false) => {
    // Always update current messages for UI
    setCurrentMessages(messages);

    // Only create/update conversations when needed
    if (messages.length > 0) {
      if (!currentConversationId) {
        // Create new conversation ONLY on first user message
        const newConversationId = `conversation_${Date.now()}`;
        const newConversation: Conversation = {
          id: newConversationId,
          title: "", // Will be generated from first message
          messages,
          createdAt: new Date(),
          updatedAt: new Date(),
        };

        setConversations(prev => [...prev, newConversation]);
        setCurrentConversationId(newConversationId);
      } else {
        // ALWAYS update existing conversation with new messages (not just on stream end)
        setConversations(prev => prev.map(conv => 
          conv.id === currentConversationId 
            ? { ...conv, messages, updatedAt: new Date() }
            : conv
        ));
      }
    }
  }, [currentConversationId]);

  const handleNewConversation = useCallback(() => {
    setCurrentConversationId(undefined);
    setCurrentMessages([]);
  }, []);

  // Remove automatic conversation creation

  const handleSelectConversation = useCallback((conversation: Conversation) => {
    setCurrentConversationId(conversation.id);
    setCurrentMessages(conversation.messages);
  }, []);

  const handleDeleteConversation = useCallback((conversationId: string) => {
    setConversations(prev => prev.filter(c => c.id !== conversationId));
    if (currentConversationId === conversationId) {
      setCurrentConversationId(undefined);
    }
  }, [currentConversationId]);

  const handleExportConversation = useCallback((conversation: Conversation) => {
    const dataStr = JSON.stringify(conversation, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = `conversation-${conversation.id}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  }, []);



  return (
    <div className={cn("flex flex-col h-full overflow-hidden", className)}>
      <div className="flex items-center gap-2 px-1 pb-2 flex-shrink-0">
        <Sparkles className="h-4 w-4 text-primary" />
        <h1 className="text-lg font-bold text-foreground">AI Form Builder</h1>
      </div>

      {/* Compact Collapsible Settings & History */}
      <div className="flex-shrink-0 pb-1">
        <div 
          className="flex items-center justify-between mb-1 p-2 bg-accent/10 hover:bg-accent/20 rounded-lg border-2 border-accent/20 hover:border-accent/30 transition-all duration-200 shadow-sm cursor-pointer"
          onClick={() => setIsTopSectionCollapsed(!isTopSectionCollapsed)}
        >
          <div className="flex items-center gap-2">
            <div className="p-1.5 bg-accent/20 rounded-md">
              <Settings className="h-4 w-4 text-accent-foreground" />
            </div>
            <span className="text-sm font-semibold text-foreground">Settings & History</span>
          </div>
          <div className="flex items-center gap-1 text-sm h-7 px-3">
            {isTopSectionCollapsed ? (
              <>
                <ChevronDown className="h-4 w-4" />
                <span className="font-medium">Show</span>
              </>
            ) : (
              <>
                <ChevronUp className="h-4 w-4" />
                <span className="font-medium">Hide</span>
              </>
            )}
          </div>
        </div>

        {!isTopSectionCollapsed && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-2 mb-1">
            <ConversationHistory
              conversations={conversations}
              currentConversationId={currentConversationId}
              onSelectConversation={handleSelectConversation}
              onDeleteConversation={handleDeleteConversation}
              onExportConversation={handleExportConversation}
            />
            <ProviderSelection
              onConfigChange={handleProviderConfigChange}
              initialConfig={providerConfig}
            />
          </div>
        )}
      </div>

      {/* Chat Interface - Takes ALL Remaining Space */}
      <div className="flex-1 grid grid-cols-1 lg:grid-cols-2 gap-2 min-h-0 overflow-hidden">
        <ChatInterface
          onFormGenerated={handleFormGenerated}
          onStreamingStateChange={handleStreamingStateChange}
          onConversationUpdate={handleConversationUpdate}
          onNewConversation={handleNewConversation}
          messages={currentMessages}
          providerConfig={providerConfig}
          className="h-full overflow-hidden"
        />

        <FormPreview
          forms={generatedForms}
          currentFormIndex={currentFormIndex}
          onFormIndexChange={handleFormIndexChange}
          onDeleteForm={handleDeleteForm}
          isStreaming={isGenerating}
          onFormSubmit={handleFormSubmit}
          className="h-full overflow-hidden"
        />
      </div>
    </div>
  );
}

// Export the main component wrapped with QueryClientProvider
export function AIBuilder(props: AIBuilderProps) {
  return (
    <QueryClientProvider client={queryClient}>
      <AIBuilderCore {...props} />
    </QueryClientProvider>
  );
}

"use client";

import { useFormedible } from "@/hooks/use-formedible";
import { z } from "zod";
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";

export type AIProvider =
  | "openai"
  | "anthropic"
  | "google"
  | "mistral"
  | "openrouter"
  | "openai-compatible";

export interface ProviderConfig {
  provider: AIProvider;
  apiKey: string;
  model?: string;
  temperature?: number;
  maxTokens?: number;
  endpoint?: string;
}

export interface ProviderSelectionProps {
  onConfigChange: (config: ProviderConfig | null) => void;
  initialConfig?: ProviderConfig | null;
  className?: string;
}

const providerSchema = z.object({
  provider: z
    .enum([
      "openai",
      "anthropic",
      "google",
      "mistral",
      "openrouter",
      "openai-compatible",
    ])
    .optional(),
  model: z.string().optional(),
  apiKey: z.string().optional(),
  temperature: z.number().min(0).max(2).default(0.7),
  maxTokens: z.number().min(1).max(32768).default(16384),
  endpoint: z.string().url().optional(),
});

const PROVIDERS = [
  { value: "openai", label: "OpenAI", requiresKey: true },
  { value: "anthropic", label: "Anthropic", requiresKey: true },
  { value: "google", label: "Google Gemini", requiresKey: true },
  { value: "mistral", label: "Mistral", requiresKey: true },
  { value: "openrouter", label: "OpenRouter", requiresKey: true },
  {
    value: "openai-compatible",
    label: "OpenAI Compatible",
    requiresKey: false,
  },
] as const;

// Separate function to get model options based on provider
const getModelOptions = (
  provider: AIProvider | undefined,
  openRouterModels: Array<{ value: string; label: string }> = [],
  openRouterLoading: boolean = false
) => {
  if (provider === "openai")
    return [
      { value: "gpt-4o", label: "GPT-4o" },
      { value: "gpt-4o-mini", label: "GPT-4o Mini" },
      { value: "gpt-4-turbo", label: "GPT-4 Turbo" },
      { value: "gpt-4", label: "GPT-4" },
      { value: "gpt-3.5-turbo", label: "GPT-3.5 Turbo" },
      { value: "gpt-4o-2024-08-06", label: "GPT-4o (2024-08-06)" },
      { value: "gpt-4o-mini-2024-07-18", label: "GPT-4o Mini (2024-07-18)" },
    ];
  if (provider === "anthropic")
    return [
      { value: "claude-opus-4-1-20250805", label: "Claude Opus 4.1" },
      { value: "claude-opus-4-20250514", label: "Claude Opus 4" },
      { value: "claude-sonnet-4-20250514", label: "Claude Sonnet 4" },
      { value: "claude-3-7-sonnet-20250219", label: "Claude 3.7 Sonnet" },
      { value: "claude-3-5-haiku-20241022", label: "Claude 3.5 Haiku" },
      { value: "claude-3-haiku-20240307", label: "Claude 3 Haiku" },
    ];
  if (provider === "google")
    return [
      { value: "gemini-2.5-pro", label: "Gemini 2.5 Pro" },
      { value: "gemini-2.5-flash", label: "Gemini 2.5 Flash" },
      { value: "gemini-2.5-flash-lite", label: "Gemini 2.5 Flash Lite" },
      { value: "gemini-2.0-flash", label: "Gemini 2.0 Flash" },
    ];
  if (provider === "mistral")
    return [
      { value: "mistral-large-2411", label: "Mistral Large 2.1" },
      { value: "mistral-medium-2508", label: "Mistral Medium 3.1" },
      { value: "codestral-2508", label: "Codestral 2508" },
      { value: "ministral-8b-2410", label: "Ministral 8B" },
      { value: "ministral-3b-2410", label: "Ministral 3B" },
      { value: "mistral-small-2506", label: "Mistral Small 3.2" },
    ];
  if (provider === "openrouter") {
    if (openRouterLoading) return [{ value: "loading", label: "Loading models..." }];
    return openRouterModels.length > 0
      ? openRouterModels.filter(model => model.value && model.value.trim() !== "")
      : [
          { value: "openai/gpt-5", label: "OpenAI GPT-5" },
          { value: "openai/gpt-5-mini", label: "OpenAI GPT-5 Mini" },
          { value: "openai/o3-pro", label: "OpenAI o3 Pro" },
          { value: "openai/o3-mini", label: "OpenAI o3 Mini" },
          {
            value: "anthropic/claude-opus-4.1",
            label: "Anthropic Claude Opus 4.1",
          },
          {
            value: "anthropic/claude-sonnet-4",
            label: "Anthropic Claude Sonnet 4",
          },
          { value: "google/gemini-2.5-pro", label: "Google Gemini 2.5 Pro" },
          { value: "deepseek/deepseek-r1", label: "DeepSeek R1" },
          {
            value: "mistralai/mistral-medium-3.1",
            label: "Mistral Medium 3.1",
          },
          { value: "qwen/qwen3-235b-a22b", label: "Qwen Qwen3 235B A22B" },
        ];
  }
  return [];
};

// OpenRouter models fetcher
const fetchOpenRouterModels = async (apiKey: string) => {
  const response = await fetch("https://openrouter.ai/api/v1/models", {
    headers: {
      Authorization: `Bearer ${apiKey}`,
      "Content-Type": "application/json",
    },
  });

  if (!response.ok) {
    throw new Error(`HTTP error! status: ${response.status}`);
  }

  const data = await response.json();
  return data.data
    .filter((model: any) => model.id && model.name && model.id.trim() !== "")
    .map((model: any) => ({
      value: model.id,
      label: `${model.name} (${model.id})`,
    }))
    .sort((a: any, b: any) => a.label.localeCompare(b.label));
};

export function ProviderSelection({
  onConfigChange,
  initialConfig,
  className,
}: ProviderSelectionProps) {
  const [currentApiKey, setCurrentApiKey] = useState<string>(initialConfig?.apiKey || "");
  const [currentProvider, setCurrentProvider] = useState<AIProvider | undefined>(initialConfig?.provider);

  // Use TanStack Query for OpenRouter models
  const {
    data: openRouterModels = [],
    isLoading: openRouterLoading,
    error: openRouterError,
  } = useQuery({
    queryKey: ["openrouter-models", currentApiKey],
    queryFn: () => fetchOpenRouterModels(currentApiKey),
    enabled: currentProvider === "openrouter" && !!currentApiKey && currentApiKey.length > 0,
    staleTime: 5 * 60 * 1000, // 5 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes
    retry: 1,
  });

  const { Form, form } = useFormedible({
    schema: providerSchema,
    layout: { type: "grid", columns: 2, gap: "lg" },
    fields: [
      // Field 1 - Provider
      {
        name: "provider",
        type: "select",
        label: "AI Provider",
        placeholder: "Select provider...",
        gridColumn: 1,
        options: PROVIDERS.map((p) => ({ value: p.value, label: p.label })),
      },
      // Field 2 - API Key (MOVED BEFORE MODEL)
      {
        name: "apiKey",
        type: "text",
        label: "API Key",
        placeholder: "sk-...",
        gridColumn: 2,
        conditional: (values) => !!values.provider,
        help: {
          tooltip:
            "Your API key is stored securely and never sent to our servers",
        },
      },
      // Field 3 - Model (NOW AFTER API KEY)
      {
        name: "model",
        type: "select",
        label: "Model",
        placeholder: "Select model...",
        gridColumn: 1,
        options: (values) => {
          const models = getModelOptions(
            values.provider as AIProvider | undefined,
            openRouterModels,
            openRouterLoading
          );

          // Show error state for OpenRouter if there's an error
          if (values.provider === "openrouter" && openRouterError) {
            return [{ value: "error", label: "Failed to load models - check API key" }];
          }

          return models;
        },
        conditional: (values) => !!values.provider,
      },
      // Field 4 - Temperature
      {
        name: "temperature",
        type: "slider",
        label: "Temperature",
        description: "Controls randomness: 0 = focused, 2 = creative",
        gridColumn: 2,
        sliderConfig: {
          min: 0,
          max: 2,
          step: 0.1,
          showValue: true,
          marks: [
            { value: 0, label: "Focused" },
            { value: 1, label: "Balanced" },
            { value: 2, label: "Creative" },
          ],
        },
        conditional: (values) => !!values.provider,
      },
      // Field 5 - Endpoint
      {
        name: "endpoint",
        type: "text",
        label: "API Endpoint",
        placeholder: "https://api.example.com/v1",
        conditional: (values) => values.provider === "openai-compatible",
        gridColumn: 1,
        description: "Base URL for OpenAI-compatible API",
      },
      // Field 6 - Max Tokens
      {
        name: "maxTokens",
        type: "number",
        label: "Max Tokens",
        placeholder: "16384",
        description: "Maximum response length",
        gridColumn: 2,
        numberConfig: {
          min: 1,
          max: 32768,
          step: 1,
        },
        conditional: (values) => !!values.provider,
      },
    ],
    formOptions: {
      defaultValues: {
        provider: initialConfig?.provider,
        model: initialConfig?.model || "",
        apiKey: initialConfig?.apiKey || "",
        endpoint: initialConfig?.endpoint || "",
        temperature: initialConfig?.temperature || 0.7,
        maxTokens: initialConfig?.maxTokens || 16384,
      },
      onSubmit: async ({ value }) => {
        const config: ProviderConfig = {
          provider: value.provider as AIProvider,
          apiKey: value.apiKey || "",
          model: value.model,
          temperature: value.temperature,
          maxTokens: value.maxTokens,
          ...(value.endpoint && { endpoint: value.endpoint }),
        };
        onConfigChange(config);
      },
    },
    submitLabel: "ðŸ’¾ Save Configuration",
    showSubmitButton: true,
    autoSubmitOnChange: false,
  });

  // Use TanStack Form subscriptions to watch specific fields without causing full rerenders
  useEffect(() => {
    const unsubscribeProvider = form.store.subscribe(() => {
      const provider = form.state.values.provider;
      if (provider !== currentProvider) {
        setCurrentProvider(provider as AIProvider);
      }
    });

    const unsubscribeApiKey = form.store.subscribe(() => {
      const apiKey = form.state.values.apiKey;
      if (apiKey && apiKey !== currentApiKey) {
        setCurrentApiKey(apiKey);
      }
    });

    return () => {
      unsubscribeProvider();
      unsubscribeApiKey();
    };
  }, [form, currentProvider, currentApiKey]);

  return <Form />;
}

"use client";

import { useState, useRef, useEffect, useCallback } from "react";
import { streamText } from "ai";
import { createOpenAI } from "@ai-sdk/openai";
import { createAnthropic } from "@ai-sdk/anthropic";
import { createGoogleGenerativeAI } from "@ai-sdk/google";
import { createMistral } from "@ai-sdk/mistral";
import { createOpenRouter } from "@openrouter/ai-sdk-provider";
import { createOpenAICompatible } from "@ai-sdk/openai-compatible";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { AlertCircle, MessageSquare, Send, StopCircle, User, Bot, Loader2, ArrowDown, Plus } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { CodeBlock } from "@/components/ui/code-block";
import { cn } from "@/lib/utils";
import type { ProviderConfig } from "./provider-selection";

// MessageContent component to handle code blocks with syntax highlighting
interface MessageContentProps {
  content: string;
}

function MessageContent({ content }: MessageContentProps) {
  // Detect and render code blocks with syntax highlighting
  const codeBlockRegex = /```(\w+)?\s*\n([\s\S]*?)\n```/g;
  const parts = [];
  let lastIndex = 0;
  let match;

  while ((match = codeBlockRegex.exec(content)) !== null) {
    // Add text before code block
    if (match.index > lastIndex) {
      parts.push(
        <span key={`text-${lastIndex}`} className="whitespace-pre-wrap break-words leading-relaxed">
          {content.slice(lastIndex, match.index)}
        </span>
      );
    }

    // Add code block with syntax highlighting
    const language = match[1] || 'text';
    const code = match[2];
    parts.push(
      <div key={`code-${match.index}`} className="my-2">
        <CodeBlock
          code={code}
          language={language}
          showLineNumbers={false}
          showCopyButton={true}
          className="text-xs"
          darkMode={true}
          scrollable={false}
        />
      </div>
    );

    lastIndex = match.index + match[0].length;
  }

  // Add remaining text
  if (lastIndex < content.length) {
    parts.push(
      <span key={`text-${lastIndex}`} className="whitespace-pre-wrap break-words leading-relaxed">
        {content.slice(lastIndex)}
      </span>
    );
  }

  return <div className="space-y-1">{parts.length > 0 ? parts : <span className="whitespace-pre-wrap break-words leading-relaxed">{content}</span>}</div>;
}

export interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
}

export interface ChatInterfaceProps {
  onFormGenerated?: (formCode: string) => void;
  onStreamingStateChange?: (isStreaming: boolean) => void;
  onConversationUpdate?: (messages: Message[], isStreamEnd?: boolean) => void;
  onNewConversation?: () => void;
  messages?: Message[];
  className?: string;
  providerConfig?: ProviderConfig | null;
}

export function ChatInterface({ onFormGenerated, onStreamingStateChange, onConversationUpdate, onNewConversation, messages: externalMessages, className, providerConfig }: ChatInterfaceProps) {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState<Message[]>(externalMessages || []);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const [showScrollToBottom, setShowScrollToBottom] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messagesContainerRef = useRef<HTMLDivElement>(null);
  const abortControllerRef = useRef<AbortController | null>(null);

  // Remove the automatic conversation completion

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  const handleScroll = useCallback(() => {
    if (!messagesContainerRef.current) return;

    const { scrollTop, scrollHeight, clientHeight } = messagesContainerRef.current;
    const isAtBottom = scrollHeight - scrollTop - clientHeight < 100;

    setShowScrollToBottom(!isAtBottom && messages.length > 0);
  }, [messages.length]);

  // Sync with external messages only when switching conversations
  useEffect(() => {
    if (externalMessages && externalMessages.length > 0 && messages.length === 0) {
      setMessages(externalMessages);
    }
  }, [externalMessages]);

  // Remove the useEffect that was causing multiple updates

  // Removed auto-scroll - let users control scrolling manually

  useEffect(() => {
    const container = messagesContainerRef.current;
    if (!container) return;

    container.addEventListener('scroll', handleScroll);
    handleScroll(); // Check initial state

    return () => container.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  const createModel = (config: ProviderConfig) => {
    const { provider, apiKey, model, endpoint } = config;

    switch (provider) {
      case 'openai':
        const openaiProvider = createOpenAI({ apiKey });
        return openaiProvider(model || 'gpt-4o');

      case 'anthropic':
        const anthropicProvider = createAnthropic({ apiKey });
        return anthropicProvider(model || 'claude-3-5-sonnet-20241022');

      case 'google':
        const googleProvider = createGoogleGenerativeAI({ apiKey });
        return googleProvider(model || 'gemini-1.5-pro');

      case 'mistral':
        const mistralProvider = createMistral({ apiKey });
        return mistralProvider(model || 'mistral-large-latest');

      case 'openrouter':
        const openrouterProvider = createOpenRouter({ apiKey });
        return openrouterProvider.chat(model || 'meta-llama/llama-3.2-3b-instruct:free');

      case 'openai-compatible':
        if (!endpoint) throw new Error('Endpoint required for OpenAI-compatible providers');
        const openaiCompatible = createOpenAICompatible({
          name: 'openai-compatible',
          baseURL: endpoint,
          ...(apiKey && { apiKey }),
        });
        return openaiCompatible(model || 'gpt-3.5-turbo');

      default:
        throw new Error('Unsupported provider');
    }
  };

  const generateResponse = async (userMessage: string) => {
    if (!providerConfig) {
      setError(new Error('Provider configuration required'));
      return;
    }

    try {
      setIsLoading(true);
      setError(null);
      onStreamingStateChange?.(true);

      const userMsg: Message = {
        id: Date.now().toString(),
        role: 'user',
        content: userMessage,
      };

      const newMessages = [...messages, userMsg];
      setMessages(newMessages);

      // Immediately save user message
      onConversationUpdate?.(newMessages, false);

      const model = createModel(providerConfig);

      const systemPrompt = `You are a helpful AI assistant for form creation. You can chat naturally with users about forms, answer questions, and help them design forms.

**IMPORTANT: Only show formedible code blocks when the user specifically asks to create, build, generate, or show a form.**

When creating forms, use formedible code blocks with complete JavaScript object literal syntax including Zod schemas:

\`\`\`formedible
{
  schema: z.object({
    firstName: z.string().min(1, 'First name is required'),
    email: z.string().email('Please enter a valid email address'),
    age: z.number().min(18, 'Must be 18 or older').optional(),
    newsletter: z.boolean().default(false)
  }),
  fields: [
    {
      name: 'firstName',
      type: 'text',
      label: 'First Name',
      placeholder: 'Enter your first name'
    },
    {
      name: 'email',
      type: 'email', 
      label: 'Email Address',
      placeholder: 'your@email.com'
    },
    {
      name: 'age',
      type: 'number',
      label: 'Age',
      placeholder: '18+'
    },
    {
      name: 'newsletter',
      type: 'checkbox',
      label: 'Subscribe to newsletter'
    }
  ],
  formOptions: {
    defaultValues: {
      firstName: '',
      email: '',
      newsletter: false
    }
  }
}
\`\`\`

**CRITICAL REQUIREMENTS:**
- ALWAYS include complete Zod schema with proper validation
- Use JavaScript object literals (unquoted keys, single quotes for strings) 
- Match schema field names EXACTLY with field names
- Include proper Zod validations: .min(), .max(), .email(), .optional(), .default()
- Available field types: text, email, password, tel, textarea, select, checkbox, switch, number, date, slider, file, rating, phone, colorPicker, location, duration, multiSelect, autocomplete, masked, object, array, radio

**Zod Schema Examples:**
- z.string().min(1, 'Required') - required text
- z.string().email('Invalid email') - email validation  
- z.number().min(18, 'Must be 18+') - number with min
- z.boolean().default(false) - checkbox with default
- z.string().optional() - optional field
- z.enum(['option1', 'option2']) - select options
- z.array(z.string()) - multiSelect

**ARRAY FIELDS - Use arrayConfig with proper configuration:**

Simple string arrays:
\`\`\`javascript
{
  name: 'contactMethods',
  type: 'array',
  label: 'Contact Email Addresses',
  arrayConfig: {
    itemType: 'email',
    itemLabel: 'Email Address',
    itemPlaceholder: 'contact@company.com',
    minItems: 1,
    maxItems: 5,
    addButtonLabel: 'Add Email',
    removeButtonLabel: 'Remove',
    defaultValue: ''
  }
}
\`\`\`

Complex object arrays:
\`\`\`javascript
{
  name: 'teamMembers',
  type: 'array',
  label: 'Team Members',
  arrayConfig: {
    itemType: 'object',
    itemLabel: 'Team Member',
    minItems: 1,
    maxItems: 10,
    sortable: true,
    addButtonLabel: 'Add Team Member',
    removeButtonLabel: 'Remove Member',
    defaultValue: {
      name: '',
      email: '',
      role: 'developer'
    },
    objectConfig: {
      fields: [
        {
          name: 'name',
          type: 'text',
          label: 'Name',
          placeholder: 'Enter name'
        },
        {
          name: 'email',
          type: 'text',
          label: 'Email',
          placeholder: 'Enter email'
        },
        {
          name: 'role',
          type: 'select',
          label: 'Role',
          options: [
            { value: 'developer', label: 'Developer' },
            { value: 'designer', label: 'Designer' }
          ]
        }
      ]
    }
  }
}
\`\`\`

**OBJECT FIELDS - Use objectConfig with fields:**

\`\`\`javascript
{
  name: 'roomDetails',
  type: 'array',
  label: 'Room Details',
  arrayConfig: {
    itemType: 'object',
    itemLabel: 'Room',
    defaultValue: {
      hasEquipment: false,
      equipmentList: ''
    },
    objectConfig: {
      collapseLabel: 'Collapse',
      expandLabel: 'Expand',
      title: 'Room Configuration',
      description: 'Define room characteristics',
      collapsible: true,
      defaultExpanded: true,
      showCard: false,
      columns: 2,
      layout: 'grid',
      fields: [
        {
          name: 'hasEquipment',
          type: 'switch',
          label: 'Has Equipment',
          description: 'Does this room have special equipment?'
        },
        {
          name: 'equipmentList',
          type: 'textarea',
          label: 'Equipment List',
          placeholder: 'Describe equipment...',
          conditional: (values) => values && values.hasEquipment === true
        }
      ]
    }
  }
}
\`\`\`

**Optional properties:**
- pages: [...] for multi-page forms
- progress: { showSteps: true, showPercentage: false }
- submitLabel: 'Submit Form'
- formOptions: { defaultValues: {...}, onSubmit: async (data) => {...} }

Chat naturally. Ask clarifying questions. Suggest improvements. Only output formedible blocks when specifically requested.`;

      const abortController = new AbortController();
      abortControllerRef.current = abortController;

      const result = await streamText({
        model,
        system: systemPrompt,
        messages: [...messages, userMsg].map(msg => ({
          role: msg.role,
          content: msg.content,
        })),
        temperature: providerConfig.temperature || 0.7,
        maxOutputTokens: providerConfig.maxTokens || 2000,
        abortSignal: abortController.signal,
      });

      const assistantMsg: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: '',
      };

      setMessages(prev => [...prev, assistantMsg]);

      let fullResponse = '';
      for await (const textPart of result.textStream) {
        if (abortController.signal.aborted) break;

        fullResponse += textPart;
        setMessages(prev => prev.map(msg => 
          msg.id === assistantMsg.id 
            ? { ...msg, content: fullResponse }
            : msg
        ));
      }

      // Extract and send formedible code blocks to preview
      if (fullResponse && onFormGenerated) {
        const formedibleMatch = fullResponse.match(/```formedible\s*\n([\s\S]*?)\n```/);
        if (formedibleMatch && formedibleMatch[1]) {
          onFormGenerated(formedibleMatch[1].trim());
        }
      }

      onStreamingStateChange?.(false);

      // Save conversation to localStorage ONLY on stream end with complete messages
      setMessages(currentMessages => {
        // Use a microtask to defer the parent update until after current render cycle
        Promise.resolve().then(() => {
          onConversationUpdate?.(currentMessages, true);
        });
        return currentMessages;
      });

    } catch (err) {
      console.error('AI Generation Error:', err);
      setError(err instanceof Error ? err : new Error('Unknown error occurred'));
    } finally {
      setIsLoading(false);
      onStreamingStateChange?.(false);
      abortControllerRef.current = null;
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || isLoading || !providerConfig) return;

    const userInput = input;
    setInput("");
    generateResponse(userInput);
  };

  const stop = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      setIsLoading(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <Card className={cn("flex flex-col h-full border-2 border-accent/30 shadow-lg", className)}>
      <CardHeader className="pb-4 bg-gradient-to-r from-accent/10 to-transparent">
        <CardTitle className="flex items-center justify-between text-lg">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-accent/20 rounded-lg">
              <MessageSquare className="h-5 w-5 text-accent-foreground" />
            </div>
            <span className="text-foreground font-semibold">AI Form Builder Chat</span>
          </div>
          {onNewConversation && (
            <Button
              variant="outline"
              size="sm"
              onClick={onNewConversation}
              className="h-8 w-8 p-0"
            >
              <Plus className="h-4 w-4" />
              <span className="sr-only">New conversation</span>
            </Button>
          )}
        </CardTitle>
      </CardHeader>

      <CardContent className="flex flex-col flex-1 p-6 min-h-0 relative">
        <div
          ref={messagesContainerRef}
          className="flex-1 overflow-y-auto space-y-4 mb-4 pr-2 min-h-0 max-h-full"
        >
          {messages.length === 0 && (
            <div className="flex flex-col items-center justify-center h-full text-center text-muted-foreground">
              <Bot className="h-12 w-12 mb-4 opacity-50" />
              <h3 className="text-lg font-medium mb-2">Welcome to AI Form Builder</h3>
              <p className="text-sm max-w-md">
                Describe the form you want to create and I'll generate it for you. 
                Try something like "Create a contact form with name, email, and message fields"
              </p>
            </div>
          )}

          {messages.map((message: Message) => (
            <div
              key={message.id}
              className={cn(
                "flex gap-3 max-w-4xl",
                message.role === "user" ? "ml-auto flex-row-reverse" : "mr-auto"
              )}
            >
              <div
                className={cn(
                  "flex h-8 w-8 shrink-0 select-none items-center justify-center rounded-full border-2 shadow-md",
                  message.role === "user"
                    ? "bg-primary text-primary-foreground border-primary/30"
                    : "bg-muted text-foreground border-border"
                )}
              >
                {message.role === "user" ? (
                  <User className="h-4 w-4" />
                ) : (
                  <Bot className="h-4 w-4" />
                )}
              </div>

              <div
                className={cn(
                  "flex flex-col gap-2 rounded-lg px-4 py-3 text-sm shadow-md border max-w-[85%]",
                  message.role === "user"
                    ? "bg-primary text-primary-foreground border-primary/30"
                    : "bg-background text-foreground border-border"
                )}
              >
                <MessageContent content={message.content} />
              </div>
            </div>
          ))}

          {isLoading && (
            <div className="flex gap-3 max-w-4xl mr-auto">
              <div className="flex h-8 w-8 shrink-0 select-none items-center justify-center rounded-full border-2 shadow-md bg-muted text-foreground border-border">
                <Bot className="h-4 w-4" />
              </div>
              <div className="flex items-center gap-2 rounded-lg px-4 py-3 text-sm shadow-md border bg-background text-foreground border-border">
                <Loader2 className="h-4 w-4 animate-spin text-primary" />
                <span>Generating response...</span>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {/* Scroll to bottom button */}
        {showScrollToBottom && (
          <Button
            onClick={scrollToBottom}
            className="absolute bottom-20 right-4 rounded-full w-10 h-10 p-0 shadow-lg z-10"
            variant="secondary"
            size="sm"
          >
            <ArrowDown className="h-4 w-4" />
            <span className="sr-only">Scroll to bottom</span>
          </Button>
        )}

        {error && (
          <Alert variant="destructive" className="mb-4">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              Failed to send message. Please check your configuration and try again.
            </AlertDescription>
          </Alert>
        )}

        <form onSubmit={handleSubmit} className="flex gap-2">
          <Textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Describe the form you want to create... (Shift+Enter for new line)"
            disabled={isLoading}
            className="flex-1 min-h-[80px] resize-none"
            rows={3}
          />

          {isLoading ? (
            <Button
              type="button"
              variant="outline"
              size="icon"
              onClick={stop}
              className="shrink-0"
            >
              <StopCircle className="h-4 w-4" />
              <span className="sr-only">Stop generation</span>
            </Button>
          ) : (
            <Button
              type="submit"
              disabled={!input.trim()}
              size="icon"
              className="shrink-0"
            >
              <Send className="h-4 w-4" />
              <span className="sr-only">Send message</span>
            </Button>
          )}
        </form>
      </CardContent>
    </Card>
  );
}

"use client";

import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { FormPreviewBase } from "@/components/formedible/builder/form-preview-base";
import { Eye, ChevronLeft, ChevronRight, Trash2 } from "lucide-react";
import { cn } from "@/lib/utils";

export interface GeneratedForm {
  id: string;
  code: string;
  timestamp: Date;
}

export interface FormPreviewProps {
  forms: GeneratedForm[];
  currentFormIndex: number;
  onFormIndexChange: (index: number) => void;
  onDeleteForm?: (index: number) => void;
  isStreaming?: boolean;
  onFormSubmit?: (formData: Record<string, unknown>) => void;
  className?: string;
}

export function FormPreview({ 
  forms, 
  currentFormIndex, 
  onFormIndexChange,
  onDeleteForm,
  isStreaming = false, 
  onFormSubmit, 
  className 
}: FormPreviewProps) {
  const currentForm = forms[currentFormIndex];

  if (forms.length === 0 && !isStreaming) {
    return (
      <FormPreviewBase 
        className={className}
        title="Form Preview"
        emptyStateMessage="Start a conversation in the chat to generate your first form. The preview will appear here once the AI creates your form."
      />
    );
  }

  return (
    <Card className={cn("flex flex-col h-full", className)}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Eye className="h-5 w-5" />
            Form Preview
            {forms.length > 0 && (
              <span className="text-sm font-normal text-muted-foreground">
                ({currentFormIndex + 1} of {forms.length})
              </span>
            )}
          </CardTitle>

          {forms.length > 1 && (
            <div className="flex items-center gap-1">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onFormIndexChange(Math.max(0, currentFormIndex - 1))}
                disabled={currentFormIndex === 0}
                className="h-8 w-8 p-0"
              >
                <ChevronLeft className="h-4 w-4" />
                <span className="sr-only">Previous form</span>
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={() => onFormIndexChange(Math.min(forms.length - 1, currentFormIndex + 1))}
                disabled={currentFormIndex === forms.length - 1}
                className="h-8 w-8 p-0"
              >
                <ChevronRight className="h-4 w-4" />
                <span className="sr-only">Next form</span>
              </Button>

              {onDeleteForm && forms.length > 1 && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onDeleteForm(currentFormIndex)}
                  className="h-8 w-8 p-0 text-destructive hover:text-destructive"
                >
                  <Trash2 className="h-4 w-4" />
                  <span className="sr-only">Delete form</span>
                </Button>
              )}
            </div>
          )}
        </div>
      </CardHeader>

      <CardContent className="flex-1 overflow-hidden p-0">
        <FormPreviewBase 
          formCode={currentForm?.code}
          isStreaming={isStreaming && !currentForm}
          onFormSubmit={onFormSubmit}
          className="border-0 h-full"
        />
      </CardContent>
    </Card>
  );
}

"use client";
import React, { useState, useEffect, useMemo, useCallback, memo } from "react";
import { z } from "zod";
import { useFormedible } from "@/hooks/use-formedible";
import type { FieldConfig, UseFormedibleOptions } from "@/lib/formedible/types";

export interface AiFormParseResult<TFormData = Record<string, unknown>> {
  schema: z.ZodSchema<TFormData>;
  formOptions: UseFormedibleOptions<TFormData>;
  success: boolean;
  error?: string;
}

export interface AiParserConfig {
  allowedFieldTypes?: string[];
  allowedKeys?: string[];
  allowedFieldKeys?: string[];
  allowedPageKeys?: string[];
  allowedProgressKeys?: string[];
  allowedFormOptionsKeys?: string[];
}

export interface AiFormRendererProps {
  code: string;
  isStreaming?: boolean;
  onParseComplete?: (result: AiFormParseResult) => void;
  onSubmit?: (formData: Record<string, unknown>) => void | Promise<void>;
  className?: string;
  parserConfig?: AiParserConfig;
  debug?: boolean;
}

const DEFAULT_FIELD_TYPES = [
  'text', 'email', 'password', 'url', 'tel', 'textarea', 'select', 
  'checkbox', 'switch', 'number', 'date', 'slider', 'file', 'rating',
  'phone', 'colorPicker', 'location', 'duration', 'multiSelect',
  'autocomplete', 'masked', 'object', 'array', 'radio'
];

const DEFAULT_KEYS = [
  'schema', 'fields', 'pages', 'progress', 'submitLabel', 'nextLabel', 
  'previousLabel', 'formClassName', 'fieldClassName', 'formOptions'
];

const DEFAULT_FIELD_KEYS = [
  'name', 'type', 'label', 'placeholder', 'description', 'options', 
  'min', 'max', 'step', 'accept', 'multiple', 'page', 'conditional',
  'section', 'ratingConfig', 'phoneConfig', 'colorConfig', 'locationConfig',
  'durationConfig', 'multiSelectConfig', 'sliderConfig', 'numberConfig',
  'dateConfig', 'fileConfig', 'textareaConfig', 'passwordConfig', 'emailConfig',
  'autocompleteConfig', 'maskedInputConfig', 'objectConfig', 'arrayConfig',
  'validation', 'group', 'tab', 'help', 'inlineValidation', 'datalist'
];

const DEFAULT_PAGE_KEYS = ['page', 'title', 'description'];

const DEFAULT_PROGRESS_KEYS = ['showSteps', 'showPercentage', 'className'];

const DEFAULT_FORM_OPTIONS_KEYS = [
  'defaultValues', 'asyncDebounceMs', 'canSubmitWhenInvalid', 'onSubmit', 'onSubmitInvalid'
];

class AiFormedibleParser {
  private config: Required<AiParserConfig>;

  constructor(config?: AiParserConfig) {
    this.config = {
      allowedFieldTypes: config?.allowedFieldTypes ?? DEFAULT_FIELD_TYPES,
      allowedKeys: config?.allowedKeys ?? DEFAULT_KEYS,
      allowedFieldKeys: config?.allowedFieldKeys ?? DEFAULT_FIELD_KEYS,
      allowedPageKeys: config?.allowedPageKeys ?? DEFAULT_PAGE_KEYS,
      allowedProgressKeys: config?.allowedProgressKeys ?? DEFAULT_PROGRESS_KEYS,
      allowedFormOptionsKeys: config?.allowedFormOptionsKeys ?? DEFAULT_FORM_OPTIONS_KEYS,
    };
  }

  parse(code: string): AiFormParseResult {
    try {
      const sanitizedCode = this.sanitizeCode(code);
      const parsed = this.parseObjectLiteral(sanitizedCode);
      const sanitized = this.validateAndSanitize(parsed);
      const { schema, formOptions } = this.createFormedibleConfig(sanitized);

      return {
        schema,
        formOptions,
        success: true,
      };
    } catch (error) {
      return {
        schema: z.object({}),
        formOptions: { fields: [], formOptions: { defaultValues: {} } },
        success: false,
        error: error instanceof Error ? error.message : String(error),
      };
    }
  }

  private sanitizeCode(code: string): string {
    let sanitized = code.replace(/\/\*[\s\S]*?\*\//g, '').replace(/\/\/.*$/gm, '');
    sanitized = sanitized.replace(/\b(eval|Function|setTimeout|setInterval|require|import)\s*\(/g, '');
    sanitized = sanitized.replace(/=>\s*{[^}]*}/g, '""');
    sanitized = sanitized.replace(/function\s*\([^)]*\)\s*{[^}]*}/g, '""');
    return sanitized;
  }

  private parseObjectLiteral(code: string): unknown {
    try {
      return JSON.parse(code);
    } catch {
      try {
        let processedCode = code.trim();
        processedCode = this.replaceZodExpressions(processedCode);
        processedCode = processedCode.replace(/([{,]\s*)([a-zA-Z_$][a-zA-Z0-9_$]*)\s*:/g, '$1"$2":');
        processedCode = processedCode.replace(/,(\s*[}\]])/g, '$1');
        processedCode = processedCode.replace(/'/g, '"');
        return JSON.parse(processedCode);
      } catch (conversionError) {
        throw new Error(`Invalid syntax: ${conversionError instanceof Error ? conversionError.message : String(conversionError)}`);
      }
    }
  }

  private replaceZodExpressions(code: string): string {
    let result = code;
    let changed = true;

    while (changed) {
      changed = false;
      const zodMatch = result.match(/z\.[a-zA-Z]+\(/);
      if (zodMatch) {
        const startIndex = zodMatch.index!;
        const openParenIndex = startIndex + zodMatch[0].length - 1;

        let depth = 1;
        let endIndex = openParenIndex + 1;

        while (endIndex < result.length && depth > 0) {
          if (result[endIndex] === '(') {
            depth++;
          } else if (result[endIndex] === ')') {
            depth--;
          }
          endIndex++;
        }

        if (depth === 0) {
          let chainEnd = endIndex;
          while (chainEnd < result.length) {
            const chainMatch = result.slice(chainEnd).match(/^\.[a-zA-Z]+\(/);
            if (chainMatch) {
              let chainDepth = 1;
              let chainParenIndex = chainEnd + chainMatch[0].length - 1;
              let chainEndIndex = chainParenIndex + 1;

              while (chainEndIndex < result.length && chainDepth > 0) {
                if (result[chainEndIndex] === '(') {
                  chainDepth++;
                } else if (result[chainEndIndex] === ')') {
                  chainDepth--;
                }
                chainEndIndex++;
              }

              if (chainDepth === 0) {
                chainEnd = chainEndIndex;
              } else {
                break;
              }
            } else {
              break;
            }
          }

          result = result.slice(0, startIndex) + '"__ZOD_SCHEMA__"' + result.slice(chainEnd);
          changed = true;
        } else {
          result = result.replace(/z\.[a-zA-Z]+/, '"__ZOD_SCHEMA__"');
          changed = true;
        }
      }
    }

    return result;
  }

  private validateAndSanitize(obj: unknown): Record<string, unknown> {
    if (typeof obj !== 'object' || obj === null) {
      throw new Error('Definition must be an object');
    }

    const sanitized: Record<string, unknown> = {};
    const input = obj as Record<string, unknown>;

    for (const [key, value] of Object.entries(input)) {
      if (!this.config.allowedKeys.includes(key)) {
        continue;
      }

      switch (key) {
        case 'schema':
          break;
        case 'fields':
          sanitized[key] = this.validateFields(value);
          break;
        case 'pages':
          sanitized[key] = this.validatePages(value);
          break;
        case 'progress':
          sanitized[key] = this.validateProgress(value);
          break;
        case 'formOptions':
          sanitized[key] = this.validateFormOptions(value);
          break;
        case 'submitLabel':
        case 'nextLabel':
        case 'previousLabel':
        case 'formClassName':
        case 'fieldClassName':
          if (typeof value === 'string') {
            sanitized[key] = value;
          }
          break;
      }
    }

    return sanitized;
  }

  private validateFields(fields: unknown): FieldConfig[] {
    if (!Array.isArray(fields)) {
      throw new Error('Fields must be an array');
    }

    return fields.map((field, index) => {
      if (typeof field !== 'object' || field === null) {
        throw new Error(`Field at index ${index} must be an object`);
      }

      const sanitizedField: Record<string, unknown> = {};
      const fieldObj = field as Record<string, unknown>;

      for (const [key, value] of Object.entries(fieldObj)) {
        if (!this.config.allowedFieldKeys.includes(key)) {
          continue;
        }

        switch (key) {
          case 'name':
            if (typeof value === 'string' && /^[a-zA-Z_$][a-zA-Z0-9_$]*$/.test(value)) {
              sanitizedField[key] = value;
            }
            break;
          case 'type':
            if (typeof value === 'string' && this.config.allowedFieldTypes.includes(value)) {
              sanitizedField[key] = value;
            }
            break;
          case 'label':
          case 'placeholder':
          case 'description':
          case 'accept':
            if (typeof value === 'string') {
              sanitizedField[key] = value;
            }
            break;
          case 'options':
            if (Array.isArray(value)) {
              sanitizedField[key] = value.filter(opt => 
                typeof opt === 'string' || 
                (typeof opt === 'object' && opt !== null && 
                 typeof (opt as { value?: unknown }).value === 'string' && 
                 typeof (opt as { label?: unknown }).label === 'string')
              );
            }
            break;
          case 'min':
          case 'max':
          case 'step':
          case 'page':
            if (typeof value === 'number' && !isNaN(value)) {
              sanitizedField[key] = value;
            }
            break;
          case 'multiple':
          case 'conditional':
            if (typeof value === 'boolean') {
              sanitizedField[key] = value;
            }
            break;
          default:
            if (typeof value === 'object' && value !== null) {
              sanitizedField[key] = value;
            }
            break;
        }
      }

      if (!sanitizedField.name || !sanitizedField.type) {
        throw new Error(`Field at index ${index} must have 'name' and 'type' properties`);
      }

      return sanitizedField as unknown as FieldConfig;
    });
  }

  private validatePages(pages: unknown): Array<{ page: number; title?: string; description?: string }> {
    if (!Array.isArray(pages)) {
      return [];
    }

    return pages.map(page => {
      if (typeof page !== 'object' || page === null) {
        return null;
      }

      const sanitizedPage: Record<string, unknown> = {};
      const pageObj = page as Record<string, unknown>;

      for (const [key, value] of Object.entries(pageObj)) {
        if (!this.config.allowedPageKeys.includes(key)) {
          continue;
        }

        switch (key) {
          case 'page':
            if (typeof value === 'number' && !isNaN(value)) {
              sanitizedPage[key] = value;
            }
            break;
          case 'title':
          case 'description':
            if (typeof value === 'string') {
              sanitizedPage[key] = value;
            }
            break;
        }
      }

      return sanitizedPage.page ? sanitizedPage : null;
    }).filter(Boolean) as Array<{ page: number; title?: string; description?: string }>;
  }

  private validateProgress(progress: unknown): Record<string, unknown> {
    if (typeof progress !== 'object' || progress === null) {
      return {};
    }

    const sanitizedProgress: Record<string, unknown> = {};
    const progressObj = progress as Record<string, unknown>;

    for (const [key, value] of Object.entries(progressObj)) {
      if (!this.config.allowedProgressKeys.includes(key)) {
        continue;
      }

      switch (key) {
        case 'showSteps':
        case 'showPercentage':
          if (typeof value === 'boolean') {
            sanitizedProgress[key] = value;
          }
          break;
        case 'className':
          if (typeof value === 'string') {
            sanitizedProgress[key] = value;
          }
          break;
      }
    }

    return sanitizedProgress;
  }

  private validateFormOptions(formOptions: unknown): Record<string, unknown> {
    if (typeof formOptions !== 'object' || formOptions === null) {
      return {};
    }

    const sanitizedOptions: Record<string, unknown> = {};
    const optionsObj = formOptions as Record<string, unknown>;

    for (const [key, value] of Object.entries(optionsObj)) {
      if (!this.config.allowedFormOptionsKeys.includes(key)) {
        continue;
      }

      switch (key) {
        case 'defaultValues':
          if (typeof value === 'object' && value !== null) {
            sanitizedOptions[key] = value;
          }
          break;
        case 'asyncDebounceMs':
          if (typeof value === 'number' && !isNaN(value)) {
            sanitizedOptions[key] = value;
          }
          break;
        case 'canSubmitWhenInvalid':
          if (typeof value === 'boolean') {
            sanitizedOptions[key] = value;
          }
          break;
      }
    }

    return sanitizedOptions;
  }

  private createFormedibleConfig(sanitized: Record<string, unknown>): { 
    schema: z.ZodSchema<Record<string, unknown>>; 
    formOptions: UseFormedibleOptions<Record<string, unknown>>; 
  } {
    const fields = sanitized.fields as FieldConfig[] || [];
    const schema = this.createSchemaFromFields(fields);
    const defaultValues = this.createDefaultValues(fields);

    const formOptions: UseFormedibleOptions<Record<string, unknown>> = {
      fields,
      schema,
      pages: sanitized.pages as UseFormedibleOptions<Record<string, unknown>>['pages'],
      progress: sanitized.progress as UseFormedibleOptions<Record<string, unknown>>['progress'],
      submitLabel: sanitized.submitLabel as string,
      nextLabel: sanitized.nextLabel as string,
      previousLabel: sanitized.previousLabel as string,
      formClassName: sanitized.formClassName as string,
      fieldClassName: sanitized.fieldClassName as string,
      formOptions: {
        defaultValues,
        ...(sanitized.formOptions as Record<string, unknown>),
      },
    };

    return { schema, formOptions };
  }

  private createSchemaFromFields(fields: FieldConfig[]): z.ZodSchema<Record<string, unknown>> {
    const schemaObj: Record<string, z.ZodTypeAny> = {};

    fields.forEach(field => {
      switch (field.type) {
        case 'text':
        case 'email':
        case 'password':
        case 'url':
        case 'tel':
        case 'textarea':
        case 'phone':
        case 'colorPicker':
        case 'autocomplete':
        case 'masked':
        case 'radio':
        case 'select':
          schemaObj[field.name] = z.string();
          break;
        case 'number':
        case 'slider':
        case 'rating':
          schemaObj[field.name] = z.number();
          break;
        case 'checkbox':
        case 'switch':
          schemaObj[field.name] = z.boolean();
          break;
        case 'date':
          schemaObj[field.name] = z.date();
          break;
        case 'file':
          schemaObj[field.name] = z.any();
          break;
        case 'location':
          schemaObj[field.name] = z.object({
            lat: z.number(),
            lng: z.number(),
            address: z.string().optional(),
            city: z.string().optional(),
            country: z.string().optional(),
          }).optional();
          break;
        case 'duration':
          schemaObj[field.name] = z.object({
            hours: z.number().min(0),
            minutes: z.number().min(0),
          }).optional();
          break;
        case 'multiSelect':
          schemaObj[field.name] = z.array(z.string());
          break;
        case 'object':
          schemaObj[field.name] = z.any();
          break;
        case 'array':
          schemaObj[field.name] = z.array(z.any());
          break;
        default:
          schemaObj[field.name] = z.string();
      }
    });

    return z.object(schemaObj);
  }

  private createDefaultValues(fields: FieldConfig[]): Record<string, unknown> {
    const defaults: Record<string, unknown> = {};

    fields.forEach(field => {
      switch (field.type) {
        case 'text':
        case 'email':
        case 'password':
        case 'url':
        case 'tel':
        case 'textarea':
        case 'phone':
        case 'autocomplete':
        case 'masked':
          defaults[field.name] = '';
          break;
        case 'number':
        case 'slider':
          defaults[field.name] = field.min || 0;
          break;
        case 'checkbox':
        case 'switch':
          defaults[field.name] = false;
          break;
        case 'date':
          defaults[field.name] = new Date().toISOString().split('T')[0];
          break;
        case 'select':
        case 'radio':
          if (field.options && Array.isArray(field.options) && field.options.length > 0) {
            const firstOption = field.options[0];
            defaults[field.name] = typeof firstOption === 'string' ? firstOption : (firstOption as { value: string }).value;
          } else {
            defaults[field.name] = '';
          }
          break;
        case 'file':
          defaults[field.name] = null;
          break;
        case 'rating':
          defaults[field.name] = 1;
          break;
        case 'colorPicker':
          defaults[field.name] = '#000000';
          break;
        case 'location':
          defaults[field.name] = undefined;
          break;
        case 'duration':
          defaults[field.name] = { hours: 0, minutes: 0 };
          break;
        case 'multiSelect':
          defaults[field.name] = [];
          break;
        case 'object':
          defaults[field.name] = {};
          break;
        case 'array':
          defaults[field.name] = [];
          break;
        default:
          defaults[field.name] = '';
      }
    });

    return defaults;
  }
}

export function parseAiToFormedible(
  code: string, 
  config?: AiParserConfig
): AiFormParseResult {
  const parser = new AiFormedibleParser(config);
  return parser.parse(code);
}

const AiFormRendererComponent: React.FC<AiFormRendererProps> = ({
  code,
  isStreaming = false,
  onParseComplete,
  onSubmit,
  className,
  parserConfig,
  debug = false,
}) => {
  const [parseResult, setParseResult] = useState<AiFormParseResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const parseFormDefinition = useCallback(async () => {
    if (!code.trim() || isStreaming) return;

    setIsLoading(true);

    try {
      const result = parseAiToFormedible(code, parserConfig);
      setParseResult(result);
      onParseComplete?.(result);

      if (debug && result.error) {
        console.error('AI Form Parser Error:', result.error);
      }
    } catch (err) {
      const errorResult: AiFormParseResult = {
        schema: z.object({}),
        formOptions: { fields: [], formOptions: { defaultValues: {} } },
        success: false,
        error: err instanceof Error ? err.message : String(err),
      };
      setParseResult(errorResult);
      onParseComplete?.(errorResult);

      if (debug) {
        console.error('AI Form Renderer Error:', err);
      }
    } finally {
      setIsLoading(false);
    }
  }, [code, isStreaming, onParseComplete, parserConfig, debug]);

  useEffect(() => {
    if (!isStreaming && code.trim()) {
      parseFormDefinition();
    }
  }, [code, isStreaming, parseFormDefinition]);

  const formConfig = useMemo(() => {
    if (!parseResult?.success || !parseResult.formOptions.fields?.length) {
      return null;
    }

    return {
      ...parseResult.formOptions,
      formOptions: {
        ...parseResult.formOptions.formOptions,
        onSubmit: async ({ value }: { value: Record<string, unknown> }) => {
          await onSubmit?.(value);
        },
      },
    };
  }, [parseResult, onSubmit]);

  const formedibleResult = useFormedible(formConfig || {
    fields: [],
    formOptions: { defaultValues: {} }
  });

  const renderedForm = useMemo(() => {
    if (isLoading) {
      return (
        <div className="flex items-center justify-center p-8">
          <div className="text-sm text-muted-foreground">Parsing form...</div>
        </div>
      );
    }

    if (parseResult && !parseResult.success) {
      return (
        <div className="p-4 border border-destructive/20 bg-destructive/10 rounded-md">
          <div className="text-sm text-destructive">
            <div className="font-medium">Parse Error</div>
            <div className="text-xs mt-1 opacity-80">{parseResult.error}</div>
          </div>
        </div>
      );
    }

    if (!formConfig || !parseResult?.success) {
      return null;
    }

    return <formedibleResult.Form />;
  }, [isLoading, parseResult, formConfig, formedibleResult]);

  return (
    <div className={className}>
      {renderedForm}
    </div>
  );
};

export const AiFormRenderer = memo(AiFormRendererComponent);

"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { History, MessageSquare, Trash2, Download, Plus } from "lucide-react";
import { cn } from "@/lib/utils";
import type { Message } from "./chat-interface";

export interface Conversation {
  id: string;
  title: string;
  messages: Message[];
  createdAt: Date;
  updatedAt: Date;
}

export interface ConversationHistoryProps {
  conversations: Conversation[];
  currentConversationId?: string;
  onSelectConversation: (conversation: Conversation) => void;
  onDeleteConversation: (conversationId: string) => void;
  onNewConversation?: () => void;
  onExportConversation?: (conversation: Conversation) => void;
  className?: string;
}

export function ConversationHistory({
  conversations,
  currentConversationId,
  onSelectConversation,
  onDeleteConversation,
  onNewConversation,
  onExportConversation,
  className,
}: ConversationHistoryProps) {

  const formatDate = (date: Date) => {
    const now = new Date();
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);

    if (diffInHours < 1) {
      return "Just now";
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)}h ago`;
    } else {
      return date.toLocaleDateString();
    }
  };

  const getConversationTitle = (conversation: Conversation) => {
    if (conversation.title) return conversation.title;

    // Generate title from first user message
    const firstUserMessage = conversation.messages?.find(m => m.role === "user");
    if (firstUserMessage?.content) {
      const text = firstUserMessage.content;
      return text.length > 50 ? text.substring(0, 50) + "..." : text;
    }

    return "New Conversation";
  };

  return (
    <Card className={cn("h-full", className)}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <History className="h-5 w-5" />
            Conversation History
            <Badge variant="secondary">
              {conversations.length}
            </Badge>
          </CardTitle>
          {onNewConversation && (
            <Button
              variant="outline"
              size="sm"
              onClick={onNewConversation}
              className="h-8 w-8 p-0"
            >
              <Plus className="h-4 w-4" />
              <span className="sr-only">New conversation</span>
            </Button>
          )}
        </div>
      </CardHeader>
      <CardContent className="p-4">
        <div className="h-[300px] overflow-y-auto">
          {conversations.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full text-center text-muted-foreground py-8">
              <MessageSquare className="h-8 w-8 mb-2 opacity-50" />
              <p className="text-sm">No conversations yet</p>
              <p className="text-xs">Start a chat to see your history</p>
            </div>
          ) : (
            <div className="space-y-2">
              {conversations.map((conversation) => (
                <div
                  key={conversation.id}
                  className={cn(
                    "group flex items-center gap-2 p-2 rounded-lg border transition-colors hover:bg-muted/50 cursor-pointer",
                    currentConversationId === conversation.id && "bg-muted border-primary"
                  )}
                  onClick={() => onSelectConversation(conversation)}
                >
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <MessageSquare className="h-3 w-3 text-muted-foreground shrink-0" />
                      <span className="text-xs text-muted-foreground">
                        {formatDate(conversation.updatedAt)}
                      </span>
                    </div>
                    <p className="text-sm font-medium truncate">
                      {getConversationTitle(conversation)}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {conversation.messages?.length || 0} messages
                    </p>
                  </div>
                  <div className="flex gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                    {onExportConversation && (
                      <Button
                        type="button"
                        variant="ghost"
                        size="icon"
                        className="h-6 w-6"
                        onClick={(e) => {
                          e.stopPropagation();
                          onExportConversation(conversation);
                        }}
                      >
                        <Download className="h-3 w-3" />
                      </Button>
                    )}
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      className="h-6 w-6 text-destructive hover:text-destructive"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteConversation(conversation.id);
                      }}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}
