"use client";

import { useState, useCallback, useEffect } from "react";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { ProviderSelection, type ProviderConfig } from "./provider-selection";
import { ChatInterface } from "./chat-interface";
import { FormPreview } from "./form-preview";
import { ConversationHistory, type Conversation } from "./conversation-history";
import { SidebarIcons, type SidebarView } from "./sidebar-icons";
import { SidebarContent } from "./sidebar-content";
import { Button } from "@/components/ui/button";
import { Sparkles, ChevronDown, ChevronUp, Settings, History, ChevronLeft, ChevronRight, Eye, FileText } from "lucide-react";
import { cn } from "@/lib/utils";
import type { Message } from "./chat-interface";
import { extractFormsFromMessages } from "@/lib/form-extraction-utils";
import { toast } from "sonner";

// Create a client instance outside the component to avoid recreation
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 5 * 60 * 1000, // 5 minutes
      gcTime: 10 * 60 * 1000, // 10 minutes
      retry: 1,
    },
  },
});

export type AIBuilderMode = "direct" | "backend";

export interface BackendConfig {
  endpoint: string;
  headers?: Record<string, string>;
}

export interface AIBuilderProps {
  className?: string;
  onFormGenerated?: (formCode: string) => void;
  onFormSubmit?: (formData: Record<string, unknown>) => void;
  mode?: AIBuilderMode;
  backendConfig?: BackendConfig;
}

const STORAGE_KEYS = {
  PROVIDER_CONFIG: "formedible-ai-builder-provider-config",
  CONVERSATIONS: "formedible-ai-builder-conversations",
  UI_STATE: "formedible-ai-builder-ui-state",
};

function AIBuilderCore({ className, onFormGenerated, onFormSubmit, mode = "direct", backendConfig }: AIBuilderProps) {
  const [providerConfig, setProviderConfig] = useState<ProviderConfig | null>(null);
  const [generatedForms, setGeneratedForms] = useState<Array<{ id: string; code: string; timestamp: Date }>>([]);
  const [currentFormIndex, setCurrentFormIndex] = useState<number>(0);
  const [isGenerating, setIsGenerating] = useState(false);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [currentConversationId, setCurrentConversationId] = useState<string | undefined>();
  const [currentMessages, setCurrentMessages] = useState<Message[]>([]);
  const [isTopSectionCollapsed, setIsTopSectionCollapsed] = useState(false);
  const [activeView, setActiveView] = useState<SidebarView | null>(null);
  const [isCollapsed, setIsCollapsed] = useState(false);

  // Check if AI service is properly configured based on mode
  const isProviderConfigured = useCallback(() => {
    if (mode === "backend") {
      // For backend mode, just check that backend config is provided
      return !!(backendConfig && backendConfig.endpoint && backendConfig.endpoint.trim().length > 0);
    }

    // For direct mode, check provider configuration
    if (!providerConfig) return false;

    // Check if provider requires API key
    const providersRequiringKey = ["openai", "anthropic", "google", "mistral", "openrouter"];
    if (providersRequiringKey.includes(providerConfig.provider as string)) {
      return !!(providerConfig.apiKey && providerConfig.apiKey.trim().length > 0);
    }

    // For openai-compatible, endpoint is required
    if (providerConfig.provider === "openai-compatible") {
      return !!(providerConfig.endpoint && providerConfig.endpoint.trim().length > 0);
    }

    return true;
  }, [mode, backendConfig, providerConfig]);

  // Load saved data on mount
  useEffect(() => {
    try {
      // Load provider config
      const savedConfig = localStorage.getItem(STORAGE_KEYS.PROVIDER_CONFIG);
      if (savedConfig) {
        setProviderConfig(JSON.parse(savedConfig));
      }

      // Load conversations
      const savedConversations = localStorage.getItem(STORAGE_KEYS.CONVERSATIONS);
      let loadedConversations: Conversation[] = [];
      if (savedConversations) {
        const parsedConversations = JSON.parse(savedConversations);
        loadedConversations = parsedConversations.map((c: any) => ({
          ...c,
          createdAt: new Date(c.createdAt),
          updatedAt: new Date(c.updatedAt),
        }));
        setConversations(loadedConversations);
      }

      // Load UI state
      const savedUIState = localStorage.getItem(STORAGE_KEYS.UI_STATE);
      if (savedUIState) {
        const uiState = JSON.parse(savedUIState);
        setIsTopSectionCollapsed(uiState.isTopSectionCollapsed || false);

        // Restore current conversation if it exists
        if (uiState.currentConversationId && loadedConversations.length > 0) {
          const currentConversation = loadedConversations.find(c => c.id === uiState.currentConversationId);
          if (currentConversation) {
            setCurrentConversationId(uiState.currentConversationId);
            setCurrentMessages(currentConversation.messages);

            // Extract forms from conversation messages using shared utility
            const extractedForms = extractFormsFromMessages(
              currentConversation.messages,
              currentConversation.id,
              new Date(currentConversation.updatedAt)
            );

            // Restore generated forms
            setGeneratedForms(extractedForms);
            setCurrentFormIndex(0);
          }
        }
      }
    } catch (error) {
      console.warn("Failed to load saved AI Builder data:", error);
    }
  }, []);

  // Save provider config when it changes
  useEffect(() => {
    if (providerConfig) {
      try {
        localStorage.setItem(STORAGE_KEYS.PROVIDER_CONFIG, JSON.stringify(providerConfig));
      } catch (error) {
        console.warn("Failed to save provider config:", error);
      }
    }
  }, [providerConfig]);

  // Save conversations when they change
  useEffect(() => {
    try {
      localStorage.setItem(STORAGE_KEYS.CONVERSATIONS, JSON.stringify(conversations));
    } catch (error) {
      console.warn("Failed to save conversations:", error);
    }
  }, [conversations]);

  // Save UI state when it changes
  useEffect(() => {
    try {
      const uiState = {
        isTopSectionCollapsed,
        currentConversationId,
      };
      localStorage.setItem(STORAGE_KEYS.UI_STATE, JSON.stringify(uiState));
    } catch (error) {
      console.warn("Failed to save UI state:", error);
    }
  }, [isTopSectionCollapsed, currentConversationId]);

  const handleProviderConfigChange = useCallback((config: ProviderConfig | null) => {
    setProviderConfig(config);
  }, []);

  const handleFormGenerated = useCallback((formCode: string) => {
    const newForm = {
      id: `form_${Date.now()}`,
      code: formCode,
      timestamp: new Date()
    };

    setGeneratedForms(prev => {
      const updated = [...prev, newForm];
      setCurrentFormIndex(updated.length - 1); // Auto-navigate to newest form
      return updated;
    });

    onFormGenerated?.(formCode);
  }, [onFormGenerated]);

  const handleStreamingStateChange = useCallback((streaming: boolean) => {
    setIsGenerating(streaming);
  }, []);

  const handleFormSubmit = useCallback((formData: Record<string, unknown>) => {
    console.log("Form submitted:", formData);
    onFormSubmit?.(formData);
  }, [onFormSubmit]);

  const handleFormIndexChange = useCallback((index: number) => {
    setCurrentFormIndex(index);
  }, []);

  const handleDeleteForm = useCallback((index: number) => {
    setGeneratedForms(prev => {
      const updated = prev.filter((_, i) => i !== index);
      // Adjust current index if necessary
      if (index <= currentFormIndex && currentFormIndex > 0) {
        setCurrentFormIndex(currentFormIndex - 1);
      } else if (index < currentFormIndex) {
        // No change needed to currentFormIndex
      } else if (updated.length === 0) {
        setCurrentFormIndex(0);
      } else if (currentFormIndex >= updated.length) {
        setCurrentFormIndex(updated.length - 1);
      }
      return updated;
    });
  }, [currentFormIndex]);

  const handleConversationUpdate = useCallback((messages: Message[], isStreamEnd: boolean = false) => {
    // Check if provider is configured before allowing conversation updates
    if (!isProviderConfigured()) {
      toast.error("Please configure your AI provider first", {
        description: "You need to set up your API key in the settings before you can chat."
      });
      return;
    }

    // Always update current messages for UI
    setCurrentMessages(messages);

    // Handle conversation creation/update
    if (messages.length > 0) {
      if (!currentConversationId) {
        // Create new conversation when first message is sent
        const newConversationId = `conversation_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

        setConversations(prev => {
          // Double check we don't already have this conversation (race condition protection)
          const exists = prev.some(conv => conv.id === newConversationId);
          if (exists) return prev;

          const newConversation: Conversation = {
            id: newConversationId,
            title: "", // Will be generated from first message
            messages,
            createdAt: new Date(),
            updatedAt: new Date(),
          };

          return [...prev, newConversation];
        });
        setCurrentConversationId(newConversationId);
      } else {
        // Update existing conversation with new messages
        setConversations(prev => prev.map(conv => 
          conv.id === currentConversationId 
            ? { ...conv, messages, updatedAt: new Date() }
            : conv
        ));
      }
    }
  }, [currentConversationId, isProviderConfigured]);

  const handleNewConversation = useCallback(() => {
    setCurrentConversationId(undefined);
    setCurrentMessages([]);
    // Clear forms when starting new conversation
    setGeneratedForms([]);
    setCurrentFormIndex(0);
  }, []);

  // Remove automatic conversation creation

  const handleSelectConversation = useCallback((conversation: Conversation) => {
    setCurrentConversationId(conversation.id);
    setCurrentMessages(conversation.messages);

    // Extract forms from conversation messages using shared utility
    const extractedForms = extractFormsFromMessages(
      conversation.messages,
      conversation.id,
      new Date(conversation.updatedAt)
    );

    console.log('Extracted forms from conversation:', extractedForms.length);

    // Update generated forms and reset index
    setGeneratedForms(extractedForms);
    setCurrentFormIndex(0);
  }, []);

  const handleDeleteConversation = useCallback((conversationId: string) => {
    setConversations(prev => prev.filter(c => c.id !== conversationId));
    if (currentConversationId === conversationId) {
      setCurrentConversationId(undefined);
      setCurrentMessages([]);
      // Clear forms when deleting current conversation
      setGeneratedForms([]);
      setCurrentFormIndex(0);
    }
  }, [currentConversationId]);

  const handleExportConversation = useCallback((conversation: Conversation) => {
    const dataStr = JSON.stringify(conversation, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = `conversation-${conversation.id}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  }, []);

  const handleToggleCollapse = useCallback(() => {
    setIsCollapsed(!isCollapsed);
  }, [isCollapsed]);

  const handleViewChange = useCallback((view: SidebarView | null) => {
    setActiveView(view);
    if (view && isCollapsed) {
      setIsCollapsed(false);
    }
  }, [isCollapsed]);



  return (
    <div className={cn("flex flex-col h-full overflow-hidden", className)}>
      <div className="flex items-center gap-2 px-1 pb-1 flex-shrink-0">
        <Sparkles className="h-4 w-4 text-primary" />
        <h1 className="text-lg font-bold text-foreground">AI Form Builder</h1>
      </div>


      {/* Main Layout: Sidebar + Content Area */}
      <div className="flex-1 flex min-h-0 overflow-hidden gap-2">
        {/* Sidebar */}
        <SidebarIcons
          isCollapsed={isCollapsed}
          onToggleCollapse={handleToggleCollapse}
          activeView={activeView}
          onViewChange={handleViewChange}
        />

        <SidebarContent
          activeView={activeView}
          isCollapsed={isCollapsed}
          conversations={conversations}
          currentConversationId={currentConversationId}
          onSelectConversation={handleSelectConversation}
          onDeleteConversation={handleDeleteConversation}
          onNewConversation={handleNewConversation}
          onExportConversation={handleExportConversation}
          providerConfig={providerConfig}
          onConfigChange={handleProviderConfigChange}
        />

        {/* Chat + Preview Grid */}
        <div className="flex-1 grid grid-cols-1 lg:grid-cols-2 gap-2 min-h-0 overflow-hidden">
          {/* Conditionally show provider form or chat interface */}
          {!isProviderConfigured() ? (
            <div className="flex flex-col h-full border-2 border-accent/30 rounded-lg shadow-lg bg-card">
              <div className="px-6 py-4 bg-gradient-to-r from-accent/10 to-transparent border-b">
                <h2 className="text-lg font-semibold text-foreground">Setup Required</h2>
                <p className="text-sm text-muted-foreground mt-1">
                  {mode === "backend" 
                    ? "Backend endpoint configuration is required"
                    : "Please configure your AI provider to start building forms"
                  }
                </p>
              </div>
              <div className="flex-1 overflow-y-auto">
                {mode === "direct" ? (
                  <ProviderSelection
                    onConfigChange={handleProviderConfigChange}
                    initialConfig={providerConfig}
                    className="h-full"
                  />
                ) : (
                  <div className="p-6">
                    <div className="text-center text-muted-foreground">
                      <h3 className="text-base font-medium mb-2">Backend Mode</h3>
                      <p className="text-sm">
                        {backendConfig?.endpoint 
                          ? `Endpoint: ${backendConfig.endpoint}`
                          : "No backend endpoint configured"
                        }
                      </p>
                      <p className="text-xs mt-4">
                        Backend configuration must be provided via props by the developer.
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <ChatInterface
              onFormGenerated={handleFormGenerated}
              onStreamingStateChange={handleStreamingStateChange}
              onConversationUpdate={handleConversationUpdate}
              onNewConversation={handleNewConversation}
              messages={currentMessages}
              providerConfig={providerConfig}
              mode={mode}
              backendConfig={backendConfig}
              className="h-full overflow-hidden"
            />
          )}

          <FormPreview
            forms={generatedForms}
            currentFormIndex={currentFormIndex}
            onFormIndexChange={handleFormIndexChange}
            onDeleteForm={handleDeleteForm}
            isStreaming={isGenerating}
            onFormSubmit={handleFormSubmit}
            className="h-full overflow-hidden"
          />
        </div>
      </div>
    </div>
  );
}

// Export the main component wrapped with QueryClientProvider
export function AIBuilder(props: AIBuilderProps) {
  return (
    <QueryClientProvider client={queryClient}>
      <AIBuilderCore {...props} />
    </QueryClientProvider>
  );
}

"use client";

import { useFormedible } from "@/hooks/use-formedible";
import { toast } from "sonner";

export type AIProvider =
  | "openai"
  | "anthropic"
  | "google"
  | "mistral"
  | "openrouter"
  | "openai-compatible";

export interface ProviderConfig {
  provider: AIProvider;
  apiKey: string;
  model?: string;
  temperature?: number;
  maxTokens?: number;
  endpoint?: string;
}

export interface ProviderSelectionProps {
  onConfigChange: (config: ProviderConfig | null) => void;
  initialConfig?: ProviderConfig | null;
  className?: string;
}

const PROVIDERS = [
  { value: "openai", label: "OpenAI", requiresKey: true },
  { value: "anthropic", label: "Anthropic", requiresKey: true },
  { value: "google", label: "Google Gemini", requiresKey: true },
  { value: "mistral", label: "Mistral", requiresKey: true },
  { value: "openrouter", label: "OpenRouter", requiresKey: true },
  {
    value: "openai-compatible",
    label: "OpenAI Compatible",
    requiresKey: false,
  },
] as const;

export function ProviderSelection({
  onConfigChange,
  initialConfig,
  className,
}: ProviderSelectionProps) {
  const { Form } = useFormedible({
    fields: [
      {
        name: "provider",
        type: "select",
        label: "Provider",
        placeholder: "Select provider...",
        options: PROVIDERS.map((p) => ({ value: p.value, label: p.label })),
      },
      {
        name: "apiKey",
        type: "password",
        label: "API Key",
        placeholder: "Enter your API key...",
        // conditional: (values) => {
        //   const provider = PROVIDERS.find(p => p.value === values.provider);
        //   return provider?.requiresKey || false;
        // },
        description:
          "Your API key is stored securely and never sent to our servers",
      },
      {
        name: "endpoint",
        type: "text",
        label: "API Endpoint",
        placeholder: "https://api.example.com/v1",
        conditional: (values) => values.provider === "openai-compatible",
        description: "Base URL for OpenAI-compatible API",
      },
    ],
    formOptions: {
      defaultValues: {
        provider: initialConfig?.provider,
        apiKey: initialConfig?.apiKey || "",
        endpoint: initialConfig?.endpoint || "",
      },
      onSubmit: async ({ value }) => {
        const config: ProviderConfig = {
          provider: value.provider as AIProvider,
          apiKey: value.apiKey || "",
          ...(value.endpoint && { endpoint: value.endpoint }),
        };

        // Validate configuration
        const providersRequiringKey = ["openai", "anthropic", "google", "mistral", "openrouter"];
        if (providersRequiringKey.includes(config.provider as string) && (!config.apiKey || config.apiKey.trim().length === 0)) {
          toast.error("API key required", {
            description: `Please enter a valid API key for ${config.provider}.`
          });
          return;
        }

        if (config.provider === "openai-compatible" && (!config.endpoint || config.endpoint.trim().length === 0)) {
          toast.error("Endpoint required", {
            description: "Please enter a valid API endpoint for OpenAI-compatible provider."
          });
          return;
        }

        onConfigChange(config);
        toast.success("Provider configured successfully!", {
          description: "You can now start building forms with AI."
        });
      },
    },
    submitLabel: "ðŸ’¾ Save Configuration",
    showSubmitButton: true,
    autoSubmitOnChange: false,
  });

  return (
    <div className={`p-4 space-y-4 ${className || ""}`}>
      <h3 className="text-base font-semibold">AI Provider</h3>
      <Form />
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import type { ProviderConfig } from "./provider-selection";
import type { AIBuilderMode, BackendConfig } from "./ai-builder";
import { ChatMessages } from "./chat-messages";
import type { Message } from "./chat-messages";


export type { Message } from "./chat-messages";

export interface ChatInterfaceProps {
  onFormGenerated?: (formCode: string) => void;
  onStreamingStateChange?: (isStreaming: boolean) => void;
  onConversationUpdate?: (messages: Message[], isStreamEnd?: boolean) => void;
  onNewConversation?: () => void;
  messages?: Message[];
  className?: string;
  providerConfig?: ProviderConfig | null;
  mode?: AIBuilderMode;
  backendConfig?: BackendConfig;
}

export function ChatInterface({
  onFormGenerated,
  onStreamingStateChange,
  onConversationUpdate,
  onNewConversation,
  messages: externalMessages,
  className,
  providerConfig,
  mode,
  backendConfig,
}: ChatInterfaceProps) {
  return (
    <ChatMessages
      onFormGenerated={onFormGenerated}
      onStreamingStateChange={onStreamingStateChange}
      onConversationUpdate={onConversationUpdate}
      onNewConversation={onNewConversation}
      messages={externalMessages}
      providerConfig={providerConfig}
      mode={mode}
      backendConfig={backendConfig}
      className={cn("h-full", className)}
    />
  );
}

"use client";

import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { FormPreviewBase } from "@/components/formedible/builder/form-preview-base";
import { SandpackPreview } from "./sandpack-preview";
import { Eye, ChevronLeft, ChevronRight, Trash2, Monitor, Play } from "lucide-react";
import { cn } from "@/lib/utils";

export interface GeneratedForm {
  id: string;
  code: string;
  timestamp: Date;
}

export type PreviewMode = "static" | "live";

const PREVIEW_MODE_STORAGE_KEY = "formedible-preview-mode";

export interface FormPreviewProps {
  forms: GeneratedForm[];
  currentFormIndex: number;
  onFormIndexChange: (index: number) => void;
  onDeleteForm?: (index: number) => void;
  isStreaming?: boolean;
  onFormSubmit?: (formData: Record<string, unknown>) => void;
  className?: string;
  /** Allow toggling between preview modes */
  enableModeToggle?: boolean;
  /** Default preview mode */
  defaultPreviewMode?: PreviewMode;
}

export function FormPreview({ 
  forms, 
  currentFormIndex, 
  onFormIndexChange,
  onDeleteForm,
  isStreaming = false, 
  onFormSubmit, 
  className,
  enableModeToggle = true,
  defaultPreviewMode = "static"
}: FormPreviewProps) {
  const currentForm = forms[currentFormIndex];

  // Preview mode state with localStorage persistence
  const [previewMode, setPreviewMode] = useState<PreviewMode>(defaultPreviewMode);

  // Load preview mode from localStorage on mount
  useEffect(() => {
    if (typeof window !== "undefined") {
      const savedMode = localStorage.getItem(PREVIEW_MODE_STORAGE_KEY) as PreviewMode;
      if (savedMode === "static" || savedMode === "live") {
        setPreviewMode(savedMode);
      }
    }
  }, []);

  // Persist preview mode to localStorage
  const handlePreviewModeChange = (mode: PreviewMode) => {
    setPreviewMode(mode);
    if (typeof window !== "undefined") {
      localStorage.setItem(PREVIEW_MODE_STORAGE_KEY, mode);
    }
  };

  if (forms.length === 0 && !isStreaming) {
    return (
      <FormPreviewBase 
        className={className}
        title=""
        emptyStateMessage="Start a conversation in the chat to generate your first form. The preview will appear here once the AI creates your form."
      />
    );
  }

  return (
    <Card className={cn("flex flex-col h-full", className)}>
      <CardHeader className="px-3 pt-1 pb-0">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Eye className="h-5 w-5" />
            Preview
            {forms.length > 0 && (
              <span className="text-sm font-normal text-muted-foreground">
                ({currentFormIndex + 1} of {forms.length})
              </span>
            )}
          </CardTitle>

          <div className="flex items-center gap-2">
            {enableModeToggle && (
              <div className="flex items-center gap-2 mr-2">
                <Tooltip>
                  <TooltipTrigger asChild>
                    <div className="flex items-center gap-2 text-sm">
                      <Monitor className="h-4 w-4 text-muted-foreground" />
                      <span className={cn("transition-colors", previewMode === "static" && "text-foreground font-medium")}>Static</span>
                    </div>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>Static preview with current form design</p>
                  </TooltipContent>
                </Tooltip>

                <Switch
                  checked={previewMode === "live"}
                  onCheckedChange={(checked) => handlePreviewModeChange(checked ? "live" : "static")}
                  aria-label="Toggle preview mode"
                  className="h-4 w-7"
                />

                <Tooltip>
                  <TooltipTrigger asChild>
                    <div className="flex items-center gap-2 text-sm">
                      <Play className="h-4 w-4 text-muted-foreground" />
                      <span className={cn("transition-colors", previewMode === "live" && "text-foreground font-medium")}>Live</span>
                    </div>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>Interactive live preview with code execution</p>
                  </TooltipContent>
                </Tooltip>
              </div>
            )}

            {forms.length > 1 && (
            <div className="flex items-center gap-1">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onFormIndexChange(Math.max(0, currentFormIndex - 1))}
                disabled={currentFormIndex === 0}
                className="h-8 w-8 p-0"
              >
                <ChevronLeft className="h-4 w-4" />
                <span className="sr-only">Previous form</span>
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={() => onFormIndexChange(Math.min(forms.length - 1, currentFormIndex + 1))}
                disabled={currentFormIndex === forms.length - 1}
                className="h-8 w-8 p-0"
              >
                <ChevronRight className="h-4 w-4" />
                <span className="sr-only">Next form</span>
              </Button>

              {onDeleteForm && forms.length > 1 && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onDeleteForm(currentFormIndex)}
                  className="h-8 w-8 p-0 text-destructive hover:text-destructive"
                >
                  <Trash2 className="h-4 w-4" />
                  <span className="sr-only">Delete form</span>
                </Button>
              )}
            </div>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="flex-1 overflow-hidden">
        {previewMode === "static" ? (
          <FormPreviewBase 
            formCode={currentForm?.code}
            isStreaming={isStreaming && !currentForm}
            onFormSubmit={onFormSubmit}
            className="border-0 h-full"
          />
        ) : (
          <SandpackPreview
            formCode={currentForm?.code}
            onFormSubmit={onFormSubmit}
            className="h-full"
            height="100%"
            isLoading={isStreaming && !currentForm}
            showCodeEditor={false}
            showConsole={false}
            showFileExplorer={false}
          />
        )}
      </CardContent>
    </Card>
  );
}

"use client";
import React, { useState, useEffect, useMemo, useCallback, memo } from "react";
import { z } from "zod";
import { useFormedible } from "@/hooks/use-formedible";
import type { FieldConfig, UseFormedibleOptions } from "@/lib/formedible/types";

export interface AiFormParseResult<TFormData = Record<string, unknown>> {
  schema: z.ZodSchema<TFormData>;
  formOptions: UseFormedibleOptions<TFormData>;
  success: boolean;
  error?: string;
}

export interface AiParserConfig {
  allowedFieldTypes?: string[];
  allowedKeys?: string[];
  allowedFieldKeys?: string[];
  allowedPageKeys?: string[];
  allowedProgressKeys?: string[];
  allowedFormOptionsKeys?: string[];
}

export interface AiFormRendererProps {
  code: string;
  isStreaming?: boolean;
  onParseComplete?: (result: AiFormParseResult) => void;
  onSubmit?: (formData: Record<string, unknown>) => void | Promise<void>;
  className?: string;
  parserConfig?: AiParserConfig;
  debug?: boolean;
}

const DEFAULT_FIELD_TYPES = [
  'text', 'email', 'password', 'url', 'tel', 'textarea', 'select', 
  'checkbox', 'switch', 'number', 'date', 'slider', 'file', 'rating',
  'phone', 'colorPicker', 'location', 'duration', 'multiSelect',
  'autocomplete', 'masked', 'object', 'array', 'radio'
];

const DEFAULT_KEYS = [
  'schema', 'fields', 'pages', 'progress', 'submitLabel', 'nextLabel', 
  'previousLabel', 'formClassName', 'fieldClassName', 'formOptions'
];

const DEFAULT_FIELD_KEYS = [
  'name', 'type', 'label', 'placeholder', 'description', 'options', 
  'min', 'max', 'step', 'accept', 'multiple', 'page', 'conditional',
  'section', 'ratingConfig', 'phoneConfig', 'colorConfig', 'locationConfig',
  'durationConfig', 'multiSelectConfig', 'sliderConfig', 'numberConfig',
  'dateConfig', 'fileConfig', 'textareaConfig', 'passwordConfig', 'emailConfig',
  'autocompleteConfig', 'maskedInputConfig', 'objectConfig', 'arrayConfig',
  'validation', 'group', 'tab', 'help', 'inlineValidation', 'datalist'
];

const DEFAULT_PAGE_KEYS = ['page', 'title', 'description'];

const DEFAULT_PROGRESS_KEYS = ['showSteps', 'showPercentage', 'className'];

const DEFAULT_FORM_OPTIONS_KEYS = [
  'defaultValues', 'asyncDebounceMs', 'canSubmitWhenInvalid', 'onSubmit', 'onSubmitInvalid'
];

class AiFormedibleParser {
  private config: Required<AiParserConfig>;

  constructor(config?: AiParserConfig) {
    this.config = {
      allowedFieldTypes: config?.allowedFieldTypes ?? DEFAULT_FIELD_TYPES,
      allowedKeys: config?.allowedKeys ?? DEFAULT_KEYS,
      allowedFieldKeys: config?.allowedFieldKeys ?? DEFAULT_FIELD_KEYS,
      allowedPageKeys: config?.allowedPageKeys ?? DEFAULT_PAGE_KEYS,
      allowedProgressKeys: config?.allowedProgressKeys ?? DEFAULT_PROGRESS_KEYS,
      allowedFormOptionsKeys: config?.allowedFormOptionsKeys ?? DEFAULT_FORM_OPTIONS_KEYS,
    };
  }

  parse(code: string): AiFormParseResult {
    try {
      const sanitizedCode = this.sanitizeCode(code);
      const parsed = this.parseObjectLiteral(sanitizedCode);
      const sanitized = this.validateAndSanitize(parsed);
      const { schema, formOptions } = this.createFormedibleConfig(sanitized);

      return {
        schema,
        formOptions,
        success: true,
      };
    } catch (error) {
      return {
        schema: z.object({}),
        formOptions: { fields: [], formOptions: { defaultValues: {} } },
        success: false,
        error: error instanceof Error ? error.message : String(error),
      };
    }
  }

  private sanitizeCode(code: string): string {
    let sanitized = code.replace(/\/\*[\s\S]*?\*\//g, '').replace(/\/\/.*$/gm, '');
    sanitized = sanitized.replace(/\b(eval|Function|setTimeout|setInterval|require|import)\s*\(/g, '');
    sanitized = sanitized.replace(/=>\s*{[^}]*}/g, '""');
    sanitized = sanitized.replace(/function\s*\([^)]*\)\s*{[^}]*}/g, '""');
    return sanitized;
  }

  private parseObjectLiteral(code: string): unknown {
    try {
      return JSON.parse(code);
    } catch {
      try {
        let processedCode = code.trim();
        processedCode = this.replaceZodExpressions(processedCode);
        processedCode = processedCode.replace(/([{,]\s*)([a-zA-Z_$][a-zA-Z0-9_$]*)\s*:/g, '$1"$2":');
        processedCode = processedCode.replace(/,(\s*[}\]])/g, '$1');
        processedCode = processedCode.replace(/'/g, '"');
        return JSON.parse(processedCode);
      } catch (conversionError) {
        throw new Error(`Invalid syntax: ${conversionError instanceof Error ? conversionError.message : String(conversionError)}`);
      }
    }
  }

  private replaceZodExpressions(code: string): string {
    let result = code;
    let changed = true;

    while (changed) {
      changed = false;
      const zodMatch = result.match(/z\.[a-zA-Z]+\(/);
      if (zodMatch) {
        const startIndex = zodMatch.index!;
        const openParenIndex = startIndex + zodMatch[0].length - 1;

        let depth = 1;
        let endIndex = openParenIndex + 1;

        while (endIndex < result.length && depth > 0) {
          if (result[endIndex] === '(') {
            depth++;
          } else if (result[endIndex] === ')') {
            depth--;
          }
          endIndex++;
        }

        if (depth === 0) {
          let chainEnd = endIndex;
          while (chainEnd < result.length) {
            const chainMatch = result.slice(chainEnd).match(/^\.[a-zA-Z]+\(/);
            if (chainMatch) {
              let chainDepth = 1;
              let chainParenIndex = chainEnd + chainMatch[0].length - 1;
              let chainEndIndex = chainParenIndex + 1;

              while (chainEndIndex < result.length && chainDepth > 0) {
                if (result[chainEndIndex] === '(') {
                  chainDepth++;
                } else if (result[chainEndIndex] === ')') {
                  chainDepth--;
                }
                chainEndIndex++;
              }

              if (chainDepth === 0) {
                chainEnd = chainEndIndex;
              } else {
                break;
              }
            } else {
              break;
            }
          }

          result = result.slice(0, startIndex) + '"__ZOD_SCHEMA__"' + result.slice(chainEnd);
          changed = true;
        } else {
          result = result.replace(/z\.[a-zA-Z]+/, '"__ZOD_SCHEMA__"');
          changed = true;
        }
      }
    }

    return result;
  }

  private validateAndSanitize(obj: unknown): Record<string, unknown> {
    if (typeof obj !== 'object' || obj === null) {
      throw new Error('Definition must be an object');
    }

    const sanitized: Record<string, unknown> = {};
    const input = obj as Record<string, unknown>;

    for (const [key, value] of Object.entries(input)) {
      if (!this.config.allowedKeys.includes(key)) {
        continue;
      }

      switch (key) {
        case 'schema':
          break;
        case 'fields':
          sanitized[key] = this.validateFields(value);
          break;
        case 'pages':
          sanitized[key] = this.validatePages(value);
          break;
        case 'progress':
          sanitized[key] = this.validateProgress(value);
          break;
        case 'formOptions':
          sanitized[key] = this.validateFormOptions(value);
          break;
        case 'submitLabel':
        case 'nextLabel':
        case 'previousLabel':
        case 'formClassName':
        case 'fieldClassName':
          if (typeof value === 'string') {
            sanitized[key] = value;
          }
          break;
      }
    }

    return sanitized;
  }

  private validateFields(fields: unknown): FieldConfig[] {
    if (!Array.isArray(fields)) {
      throw new Error('Fields must be an array');
    }

    return fields.map((field, index) => {
      if (typeof field !== 'object' || field === null) {
        throw new Error(`Field at index ${index} must be an object`);
      }

      const sanitizedField: Record<string, unknown> = {};
      const fieldObj = field as Record<string, unknown>;

      for (const [key, value] of Object.entries(fieldObj)) {
        if (!this.config.allowedFieldKeys.includes(key)) {
          continue;
        }

        switch (key) {
          case 'name':
            if (typeof value === 'string' && /^[a-zA-Z_$][a-zA-Z0-9_$]*$/.test(value)) {
              sanitizedField[key] = value;
            }
            break;
          case 'type':
            if (typeof value === 'string' && this.config.allowedFieldTypes.includes(value)) {
              sanitizedField[key] = value;
            }
            break;
          case 'label':
          case 'placeholder':
          case 'description':
          case 'accept':
            if (typeof value === 'string') {
              sanitizedField[key] = value;
            }
            break;
          case 'options':
            if (Array.isArray(value)) {
              sanitizedField[key] = value.filter(opt => 
                typeof opt === 'string' || 
                (typeof opt === 'object' && opt !== null && 
                 typeof (opt as { value?: unknown }).value === 'string' && 
                 typeof (opt as { label?: unknown }).label === 'string')
              );
            }
            break;
          case 'min':
          case 'max':
          case 'step':
          case 'page':
            if (typeof value === 'number' && !isNaN(value)) {
              sanitizedField[key] = value;
            }
            break;
          case 'multiple':
          case 'conditional':
            if (typeof value === 'boolean') {
              sanitizedField[key] = value;
            }
            break;
          default:
            if (typeof value === 'object' && value !== null) {
              sanitizedField[key] = value;
            }
            break;
        }
      }

      if (!sanitizedField.name || !sanitizedField.type) {
        throw new Error(`Field at index ${index} must have 'name' and 'type' properties`);
      }

      return sanitizedField as unknown as FieldConfig;
    });
  }

  private validatePages(pages: unknown): Array<{ page: number; title?: string; description?: string }> {
    if (!Array.isArray(pages)) {
      return [];
    }

    return pages.map(page => {
      if (typeof page !== 'object' || page === null) {
        return null;
      }

      const sanitizedPage: Record<string, unknown> = {};
      const pageObj = page as Record<string, unknown>;

      for (const [key, value] of Object.entries(pageObj)) {
        if (!this.config.allowedPageKeys.includes(key)) {
          continue;
        }

        switch (key) {
          case 'page':
            if (typeof value === 'number' && !isNaN(value)) {
              sanitizedPage[key] = value;
            }
            break;
          case 'title':
          case 'description':
            if (typeof value === 'string') {
              sanitizedPage[key] = value;
            }
            break;
        }
      }

      return sanitizedPage.page ? sanitizedPage : null;
    }).filter(Boolean) as Array<{ page: number; title?: string; description?: string }>;
  }

  private validateProgress(progress: unknown): Record<string, unknown> {
    if (typeof progress !== 'object' || progress === null) {
      return {};
    }

    const sanitizedProgress: Record<string, unknown> = {};
    const progressObj = progress as Record<string, unknown>;

    for (const [key, value] of Object.entries(progressObj)) {
      if (!this.config.allowedProgressKeys.includes(key)) {
        continue;
      }

      switch (key) {
        case 'showSteps':
        case 'showPercentage':
          if (typeof value === 'boolean') {
            sanitizedProgress[key] = value;
          }
          break;
        case 'className':
          if (typeof value === 'string') {
            sanitizedProgress[key] = value;
          }
          break;
      }
    }

    return sanitizedProgress;
  }

  private validateFormOptions(formOptions: unknown): Record<string, unknown> {
    if (typeof formOptions !== 'object' || formOptions === null) {
      return {};
    }

    const sanitizedOptions: Record<string, unknown> = {};
    const optionsObj = formOptions as Record<string, unknown>;

    for (const [key, value] of Object.entries(optionsObj)) {
      if (!this.config.allowedFormOptionsKeys.includes(key)) {
        continue;
      }

      switch (key) {
        case 'defaultValues':
          if (typeof value === 'object' && value !== null) {
            sanitizedOptions[key] = value;
          }
          break;
        case 'asyncDebounceMs':
          if (typeof value === 'number' && !isNaN(value)) {
            sanitizedOptions[key] = value;
          }
          break;
        case 'canSubmitWhenInvalid':
          if (typeof value === 'boolean') {
            sanitizedOptions[key] = value;
          }
          break;
      }
    }

    return sanitizedOptions;
  }

  private createFormedibleConfig(sanitized: Record<string, unknown>): { 
    schema: z.ZodSchema<Record<string, unknown>>; 
    formOptions: UseFormedibleOptions<Record<string, unknown>>; 
  } {
    const fields = sanitized.fields as FieldConfig[] || [];
    const schema = this.createSchemaFromFields(fields);
    const defaultValues = this.createDefaultValues(fields);

    const formOptions: UseFormedibleOptions<Record<string, unknown>> = {
      fields,
      schema,
      pages: sanitized.pages as UseFormedibleOptions<Record<string, unknown>>['pages'],
      progress: sanitized.progress as UseFormedibleOptions<Record<string, unknown>>['progress'],
      submitLabel: sanitized.submitLabel as string,
      nextLabel: sanitized.nextLabel as string,
      previousLabel: sanitized.previousLabel as string,
      formClassName: sanitized.formClassName as string,
      fieldClassName: sanitized.fieldClassName as string,
      formOptions: {
        defaultValues,
        ...(sanitized.formOptions as Record<string, unknown>),
      },
    };

    return { schema, formOptions };
  }

  private createSchemaFromFields(fields: FieldConfig[]): z.ZodSchema<Record<string, unknown>> {
    const schemaObj: Record<string, z.ZodTypeAny> = {};

    fields.forEach(field => {
      switch (field.type) {
        case 'text':
        case 'email':
        case 'password':
        case 'url':
        case 'tel':
        case 'textarea':
        case 'phone':
        case 'colorPicker':
        case 'autocomplete':
        case 'masked':
        case 'radio':
        case 'select':
          schemaObj[field.name] = z.string();
          break;
        case 'number':
        case 'slider':
        case 'rating':
          schemaObj[field.name] = z.number();
          break;
        case 'checkbox':
        case 'switch':
          schemaObj[field.name] = z.boolean();
          break;
        case 'date':
          schemaObj[field.name] = z.date();
          break;
        case 'file':
          schemaObj[field.name] = z.any();
          break;
        case 'location':
          schemaObj[field.name] = z.object({
            lat: z.number(),
            lng: z.number(),
            address: z.string().optional(),
            city: z.string().optional(),
            country: z.string().optional(),
          }).optional();
          break;
        case 'duration':
          schemaObj[field.name] = z.object({
            hours: z.number().min(0),
            minutes: z.number().min(0),
          }).optional();
          break;
        case 'multiSelect':
          schemaObj[field.name] = z.array(z.string());
          break;
        case 'object':
          schemaObj[field.name] = z.any();
          break;
        case 'array':
          schemaObj[field.name] = z.array(z.any());
          break;
        default:
          schemaObj[field.name] = z.string();
      }
    });

    return z.object(schemaObj);
  }

  private createDefaultValues(fields: FieldConfig[]): Record<string, unknown> {
    const defaults: Record<string, unknown> = {};

    fields.forEach(field => {
      switch (field.type) {
        case 'text':
        case 'email':
        case 'password':
        case 'url':
        case 'tel':
        case 'textarea':
        case 'phone':
        case 'autocomplete':
        case 'masked':
          defaults[field.name] = '';
          break;
        case 'number':
        case 'slider':
          defaults[field.name] = field.min || 0;
          break;
        case 'checkbox':
        case 'switch':
          defaults[field.name] = false;
          break;
        case 'date':
          defaults[field.name] = new Date().toISOString().split('T')[0];
          break;
        case 'select':
        case 'radio':
          if (field.options && Array.isArray(field.options) && field.options.length > 0) {
            const firstOption = field.options[0];
            defaults[field.name] = typeof firstOption === 'string' ? firstOption : (firstOption as { value: string }).value;
          } else {
            defaults[field.name] = '';
          }
          break;
        case 'file':
          defaults[field.name] = null;
          break;
        case 'rating':
          defaults[field.name] = 1;
          break;
        case 'colorPicker':
          defaults[field.name] = '#000000';
          break;
        case 'location':
          defaults[field.name] = undefined;
          break;
        case 'duration':
          defaults[field.name] = { hours: 0, minutes: 0 };
          break;
        case 'multiSelect':
          defaults[field.name] = [];
          break;
        case 'object':
          defaults[field.name] = {};
          break;
        case 'array':
          defaults[field.name] = [];
          break;
        default:
          defaults[field.name] = '';
      }
    });

    return defaults;
  }
}

export function parseAiToFormedible(
  code: string, 
  config?: AiParserConfig
): AiFormParseResult {
  const parser = new AiFormedibleParser(config);
  return parser.parse(code);
}

const AiFormRendererComponent: React.FC<AiFormRendererProps> = ({
  code,
  isStreaming = false,
  onParseComplete,
  onSubmit,
  className,
  parserConfig,
  debug = false,
}) => {
  const [parseResult, setParseResult] = useState<AiFormParseResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const parseFormDefinition = useCallback(async () => {
    if (!code.trim() || isStreaming) return;

    setIsLoading(true);

    try {
      const result = parseAiToFormedible(code, parserConfig);
      setParseResult(result);
      onParseComplete?.(result);

      if (debug && result.error) {
        console.error('AI Form Parser Error:', result.error);
      }
    } catch (err) {
      const errorResult: AiFormParseResult = {
        schema: z.object({}),
        formOptions: { fields: [], formOptions: { defaultValues: {} } },
        success: false,
        error: err instanceof Error ? err.message : String(err),
      };
      setParseResult(errorResult);
      onParseComplete?.(errorResult);

      if (debug) {
        console.error('AI Form Renderer Error:', err);
      }
    } finally {
      setIsLoading(false);
    }
  }, [code, isStreaming, onParseComplete, parserConfig, debug]);

  useEffect(() => {
    if (!isStreaming && code.trim()) {
      parseFormDefinition();
    }
  }, [code, isStreaming, parseFormDefinition]);

  const formConfig = useMemo(() => {
    if (!parseResult?.success || !parseResult.formOptions.fields?.length) {
      return null;
    }

    return {
      ...parseResult.formOptions,
      formOptions: {
        ...parseResult.formOptions.formOptions,
        onSubmit: async ({ value }: { value: Record<string, unknown> }) => {
          await onSubmit?.(value);
        },
      },
    };
  }, [parseResult, onSubmit]);

  const formedibleResult = useFormedible(formConfig || {
    fields: [],
    formOptions: { defaultValues: {} }
  });

  const renderedForm = useMemo(() => {
    if (isLoading) {
      return (
        <div className="flex items-center justify-center p-8">
          <div className="text-sm text-muted-foreground">Parsing form...</div>
        </div>
      );
    }

    if (parseResult && !parseResult.success) {
      return (
        <div className="p-4 border border-destructive/20 bg-destructive/10 rounded-md">
          <div className="text-sm text-destructive">
            <div className="font-medium">Parse Error</div>
            <div className="text-xs mt-1 opacity-80">{parseResult.error}</div>
          </div>
        </div>
      );
    }

    if (!formConfig || !parseResult?.success) {
      return null;
    }

    return <formedibleResult.Form />;
  }, [isLoading, parseResult, formConfig, formedibleResult]);

  return (
    <div className={className}>
      {renderedForm}
    </div>
  );
};

export const AiFormRenderer = memo(AiFormRendererComponent);

"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { History, MessageSquare, Trash2, Download, Plus } from "lucide-react";
import { cn } from "@/lib/utils";
import type { Message } from "./chat-interface";

export interface Conversation {
  id: string;
  title: string;
  messages: Message[];
  createdAt: Date;
  updatedAt: Date;
}

export interface ConversationHistoryProps {
  conversations: Conversation[];
  currentConversationId?: string;
  onSelectConversation: (conversation: Conversation) => void;
  onDeleteConversation: (conversationId: string) => void;
  onNewConversation?: () => void;
  onExportConversation?: (conversation: Conversation) => void;
  className?: string;
}

export function ConversationHistory({
  conversations,
  currentConversationId,
  onSelectConversation,
  onDeleteConversation,
  onNewConversation,
  onExportConversation,
  className,
}: ConversationHistoryProps) {

  const formatDate = (date: Date) => {
    const now = new Date();
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);

    if (diffInHours < 1) {
      return "Just now";
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)}h ago`;
    } else {
      return date.toLocaleDateString();
    }
  };

  const getConversationTitle = (conversation: Conversation) => {
    if (conversation.title) return conversation.title;

    // Generate title from first user message
    const firstUserMessage = conversation.messages?.find(m => m.role === "user");
    if (firstUserMessage?.content) {
      const text = firstUserMessage.content;
      return text.length > 50 ? text.substring(0, 50) + "..." : text;
    }

    return "New Conversation";
  };

  return (
    <Card className={cn("h-full", className)}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <History className="h-5 w-5" />
            Conversation History
            <Badge variant="secondary">
              {conversations.length}
            </Badge>
          </CardTitle>
          {onNewConversation && (
            <Button
              variant="outline"
              size="sm"
              onClick={onNewConversation}
              className="h-8 w-8 p-0"
            >
              <Plus className="h-4 w-4" />
              <span className="sr-only">New conversation</span>
            </Button>
          )}
        </div>
      </CardHeader>
      <CardContent className="px-4 pt-0 pb-4">
        <div className="h-[300px] overflow-y-auto">
          {conversations.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full text-center text-muted-foreground py-8">
              <MessageSquare className="h-8 w-8 mb-2 opacity-50" />
              <p className="text-sm">No conversations yet</p>
              <p className="text-xs">Start a chat to see your history</p>
            </div>
          ) : (
            <div className="space-y-1">
              {conversations.map((conversation) => (
                <div
                  key={conversation.id}
                  className={cn(
                    "group flex items-center justify-between gap-2 px-2 py-1.5 rounded hover:bg-muted/50 cursor-pointer text-sm",
                    currentConversationId === conversation.id && "bg-muted"
                  )}
                  onClick={() => onSelectConversation(conversation)}
                >
                  <div className="flex-1 min-w-0">
                    <p className="font-medium truncate text-sm">
                      {getConversationTitle(conversation)}
                    </p>
                  </div>
                  <div className="flex gap-1 opacity-0 group-hover:opacity-100 transition-opacity shrink-0">
                    {onExportConversation && (
                      <Button
                        type="button"
                        variant="ghost"
                        size="icon"
                        className="h-5 w-5"
                        onClick={(e) => {
                          e.stopPropagation();
                          onExportConversation(conversation);
                        }}
                      >
                        <Download className="h-3 w-3" />
                      </Button>
                    )}
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      className="h-5 w-5 text-destructive hover:text-destructive"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteConversation(conversation.id);
                      }}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { useState, useRef, useEffect, useCallback } from "react";
import { streamText, type LanguageModel } from "ai";
