// MessageContent component to handle code blocks with syntax highlighting
interface MessageContentProps {
  content: string;
}

function MessageContent({ content }: MessageContentProps) {
  // Detect and render code blocks with syntax highlighting
  const codeBlockRegex = /```(\w+)?\s*\n([\s\S]*?)\n```/g;
  const parts = [];
  let lastIndex = 0;
  let match;

  while ((match = codeBlockRegex.exec(content)) !== null) {
    // Add text before code block
    if (match.index > lastIndex) {
      parts.push(
        <span
          key={`text-${lastIndex}`}
          className="whitespace-pre-wrap break-words leading-relaxed"
        >
          {content.slice(lastIndex, match.index)}
        </span>
      );
    }

    // Add code block with syntax highlighting
    const language = match[1] || "text";
    const code = match[2];
    parts.push(
      <div key={`code-${match.index}`} className="my-2">
        <CodeBlock
          code={code}
          language={language}
          showLineNumbers={false}
          showCopyButton={true}
          className="text-xs"
          darkMode={true}
          scrollable={false}
        />
      </div>
    );

    lastIndex = match.index + match[0].length;
  }

  // Add remaining text
  if (lastIndex < content.length) {
    parts.push(
      <span
        key={`text-${lastIndex}`}
        className="whitespace-pre-wrap break-words leading-relaxed"
      >
        {content.slice(lastIndex)}
      </span>
    );
  }

  return (
    <div className="space-y-1">
      {parts.length > 0 ? (
        parts
      ) : (
        <span className="whitespace-pre-wrap break-words leading-relaxed">
          {content}
        </span>
      )}
    </div>
  );
}

export interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
}

export interface ChatMessagesProps {
  onFormGenerated?: (formCode: string) => void;
  onStreamingStateChange?: (isStreaming: boolean) => void;
  onConversationUpdate?: (messages: Message[], isStreamEnd?: boolean) => void;
  onNewConversation?: () => void;
  messages?: Message[];
  className?: string;
  providerConfig?: ProviderConfig | null;
  mode?: AIBuilderMode;
  backendConfig?: BackendConfig;
}

export function ChatMessages({
  onFormGenerated,
  onStreamingStateChange,
  onConversationUpdate,
  onNewConversation,
  messages: externalMessages,
  className,
  providerConfig,
  mode = "direct",
  backendConfig,
}: ChatMessagesProps) {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState<Message[]>(externalMessages || []);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const [showScrollToBottom, setShowScrollToBottom] = useState(false);
  const messagesContainerRef = useRef<HTMLDivElement>(null);
  const abortControllerRef = useRef<AbortController | null>(null);

  // Remove the automatic conversation completion

  const scrollToBottom = useCallback(() => {
    const container = messagesContainerRef.current;
    if (container) {
      container.scrollTop = container.scrollHeight;
    }
  }, []);

  const handleScroll = useCallback(() => {
    if (!messagesContainerRef.current) return;

    const { scrollTop, scrollHeight, clientHeight } =
      messagesContainerRef.current;
    const isAtBottom = scrollHeight - scrollTop - clientHeight < 100;

    setShowScrollToBottom(!isAtBottom && messages.length > 0);
  }, [messages.length]);

  // Sync with external messages when switching conversations
  useEffect(() => {
    if (externalMessages !== undefined) {
      setMessages(externalMessages);
    }
  }, [externalMessages]);

  // Remove the useEffect that was causing multiple updates

  // Removed auto-scroll - let users control scrolling manually

  useEffect(() => {
    const container = messagesContainerRef.current;
    if (!container) return;

    container.addEventListener("scroll", handleScroll);
    handleScroll(); // Check initial state

    return () => container.removeEventListener("scroll", handleScroll);
  }, [handleScroll]);

  const createModel = (config: ProviderConfig): LanguageModel => {
    const { provider, apiKey, model, endpoint } = config;

    switch (provider) {
      case "openai": {
        const openaiProvider = createOpenAI({ apiKey });
        // Flagship latest; swap to "gpt-5-mini" if you prefer cost/latency.
        return openaiProvider(model || "gpt-5");
      }

      case "anthropic": {
        const anthropicProvider = createAnthropic({ apiKey });
        // Latest Sonnet generation
        // TODO : fix this shit ! -_-
        return anthropicProvider(
          model || "claude-sonnet-4"
        ) as unknown as LanguageModel;
      }

      case "google": {
        const googleProvider = createGoogleGenerativeAI({ apiKey });
        // Latest top reasoning model in Gemini API
        return googleProvider(model || "gemini-2.5-pro");
      }

      case "mistral": {
        const mistralProvider = createMistral({ apiKey });
        // Mistral Large 2 (GA, sometimes shown as mistral-large-2407)
        return mistralProvider(model || "mistral-large-2407");
      }

      case "openrouter": {
        const openrouterProvider = createOpenRouter({ apiKey });
        // OpenRouter must route to Kimi K2 per your requirement
        // Common IDs: "openrouter/kimiplus-k2" (preferred) or "moonshotai/moonshot-k2"
        return openrouterProvider.chat(
          model || "openrouter/kimiplus-k2"
        ) as LanguageModel;
      }

      case "openai-compatible": {
        if (!endpoint)
          throw new Error("Endpoint required for OpenAI-compatible providers");
        const openaiCompatible = createOpenAICompatible({
          name: "openai-compatible",
          baseURL: endpoint,
          ...(apiKey && { apiKey }),
        });
        // Use a modern widely supported baseline; adjust to your endpoint's catalog
        return openaiCompatible(model || "gpt-4.1-mini");
      }

      default:
        throw new Error("Unsupported provider");
    }
  };

  const isServiceConfigured = (): boolean => {
    if (mode === "backend") {
      return !!(backendConfig && backendConfig.endpoint && backendConfig.endpoint.trim().length > 0);
    }

    // For direct mode, check provider configuration
    if (!providerConfig) return false;

    // Check if provider requires API key
    const providersRequiringKey = ["openai", "anthropic", "google", "mistral", "openrouter"];
    if (providersRequiringKey.includes(providerConfig.provider as string)) {
      return !!(providerConfig.apiKey && providerConfig.apiKey.trim().length > 0);
    }

    // For openai-compatible, endpoint is required
    if (providerConfig.provider === "openai-compatible") {
      return !!(providerConfig.endpoint && providerConfig.endpoint.trim().length > 0);
    }

    return true;
  };

  // AI service abstraction - handles both direct and backend modes
  const generateAIResponse = async (
    userMessage: string, 
    conversationMessages: Message[],
    systemPrompt: string,
    abortSignal: AbortSignal
  ): Promise<AsyncIterable<string>> => {
    if (mode === "backend") {
      // Backend mode - make API call to developer's endpoint
      if (!backendConfig?.endpoint) {
        throw new Error("Backend endpoint not configured");
      }

      const response = await fetch(backendConfig.endpoint, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          ...backendConfig.headers,
        },
        body: JSON.stringify({
          messages: conversationMessages.map(msg => ({
            role: msg.role,
            content: msg.content,
          })),
          systemPrompt,
          userMessage,
        }),
        signal: abortSignal,
      });

      if (!response.ok) {
        throw new Error(`Backend request failed: ${response.status} ${response.statusText}`);
      }

      if (!response.body) {
        throw new Error("No response body from backend");
      }

      // Return async iterator for streaming response
      return {
        async *[Symbol.asyncIterator]() {
          const reader = response.body!.getReader();
          const decoder = new TextDecoder();

          try {
            while (true) {
              const { done, value } = await reader.read();
              if (done) break;

              const chunk = decoder.decode(value, { stream: true });
              yield chunk;
            }
          } finally {
            reader.releaseLock();
          }
        },
      };
    } else {
      // Direct mode - use AI SDK as before
      if (!providerConfig) {
        throw new Error("Provider configuration required");
      }

      const model = createModel(providerConfig);
      const result = streamText({
        model,
        system: systemPrompt,
        messages: conversationMessages.map((msg) => ({
          role: msg.role,
          content: msg.content,
        })),
        temperature: providerConfig.temperature || 0.7,
        maxOutputTokens: providerConfig.maxTokens || 2000,
        abortSignal,
      });

      return result.textStream;
    }
  };

  const generateResponse = async (userMessage: string) => {
    if (!isServiceConfigured()) {
      toast.error(mode === "backend" ? "Backend not configured" : "Provider not configured", {
        description: mode === "backend" 
          ? "Backend endpoint configuration is required to send messages."
          : "Please configure your AI provider with a valid API key before sending messages."
      });
      setError(new Error("Service configuration required"));
      return;
    }

    try {
      setIsLoading(true);
      setError(null);
      onStreamingStateChange?.(true);

      const userMsg: Message = {
        id: Date.now().toString(),
        role: "user",
        content: userMessage,
      };

      const newMessages = [...messages, userMsg];
      setMessages(newMessages);

      // Get parser configuration from localStorage or use defaults
      let parserConfig = defaultParserConfig;
      try {
        const savedConfig = localStorage.getItem('formedible-parser-config');
        if (savedConfig) {
          const parsed = JSON.parse(savedConfig);
          parserConfig = { ...defaultParserConfig, ...parsed };
        }
      } catch (error) {
        console.warn('Failed to load parser config, using defaults:', error);
      }

      // Generate dynamic system prompt based on configuration
      const configuredSystemPrompt = generateSystemPrompt(parserConfig);

      const baseSystemPrompt = `You are a helpful AI assistant for form creation. You can chat naturally with users about forms, answer questions, and help them design forms.

**IMPORTANT: Only show formedible code blocks when the user specifically asks to create, build, generate, or show a form.**

When creating forms, use formedible code blocks with complete JavaScript object literal syntax including Zod schemas. Chat naturally. Ask clarifying questions. Suggest improvements. Only output formedible blocks when specifically requested.`;

      const systemPrompt = configuredSystemPrompt 
        ? `${baseSystemPrompt}\n\n${configuredSystemPrompt}` 
        : baseSystemPrompt;

      const abortController = new AbortController();
      abortControllerRef.current = abortController;

      // Use the AI service abstraction
      const textStream = await generateAIResponse(
        userMessage,
        [...messages, userMsg],
        systemPrompt,
        abortController.signal
      );

      const assistantMsg: Message = {
        id: (Date.now() + 1).toString(),
        role: "assistant",
        content: "",
      };

      setMessages((prev) => [...prev, assistantMsg]);

      let fullResponse = "";
      for await (const textPart of textStream) {
        if (abortController.signal.aborted) break;

        fullResponse += textPart;
        setMessages((prev) =>
          prev.map((msg) =>
            msg.id === assistantMsg.id ? { ...msg, content: fullResponse } : msg
          )
        );
      }

      // Extract and send formedible code blocks to preview
      if (fullResponse && onFormGenerated) {
        const code = extractFormedibleCode(fullResponse);
        if (code) {
          onFormGenerated(code);
        }
      }

      onStreamingStateChange?.(false);

      // Save conversation to localStorage ONLY on stream end with complete messages
      const finalMessages = [
        ...newMessages, // This includes user message
        { ...assistantMsg, content: fullResponse },
      ];
      onConversationUpdate?.(finalMessages, true);
    } catch (err) {
      console.error("AI Generation Error:", err);
      setError(
        err instanceof Error ? err : new Error("Unknown error occurred")
      );
    } finally {
      setIsLoading(false);
      onStreamingStateChange?.(false);
      abortControllerRef.current = null;
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    if (!isServiceConfigured()) {
      toast.error(mode === "backend" ? "Backend configuration required" : "Provider configuration required", {
        description: mode === "backend" 
          ? "Backend endpoint configuration is required to send messages."
          : "Please set up your AI provider with a valid API key in the settings before sending messages."
      });
      return;
    }

    const userInput = input;
    setInput("");
    generateResponse(userInput);
  };

  const stop = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      setIsLoading(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <Card
      className={cn(
        "flex flex-col h-full max-h-full border-2 border-accent/30 shadow-lg !py-0 !gap-0 overflow-hidden",
        className
      )}
    >
      <CardHeader className="px-3 pt-1 pb-0 bg-gradient-to-r from-accent/10 to-transparent">
        <CardTitle className="flex items-center justify-between text-lg">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-accent/20 rounded-lg">
              <MessageSquare className="h-5 w-5 text-accent-foreground" />
            </div>
            <span className="text-foreground font-semibold">
              AI Form Builder Chat
            </span>
          </div>
          {onNewConversation && (
            <Button
              variant="outline"
              size="sm"
              onClick={onNewConversation}
              className="h-8 w-8 p-0"
            >
              <Plus className="h-4 w-4" />
              <span className="sr-only">New conversation</span>
            </Button>
          )}
        </CardTitle>
      </CardHeader>

      <CardContent className="flex flex-col flex-1 px-3 pt-0 pb-1 min-h-0 max-h-full relative overflow-hidden">
        <div
          ref={messagesContainerRef}
          className="flex-1 overflow-y-auto space-y-3 mb-1 pr-2 min-h-0"
        >
          {messages.length === 0 && (
            <div className="flex flex-col items-center justify-center h-full text-center text-muted-foreground">
              <Bot className="h-12 w-12 mb-4 opacity-50" />
              <h3 className="text-lg font-medium mb-2">
                Welcome to AI Form Builder
              </h3>
              <p className="text-sm max-w-md">
                Describe the form you want to create and I'll generate it for
                you. Try something like "Create a contact form with name, email,
                and message fields"
              </p>
            </div>
          )}

          {messages.map((message: Message) => (
            <div
              key={message.id}
              className={cn(
                "flex gap-3 max-w-4xl",
                message.role === "user" ? "ml-auto flex-row-reverse" : "mr-auto"
              )}
            >
              <div
                className={cn(
                  "flex h-8 w-8 shrink-0 select-none items-center justify-center rounded-full border-2 shadow-md",
                  message.role === "user"
                    ? "bg-primary text-primary-foreground border-primary/30"
                    : "bg-muted text-foreground border-border"
                )}
              >
                {message.role === "user" ? (
                  <User className="h-4 w-4" />
                ) : (
                  <Bot className="h-4 w-4" />
                )}
              </div>

              <div
                className={cn(
                  "flex flex-col gap-2 rounded-lg px-4 py-3 text-sm shadow-md border max-w-[85%]",
                  message.role === "user"
                    ? "bg-primary text-primary-foreground border-primary/30"
                    : "bg-background text-foreground border-border"
                )}
              >
                <MessageContent content={message.content} />
              </div>
            </div>
          ))}

          {isLoading && (
            <div className="flex gap-3 max-w-4xl mr-auto">
              <div className="flex h-8 w-8 shrink-0 select-none items-center justify-center rounded-full border-2 shadow-md bg-muted text-foreground border-border">
                <Bot className="h-4 w-4" />
              </div>
              <div className="flex items-center gap-2 rounded-lg px-4 py-3 text-sm shadow-md border bg-background text-foreground border-border">
                <Loader2 className="h-4 w-4 animate-spin text-primary" />
                <span>Generating response...</span>
              </div>
            </div>
          )}

        </div>

        {/* Scroll to bottom button */}
        {showScrollToBottom && (
          <Button
            onClick={scrollToBottom}
            className="absolute bottom-20 right-4 rounded-full w-10 h-10 p-0 shadow-lg z-10"
            variant="secondary"
            size="sm"
          >
            <ArrowDown className="h-4 w-4" />
            <span className="sr-only">Scroll to bottom</span>
          </Button>
        )}

        {error && (
          <Alert variant="destructive" className="mb-4">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              Failed to send message. Please check your configuration and try
              again.
            </AlertDescription>
          </Alert>
        )}

        <form onSubmit={handleSubmit} className="flex gap-2">
          <Textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Describe the form you want to create... (Shift+Enter for new line)"
            disabled={isLoading}
            className="flex-1 min-h-[80px] resize-none"
            rows={3}
          />

          {isLoading ? (
            <Button
              type="button"
              variant="outline"
              size="icon"
              onClick={stop}
              className="shrink-0"
            >
              <StopCircle className="h-4 w-4" />
              <span className="sr-only">Stop generation</span>
            </Button>
          ) : (
            <Button
              type="submit"
              disabled={!input.trim() || !isServiceConfigured()}
              size="icon"
              className="shrink-0"
            >
              <Send className="h-4 w-4" />
              <span className="sr-only">Send message</span>
            </Button>
          )}
        </form>
      </CardContent>
    </Card>
  );
}

"use client";

import { Button } from "@/components/ui/button";
import { 
  ChevronLeft, 
  ChevronRight, 
  History, 
  Settings, 
  Globe,
  FileText 
} from "lucide-react";
import { cn } from "@/lib/utils";

export type SidebarView = "history" | "settings" | "provider" | "parser";

interface SidebarIconsProps {
  isCollapsed: boolean;
  onToggleCollapse: () => void;
  activeView: SidebarView | null;
  onViewChange: (view: SidebarView | null) => void;
}

export function SidebarIcons({
  isCollapsed,
  onToggleCollapse,
  activeView,
  onViewChange,
}: SidebarIconsProps) {
  const handleIconClick = (view: SidebarView) => {
    if (activeView === view) {
      // If clicking the same icon, close the sidebar
      onViewChange(null);
    } else {
      // If clicking different icon, switch view
      onViewChange(view);
    }
  };

  return (
    <div className="flex flex-col w-12 bg-muted/20 border-r border-border">
      {/* Collapse/expand button */}
      <Button
        variant="ghost"
        size="icon"
        onClick={onToggleCollapse}
        className="h-12 w-12 rounded-none border-b border-border hover:bg-muted/40"
      >
        {isCollapsed ? (
          <ChevronRight className="h-4 w-4" />
        ) : (
          <ChevronLeft className="h-4 w-4" />
        )}
        <span className="sr-only">
          {isCollapsed ? "Expand sidebar" : "Collapse sidebar"}
        </span>
      </Button>

      {/* Icon buttons */}
      <div className="flex flex-col">
        <Button
          variant="ghost"
          size="icon"
          onClick={() => handleIconClick("history")}
          className={cn(
            "h-12 w-12 rounded-none hover:bg-muted/40",
            activeView === "history" && "bg-muted text-foreground"
          )}
        >
          <History className="h-4 w-4" />
          <span className="sr-only">History</span>
        </Button>

        <Button
          variant="ghost"
          size="icon"
          onClick={() => handleIconClick("settings")}
          className={cn(
            "h-12 w-12 rounded-none hover:bg-muted/40",
            activeView === "settings" && "bg-muted text-foreground"
          )}
        >
          <Settings className="h-4 w-4" />
          <span className="sr-only">Settings</span>
        </Button>

        <Button
          variant="ghost"
          size="icon"
          onClick={() => handleIconClick("provider")}
          className={cn(
            "h-12 w-12 rounded-none hover:bg-muted/40",
            activeView === "provider" && "bg-muted text-foreground"
          )}
        >
          <Globe className="h-4 w-4" />
          <span className="sr-only">Provider</span>
        </Button>

        <Button
          variant="ghost"
          size="icon"
          onClick={() => handleIconClick("parser")}
          className={cn(
            "h-12 w-12 rounded-none hover:bg-muted/40",
            activeView === "parser" && "bg-muted text-foreground"
          )}
        >
          <FileText className="h-4 w-4" />
          <span className="sr-only">Form Parser Settings</span>
        </Button>
      </div>
    </div>
  );
}

"use client";

import { Card } from "@/components/ui/card";
import { cn } from "@/lib/utils";
import type { SidebarView } from "./sidebar-icons";
import { ConversationHistory, type Conversation, type ConversationHistoryProps } from "./conversation-history";
import { AgentSettings } from "./agent-settings";
import { ProviderSelection } from "./provider-selection";
import { ParserSettings } from "./parser-settings";
import type { ProviderConfig } from "./provider-selection";

interface SidebarContentProps {
  activeView: SidebarView | null;
  isCollapsed: boolean;
  className?: string;

  // History props
  conversations: Conversation[];
  currentConversationId?: string;
  onSelectConversation: (conversation: Conversation) => void;
  onDeleteConversation: (conversationId: string) => void;
  onNewConversation?: () => void;
  onExportConversation?: (conversation: Conversation) => void;

  // Settings props  
  providerConfig: ProviderConfig | null;
  onConfigChange: (config: ProviderConfig | null) => void;
}

export function SidebarContent({
  activeView,
  isCollapsed,
  className,
  conversations,
  currentConversationId,
  onSelectConversation,
  onDeleteConversation,
  onNewConversation,
  onExportConversation,
  providerConfig,
  onConfigChange,
}: SidebarContentProps) {
  if (isCollapsed || !activeView) {
    return null;
  }

  const renderContent = () => {
    switch (activeView) {
      case "history":
        return (
          <ConversationHistory
            conversations={conversations}
            currentConversationId={currentConversationId}
            onSelectConversation={onSelectConversation}
            onDeleteConversation={onDeleteConversation}
            onNewConversation={onNewConversation}
            onExportConversation={onExportConversation}
            className="border-0 shadow-none h-full"
          />
        );

      case "settings":
        return (
          <Card className="h-full">
            <AgentSettings
              providerConfig={providerConfig}
              onConfigChange={onConfigChange}
              className="h-full"
            />
          </Card>
        );

      case "provider":
        return (
          <Card className="h-full">
            <ProviderSelection
              onConfigChange={onConfigChange}
              initialConfig={providerConfig}
              className="h-full"
            />
          </Card>
        );

      case "parser":
        return (
          <ParserSettings
            className="h-full"
          />
        );

      default:
        return null;
    }
  };

  return (
    <div
      className={cn(
        "w-80 border-r border-border bg-background/50 transition-all duration-200 ease-in-out overflow-hidden",
        isCollapsed ? "w-0" : "w-80",
        className
      )}
    >
      <div className="h-full p-2 overflow-y-auto">
        {renderContent()}
      </div>
    </div>
  );
}

"use client";

import { useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { useFormedible } from "@/hooks/use-formedible";
import type { ProviderConfig, AIProvider } from "./provider-selection";

interface AgentSettingsProps {
  providerConfig: ProviderConfig | null;
  onConfigChange: (config: ProviderConfig | null) => void;
  className?: string;
}

// OpenRouter models fetcher with memoization
const fetchOpenRouterModels = async (apiKey: string) => {
  const response = await fetch("https://openrouter.ai/api/v1/models", {
    headers: {
      Authorization: `Bearer ${apiKey}`,
      "Content-Type": "application/json",
    },
  });

  if (!response.ok) {
    throw new Error(`HTTP error! status: ${response.status}`);
  }

  const data = await response.json();
  return data.data
    .filter((model: any) => model.id && model.name && model.id.trim() !== "")
    .map((model: any) => ({
      value: model.id,
      label: model.name,
    }))
    .sort((a: any, b: any) => a.label.localeCompare(b.label));
};

// Memoized function to get model options based on provider
const useModelOptions = (
  provider: AIProvider | undefined,
  openRouterModels: Array<{ value: string; label: string }> = [],
  openRouterLoading: boolean = false
) => {
  return useMemo(() => {
    if (provider === "openai")
      return [
        { value: "gpt-5", label: "GPT-5" },
        { value: "gpt-5-mini", label: "GPT-5 Mini" },
        { value: "gpt-4o", label: "GPT-4o" },
        { value: "gpt-4o-mini", label: "GPT-4o Mini" },
      ];
    if (provider === "anthropic")
      return [
        { value: "claude-sonnet-4", label: "Claude Sonnet 4" },
        { value: "claude-3-5-sonnet-20241022", label: "Claude 3.5 Sonnet" },
        { value: "claude-3-haiku-20240307", label: "Claude 3 Haiku" },
      ];
    if (provider === "google")
      return [
        { value: "gemini-2.5-pro", label: "Gemini 2.5 Pro" },
        { value: "gemini-1.5-pro", label: "Gemini 1.5 Pro" },
        { value: "gemini-1.5-flash", label: "Gemini 1.5 Flash" },
      ];
    if (provider === "mistral")
      return [
        { value: "mistral-large-2407", label: "Mistral Large 2407" },
        { value: "mistral-medium-2312", label: "Mistral Medium 2312" },
        { value: "mistral-small-2312", label: "Mistral Small 2312" },
      ];
    if (provider === "openrouter") {
      if (openRouterLoading) return [{ value: "loading", label: "Loading models..." }];
      return openRouterModels.length > 0
        ? openRouterModels.filter(model => model.value && model.value.trim() !== "")
        : [
            { value: "openrouter/kimiplus-k2", label: "OpenRouter Kimi Plus K2" },
            { value: "moonshotai/moonshot-k2", label: "Moonshot K2" },
          ];
    }
    if (provider === "openai-compatible")
      return [
        { value: "gpt-4.1-mini", label: "GPT-4.1 Mini" },
        { value: "custom-model", label: "Custom Model" },
      ];
    return [];
  }, [provider, openRouterModels, openRouterLoading]);
};

export function AgentSettings({ providerConfig, onConfigChange, className }: AgentSettingsProps) {
  // Use TanStack Query for OpenRouter models with proper caching
  const {
    data: openRouterModels = [],
    isLoading: openRouterLoading,
    error: openRouterError,
  } = useQuery({
    queryKey: ["openrouter-models", providerConfig?.apiKey],
    queryFn: () => fetchOpenRouterModels(providerConfig?.apiKey || ""),
    enabled: providerConfig?.provider === "openrouter" && !!providerConfig?.apiKey && providerConfig.apiKey.length > 0,
    staleTime: 5 * 60 * 1000, // 5 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes
    retry: 1,
  });

  const availableModels = useModelOptions(
    providerConfig?.provider,
    openRouterModels,
    openRouterLoading
  );

  const { Form } = useFormedible({
    fields: !providerConfig ? [] : [
      {
        name: "model",
        type: "select",
        label: "Model",
        placeholder: "Select a model",
        options: providerConfig?.provider === "openrouter" && openRouterError
          ? [{ value: "error", label: "Failed to load models - check API key" }]
          : availableModels,
      },
      {
        name: "temperature",
        type: "slider",
        label: `Temperature: ${(providerConfig?.temperature || 0.7).toFixed(2)}`,
        sliderConfig: {
          min: 0,
          max: 1,
          step: 0.01,
        },
        description: "Controls randomness: Focused â† â†’ Creative",
      },
      {
        name: "maxTokens",
        type: "number",
        label: "Max Tokens",
        placeholder: "2000",
        description: "Maximum response length",
        numberConfig: {
          min: 1,
          max: 32768,
        },
      },
    ],
    formOptions: {
      defaultValues: {
        model: providerConfig?.model || "",
        temperature: providerConfig?.temperature || 0.7,
        maxTokens: providerConfig?.maxTokens || 2000,
      },
      onSubmit: async ({ value }) => {
        if (!providerConfig) return;
        const updatedConfig: ProviderConfig = {
          ...providerConfig,
          model: value.model,
          temperature: value.temperature,
          maxTokens: value.maxTokens,
        };
        onConfigChange(updatedConfig);
      },
    },
    submitLabel: "ðŸ’¾ Save Model Settings",
    showSubmitButton: !providerConfig ? false : true,
    autoSubmitOnChange: false,
  });

  return (
    <div className={`p-4 space-y-4 ${className || ""}`}>
      <h3 className="text-base font-semibold">Model Settings</h3>
      {!providerConfig ? (
        <p className="text-muted-foreground">Select a provider first to configure model settings</p>
      ) : (
        <Form />
      )}
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useFormedible } from "@/hooks/use-formedible";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { 
  parserConfigFields, 
  defaultParserConfig, 
  validateParserConfig,
  mergeParserConfig,
  generateSystemPrompt,
  type ParserConfig 
} from "@/lib/formedible/parser-config-schema";
import { Info, RotateCcw, Copy, Check } from "lucide-react";

interface ParserSettingsProps {
  className?: string;
  onConfigChange?: (config: ParserConfig) => void;
}

export function ParserSettings({ className, onConfigChange }: ParserSettingsProps) {
  const [config, setConfig] = useState<ParserConfig>(defaultParserConfig);
  const [lastSavedConfig, setLastSavedConfig] = useState<ParserConfig>(defaultParserConfig);
  const [copied, setCopied] = useState(false);

  // Load saved config on mount
  useEffect(() => {
    try {
      const saved = localStorage.getItem('formedible-parser-config');
      if (saved) {
        const parsed = JSON.parse(saved);
        if (validateParserConfig(parsed)) {
          setConfig(parsed);
          setLastSavedConfig(parsed);
        }
      }
    } catch (error) {
      console.warn('Failed to load parser config:', error);
    }
  }, []);

  // Check if there are unsaved changes
  const hasChanges = JSON.stringify(config) !== JSON.stringify(lastSavedConfig);

  // Save config to localStorage and notify parent
  const handleSaveConfig = (newConfig: ParserConfig) => {
    try {
      localStorage.setItem('formedible-parser-config', JSON.stringify(newConfig));
      setConfig(newConfig);
      setLastSavedConfig(newConfig);
      onConfigChange?.(newConfig);
    } catch (error) {
      console.error('Failed to save parser config:', error);
    }
  };

  // Reset to defaults
  const handleReset = () => {
    const resetConfig = { ...defaultParserConfig };
    handleSaveConfig(resetConfig);
  };

  // Copy system prompt to clipboard
  const handleCopySystemPrompt = async () => {
    try {
      const systemPrompt = generateSystemPrompt(lastSavedConfig);
      const promptToCopy = systemPrompt || 'System prompt will be generated based on current configuration...';

      await navigator.clipboard.writeText(promptToCopy);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy system prompt:', error);
    }
  };

  // Create form fields with conditional logic
  const formFields = [
    // Basic Configuration Section
    ...parserConfigFields
      .filter(field => 
        ['strictValidation', 'enableSchemaInference', 'fieldTypeValidation'].includes(field.name)
      )
      .map(field => ({
        ...field,
      })),

    // Show Advanced Settings (after basic fields)
    {
      name: 'showAdvanced',
      type: 'switch',
      label: 'Show Advanced Settings',
      description: 'Display advanced configuration options',
      defaultValue: false,
    },

    // Advanced Configuration Section (conditional)
    ...parserConfigFields
      .filter(field => 
        !['strictValidation', 'enableSchemaInference', 'fieldTypeValidation', 'selectFields', 'systemPromptFields', 'includeTabFormatting', 'includePageFormatting'].includes(field.name)
      )
      .map(field => ({
        ...field,
        conditional: (values: any) => values.showAdvanced === true,
      })),

    // System Prompt Fields
    ...parserConfigFields
      .filter(field => 
        ['selectFields', 'systemPromptFields'].includes(field.name)
      )
      .map(field => ({
        ...field,
        conditional: field.name === 'systemPromptFields'
          ? (values: any) => values.selectFields === true
          : undefined
      })),

    // Formatting Options (always visible)
    ...parserConfigFields
      .filter(field => 
        ['includeTabFormatting', 'includePageFormatting'].includes(field.name)
      )
  ];

  const { Form, form } = useFormedible<ParserConfig & { showAdvanced: boolean }>({
    fields: formFields,
    formOptions: {
      defaultValues: { 
        ...config, 
        showAdvanced: false,
        // Ensure all system prompt fields have proper defaults
        selectFields: config.selectFields ?? false,
        systemPromptFields: config.systemPromptFields ?? defaultParserConfig.systemPromptFields,
        includeTabFormatting: config.includeTabFormatting ?? defaultParserConfig.includeTabFormatting,
        includePageFormatting: config.includePageFormatting ?? defaultParserConfig.includePageFormatting
      },
      onSubmit: async ({ value }) => {
        // Extract showAdvanced and save the rest
        const { showAdvanced, ...parserConfig } = value;
        const mergedConfig = mergeParserConfig({ ...config, ...parserConfig });
        handleSaveConfig(mergedConfig);
      },
      // NO onChange - we only update when the user clicks Save!
    },
    submitLabel: hasChanges ? "Save Settings" : "Settings Saved",
    showSubmitButton: hasChanges,
    autoSubmitOnChange: false,
    layout: {
      type: 'grid',
      columns: 1,
      gap: 'md'
    }
  });

  return (
    <div className={`space-y-4 ${className || ""}`}>
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2 text-base">
            <Info className="h-4 w-4" />
            Form Parser Settings
          </CardTitle>
          <CardDescription>
            Configure how the Formedible parser processes form definitions and handles AI interactions.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">

          {/* Formedible Form */}
          <div className="space-y-4">
            <Form />
          </div>

          {/* Action Buttons */}
          <div className="flex flex-wrap gap-2 pt-4 border-t">
            <Button 
              onClick={handleReset}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
            >
              <RotateCcw className="h-3 w-3" />
              Reset to Defaults
            </Button>

            <Button
              onClick={handleCopySystemPrompt}
              variant={copied ? "default" : "outline"}
              size="sm"
              className="flex items-center gap-2"
            >
              {copied ? (
                <>
                  <Check className="h-3 w-3" />
                  Copied!
                </>
              ) : (
                <>
                  <Copy className="h-3 w-3" />
                  Copy Prompt
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* System Prompt Preview */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-sm">System Prompt Preview</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="p-3 bg-muted rounded-md text-xs overflow-auto max-h-64">
            <pre className="whitespace-pre-wrap">
              {generateSystemPrompt(lastSavedConfig) || 'System prompt will be generated based on current saved configuration...'}
            </pre>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

