  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  never
>;

export interface BaseFieldProps {
  fieldApi: AnyFieldApi;
  label?: string;
  placeholder?: string;
  wrapperClassName?: string;
  labelClassName?: string;
  disabled?: boolean;
}

export interface FieldComponentProps extends BaseFieldProps {
