  [key: string]: unknown;
}

export interface FieldConfig {
  name: string;
  type: string;
  label?: string;
  placeholder?: string;
  required?: boolean;
  defaultValue?: unknown;
  options?: string[] | Array<{ value: string; label: string }>;
  validation?: z.ZodSchema<unknown>;
  page?: number;
  [key: string]: unknown;
}

