          }}
          options={{
            bundlerURL: "https://sandpack-bundler.codesandbox.io",
            visibleFiles: showCodeEditor ? ["/FormComponent.tsx", "/App.tsx"] : [],
            activeFile: "/FormComponent.tsx",
            initMode: "lazy",
            autorun: true,
