        >
          <SandpackLayout 
            style={{ height: "100%", width: "100%" }}
            className={showToolbar ? "rounded-b-lg" : "border rounded-lg"}
          >
            {showFileExplorer && <SandpackFileExplorer />}
            {showCodeEditor && (
              <SandpackCodeEditor
                showTabs
                showLineNumbers
                showInlineErrors
                closableTabs
