export async function setCachedTemplate(
  cacheKey: string, 
  template: SandpackFiles, 
  formCode: string
): Promise<void> {
  const metadata = extractTemplateMetadata(formCode);

  await Promise.all([
    templateCache.set(cacheKey, template, metadata),
    metadataCache.set(cacheKey, metadata)
  ]);
}

