                  form={fieldApi.form}
                  currentValues={
                    (value[index] || {}) as Record<string, unknown>
                  }
                />
              </div>

              {canRemove && (
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => removeItem(index)}
                  className="mt-2 h-8 w-8 p-0 text-destructive hover:text-destructive"
