  "/src/hooks/use-dropdown.ts": "import { useEffect, useRef, useState } from "react";

export interface UseDropdownReturn {
  isOpen: boolean;
  setIsOpen: (open: boolean) => void;
  containerRef: React.RefObject<HTMLDivElement | null>;
  toggle: () => void;
  close: () => void;
  open: () => void;
}

export function useDropdown(initialOpen = false): UseDropdownReturn {
  const [isOpen, setIsOpen] = useState(initialOpen);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener("mousedown", handleClickOutside);
      return () => document.removeEventListener("mousedown", handleClickOutside);
    }
  }, [isOpen]);

  const toggle = () => setIsOpen(!isOpen);
  const close = () => setIsOpen(false);
  const open = () => setIsOpen(true);

  return {
    isOpen,
    setIsOpen,
    containerRef,
    toggle,
    close,
    open,
  };
}",
  "/src/hooks/use-field-state.ts": "import type { AnyFieldApi } from "@tanstack/react-form";

export interface UseFieldStateReturn {
  name: string;
  value: unknown;
  isDisabled: boolean;
  hasErrors: boolean;
  errors: string[];
  onBlur: () => void;
  onChange: (value: unknown) => void;
}

export function useFieldState(fieldApi: AnyFieldApi): UseFieldStateReturn {
  const name = fieldApi.name;
  const value = fieldApi.state.value;
  const isDisabled = fieldApi.form.state.isSubmitting;
  const isTouched = fieldApi.state.meta.isTouched;
  const errors = fieldApi.state.meta.errors || [];
  const hasErrors = isTouched && errors.length > 0;

  const onBlur = () => {
    fieldApi.handleBlur();
  };

  const onChange = (newValue: unknown) => {
    fieldApi.handleChange(newValue);
  };

  return {
    name,
    value,
    isDisabled,
    hasErrors,
    errors,
    onBlur,
    onChange,
  };
}
