"use client";
import React, { useMemo } from "react";
import { CodeBlock } from "@/components/ui/code-block";
import type { FieldConfig, DynamicText } from "@/lib/formedible/types";
import { resolveDynamicText } from "@/lib/formedible/template-interpolation";

interface FormField extends FieldConfig {
  id: string;
  label: string; // Make label required for builder
  help?: {
    text?: string;
    tooltip?: string;
    position?: "top" | "bottom" | "left" | "right";
    link?: { url: string; text: string };
  };
  inlineValidation?: {
    enabled?: boolean;
    debounceMs?: number;
    showSuccess?: boolean;
  };
  options?: string[] | Array<{ value: string; label: string }> | ((values: Record<string, unknown>) => string[] | Array<{ value: string; label: string }>);
  arrayConfig?: any;
  datalist?: any;
  multiSelectConfig?: any;
  colorConfig?: any;
  ratingConfig?: any;
  phoneConfig?: any;
}

interface CodeGeneratorProps {
  formTitle: string;
  formDescription: string;
  fields: FormField[];
  pages: Array<{ page: number; title: string; description?: string }>;
  settings: {
    submitLabel: string;
    nextLabel: string;
    previousLabel: string;
    showProgress: boolean;
  };
}

export const CodeGenerator: React.FC<CodeGeneratorProps> = ({
  formTitle,
  formDescription,
  fields,
  pages,
  settings,
}) => {
  const generatedCode = useMemo(() => {
    if (fields.length === 0) {
      return `import { useFormedible } from 'formedible';
import { z } from 'zod';

export const MyForm = () => {
  const { Form } = useFormedible({
    schema: z.object({}),
    fields: [],
    formOptions: {
      onSubmit: async ({ value }) => {
        console.log('Form submitted:', value);
      },
    },
  });

  return <Form />;
};`;
    }

    const formConfig = {
      title: formTitle,
      description: formDescription,
      schema: "z.object(schemaFields)",
      fields: fields.map((field) => ({
        name: field.name,
        type: field.type,
        label: resolveDynamicText(field.label, {}) || field.label,
        placeholder: resolveDynamicText(field.placeholder, {}) || field.placeholder,
        description: resolveDynamicText(field.description, {}) || field.description,
        page: field.page || 1,
        group: field.group,
        section: field.section,
        help: field.help,
        inlineValidation: field.inlineValidation,
        ...(field.options && { options: field.options }),
        ...(field.arrayConfig && { arrayConfig: field.arrayConfig }),
        ...(field.datalist && { datalist: field.datalist }),
        ...(field.multiSelectConfig && { multiSelectConfig: field.multiSelectConfig }),
        ...(field.colorConfig && { colorConfig: field.colorConfig }),
        ...(field.ratingConfig && { ratingConfig: field.ratingConfig }),
        ...(field.phoneConfig && { phoneConfig: field.phoneConfig }),
      })),
      pages: pages,
      submitLabel: settings.submitLabel,
      nextLabel: settings.nextLabel,
      previousLabel: settings.previousLabel,
      progress: settings.showProgress
        ? { showSteps: true, showPercentage: true }
        : undefined,
      formOptions: {
        onSubmit: "async ({ value }) => {\n      console.log('Form submitted:', value);\n      // Handle form submission here\n    }",
      },
    };

    // Generate schema fields
    const schemaFieldsCode = fields.map((field) => {
      let fieldSchema = "";

      switch (field.type) {
        case "number":
        case "slider":
        case "rating":
          fieldSchema = "z.number()";
          break;
        case "checkbox":
        case "switch":
          fieldSchema = "z.boolean()";
          break;
        case "date":
          fieldSchema = "z.string()";
          break;
        case "multiSelect":
        case "array":
          fieldSchema = "z.array(z.string())";
          break;
        default:
          fieldSchema = "z.string()";
      }

      if (field.required) {
        if (field.type === "checkbox" || field.type === "switch") {
          fieldSchema = `${fieldSchema}.refine((val) => val === true, {
    message: '${field.label} is required',
  })`;
        } else if (field.type !== "number" && field.type !== "slider" && field.type !== "rating") {
          fieldSchema = `${fieldSchema}.min(1, '${field.label} is required')`;
        }
      } else {
        fieldSchema = `${fieldSchema}.optional()`;
      }

      return `  ${field.name}: ${fieldSchema}`;
    }).join(",\n");

    const configString = JSON.stringify(formConfig, null, 2)
      .replace('"z.object(schemaFields)"', 'z.object(schemaFields)')
      .replace('"async ({ value }) => {\\n      console.log(\'Form submitted:\', value);\\n      // Handle form submission here\\n    }"', 
        'async ({ value }) => {\n      console.log(\'Form submitted:\', value);\n      // Handle form submission here\n    }');

    return `import { useFormedible } from 'formedible';
import { z } from 'zod';

export const MyForm = () => {
  // Define the schema for form validation
  const schemaFields = {
${schemaFieldsCode}
  };

  const formConfig = ${configString};

  const { Form } = useFormedible(formConfig);

  return <Form />;
};`;
  }, [formTitle, formDescription, fields, pages, settings]);

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold mb-2">Generated Code</h2>
        <p className="text-muted-foreground text-lg mb-6">
          Copy this code to use your form in your application
        </p>
      </div>

      {fields.length === 0 ? (
        <div className="flex items-center justify-center h-64 border-2 border-dashed border-muted-foreground/25 rounded-lg">
          <div className="text-center">
            <h3 className="text-lg font-medium text-foreground mb-2">No Fields Added</h3>
            <p className="text-muted-foreground">Add some fields to generate code</p>
          </div>
        </div>
      ) : (
        <CodeBlock
          code={generatedCode}
          language="tsx"
          title="MyForm.tsx"
          showCopyButton={true}
          showLineNumbers={true}
        />
      )}
    </div>
  );
};

import type { FormField } from "@/lib/formedible/builder-types";

const FIELD_TYPES = [
  { value: "text", label: "Text Input", icon: "üìù" },
  { value: "email", label: "Email", icon: "üìß" },
  { value: "password", label: "Password", icon: "üîí" },
  { value: "textarea", label: "Textarea", icon: "üìÑ" },
  { value: "number", label: "Number", icon: "üî¢" },
  { value: "select", label: "Select", icon: "üìã" },
  { value: "radio", label: "Radio Group", icon: "‚ö™" },
  { value: "multiSelect", label: "Multi-Select", icon: "‚òëÔ∏è" },
  { value: "checkbox", label: "Checkbox", icon: "‚úÖ" },
  { value: "switch", label: "Switch", icon: "üîò" },
  { value: "date", label: "Date Picker", icon: "üìÖ" },
  { value: "slider", label: "Slider", icon: "üéöÔ∏è" },
  { value: "rating", label: "Rating", icon: "‚≠ê" },
  { value: "colorPicker", label: "Color Picker", icon: "üé®" },
  { value: "phone", label: "Phone Number", icon: "üìû" },
  { value: "file", label: "File Upload", icon: "üìé" },
  { value: "array", label: "Array Field", icon: "üìö" },
];

// ISOLATED FIELD STORE - SEPARATE NOTIFICATIONS FOR STRUCTURE VS FIELD UPDATES
class FieldStore {
  private fields: Record<string, FormField> = {};
  private fieldOrder: string[] = [];
  private structureListeners: Set<() => void> = new Set(); // For add/delete/reorder
  private fieldUpdateCallbacks: Map<string, Set<(field: FormField) => void>> = new Map(); // Field-specific subscribers

  addField(type: string, selectedPage?: number): string {
    const id = `field_${Date.now()}`;
    const newField: FormField = {
      id,
      name: `field_${Object.keys(this.fields).length + 1}`,
      type,
      label: `${FIELD_TYPES.find((t) => t.value === type)?.label || type} Field`,
      required: false,
      page: selectedPage || 1,
    };

    this.fields[id] = newField;
    this.fieldOrder.push(id);
    this.notifyStructureListeners(); // Structure change
    return id;
  }

  updateField(fieldId: string, updatedField: FormField): void {
    this.fields[fieldId] = updatedField;
    // DO NOT notify structure listeners - field updates should not cause parent re-renders!
  }

  // Notify specific field subscribers
  notifyFieldUpdate(fieldId: string, field: FormField): void {
    const subscribers = this.fieldUpdateCallbacks.get(fieldId);
    if (subscribers) {
      subscribers.forEach(callback => callback(field));
    }
  }

  deleteField(fieldId: string): void {
    delete this.fields[fieldId];
    this.fieldOrder = this.fieldOrder.filter(id => id !== fieldId);
    this.notifyStructureListeners(); // Structure change
  }

  duplicateField(fieldId: string): string | null {
    const field = this.fields[fieldId];
    if (field) {
      const id = `field_${Date.now()}`;
      const newField: FormField = {
        ...field,
        id,
        name: `${field.name}_copy`,
        label: `${field.label} (Copy)`,
      };
      this.fields[id] = newField;
      this.fieldOrder.push(id);
      this.notifyStructureListeners(); // Structure change
      return id;
    }
    return null;
  }

  getField(fieldId: string): FormField | undefined {
    return this.fields[fieldId];
  }

  getAllFields(): FormField[] {
    return this.fieldOrder.map(id => this.fields[id]).filter(Boolean);
  }

  getFieldsByPage(page: number): FormField[] {
    return this.fieldOrder.map(id => this.fields[id]).filter(field => field && (field.page || 1) === page);
  }

  getFieldsByTab(tabId: string): FormField[] {
    return this.fieldOrder.map(id => this.fields[id]).filter(field => field && field.tab === tabId);
  }

  // Subscribe to structure changes only (add/delete/reorder)
  subscribe(listener: () => void): () => void {
    this.structureListeners.add(listener);
    return () => this.structureListeners.delete(listener);
  }

  // Subscribe to specific field updates
  subscribeToFieldUpdates(fieldId: string, callback: (field: FormField) => void): () => void {
    if (!this.fieldUpdateCallbacks.has(fieldId)) {
      this.fieldUpdateCallbacks.set(fieldId, new Set());
    }
    this.fieldUpdateCallbacks.get(fieldId)!.add(callback);

    return () => {
      const subscribers = this.fieldUpdateCallbacks.get(fieldId);
      if (subscribers) {
        subscribers.delete(callback);
        if (subscribers.size === 0) {
          this.fieldUpdateCallbacks.delete(fieldId);
        }
      }
    };
  }

  private notifyStructureListeners(): void {
    this.structureListeners.forEach(listener => listener());
  }

  // Import/Export methods
  clear(): void {
    this.fields = {};
    this.fieldOrder = [];
    this.notifyStructureListeners();
  }

  importFields(fields: any[]): void {
    this.clear();
    fields.forEach((field: any) => {
      const newFieldId = this.addField(field.type, field.page || 1);
      const newField: FormField = {
        id: newFieldId,
        name: field.name,
        type: field.type,
        label: field.label,
        placeholder: field.placeholder,
        description: field.description,
        required: field.required || false,
        page: field.page || 1,
        group: field.group,
        section: field.section,
        help: field.help,
        inlineValidation: field.inlineValidation,
        options: field.options,
        arrayConfig: field.arrayConfig,
        datalist: field.datalist,
        multiSelectConfig: field.multiSelectConfig,
        colorConfig: field.colorConfig,
        ratingConfig: field.ratingConfig,
        phoneConfig: field.phoneConfig,
        sliderConfig: field.sliderConfig,
        numberConfig: field.numberConfig,
        dateConfig: field.dateConfig,
        fileConfig: field.fileConfig,
        textareaConfig: field.textareaConfig,
        passwordConfig: field.passwordConfig,
        emailConfig: field.emailConfig,
        validation: field.validation,
      };
      this.updateField(newFieldId, newField);
    });
  }
}

