"use client";
import React, { createContext, useContext, useState, useRef, useCallback } from "react";
import { z } from "zod";
import { globalFieldStore } from "./field-store";
import type {
  BuilderContextType,
  FormMetadata,
  FormField,
  FormConfig,
  TabContentProps,
} from "@/lib/formedible/builder-types";

const BuilderContext = createContext<BuilderContextType | null>(null);

export const useBuilderContext = () => {
  const context = useContext(BuilderContext);
  if (!context) {
    throw new Error("useBuilderContext must be used within a BuilderProvider");
  }
  return context;
};

interface BuilderProviderProps {
  children: React.ReactNode;
  initialMetadata?: Partial<FormMetadata>;
  initialData?: {
    title: string;
    description?: string;
    config: string;
  };
  onSubmit?: (formData: {
    title: string;
    description?: string;
    config: string;
  }) => void;
  isLoading?: boolean;
}

export const BuilderProvider: React.FC<BuilderProviderProps> = ({
  children,
  initialMetadata = {},
  initialData,
  onSubmit,
  isLoading = false,
}) => {
  // Form metadata state (minimal, non-rerendering) - USING REF TO AVOID RE-RENDERS
  const formMetaRef = useRef<FormMetadata>({
    title: "My Form",
    description: "A form built with Formedible",
    pages: [{ page: 1, title: "Page 1", description: "First page" }],
    tabs: [{ id: "general", label: "General", description: "General information" }],
    layoutType: "pages",
    settings: {
      submitLabel: "Submit",
      nextLabel: "Next",
      previousLabel: "Previous",
      showProgress: true,
    },
    ...initialMetadata,
  });

  // UI state (minimal, only for display)
  const [selectedFieldId, setSelectedFieldId] = useState<string | null>(null);
  const [selectedPageId, setSelectedPageId] = useState<number | null>(null);
  const [selectedTabId, setSelectedTabId] = useState<string | null>(null);
  const [editingPageId, setEditingPageId] = useState<number | null>(null);
  const [editingTabId, setEditingTabId] = useState<string | null>(null);
  const [previewMode, setPreviewMode] = useState<"desktop" | "tablet" | "mobile">("desktop");

  // Force rerender for structure changes
  const [, forceUpdateState] = useState({});
  const forceRerender = useCallback(() => forceUpdateState({}), []);

  // Subscribe to global field store changes
  React.useEffect(() => {
    const unsubscribe = globalFieldStore.subscribe(forceRerender);
    return unsubscribe;
  }, [forceRerender]);

  // Load initial config once without rerenders
  const [isInitialized, setIsInitialized] = useState(false);

  React.useEffect(() => {
    if (initialData && !isInitialized) {
      // Update form metadata
      formMetaRef.current.title = initialData.title;
      formMetaRef.current.description = initialData.description || "";

      // Parse and load config
      try {
        const config = JSON.parse(initialData.config);
        if (config.fields) {
          globalFieldStore.importFields(config.fields);
        }
        if (config.pages) {
          formMetaRef.current.pages = config.pages;
        }
        if (config.tabs) {
          formMetaRef.current.tabs = config.tabs;
        }
        if (config.layoutType) {
          formMetaRef.current.layoutType = config.layoutType;
        }
        if (config.settings) {
          formMetaRef.current.settings = {
            ...formMetaRef.current.settings,
            ...config.settings,
          };
        }
      } catch (error) {
        console.warn("Failed to parse initial config:", error);
      }

      setIsInitialized(true);
    }
  }, [initialData, isInitialized]);

  // Form metadata operations - DON'T TRIGGER RE-RENDERS
  const onFormMetadataChange = useCallback((updates: Partial<FormMetadata>) => {
    Object.assign(formMetaRef.current, updates);
  }, []);

  // Get current form metadata (non-reactive)
  const getFormMetadata = useCallback(() => formMetaRef.current, []);

  // Field operations
  const onAddField = useCallback((type: string) => {
    const newFieldId = globalFieldStore.addField(type, selectedPageId || 1);
    const field = globalFieldStore.getField(newFieldId);
    if (field && formMetaRef.current.layoutType === "tabs" && selectedTabId) {
      globalFieldStore.updateField(newFieldId, { ...field, tab: selectedTabId });
    }
    setSelectedFieldId(newFieldId);
  }, [selectedPageId, selectedTabId]);

  const onSelectField = useCallback((fieldId: string | null) => {
    setSelectedFieldId(fieldId);
  }, []);

  const onDeleteField = useCallback((fieldId: string) => {
    globalFieldStore.deleteField(fieldId);
    if (selectedFieldId === fieldId) {
      setSelectedFieldId(null);
    }
  }, [selectedFieldId]);

  const onDuplicateField = useCallback((fieldId: string) => {
    const newFieldId = globalFieldStore.duplicateField(fieldId);
    if (newFieldId) {
      setSelectedFieldId(newFieldId);
    }
  }, []);

  // Data access operations
  const getAllFields = useCallback(() => globalFieldStore.getAllFields(), []);
  const getFieldsByPage = useCallback((page: number) => globalFieldStore.getFieldsByPage(page), []);
  const getFieldsByTab = useCallback((tabId: string) => globalFieldStore.getFieldsByTab(tabId), []);

  // Generate form configuration
  const getFormConfig = useCallback((): FormConfig => {
    const currentFields = globalFieldStore.getAllFields();
    const schemaFields: Record<string, any> = {};

    currentFields.forEach((field) => {
      let fieldSchema: any;

      switch (field.type) {
        case "number":
        case "slider":
        case "rating":
          fieldSchema = z.number();
          break;
        case "checkbox":
        case "switch":
          fieldSchema = z.boolean();
          break;
        case "date":
          fieldSchema = z.string();
          break;
        case "multiSelect":
        case "array":
          fieldSchema = z.array(z.string());
          break;
        case "object":
          fieldSchema = z.object({});
          break;
        default:
          fieldSchema = z.string();
      }

      if (field.required) {
        if (field.type === "number" || field.type === "slider" || field.type === "rating") {
          // For numbers, required means not null/undefined
        } else if (field.type === "checkbox" || field.type === "switch") {
          fieldSchema = fieldSchema.refine((val: boolean) => val === true, {
            message: field.validationConfig?.customMessages?.required || `${field.label} is required`,
          });
        } else if (typeof fieldSchema.min === "function") {
          fieldSchema = fieldSchema.min(1, field.validationConfig?.customMessages?.required || `${field.label} is required`);
        }
      } else {
        fieldSchema = fieldSchema.optional();
      }

      schemaFields[field.name] = fieldSchema;
    });

    return {
      title: formMetaRef.current.title,
      description: formMetaRef.current.description,
      schema: z.object(schemaFields),
      settings: formMetaRef.current.settings,
      fields: currentFields.map((field) => ({
        id: field.id,
        name: field.name,
        type: field.type,
        label: field.label,
        placeholder: field.placeholder,
        description: field.description,
        page: field.page || 1,
        tab: field.tab,
        group: field.group,
        section: field.section,
        help: field.help,
        inlineValidation: field.inlineValidation,
        ...(field.options && { options: field.options }),
        ...(field.arrayConfig && { arrayConfig: field.arrayConfig }),
        ...(field.datalist && { datalist: field.datalist }),
        ...(field.multiSelectConfig && { multiSelectConfig: field.multiSelectConfig }),
        ...(field.colorConfig && { colorConfig: field.colorConfig }),
        ...(field.ratingConfig && { ratingConfig: field.ratingConfig }),
        ...(field.phoneConfig && { phoneConfig: field.phoneConfig }),
      })),
      pages: formMetaRef.current.layoutType === "pages" && formMetaRef.current.pages.length > 1 ? formMetaRef.current.pages : [],
      tabs: formMetaRef.current.layoutType === "tabs" ? formMetaRef.current.tabs : undefined,
      submitLabel: formMetaRef.current.settings.submitLabel,
      nextLabel: formMetaRef.current.settings.nextLabel,
      previousLabel: formMetaRef.current.settings.previousLabel,
      progress: formMetaRef.current.settings.showProgress
        ? { showSteps: true, showPercentage: true }
        : undefined,
      formOptions: {
        onSubmit: async ({ value }: any) => {
          console.log("Form submitted:", value);
          alert("Form submitted! Check console for values.");
        },
      },
    };
  }, []);

  // Export configuration
  const exportConfig = useCallback(() => {
    const formConfig = {
      title: formMetaRef.current.title,
      description: formMetaRef.current.description,
      fields: globalFieldStore.getAllFields().map((field) => ({
        name: field.name,
        type: field.type,
        label: field.label,
        placeholder: field.placeholder,
        description: field.description,
        required: field.required,
        page: field.page || 1,
        tab: field.tab,
        group: field.group,
        section: field.section,
        help: field.help,
        inlineValidation: field.inlineValidation,
        options: field.options,
        arrayConfig: field.arrayConfig,
        datalist: field.datalist,
        multiSelectConfig: field.multiSelectConfig,
        colorConfig: field.colorConfig,
        ratingConfig: field.ratingConfig,
        phoneConfig: field.phoneConfig,
      })),
      pages: formMetaRef.current.pages,
      tabs: formMetaRef.current.tabs,
      layoutType: formMetaRef.current.layoutType,
      settings: formMetaRef.current.settings,
    };

    const blob = new Blob([JSON.stringify(formConfig, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${formMetaRef.current.title.toLowerCase().replace(/\s+/g, "-")}-form.json`;
    a.click();
    URL.revokeObjectURL(url);
  }, []);

  // Import configuration
  const importConfig = useCallback(() => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = ".json";
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (!file) return;

      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const config = JSON.parse(e.target?.result as string);

          // Import fields
          globalFieldStore.importFields(config.fields || []);

          // Update form config - DIRECT ASSIGNMENT TO AVOID RE-RENDERS
          Object.assign(formMetaRef.current, {
            title: config.title || "Imported Form",
            description: config.description || "Imported from JSON",
            pages: config.pages || [{ page: 1, title: "Page 1", description: "" }],
            tabs: config.tabs || [{ id: "general", label: "General", description: "General information" }],
            layoutType: config.layoutType || "pages",
            settings: {
              submitLabel: config.settings?.submitLabel || config.submitLabel || "Submit",
              nextLabel: config.settings?.nextLabel || config.nextLabel || "Next",
              previousLabel: config.settings?.previousLabel || config.previousLabel || "Previous",
              showProgress: config.settings?.showProgress ?? (!!config.progress),
            }
          });

          setSelectedFieldId(null);
          forceRerender();
        } catch (error) {
          alert("Error importing configuration. Please check the file format.");
          console.error("Import error:", error);
        }
      };
      reader.readAsText(file);
    };
    input.click();
  }, [forceRerender]);

  // Submit configuration to parent
  const submitConfig = useCallback(() => {
    if (onSubmit) {
      const config = {
        title: formMetaRef.current.title,
        description: formMetaRef.current.description,
        fields: globalFieldStore.getAllFields().map((field) => ({
          name: field.name,
          type: field.type,
          label: field.label,
          placeholder: field.placeholder,
          description: field.description,
          required: field.required,
          page: field.page || 1,
          tab: field.tab,
          group: field.group,
          section: field.section,
          help: field.help,
          inlineValidation: field.inlineValidation,
          options: field.options,
          arrayConfig: field.arrayConfig,
          datalist: field.datalist,
          multiSelectConfig: field.multiSelectConfig,
          colorConfig: field.colorConfig,
          ratingConfig: field.ratingConfig,
          phoneConfig: field.phoneConfig,
        })),
        pages: formMetaRef.current.pages,
        tabs: formMetaRef.current.tabs,
        layoutType: formMetaRef.current.layoutType,
        settings: formMetaRef.current.settings,
      };
      onSubmit({
        title: config.title,
        description: config.description,
        config: JSON.stringify(config),
      });
    }
  }, [onSubmit]);

  // Add keyboard shortcut for save (Ctrl+S)
  React.useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.ctrlKey || e.metaKey) && e.key === "s") {
        e.preventDefault();
        submitConfig();
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [submitConfig]);

  const contextValue: BuilderContextType = {
    // Form metadata - NON-REACTIVE
    getFormMetadata,
    onFormMetadataChange,

    // Field operations
    selectedFieldId,
    onSelectField,
    onAddField,
    onDeleteField,
    onDuplicateField,

    // UI state
    selectedPageId,
    selectedTabId,
    setSelectedPageId,
    setSelectedTabId,
    editingPageId,
    setEditingPageId,
    editingTabId,
    setEditingTabId,
    previewMode,
    setPreviewMode,

    // Data access
    getAllFields,
    getFieldsByPage,
    getFieldsByTab,
    getFormConfig,

    // Import/Export
    exportConfig,
    importConfig,

    // Context-specific
    forceRerender,

    // Submit functionality
    submitConfig,
    isLoading,
  };

  return (
    <BuilderContext.Provider value={contextValue}>
      {children}
    </BuilderContext.Provider>
  );
};

"use client";
import React, { useState, useMemo } from "react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { cn } from "@/lib/utils";
import { useBuilderContext } from "./BuilderContext";
import type { TabConfig, TabContainerProps } from "@/lib/formedible/builder-types";

export const TabContainer: React.FC<TabContainerProps> = ({
  tabs,
  defaultTab,
  onTabChange,
  className,
}) => {
  const builderContext = useBuilderContext();

  // Filter and sort tabs
  const activeTabs = useMemo(() => {
    return tabs
      .filter(tab => tab.enabled !== false)
      .sort((a, b) => {
        // Sort by order if provided, otherwise maintain array order
        const orderA = a.order ?? 0;
        const orderB = b.order ?? 0;
        return orderA - orderB;
      });
  }, [tabs]);

  // Determine initial active tab
  const initialTab = useMemo(() => {
    if (defaultTab && activeTabs.some(tab => tab.id === defaultTab)) {
      return defaultTab;
    }
    return activeTabs.length > 0 ? activeTabs[0].id : "";
  }, [defaultTab, activeTabs]);

  const [activeTab, setActiveTab] = useState(initialTab);

  const handleTabChange = (value: string) => {
    setActiveTab(value);
    onTabChange?.(value);
  };

  if (activeTabs.length === 0) {
    return (
      <div className="flex items-center justify-center h-64 border-2 border-dashed border-muted-foreground/25 rounded-lg">
        <div className="text-center">
          <h3 className="text-lg font-medium text-foreground mb-2">No Tabs Available</h3>
          <p className="text-muted-foreground">No enabled tabs to display</p>
        </div>
      </div>
    );
  }

  return (
    <Tabs 
      value={activeTab} 
      onValueChange={handleTabChange} 
      className={cn("flex-1 flex flex-col", className)}
    >
      <TabsList className="w-full justify-start border-b rounded-none h-14 bg-card p-0">
        {activeTabs.map((tab) => {
          const IconComponent = tab.icon;
          return (
            <TabsTrigger 
              key={tab.id} 
              value={tab.id} 
              className="flex items-center gap-2 h-full px-6"
            >
              {IconComponent && <IconComponent className="h-4 w-4" />}
              {tab.label}
            </TabsTrigger>
          );
        })}
      </TabsList>

      <div className="flex-1 min-h-0">
        {activeTabs.map((tab) => {
          const TabComponent = tab.component;
          return (
            <TabsContent 
              key={tab.id} 
              value={tab.id} 
              className="h-full m-0"
            >
              <TabComponent {...builderContext} />
            </TabsContent>
          );
        })}
      </div>
    </Tabs>
  );
};

import { Settings, Eye, Code } from "lucide-react";
import { BuilderTabContent } from "./tabs/BuilderTabContent";
import { PreviewTabContent } from "./tabs/PreviewTabContent";
import { CodeTabContent } from "./tabs/CodeTabContent";
import type { TabConfig } from "@/lib/formedible/builder-types";

export const builderTab: TabConfig = {
  id: "builder",
  label: "Builder",
  icon: Settings,
  component: BuilderTabContent,
  enabled: true,
  order: 1,
};

export const previewTab: TabConfig = {
  id: "preview",
  label: "Preview",
  icon: Eye,
  component: PreviewTabContent,
  enabled: true,
  order: 2,
};

export const codeTab: TabConfig = {
  id: "code",
  label: "Code",
  icon: Code,
  component: CodeTabContent,
  enabled: true,
  order: 3,
};

export const defaultTabs: TabConfig[] = [
  builderTab,
  previewTab,
  codeTab,
];

// Helper functions for common tab configurations
export const getBuilderOnlyTabs = (): TabConfig[] => [builderTab];

export const getBuilderAndPreviewTabs = (): TabConfig[] => [builderTab, previewTab];

export const getBuilderAndCodeTabs = (): TabConfig[] => [builderTab, codeTab];

export const getPreviewAndCodeTabs = (): TabConfig[] => [previewTab, codeTab];

// Factory function to create custom tab orders
export const createTabsWithOrder = (tabIds: string[]): TabConfig[] => {
  const tabMap = new Map([
    ["builder", builderTab],
    ["preview", previewTab],
    ["code", codeTab],
  ]);

  return tabIds
    .map(id => tabMap.get(id))
    .filter((tab): tab is TabConfig => tab !== undefined)
    .map((tab, index) => ({ ...tab, order: index + 1 }));
};

// Factory function to disable specific tabs
export const createTabsWithDisabled = (disabledIds: string[]): TabConfig[] => {
  return defaultTabs.map(tab => ({
    ...tab,
    enabled: !disabledIds.includes(tab.id),
  }));
};

