    visualizationComponent?: React.ComponentType<{
      value: number;
      displayValue: string | number;
      label?: string;
      isActive: boolean;
    }>;
    // Legacy and additional config
    valueLabelPrefix?: string;
    valueLabelSuffix?: string;
    valueDisplayPrecision?: number;
    showRawValue?: boolean;
    showValue?: boolean;
    showTooltip?: boolean;
    showTicks?: boolean;
