export interface SliderFieldSpecificProps extends BaseFieldProps {
  sliderConfig?: {
    min?: number;
    max?: number;
    step?: number;
    // Value mapping between slider value (int) and display value (arbitrary)
    valueMapping?: Array<{
      sliderValue: number;
