// Constants - prevent re-creation
const DEFAULT_LOCATION = { lat: 51.5074, lng: -0.1278 };

// Load Leaflet CSS and JS dynamically
const loadLeaflet = () => {
  if (typeof (window as any).L !== "undefined") {
    return Promise.resolve();
  }

  return new Promise<void>((resolve, reject) => {
    // Load CSS
    const link = document.createElement("link");
    link.rel = "stylesheet";
    link.href = "https://unpkg.com/leaflet@1.9.4/dist/leaflet.css";
    document.head.appendChild(link);

    // Load JS
    const script = document.createElement("script");
    script.src = "https://unpkg.com/leaflet@1.9.4/dist/leaflet.js";
    script.onload = () => resolve();
    script.onerror = () => reject(new Error("Failed to load Leaflet"));
    document.head.appendChild(script);
  });
};

interface LocationPickerFieldProps extends BaseFieldProps {
  locationConfig?: LocationConfig;
}

export const LocationPickerField: React.FC<LocationPickerFieldProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  wrapperClassName,
  labelClassName,
  inputClassName,
  locationConfig,
}) => {
  // Extract config with defaults - NO objects created inline
  const config = locationConfig || {};
  const defaultLocation = config.defaultLocation || DEFAULT_LOCATION;
  const zoom = config.zoom || 10;
  const searchPlaceholder = config.searchPlaceholder || " Search for an address or place...";
  const enableSearch = config.enableSearch !== false;
  const enableGeolocation = config.enableGeolocation !== false;
  const enableManualEntry = config.enableManualEntry !== false;
  const showMap = config.showMap !== false;
  const mapProvider = config.mapProvider || "openstreetmap";
  const searchCallback = config.searchCallback;
  const reverseGeocodeCallback = config.reverseGeocodeCallback;
  const mapRenderCallback = config.mapRenderCallback;

  // Search options with defaults
  const searchOpts = config.searchOptions || {};
  const debounceMs = searchOpts.debounceMs || 300;
  const minQueryLength = searchOpts.minQueryLength || 2;
  const maxResults = searchOpts.maxResults || 5;

  // UI options with defaults
  const uiOpts = config.ui || {};
  const showCoordinates = uiOpts.showCoordinates !== false;
  const showAddress = uiOpts.showAddress !== false;
  const mapHeight = uiOpts.mapHeight || 400;
  const coordinatesFormat = uiOpts.coordinatesFormat || "decimal";

  // Simple state - no complex dependencies
  const [searchQuery, setSearchQuery] = useState("");
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState<LocationSearchResult[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [currentLocation, setCurrentLocation] = useState<LocationValue | null>(fieldApi.state?.value || null);
  const [geoError, setGeoError] = useState<string | null>(null);
  const [manualLat, setManualLat] = useState("");
  const [manualLng, setManualLng] = useState("");
  const [isManualDialogOpen, setIsManualDialogOpen] = useState(false);
  const [leafletLoaded, setLeafletLoaded] = useState(false);

  const mapRef = useRef<HTMLDivElement>(null);
  const searchTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const mapInstanceRef = useRef<any>(null);

  // Load Leaflet on mount only if map is enabled - simple
  useEffect(() => {
    if (!showMap) {
      setLeafletLoaded(false);
      return;
    }

    loadLeaflet()
      .then(() => setLeafletLoaded(true))
      .catch(() => setGeoError("Failed to load map library"));
  }, [showMap]);

  // Search function - simple, no complex dependencies
  const performSearch = useCallback(async (query: string) => {
    if (!query.trim() || query.length < minQueryLength) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    setIsSearching(true);
    try {
      let results: LocationSearchResult[];

      if (searchCallback) {
        results = await searchCallback(query, { limit: maxResults });
      } else {
        results = await builtInProviders.nominatim(query, { 
          limit: maxResults,
          ...config.openStreetMap 
        });
      }

      setSearchResults(results);
      setShowResults(true);
    } catch (error) {
      console.error("Location search error:", error);
      setSearchResults([]);
      setGeoError("Search failed. Please try again.");
    } finally {
      setIsSearching(false);
    }
  }, [searchCallback, minQueryLength, maxResults, config.openStreetMap]);

  // Debounced search - simple
  useEffect(() => {
    if (!enableSearch) return;

    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    searchTimeoutRef.current = setTimeout(() => {
      performSearch(searchQuery);
    }, debounceMs);

    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, [searchQuery, enableSearch, performSearch, debounceMs]);

  // Location selection - simple
  const handleLocationSelect = useCallback(async (location: LocationValue) => {
    // Add address if missing
    if (!location.address && location.lat && location.lng) {
      try {
        let geocodedLocation: LocationValue;

        if (reverseGeocodeCallback) {
          geocodedLocation = await reverseGeocodeCallback(location.lat, location.lng);
        } else {
          geocodedLocation = await builtInProviders.nominatimReverse(
            location.lat, 
            location.lng, 
            config.openStreetMap
          );
        }

        location = { ...location, ...geocodedLocation };
      } catch (error) {
        console.error("Reverse geocoding error:", error);
        location.address = `${location.lat}, ${location.lng}`;
      }
    }

    setCurrentLocation(location);
    fieldApi.handleChange(location);
    setShowResults(false);
    setSearchQuery(location.address || `${location.lat}, ${location.lng}`);
    setGeoError(null);

    if (mapInstanceRef.current?.updateLocation) {
      mapInstanceRef.current.updateLocation(location);
    }
  }, [fieldApi, reverseGeocodeCallback, config.openStreetMap]);

  // Initialize map - simple
  useEffect(() => {
    if (!showMap || !mapRef.current || !leafletLoaded) return;

    const mapRenderer = mapRenderCallback || defaultMapRenderer;

    const mapInstance = mapRenderer({
      location: currentLocation,
      onLocationSelect: handleLocationSelect,
      mapContainer: mapRef.current,
      zoom,
      readonly: false,
      defaultLocation,
    });

    mapInstanceRef.current = mapInstance;

    // Apply tile provider if not default
    if (mapProvider !== "openstreetmap") {
      const tileConfig = TILE_PROVIDERS[mapProvider as keyof typeof TILE_PROVIDERS];
      if (tileConfig && 'switchTileLayer' in mapInstance) {
        (mapInstance as any).switchTileLayer(tileConfig);
      }
    }

    return () => {
      if (mapInstance.cleanup) {
        mapInstance.cleanup();
      }
    };
  }, [showMap, leafletLoaded, currentLocation, handleLocationSelect, mapRenderCallback, zoom, defaultLocation, mapProvider]);

  // Simple handlers - no complex state
  const handleGetCurrentLocation = () => {
    if (!enableGeolocation || !navigator.geolocation) {
      setGeoError("Geolocation is not supported by your browser");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const location: LocationValue = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          address: "Current Location",
        };
        handleLocationSelect(location);
      },
      (error) => {
        const errorMessages: Record<number, string> = {
          1: "Location access denied. Please enable location permissions.",
          2: "Location unavailable. Please try again.",
          3: "Location request timed out. Please try again.",
        };
        setGeoError(errorMessages[error.code] || "Failed to get location");
      }
    );
  };

  const handleManualCoordinatesSubmit = () => {
    const lat = parseFloat(manualLat);
    const lng = parseFloat(manualLng);
    if (!isNaN(lat) && !isNaN(lng)) {
      const location: LocationValue = {
        lat,
        lng,
        address: `${lat}, ${lng}`,
      };
      handleLocationSelect(location);
      setIsManualDialogOpen(false);
      setManualLat("");
      setManualLng("");
    } else {
      setGeoError("Invalid coordinates. Please enter valid numbers.");
    }
  };

  const handleClearLocation = () => {
    setCurrentLocation(null);
    fieldApi.handleChange(null);
    setSearchQuery("");
    setGeoError(null);

    if (mapInstanceRef.current?.updateLocation) {
      mapInstanceRef.current.updateLocation(null);
    }
  };

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="space-y-3">
        {/* Address Search Input */}
        {enableSearch && (
          <div className="relative">
            <div className="relative">
              <Input
                id={fieldApi.name}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder={placeholder || searchPlaceholder}
                className={cn(
                  "pl-10 pr-4",
                  inputClassName,
                  uiOpts.searchInputClassName,
                  isSearching && "animate-pulse"
                )}
                onFocus={() => searchResults.length > 0 && setShowResults(true)}
                onBlur={() => setTimeout(() => setShowResults(false), 200)}
              />
              <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground">
                {isSearching ? (
                  <div className="w-4 h-4 border-2 border-muted-foreground border-t-transparent rounded-full animate-spin" />
                ) : (
                  <span className="text-sm"></span>
                )}
              </div>
              {searchQuery && (
                <button
                  type="button"
                  onClick={() => {
                    setSearchQuery("");
                    setSearchResults([]);
                    setShowResults(false);
                  }}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-muted-foreground hover:text-foreground"
                >

                </button>
              )}
            </div>

            {/* Enhanced Search Results */}
            {showResults && searchResults.length > 0 && (
              <Card className="absolute top-full left-0 right-0 z-[999] mt-1 max-h-60 overflow-y-auto shadow-lg border-2">
                <div className="p-1">
                  {searchResults.map((result) => (
                    <button
                      key={result.id}
                      type="button"
                      className="w-full text-left p-3 hover:bg-muted/80 rounded-md text-sm transition-colors border-b border-border/50 last:border-b-0"
                      onClick={() => handleLocationSelect(result)}
                    >
                      <div className="flex items-start gap-2">
                        <span className="text-lg mt-0.5"></span>
                        <div className="flex-1 min-w-0">
                          <div className="font-medium text-foreground truncate">
                            {result.address}
                          </div>
                          {showCoordinates && (
                            <div className="text-xs text-muted-foreground mt-1">
                              {formatCoordinates(
                                result.lat,
                                result.lng,
                                coordinatesFormat
                              )}
                            </div>
                          )}
                          {result.city && (
                            <div className="text-xs text-muted-foreground/80 mt-0.5">
                              {[result.city, result.state, result.country]
                                .filter(Boolean)
                                .join(", ")}
                            </div>
                          )}
                        </div>
                      </div>
                    </button>
                  ))}
                </div>
              </Card>
            )}

            {/* No Results Message */}
            {showResults &&
              searchResults.length === 0 &&
              !isSearching &&
              searchQuery.length >= minQueryLength && (
                <Card className="absolute top-full left-0 right-0 z-[70] mt-1 shadow-lg">
                  <div className="p-4 text-center text-muted-foreground">
                    <div className="text-2xl mb-2"></div>
                    <div className="text-sm">
                      No locations found for "{searchQuery}"
                    </div>
                    <div className="text-xs mt-1">
                      Try a different search term or use coordinates
                    </div>
                  </div>
                </Card>
              )}
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex flex-wrap gap-2">
          {enableGeolocation && (
            <Button
              type="button"
              variant="outline"
              size="sm"
              onClick={handleGetCurrentLocation}
            >
               Current Location
            </Button>
          )}

          {enableManualEntry && (
            <Dialog
              open={isManualDialogOpen}
              onOpenChange={setIsManualDialogOpen}
            >
              <DialogTrigger asChild>
                <Button type="button" variant="outline" size="sm">
                   Enter Coordinates
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                  <DialogTitle>Enter Coordinates</DialogTitle>
                  <DialogDescription>
                    Enter the latitude and longitude coordinates.
                  </DialogDescription>
                </DialogHeader>
                <div className="grid gap-4 py-4">
                  <div className="grid grid-cols-4 items-center gap-4">
                    <Label htmlFor="latitude" className="text-right">
                      Latitude
                    </Label>
                    <Input
                      id="latitude"
                      value={manualLat}
                      onChange={(e) => setManualLat(e.target.value)}
                      className="col-span-3"
                      placeholder="e.g., 40.7128"
                    />
                  </div>
                  <div className="grid grid-cols-4 items-center gap-4">
                    <Label htmlFor="longitude" className="text-right">
                      Longitude
                    </Label>
                    <Input
                      id="longitude"
                      value={manualLng}
                      onChange={(e) => setManualLng(e.target.value)}
                      className="col-span-3"
                      placeholder="e.g., -74.0060"
                    />
                  </div>
                </div>
                <DialogFooter>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setIsManualDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="button" onClick={handleManualCoordinatesSubmit}>
                    Set Location
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          )}

          {currentLocation && (
            <Button
              type="button"
              variant="outline"
              size="sm"
              onClick={handleClearLocation}
            >
               Clear
            </Button>
          )}
        </div>

        {/* Map */}
        {showMap && (
          <div className="relative">
            {!leafletLoaded && (
              <div className="absolute inset-0 flex items-center justify-center bg-muted rounded-md">
                <div className="text-muted-foreground">Loading map...</div>
              </div>
            )}
            <div
              ref={mapRef}
              className={cn("w-full border rounded-md", uiOpts.mapClassName)}
              style={{ height: `${mapHeight}px`, minHeight: "300px" }}
            />
          </div>
        )}

        {/* Location Display */}
        {currentLocation && (
          <div className="text-sm space-y-1">
            {showAddress && currentLocation.address && (
              <div className="font-medium">{currentLocation.address}</div>
            )}
            {showCoordinates && (
              <div className="text-muted-foreground">
                {" "}
                {formatCoordinates(
                  currentLocation.lat,
                  currentLocation.lng,
                  coordinatesFormat
                )}
              </div>
            )}
            {currentLocation.city && (
              <div className="text-muted-foreground text-xs">
                {[
                  currentLocation.city,
                  currentLocation.state,
                  currentLocation.country,
                ]
                  .filter(Boolean)
                  .join(", ")}
              </div>
            )}
          </div>
        )}

        {/* Error Display */}
        {geoError && (
          <div className="text-sm text-destructive bg-destructive/10 p-2 rounded-md">
            {geoError}
          </div>
        )}
      </div>
    </FieldWrapper>
  );
};

"use client";
import React, { useState, useEffect, useRef } from "react";
import type { BaseFieldProps } from "@/lib/formedible/types";
import { FieldWrapper } from "./base-field-wrapper";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { cn } from "@/lib/utils";

interface MaskedInputFieldProps extends BaseFieldProps {
  maskedInputConfig?: {
    mask: string | ((value: string) => string);
    placeholder?: string;
    showMask?: boolean;
    guide?: boolean;
    keepCharPositions?: boolean;
    pipe?: (conformedValue: string, config: unknown) => false | string | { value: string; indexesOfPipedChars: number[] };
  };
}

// Common mask patterns
const MASK_PATTERNS = {
  phone: '(000) 000-0000',
  ssn: '000-00-0000',
  creditCard: '0000 0000 0000 0000',
  date: '00/00/0000',
  time: '00:00',
  zipCode: '00000',
  zipCodeExtended: '00000-0000',
  currency: '$0,000.00',
};

export const MaskedInputField: React.FC<MaskedInputFieldProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  wrapperClassName,
  labelClassName,
  inputClassName,
  maskedInputConfig = {},
}) => {
  const name = fieldApi.name;

  const {
    mask = '',
    showMask = false,
    guide = true,

    pipe
  } = maskedInputConfig;

  const [displayValue, setDisplayValue] = useState('');
  const [rawValue, setRawValue] = useState('');
  const inputRef = useRef<HTMLInputElement>(null);

  // Apply mask to value
  const applyMask = React.useCallback((value: string): string => {
    if (!mask) return value;

    if (typeof mask === 'function') {
      return mask(value);
    }

    // Handle string mask patterns
    let maskedValue = '';
    let digitIndex = 0;
    let letterIndex = 0;
    const cleanDigits = value.replace(/\D/g, ''); // Extract digits
    const cleanLetters = value.replace(/[^a-zA-Z]/g, ''); // Extract letters

    for (let i = 0; i < mask.length; i++) {
      const maskChar = mask[i];

      if (maskChar === '0' || maskChar === '9') {
        // Digit placeholder
        if (digitIndex < cleanDigits.length) {
          maskedValue += cleanDigits[digitIndex];
          digitIndex++;
        } else if (guide && showMask) {
          maskedValue += '_';
        } else {
          break; // Stop if no more digits and not showing guide
        }
      } else if (maskChar === 'A' || maskChar === 'a') {
        // Letter placeholder
        if (letterIndex < cleanLetters.length) {
          const char = cleanLetters[letterIndex];
          maskedValue += maskChar === 'A' ? char.toUpperCase() : char.toLowerCase();
          letterIndex++;
        } else if (guide && showMask) {
          maskedValue += '_';
        } else {
          break; // Stop if no more letters and not showing guide
        }
      } else {
        // Literal character
        maskedValue += maskChar;
      }
    }

    // Apply pipe function if provided
    if (pipe) {
      const piped = pipe(maskedValue, { mask, guide, showMask });
      if (piped === false) {
        return displayValue; // Reject the change
      }
      if (typeof piped === 'string') {
        return piped;
      }
      if (piped && typeof piped === 'object' && piped.value) {
        return piped.value;
      }
    }

    return maskedValue;
  }, [mask, guide, showMask, pipe, displayValue]);

  // Initialize from field value
  useEffect(() => {
    const value = fieldApi.state?.value || '';
    setRawValue(value);
    setDisplayValue(applyMask(value));
  }, [fieldApi.state?.value, applyMask]);

  // Extract raw value from masked value
  const extractRawValue = (maskedValue: string): string => {
    if (!mask || typeof mask === 'function') {
      return maskedValue;
    }

    // For string masks, extract only the actual input characters
    let rawValue = '';
    let maskIndex = 0;

    for (let i = 0; i < maskedValue.length && maskIndex < mask.length; i++) {
      const char = maskedValue[i];
      const maskChar = mask[maskIndex];

      if (maskChar === '0' || maskChar === '9') {
        if (/\d/.test(char)) {
          rawValue += char;
        }
        maskIndex++;
      } else if (maskChar === 'A' || maskChar === 'a') {
        if (/[a-zA-Z]/.test(char)) {
          rawValue += char;
        }
        maskIndex++;
      } else if (char === maskChar) {
        // Skip literal characters
        maskIndex++;
      } else {
        // Character doesn't match mask, skip it
        continue;
      }
    }

    return rawValue;
  };

  // Handle input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    const newRawValue = extractRawValue(inputValue);
    const newDisplayValue = applyMask(newRawValue);

    setRawValue(newRawValue);
    setDisplayValue(newDisplayValue);
    fieldApi.handleChange(newRawValue);
  };

  // Handle key down for better UX
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    const input = e.target as HTMLInputElement;
    const { selectionStart, selectionEnd } = input;

    // Handle backspace to skip over literal characters
    if (e.key === 'Backspace' && selectionStart !== null && selectionEnd !== null && selectionStart === selectionEnd && selectionStart > 0) {
      const maskChar = typeof mask === 'string' ? mask[selectionStart - 1] : '';

      // If the previous character is a literal (not a placeholder), skip it
      if (maskChar && maskChar !== '0' && maskChar !== '9' && maskChar !== 'A' && maskChar !== 'a') {
        e.preventDefault();
        const newCursorPos = selectionStart - 1;
        setTimeout(() => {
          if (inputRef.current) {
            inputRef.current.setSelectionRange(newCursorPos, newCursorPos);
          }
        }, 0);
      }
    }
  };

  // Get placeholder text
  const getPlaceholder = (): string => {
    if (placeholder) return placeholder;
    if (maskedInputConfig.placeholder) return maskedInputConfig.placeholder;
    if (showMask && typeof mask === 'string') {
      return mask.replace(/[09Aa]/g, '_');
    }
    return '';
  };

  return (
    <div className={cn("space-y-2", wrapperClassName)}>
      {label && (
        <Label htmlFor={name} className={labelClassName}>
          {label}
        </Label>
      )}

      {description && (
        <p className="text-sm text-muted-foreground">{description}</p>
      )}

      <Input
        ref={inputRef}
        id={name}
        name={name}
        value={displayValue}
        onChange={handleInputChange}
        onKeyDown={handleKeyDown}
        placeholder={getPlaceholder()}
        className={inputClassName}
      />

      {/* Show mask pattern hint */}
      {mask && typeof mask === 'string' && (
        <div className="text-xs text-muted-foreground">
          Format: {mask.replace(/[09]/g, '#').replace(/[Aa]/g, 'A')}
        </div>
      )}

      {/* Show raw value for debugging */}
      {process.env.NODE_ENV === 'development' && rawValue !== displayValue && (
        <div className="text-xs text-muted-foreground">
          Raw value: {rawValue}
        </div>
      )}

      {fieldApi.state?.meta?.errors && fieldApi.state?.meta?.errors.length > 0 && (
        <p className="text-sm text-destructive">
          {fieldApi.state?.meta?.errors[0]}
        </p>
      )}
    </div>
  );
};

// Export common mask patterns for convenience
export { MASK_PATTERNS };

'use client';
import React, { useState, useRef, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';

import { cn } from '@/lib/utils';
import { X, ChevronDown, Check } from 'lucide-react';
import type { MultiSelectFieldSpecificProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';


export const MultiSelectField: React.FC<MultiSelectFieldSpecificProps> = ({
  fieldApi,
  options = [],
  multiSelectConfig = {},

  ...wrapperProps
}) => {
  const {
    maxSelections = Infinity,
    searchable = true,
    creatable = false,
    placeholder = 'Select options...',
    noOptionsText = 'No options found',
  } = multiSelectConfig;

  const name = fieldApi.name;
  const selectedValues = Array.isArray(fieldApi.state?.value) ? fieldApi.state?.value : [];

  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const containerRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const normalizedOptions = options.map(option => 
    typeof option === 'string' 
      ? { value: option, label: option }
      : option
  );

  // Filter options based on search query
  const filteredOptions = normalizedOptions.filter(option =>
    option.label.toLowerCase().includes(searchQuery.toLowerCase()) ||
    option.value.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Add create option if enabled and query doesn't match existing options
  const canCreate = creatable && 
    searchQuery.trim() && 
    !normalizedOptions.some(opt => 
      opt.value.toLowerCase() === searchQuery.toLowerCase() ||
      opt.label.toLowerCase() === searchQuery.toLowerCase()
    ) &&
    !selectedValues.includes(searchQuery.trim());

  const displayOptions = [...filteredOptions];
  if (canCreate) {
    displayOptions.unshift({
      value: searchQuery.trim(),
      label: `Create "${searchQuery.trim()}"`,
      isCreateOption: true
    } as { value: string; label: string; isCreateOption: true });
  }

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
        setIsOpen(false);
        setSearchQuery('');
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleSelect = (optionValue: string) => {
    if (selectedValues.includes(optionValue)) {
      // Remove if already selected
      const newValues = selectedValues.filter(v => v !== optionValue);
      fieldApi.handleChange(newValues);
    } else if (selectedValues.length < maxSelections) {
      // Add if not at max selections
      const newValues = [...selectedValues, optionValue];
      fieldApi.handleChange(newValues);
    }

    setSearchQuery('');
    if (!searchable) {
      setIsOpen(false);
    }
    inputRef.current?.focus();
  };

  const handleRemove = (valueToRemove: string) => {
    const newValues = selectedValues.filter(v => v !== valueToRemove);
    fieldApi.handleChange(newValues);
    fieldApi.handleBlur();
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Backspace' && !searchQuery && selectedValues.length > 0) {
      // Remove last selected item on backspace
      handleRemove(selectedValues[selectedValues.length - 1]);
    } else if (e.key === 'Enter' && canCreate) {
      e.preventDefault();
      handleSelect(searchQuery.trim());
    } else if (e.key === 'Escape') {
      setIsOpen(false);
      setSearchQuery('');
    }
  };

  const getSelectedLabels = () => {
    return selectedValues.map(value => {
      const option = normalizedOptions.find(opt => opt.value === value);
      return option ? option.label : value;
    });
  };

  const isDisabled = fieldApi.form.state.isSubmitting;

  return (
    <FieldWrapper fieldApi={fieldApi} {...wrapperProps}>
      <div className="space-y-2" ref={containerRef}>
        {wrapperProps.label && maxSelections < Infinity && (
          <div className="text-sm text-muted-foreground">
            ({selectedValues.length}/{maxSelections})
          </div>
        )}

        <div className="relative">
          {/* Selected items display */}
          <div
            className={cn(
              "min-h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background",
              "focus-within:ring-2 focus-within:ring-ring focus-within:ring-offset-2",
              fieldApi.state?.meta?.errors.length ? "border-destructive" : "",
              isDisabled ? "opacity-50 cursor-not-allowed" : "cursor-text"
            )}
            onClick={() => {
              if (!isDisabled) {
                setIsOpen(true);
                inputRef.current?.focus();
              }
            }}
          >
          <div className="flex flex-wrap gap-1 items-center">
            {/* Selected tags */}
            {selectedValues.map((value, index) => {
              const label = getSelectedLabels()[index];
              return (
                <Badge
                  key={value}
                  variant="secondary"
                  className="text-xs h-6 px-2 gap-1"
                >
                  {label}
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="h-3 w-3 p-0 hover:bg-destructive hover:text-destructive-foreground"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleRemove(value);
                    }}
                    disabled={isDisabled}
                  >
                    <X className="h-2 w-2" />
                  </Button>
                </Badge>
              );
            })}

            {/* Search input */}
            {searchable && (
              <Input
                ref={inputRef}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyDown={handleKeyDown}
                onFocus={() => setIsOpen(true)}
                onBlur={fieldApi.handleBlur}
                placeholder={selectedValues.length === 0 ? placeholder : ''}
                className="border-0 p-0 h-6 focus-visible:ring-0 focus-visible:ring-offset-0 bg-transparent"
                disabled={isDisabled || selectedValues.length >= maxSelections}
              />
            )}

            {/* Dropdown indicator */}
            <ChevronDown 
              className={cn(
                "h-4 w-4 text-muted-foreground transition-transform ml-auto",
                isOpen ? "rotate-180" : ""
              )} 
            />
          </div>
        </div>

        {/* Dropdown */}
        {isOpen && (
          <div className="absolute z-50 w-full mt-1 bg-popover border rounded-md shadow-lg max-h-60 overflow-y-auto">
            {displayOptions.length === 0 ? (
              <div className="p-2 text-sm text-muted-foreground text-center">
                {noOptionsText}
              </div>
            ) : (
              displayOptions.map((option: { value: string; label: string; isCreateOption?: boolean }, index) => {
                const isSelected = selectedValues.includes(option.value);
                const isDisabled = !isSelected && selectedValues.length >= maxSelections;

                return (
                  <button
                    key={`${option.value}-${index}`}
                    type="button"
                    className={cn(
                      "w-full px-3 py-2 text-left text-sm hover:bg-accent hover:text-accent-foreground",
                      "flex items-center justify-between",
                      isSelected ? "bg-accent" : "",
                      isDisabled ? "opacity-50 cursor-not-allowed" : "",
                      option.isCreateOption ? "font-medium text-primary" : ""
                    )}
                    onClick={() => !isDisabled && handleSelect(option.value)}
                    disabled={isDisabled}
                  >
                    <span>{option.label}</span>
                    {isSelected && <Check className="h-4 w-4" />}
                  </button>
                );
              })
            )}
          </div>
        )}
      </div>

      </div>
    </FieldWrapper>
  );
};

'use client';
import React from 'react';
import { Input } from '@/components/ui/input';
import { cn } from '@/lib/utils';
import type { NumberFieldSpecificProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';


export const NumberField: React.FC<NumberFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
  min,
  max,
  step,
}) => {
  const name = fieldApi.name;
  const value = fieldApi.state?.value as number | string | undefined;
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;
  const hasErrors = fieldApi.state?.meta?.isTouched && fieldApi.state?.meta?.errors?.length > 0;

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = e.target.value;
    let parsedValue: number | string | undefined;

    if (val === '') {
      parsedValue = undefined;
    } else {
      const num = parseFloat(val);
      parsedValue = isNaN(num) ? val : num;
    }

    fieldApi.handleChange(parsedValue);
  };

  const onBlur = () => {
    fieldApi.handleBlur();
  };

  let displayValue: string | number = '';
  if (typeof value === 'number') {
    displayValue = value;
  } else if (typeof value === 'string') {
    displayValue = value;
  }

  const computedInputClassName = cn(
    inputClassName,
    hasErrors ? "border-destructive" : ""
  );

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <Input
        id={name}
        name={name}
        type="number"
        value={displayValue}
        onBlur={onBlur}
        onChange={onChange}
        placeholder={placeholder}
        className={computedInputClassName}
        disabled={isDisabled}
        min={min}
        max={max}
        step={step}
      />
    </FieldWrapper>
  );
};

"use client";
import React from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import type { BaseFieldProps, ObjectFieldProps, LayoutConfig } from "@/lib/formedible/types";
import { FieldWrapper } from './base-field-wrapper';
import { NestedFieldRenderer } from './shared-field-renderer';

export const ObjectField: React.FC<ObjectFieldProps> = ({
  fieldApi,
  objectConfig,
  disabled,
  form,
  ...wrapperProps
}) => {
  const [isExpanded, setIsExpanded] = React.useState(
    objectConfig?.defaultExpanded !== false
  );

  // Create a properly typed mockFieldApi that includes the form property
  const createMockFieldApi = (fieldName: string, fieldValue: unknown) => {
    return {
      name: `${fieldApi.name}.${fieldName}`,
      form: fieldApi.form, // Include the form property to fix the bug
      state: {
        ...fieldApi.state,
        value: fieldValue,
        meta: {
          ...fieldApi.state.meta,
          errors: [], // Reset errors for subfield
          isTouched: false, // Reset touched state for subfield
        }
      },
      handleChange: (value: unknown) => {
        const currentValue = fieldApi.state?.value || {};
        fieldApi.handleChange({
          ...currentValue,
          [fieldName]: value
        });
      },
      handleBlur: fieldApi.handleBlur,
    };
  };

  const renderField = (subFieldConfig: any) => {
    const fieldValue = fieldApi.state?.value?.[subFieldConfig.name] || '';
    const mockFieldApi = createMockFieldApi(subFieldConfig.name, fieldValue) as unknown as BaseFieldProps['fieldApi'];

    return (
      <div key={subFieldConfig.name}>
        <NestedFieldRenderer
          fieldConfig={subFieldConfig}
          fieldApi={mockFieldApi}
          form={form}
          currentValues={form?.state?.values || {}}
        />
      </div>
    );
  };

  const getLayoutClasses = () => {
    const layout = objectConfig?.layout || "vertical";
    const columns = objectConfig?.columns || 2;

    switch (layout) {
      case "horizontal":
        return "flex flex-wrap gap-4";
      case "grid":
        return `grid grid-cols-1 md:grid-cols-${columns} gap-4`;
      default:
        return "space-y-4";
    }
  };

  const content = (
    <FieldWrapper fieldApi={fieldApi} {...wrapperProps}>
      <div className="space-y-4">
        {/* Object title and description */}
        {(objectConfig?.title || objectConfig?.description) && (
          <div className="space-y-1">
            {objectConfig?.title && (
              <div className="flex items-center justify-between">
                <h4 className="text-sm font-medium text-muted-foreground">
                  {objectConfig.title}
                </h4>
                {objectConfig?.collapsible && (
                  <button
                    type="button"
                    onClick={() => setIsExpanded(!isExpanded)}
                    className="text-xs text-muted-foreground hover:text-foreground"
                  >
                    {isExpanded ? (objectConfig?.collapseLabel || "Collapse") : (objectConfig?.expandLabel || "Expand")}
                  </button>
                )}
              </div>
            )}
            {objectConfig?.description && (
              <p className="text-xs text-muted-foreground">
                {objectConfig.description}
              </p>
            )}
          </div>
        )}

        {/* Fields */}
        {(!objectConfig?.collapsible || isExpanded) && (
          <>
            {objectConfig?.title && <div className="border-t my-4" />}
            <div className={getLayoutClasses()}>
              {objectConfig?.fields?.map(renderField)}
            </div>
          </>
        )}

        {/* Show field errors */}
        {fieldApi.state?.meta?.errors && fieldApi.state?.meta?.errors.length > 0 && (
          <div className="text-sm text-destructive">
            {fieldApi.state?.meta?.errors.join(", ")}
          </div>
        )}
      </div>
    </FieldWrapper>
  );

  // Wrap in card if specified
  if (objectConfig?.showCard) {
    return (
      <Card className="w-full">
        {(objectConfig?.title || objectConfig?.description) && (
          <CardHeader className="pb-3">
            {objectConfig?.title && (
              <div className="flex items-center justify-between">
                <CardTitle className="text-base">{objectConfig.title}</CardTitle>
                {objectConfig?.collapsible && (
                  <button
                    type="button"
                    onClick={() => setIsExpanded(!isExpanded)}
                    className="text-xs text-muted-foreground hover:text-foreground"
                  >
                    {isExpanded ? (objectConfig?.collapseLabel || "Collapse") : (objectConfig?.expandLabel || "Expand")}
                  </button>
                )}
              </div>
            )}
            {objectConfig?.description && (
              <p className="text-sm text-muted-foreground mt-1">
                {objectConfig.description}
              </p>
            )}
          </CardHeader>
        )}
        <CardContent className="pt-0">
          {(!objectConfig?.collapsible || isExpanded) && (
            <>
              <div className={getLayoutClasses()}>
                {objectConfig?.fields?.map(renderField)}
              </div>
            </>
          )}

          {/* Show field errors */}
          {fieldApi.state?.meta?.errors && fieldApi.state?.meta?.errors.length > 0 && (
            <div className="text-sm text-destructive mt-4">
              {fieldApi.state?.meta?.errors.join(", ")}
            </div>
          )}
        </CardContent>
      </Card>
    );
  }

  return content;
};

'use client';
import React, { useState, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { ChevronDown, Phone } from 'lucide-react';
import type { PhoneFieldSpecificProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';


// Common country codes and their formatting
const COUNTRY_CODES = {
  US: { code: '+1', name: 'United States', flag: '', format: '(###) ###-####' },
  CA: { code: '+1', name: 'Canada', flag: '', format: '(###) ###-####' },
  GB: { code: '+44', name: 'United Kingdom', flag: '', format: '#### ### ####' },
  FR: { code: '+33', name: 'France', flag: '', format: '## ## ## ## ##' },
  DE: { code: '+49', name: 'Germany', flag: '', format: '### ### ####' },
  IT: { code: '+39', name: 'Italy', flag: '', format: '### ### ####' },
  ES: { code: '+34', name: 'Spain', flag: '', format: '### ### ###' },
  AU: { code: '+61', name: 'Australia', flag: '', format: '#### ### ###' },
  JP: { code: '+81', name: 'Japan', flag: '', format: '##-####-####' },
  CN: { code: '+86', name: 'China', flag: '', format: '### #### ####' },
  IN: { code: '+91', name: 'India', flag: '', format: '##### #####' },
  BR: { code: '+55', name: 'Brazil', flag: '', format: '(##) #####-####' },
  MX: { code: '+52', name: 'Mexico', flag: '', format: '## #### ####' },
  RU: { code: '+7', name: 'Russia', flag: '', format: '### ###-##-##' },
  KR: { code: '+82', name: 'South Korea', flag: '', format: '##-####-####' },
};

const formatPhoneNumber = (value: string, format: string): string => {
  // Remove all non-digits
  const digits = value.replace(/\D/g, '');

  // Apply format pattern
  let formatted = '';
  let digitIndex = 0;

  for (const char of format) {
    if (char === '#' && digitIndex < digits.length) {
      formatted += digits[digitIndex];
      digitIndex++;
    } else if (char !== '#') {
      formatted += char;
    } else {
      break;
    }
  }

  return formatted;
};

const extractDigits = (value: string): string => {
  return value.replace(/\D/g, '');
};

export const PhoneField: React.FC<PhoneFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  placeholder: fieldPlaceholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
  phoneConfig = {},
}) => {
  const {
    defaultCountry = 'US',
    format = 'national',
    allowedCountries,
    placeholder,
  } = phoneConfig;

  const name = fieldApi.name;
  const value = (fieldApi.state?.value as string) || '';
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;
  const hasErrors = fieldApi.state?.meta?.isTouched && fieldApi.state?.meta?.errors?.length > 0;

  const [selectedCountry, setSelectedCountry] = useState(defaultCountry);
  const [isCountryDropdownOpen, setIsCountryDropdownOpen] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const availableCountries = allowedCountries 
    ? Object.entries(COUNTRY_CODES).filter(([code]) => allowedCountries.includes(code))
    : Object.entries(COUNTRY_CODES);

  const currentCountry = COUNTRY_CODES[selectedCountry as keyof typeof COUNTRY_CODES];

  // Parse existing value on mount
  useEffect(() => {
    if (value) {
      // Try to extract country code and phone number
      const digits = extractDigits(value);

      // Find matching country code
      const matchingCountry = Object.entries(COUNTRY_CODES).find(([_, country]) => {
        const countryDigits = extractDigits(country.code);
        return digits.startsWith(countryDigits);
      });

      if (matchingCountry) {
        const [countryCode, countryData] = matchingCountry;
        setSelectedCountry(countryCode);

        const countryCodeDigits = extractDigits(countryData.code);
        const phoneDigits = digits.slice(countryCodeDigits.length);
        setPhoneNumber(formatPhoneNumber(phoneDigits, countryData.format));
      } else {
        setPhoneNumber(value);
      }
    }
  }, [value]);

  const handlePhoneNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    const digits = extractDigits(inputValue);

    // Format the phone number according to country format
    const formatted = formatPhoneNumber(digits, currentCountry.format);
    setPhoneNumber(formatted);

    // Create the final value based on format preference
    const finalValue = format === 'international' 
      ? `${currentCountry.code} ${formatted}`.trim()
      : formatted;

    fieldApi.handleChange(finalValue);
  };

  const handleCountryChange = (countryCode: string) => {
    setSelectedCountry(countryCode);
    setIsCountryDropdownOpen(false);

    // Update the value with new country code
    const newCountry = COUNTRY_CODES[countryCode as keyof typeof COUNTRY_CODES];
    const digits = extractDigits(phoneNumber);
    const formatted = formatPhoneNumber(digits, newCountry.format);

    const finalValue = format === 'international' 
      ? `${newCountry.code} ${formatted}`.trim()
      : formatted;

    fieldApi.handleChange(finalValue);
  };

  const getPlaceholder = (): string => {
    if (placeholder) return placeholder;

    const exampleNumber = formatPhoneNumber('1234567890', currentCountry.format);
    return format === 'international' 
      ? `${currentCountry.code} ${exampleNumber}`
      : exampleNumber;
  };

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="space-y-2">
        <div className="flex">
          {/* Country selector */}
          <div className="relative">
            <Button
              type="button"
              variant="outline"
              className={cn(
                "rounded-r-none border-r-0 px-3 h-10 min-w-[80px]",
                hasErrors ? "border-destructive" : ""
              )}
              onClick={() => setIsCountryDropdownOpen(!isCountryDropdownOpen)}
              disabled={isDisabled}
            >
              <span className="flex items-center gap-2">
                <span className="text-base">{currentCountry.flag}</span>
                {format === 'international' && (
                  <span className="text-xs text-muted-foreground">
                    {currentCountry.code}
                  </span>
                )}
                <ChevronDown className="h-3 w-3" />
              </span>
            </Button>

            {/* Country dropdown */}
            {isCountryDropdownOpen && (
              <div className="absolute z-50 top-full left-0 mt-1 bg-popover border rounded-md shadow-lg max-h-60 overflow-y-auto min-w-[200px]">
                {availableCountries.map(([code, country]) => (
                  <button
                    key={code}
                    type="button"
                    className={cn(
                      "w-full px-3 py-2 text-left text-sm hover:bg-accent hover:text-accent-foreground",
                      "flex items-center gap-3",
                      selectedCountry === code ? "bg-accent" : ""
                    )}
                    onClick={() => handleCountryChange(code)}
                  >
                    <span className="text-base">{country.flag}</span>
                    <div className="flex-1">
                      <div className="font-medium">{country.name}</div>
                      <div className="text-xs text-muted-foreground">{country.code}</div>
                    </div>
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Phone number input */}
          <Input
            value={phoneNumber}
            onChange={handlePhoneNumberChange}
            onBlur={() => fieldApi.handleBlur()}
            placeholder={getPlaceholder()}
            className={cn(
              "rounded-l-none flex-1",
              hasErrors ? "border-destructive" : "",
              inputClassName
            )}
            disabled={isDisabled}
          />
        </div>

        {/* Format hint */}
        <div className="flex items-center gap-2 text-xs text-muted-foreground">
          <Phone className="h-3 w-3" />
          <span>
            Format: {currentCountry.format.replace(/#/g, '0')}
            {format === 'international' && ` (${currentCountry.code})`}
          </span>
        </div>
      </div>
    </FieldWrapper>
  );
};

'use client';
