export const SliderField: React.FC<SliderFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  inputClassName,
  labelClassName,
  wrapperClassName,
  sliderConfig,
  // Backwards compatibility props
  min: directMin = 0,
  max: directMax = 100,
  step: directStep = 1,
  valueLabelPrefix: directPrefix = "",
  valueLabelSuffix: directSuffix = "",
  valueDisplayPrecision: directPrecision = 0,
  showRawValue: directShowRaw = false,
}) => {
  const name = fieldApi.name;
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;

  // Use sliderConfig if provided, otherwise use direct props
  const config = sliderConfig || {
    min: directMin,
    max: directMax,
    step: directStep,
    valueLabelPrefix: directPrefix,
    valueLabelSuffix: directSuffix,
    valueDisplayPrecision: directPrecision,
    showRawValue: directShowRaw,
  };

  const {
    min = 0,
    max = 100,
    step = 1,
    valueMapping,
    gradientColors,
    visualizationComponent: VisualizationComponent,
    valueLabelPrefix = "",
    valueLabelSuffix = "",
    valueDisplayPrecision = 0,
    showRawValue = false,
    showValue = true,
    marks = [],
  } = config;

  const fieldValue =
    typeof fieldApi.state?.value === "number" ? fieldApi.state?.value : min;

  // Get display value from mapping or calculate it
  const getDisplayValue = (sliderValue: number) => {
    if (valueMapping) {
      const mapping = valueMapping.find((m) => m.sliderValue === sliderValue);
      return mapping ? mapping.displayValue : sliderValue;
    }
    return sliderValue.toFixed(valueDisplayPrecision);
  };

  const displayValue = getDisplayValue(fieldValue);
  const mappingItem = valueMapping?.find((m) => m.sliderValue === fieldValue);

  const onValueChange = (valueArray: number[]) => {
    const newValue = valueArray[0];
    fieldApi.handleChange(newValue);
  };

  const onBlur = () => {
    fieldApi.handleBlur();
  };

  // Custom label with value display
  const customLabel =
    label && showValue
      ? `${label} (${valueLabelPrefix}${displayValue}${valueLabelSuffix})`
      : label;

  // Generate unique ID for this slider instance
  const sliderId = `slider-${name}-${Math.random().toString(36).substr(2, 9)}`;

  // Calculate current color based on slider value
  const getCurrentColor = () => {
    if (!gradientColors) return null;

    const percentage = ((fieldValue - min) / (max - min)) * 100;

    // Parse hex colors
    const startColor = gradientColors.start;
    const endColor = gradientColors.end;

    // Convert hex to RGB
    const hexToRgb = (hex: string) => {
      const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
      return result ? {
        r: parseInt(result[1], 16),
        g: parseInt(result[2], 16),
        b: parseInt(result[3], 16)
      } : null;
    };

    const startRgb = hexToRgb(startColor);
    const endRgb = hexToRgb(endColor);

    if (!startRgb || !endRgb) return startColor;

    // Interpolate between start and end colors
    const r = Math.round(startRgb.r + (endRgb.r - startRgb.r) * (percentage / 100));
    const g = Math.round(startRgb.g + (endRgb.g - startRgb.g) * (percentage / 100));
    const b = Math.round(startRgb.b + (endRgb.b - startRgb.b) * (percentage / 100));

    return `rgb(${r}, ${g}, ${b})`;
  };

  const currentColor = getCurrentColor();

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={customLabel}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="space-y-4">
        {showRawValue && (
          <div className="text-xs text-muted-foreground">
            Raw: {fieldApi.state?.value}
          </div>
        )}

        {/* Custom visualization component if provided */}
        {VisualizationComponent && valueMapping && (
          <div className="flex justify-between items-center mb-2">
            {valueMapping.map((mapping, index) => (
              <VisualizationComponent
                key={index}
                value={mapping.sliderValue}
                displayValue={mapping.displayValue}
                label={mapping.label}
                isActive={fieldValue === mapping.sliderValue}
              />
            ))}
          </div>
        )}

        <div className="relative">
          {gradientColors && currentColor && (
            <style>{`
              .${sliderId} [data-slot="slider-range"] {
                background: ${currentColor} !important;
              }
            `}</style>
          )}
          <Slider
            id={name}
            name={name}
            value={[fieldValue]}
            onValueChange={onValueChange}
            onBlur={onBlur}
            disabled={isDisabled}
            min={min}
            max={max}
            step={step}
            className={cn(inputClassName, gradientColors && sliderId)}
          />

          {/* Marks display */}
          {marks.length > 0 && (
            <div className="flex justify-between text-xs text-muted-foreground mt-2">
              {marks.map((mark, index) => (
                <span key={index} className="text-center">
                  {mark.label}
                </span>
              ))}
            </div>
          )}
        </div>

        {/* Display current mapping info */}
        {mappingItem?.label && (
          <div className="text-sm text-muted-foreground text-center">
            {mappingItem.label}
          </div>
        )}
      </div>
    </FieldWrapper>
  );
};

import React from 'react';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { cn } from '@/lib/utils';
import type { BaseFieldProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';

export const SwitchField: React.FC<BaseFieldProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
}) => {
  const name = fieldApi.name;
  const value = fieldApi.state?.value as boolean | undefined;
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;

  const onCheckedChange = (checked: boolean) => {
    fieldApi.handleChange(checked);
  };

  const onBlur = () => {
    fieldApi.handleBlur();
  };

  return (
    // Note: We pass label={undefined} to FieldWrapper and render the label manually
    // because Switch components need the label positioned next to (not above) the control
    <FieldWrapper 
      fieldApi={fieldApi} 
      label={undefined}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="flex items-center space-x-2">
        <Switch
          id={name}
          checked={!!value}
          onCheckedChange={onCheckedChange}
          onBlur={onBlur}
          disabled={isDisabled}
          aria-describedby={description ? `${name}-description` : undefined}
        />
        {label && (
          <Label 
            htmlFor={name} 
            className={cn("text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70", labelClassName)}
          >
            {label}
          </Label>
        )}
      </div>
    </FieldWrapper>
  );
};

"use client";
import React, { useState, useEffect, useCallback, useMemo } from "react";
import { Input } from "@/components/ui/input";
import { cn } from "@/lib/utils";
import type { TextFieldSpecificProps } from "@/lib/formedible/types";
import { FieldWrapper } from "./base-field-wrapper";


export const TextField: React.FC<TextFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
  type = "text",
  datalist,
}) => {
  const name = fieldApi.name;
  const value = fieldApi.state?.value as string | number | undefined;
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;
  const hasErrors = fieldApi.state?.meta?.isTouched && fieldApi.state?.meta?.errors?.length > 0;

  // Datalist state
  const [datalistOptions, setDatalistOptions] = useState<string[]>(
    datalist?.options || []
  );
  const [isLoadingOptions, setIsLoadingOptions] = useState(false);
  const [lastQuery, setLastQuery] = useState("");

  // Debounced async options fetching
  const fetchAsyncOptions = useCallback(
    async (query: string) => {
      if (!datalist?.asyncOptions) return;

      const minChars = datalist.minChars || 1;
      if (query.length < minChars) {
        setDatalistOptions(datalist.options || []);
        return;
      }

      if (query === lastQuery) return;

      setIsLoadingOptions(true);
      setLastQuery(query);

      try {
        const results = await datalist.asyncOptions(query);
        const maxResults = datalist.maxResults || 10;
        const limitedResults = results.slice(0, maxResults);

        // Combine static options with async results
        const staticOptions = datalist.options || [];
        const combinedOptions = [...staticOptions, ...limitedResults];

        // Remove duplicates
        const uniqueOptions = Array.from(new Set(combinedOptions));

        setDatalistOptions(uniqueOptions);
      } catch (error) {
        console.error("Error fetching datalist options:", error);
        // Fallback to static options on error
        setDatalistOptions(datalist.options || []);
      } finally {
        setIsLoadingOptions(false);
      }
    },
    [datalist, lastQuery]
  );

  // Debounced effect for async options
  useEffect(() => {
    if (!datalist?.asyncOptions) return;

    const debounceMs = datalist.debounceMs || 300;
    const currentValue = String(value || "");

    const timeoutId = setTimeout(() => {
      fetchAsyncOptions(currentValue);
    }, debounceMs);

    return () => clearTimeout(timeoutId);
  }, [value, fetchAsyncOptions, datalist]);

  // Generate unique datalist id
  const datalistId = useMemo(
    () => (datalist ? `${name}-datalist` : undefined),
    [name, datalist]
  );

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    fieldApi.handleChange(e.target.value);
  };

  const onBlur = () => {
    fieldApi.handleBlur();
  };

  const computedInputClassName = cn(
    inputClassName,
    hasErrors ? "border-destructive" : "",
    isLoadingOptions ? "pr-8" : ""
  );

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="relative">
        <Input
          id={name}
          name={name}
          type={type}
          value={value === undefined || value === null ? "" : String(value)}
          onBlur={onBlur}
          onChange={onChange}
          placeholder={placeholder}
          className={computedInputClassName}
          disabled={isDisabled}
          list={datalistId}
          autoComplete={datalist ? "off" : undefined}
        />
        {isLoadingOptions && (
          <div className="absolute right-2 top-1/2 transform -translate-y-1/2">
            <span className="text-xs text-muted-foreground">Loading...</span>
          </div>
        )}
        {datalist && datalistOptions.length > 0 && (
          <datalist id={datalistId}>
            {datalistOptions.map((option, index) => (
              <option key={`${option}-${index}`} value={option} />
            ))}
          </datalist>
        )}
      </div>
    </FieldWrapper>
  );
};

