"use client";
import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import type { FormStepperStep, FormStepperProps } from "@/lib/formedible/types";


export const FormStepper: React.FC<FormStepperProps> = ({
  children,
  steps,
  currentStep = 0,
  onStepChange,
  onComplete,
  className,
  allowSkip = false,
  showStepNumbers = true,
}) => {
  const [activeStep, setActiveStep] = useState(currentStep);
  const [completedSteps, setCompletedSteps] = useState<Set<number>>(new Set());

  const handleStepChange = (stepIndex: number) => {
    setActiveStep(stepIndex);
    onStepChange?.(stepIndex);
  };

  const markStepComplete = (stepIndex: number) => {
    setCompletedSteps(prev => new Set(prev).add(stepIndex));
  };

  const handleNext = () => {
    // Mark current step as completed when moving to next
    markStepComplete(activeStep);

    if (activeStep < steps.length - 1) {
      handleStepChange(activeStep + 1);
    } else {
      onComplete?.();
    }
  };



  const handlePrevious = () => {
    if (activeStep > 0) {
      handleStepChange(activeStep - 1);
    }
  };

  const handleSkip = () => {
    if (allowSkip && steps[activeStep]?.optional) {
      handleNext();
    }
  };

  const canGoToStep = (stepIndex: number) => {
    // Can go to current step, previous steps, or next step if current is completed
    return stepIndex <= activeStep || completedSteps.has(stepIndex - 1);
  };

  const isStepCompleted = (stepIndex: number) => {
    return completedSteps.has(stepIndex);
  };

  const isStepActive = (stepIndex: number) => {
    return stepIndex === activeStep;
  };

  return (
    <div className={cn("space-y-6", className)}>
      {children}

      {/* Step indicator */}
      <div className="flex items-center justify-between">
        {steps.map((step, index) => (
          <React.Fragment key={step.id}>
            <div className="flex flex-col items-center">
              <button
                type="button"
                onClick={() => canGoToStep(index) && handleStepChange(index)}
                disabled={!canGoToStep(index)}
                aria-label={`Go to step ${index + 1}: ${step.title}`}
                aria-current={isStepActive(index) ? 'step' : undefined}
                tabIndex={canGoToStep(index) ? 0 : -1}
                className={cn(
                  "w-10 h-10 rounded-full border-2 flex items-center justify-center text-sm font-medium transition-colors",
                  isStepActive(index) && "border-primary bg-primary text-primary-foreground",
                  isStepCompleted(index) && !isStepActive(index) && "border-primary bg-primary text-primary-foreground",
                  !isStepActive(index) && !isStepCompleted(index) && "border-muted-foreground text-muted-foreground",
                  canGoToStep(index) && "cursor-pointer hover:border-primary",
                  !canGoToStep(index) && "cursor-not-allowed opacity-50"
                )}
              >
                {isStepCompleted(index) ? (
                  ""
                ) : showStepNumbers ? (
                  index + 1
                ) : (
                  ""
                )}
              </button>

              <div className="mt-2 text-center">
                <div className={cn(
                  "text-sm font-medium",
                  isStepActive(index) && "text-primary",
                  !isStepActive(index) && "text-muted-foreground"
                )}>
                  {step.title}
                  {step.optional && (
                    <span className="text-xs text-muted-foreground ml-1">(optional)</span>
                  )}
                </div>
                {step.description && (
                  <div className="text-xs text-muted-foreground mt-1">
                    {step.description}
                  </div>
                )}
              </div>
            </div>

            {/* Connector line */}
            {index < steps.length - 1 && (
              <div className={cn(
                "flex-1 h-0.5 mx-4",
                isStepCompleted(index) ? "bg-primary" : "bg-muted"
              )} />
            )}
          </React.Fragment>
        ))}
      </div>

      {/* Step content */}
      <div className="min-h-[200px]">
        {steps[activeStep]?.content}
      </div>

      {/* Navigation buttons */}
      <div className="flex justify-between">
        <Button
          type="button"
          variant="outline"
          onClick={handlePrevious}
          disabled={activeStep === 0}
        >
          Previous
        </Button>

        <div className="flex gap-2">
          {allowSkip && steps[activeStep]?.optional && (
            <Button
              type="button"
              variant="ghost"
              onClick={handleSkip}
            >
              Skip
            </Button>
          )}

          <Button
            type="button"
            onClick={handleNext}
          >
            {activeStep === steps.length - 1 ? 'Complete' : 'Next'}
          </Button>
        </div>
      </div>
    </div>
  );
};

"use client";
import React from "react";
import { cn } from "@/lib/utils";
import type { FormGridProps } from "@/lib/formedible/types";

export const FormGrid: React.FC<FormGridProps> = ({
  children,
  columns = 2,
  gap = "4",
  responsive = true,
  className,
}) => {
  // Filter out null, undefined, and false children to prevent empty grid cells
  const validChildren = React.Children.toArray(children).filter(
    // @ts-expect-error raf de ta mr2
    (child) => child !== null && child !== undefined && child !== false
  );
  // If no valid children, render nothing
  if (validChildren.length === 0) {
    return null;
  }

  // Determine the actual columns to use based on available children
  const actualColumns = Math.min(columns, validChildren.length, 12);
  const gapClasses = {
    "0": "gap-0",
    "1": "gap-1",
    "2": "gap-2",
    "3": "gap-3",
    "4": "gap-4",
    "5": "gap-5",
    "6": "gap-6",
    "7": "gap-7",
    "8": "gap-8",
    "9": "gap-9",
    "10": "gap-10",
    "11": "gap-11",
    "12": "gap-12",
  };

  const gridColsClasses = {
    1: "grid-cols-1",
    2: "grid-cols-2",
    3: "grid-cols-3",
    4: "grid-cols-4",
    5: "grid-cols-5",
    6: "grid-cols-6",
    7: "grid-cols-7",
    8: "grid-cols-8",
    9: "grid-cols-9",
    10: "grid-cols-10",
    11: "grid-cols-11",
    12: "grid-cols-12",
  };

  const gridClasses = cn(
    "grid",
    gapClasses[gap as keyof typeof gapClasses],
    responsive
      ? {
          "grid-cols-1": true,
          "md:grid-cols-2": actualColumns >= 2,
          "lg:grid-cols-3": actualColumns >= 3,
          "xl:grid-cols-4": actualColumns >= 4 && columns >= 4,
        }
      : gridColsClasses[actualColumns as keyof typeof gridColsClasses],
    className
  );

  return <div className={gridClasses}>{validChildren}</div>;
};

