import React from 'react';
import type { AnyFieldApi } from '@tanstack/react-form';
import type { FormApi, ValidationError } from '@tanstack/form-core';

// Validation error types based on TanStack Form
export type FormedibleValidationError = ValidationError;

// Validation error that can be a string, Error object, or custom validation result
export type FieldValidationError = string | Error | ValidationError;

// Array of validation errors for a field
export type FieldValidationErrors = FieldValidationError[];

// Type alias for our FormApi - use the core FormApi type which is what useForm actually returns
export type FormedibleFormApi<TFormData = Record<string, unknown>> = FormApi<
  TFormData,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined
>;

// Option types for select, radio, and multi-select fields
export type FieldOption = {
  value: string;
  label: string;
  disabled?: boolean;
  description?: string;
};

export type FieldOptions = string[] | FieldOption[];

// Normalize options to consistent format
export type NormalizedFieldOption = FieldOption;

// Props that all basic field components rendered by FormedibleRoot will receive
export interface BaseFieldProps {
  fieldApi: AnyFieldApi;
  label?: string;
  description?: string;
  placeholder?: string;
  inputClassName?: string;   // For the <Input /> component itself
  labelClassName?: string;   // For the <Label /> component
  wrapperClassName?: string; // For the div wrapping label and input
}

// Specific field component prop types
export interface SelectFieldProps extends BaseFieldProps {
  options: FieldOptions;
  placeholder?: string;
}

export interface RadioFieldProps extends BaseFieldProps {
  options: FieldOptions;
}

export interface MultiSelectFieldProps extends BaseFieldProps {
  options: FieldOptions;
  placeholder?: string;
  maxSelections?: number;
}

export interface ArrayFieldProps extends BaseFieldProps {
  arrayConfig: {
    itemType: string;
    itemLabel?: string;
    itemPlaceholder?: string;
    itemValidation?: unknown;
    minItems?: number;
    maxItems?: number;
    addButtonLabel?: string;
    removeButtonLabel?: string;
    sortable?: boolean;
    defaultValue?: unknown;
    itemComponent?: React.ComponentType<BaseFieldProps>;
    itemProps?: Record<string, unknown>;
  };
}

export interface TextFieldProps extends BaseFieldProps {
  type?: 'text' | 'email' | 'password' | 'url' | 'tel';
  datalist?: string[];
  maxLength?: number;
  minLength?: number;
  pattern?: string;
  autoComplete?: string;
}

export interface NumberFieldProps extends BaseFieldProps {
  min?: number;
  max?: number;
  step?: number;
  precision?: number;
}

export interface DateFieldProps extends BaseFieldProps {
  dateConfig?: {
    format?: string;
    minDate?: Date;
    maxDate?: Date;
    disabledDates?: Date[];
    showTime?: boolean;
    timeFormat?: string;
  };
}

export interface SliderFieldProps extends BaseFieldProps {
  sliderConfig?: {
    min?: number;
    max?: number;
    step?: number;
    showValue?: boolean;
    showTicks?: boolean;
    orientation?: 'horizontal' | 'vertical';
  };
}

export interface FileUploadFieldProps extends BaseFieldProps {
  fileConfig?: {
    accept?: string;
    multiple?: boolean;
    maxSize?: number;
    maxFiles?: number;
    allowedTypes?: string[];
    uploadUrl?: string;
    onUpload?: (files: File[]) => Promise<string[]>;
  };
}

export interface LocationPickerFieldProps extends BaseFieldProps {
  locationConfig?: LocationConfig;
}

export interface DurationPickerFieldProps extends BaseFieldProps {
  durationConfig?: DurationConfig;
}

export interface AutocompleteFieldProps extends BaseFieldProps {
  autocompleteConfig?: AutocompleteConfig;
}

export interface MaskedInputFieldProps extends BaseFieldProps {
  maskedConfig?: MaskedInputConfig;
}

export interface ColorPickerFieldProps extends BaseFieldProps {
  colorConfig?: {
    format?: 'hex' | 'rgb' | 'hsl';
    showAlpha?: boolean;
    presetColors?: string[];
    allowCustom?: boolean;
  };
}

export interface RatingFieldProps extends BaseFieldProps {
  ratingConfig?: {
    max?: number;
    allowHalf?: boolean;
    allowClear?: boolean;
    icon?: 'star' | 'heart' | 'thumbs' | React.ComponentType;
    size?: 'sm' | 'md' | 'lg' | 'small' | 'medium' | 'large';
    showValue?: boolean;
  };
}

export interface PhoneFieldProps extends BaseFieldProps {
  phoneConfig?: {
    defaultCountry?: string;
    preferredCountries?: string[];
    onlyCountries?: string[];
    excludeCountries?: string[];
    format?: 'national' | 'international';
  };
}

// Union type for all possible field component props - using intersection for flexibility
export type FieldComponentProps = BaseFieldProps & {
  // Optional props that specific field types might need
  options?: FieldOptions;
  arrayConfig?: ArrayFieldProps['arrayConfig'];
  type?: TextFieldProps['type'];
  datalist?: string[];
  dateConfig?: DateFieldProps['dateConfig'];
  sliderConfig?: SliderFieldProps['sliderConfig'];
  fileConfig?: FileUploadFieldProps['fileConfig'];
  locationConfig?: LocationPickerFieldProps['locationConfig'];
  durationConfig?: DurationPickerFieldProps['durationConfig'];
  autocompleteConfig?: AutocompleteFieldProps['autocompleteConfig'];
  maskedConfig?: MaskedInputFieldProps['maskedConfig'];
  colorConfig?: ColorPickerFieldProps['colorConfig'];
  ratingConfig?: RatingFieldProps['ratingConfig'];
  phoneConfig?: PhoneFieldProps['phoneConfig'];
  // Legacy support for existing configurations
  multiSelectConfig?: {
    maxSelections?: number;
    searchable?: boolean;
    creatable?: boolean;
    placeholder?: string;
    noOptionsText?: string;
    loadingText?: string;
  };
  maskedInputConfig?: MaskedInputConfig;
  // Allow additional props for extensibility
  [key: string]: unknown;
};

// Cross-field validation configuration
export interface CrossFieldValidation<TFormValues = Record<string, unknown>> {
  fields: (keyof TFormValues)[];
  validator: (values: Partial<TFormValues>) => string | null;
  message: string;
}

// Async validation configuration
export interface AsyncValidation {
  validator: (value: unknown) => Promise<string | null>;
  debounceMs?: number;
  loadingMessage?: string;
}

// Form analytics and tracking configuration
export interface FormAnalytics {
  onFieldFocus?: (fieldName: string, timestamp: number) => void;
  onFieldBlur?: (fieldName: string, timeSpent: number) => void;
  onFormAbandon?: (completionPercentage: number) => void;
  onPageChange?: (fromPage: number, toPage: number, timeSpent: number) => void;
  onFieldChange?: (fieldName: string, value: unknown, timestamp: number) => void;
  onFormComplete?: (timeSpent: number, formData: unknown) => void;
}

// Layout configuration for forms
export interface LayoutConfig {
  type: 'grid' | 'flex' | 'tabs' | 'accordion' | 'stepper';
  columns?: number;
  gap?: string;
  responsive?: boolean;
  className?: string;
}

// Conditional sections configuration
export interface ConditionalSection<TFormValues = Record<string, unknown>> {
  condition: (values: TFormValues) => boolean;
  fields: string[];
  layout?: LayoutConfig;
}

// Location picker configuration
export interface LocationConfig {
  apiKey?: string;
  defaultLocation?: { lat: number; lng: number };
  zoom?: number;
  searchPlaceholder?: string;
  enableSearch?: boolean;
  enableGeolocation?: boolean;
  mapProvider?: 'google' | 'openstreetmap';
}

// Duration picker configuration
export interface DurationConfig {
  format?: 'hms' | 'hm' | 'ms' | 'hours' | 'minutes' | 'seconds';
  maxHours?: number;
  maxMinutes?: number;
  maxSeconds?: number;
  showLabels?: boolean;
  allowNegative?: boolean;
}

// Autocomplete configuration
export interface AutocompleteConfig {
  options?: string[] | { value: string; label: string }[];
  asyncOptions?: (query: string) => Promise<string[] | { value: string; label: string }[]>;
  debounceMs?: number;
  minChars?: number;
  maxResults?: number;
  allowCustom?: boolean;
  placeholder?: string;
  noOptionsText?: string;
  loadingText?: string;
}

// Masked input configuration
export interface MaskedInputConfig {
  mask: string | ((value: string) => string);
  placeholder?: string;
  showMask?: boolean;
  guide?: boolean;
  keepCharPositions?: boolean;
  pipe?: (conformedValue: string, config: unknown) => false | string | { value: string; indexesOfPipedChars: number[] };
}

'use client';
import React, { useCallback, useMemo, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { cn } from '@/lib/utils';
import { Plus, Trash2, GripVertical } from 'lucide-react';
import type { BaseFieldProps } from '@/lib/formedible/types';
import { TextField } from './text-field';
import { NumberField } from './number-field';
import { TextareaField } from './textarea-field';
import { SelectField } from './select-field';
import { CheckboxField } from './checkbox-field';
import { SwitchField } from './switch-field';
import { DateField } from './date-field';
import { SliderField } from './slider-field';
import { FileUploadField } from './file-upload-field';

// Map of field types to components
const fieldTypeComponents: Record<string, React.ComponentType<any>> = {
  text: TextField,
  email: TextField,
  password: TextField,
  url: TextField,
  tel: TextField,
  number: NumberField,
  textarea: TextareaField,
  select: SelectField,
  checkbox: CheckboxField,
  switch: SwitchField,
  date: DateField,
  slider: SliderField,
  file: FileUploadField,
};

export interface ArrayFieldSpecificProps extends BaseFieldProps {
  arrayConfig: {
    itemType: string;
    itemLabel?: string;
    itemPlaceholder?: string;
    itemValidation?: unknown;
    minItems?: number;
    maxItems?: number;
    addButtonLabel?: string;
    removeButtonLabel?: string;
    itemComponent?: React.ComponentType<any>;
    sortable?: boolean;
    defaultValue?: unknown;
    // Additional props to pass to item components
    itemProps?: Record<string, unknown>;
  };
}

export const ArrayField: React.FC<ArrayFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  wrapperClassName,
  labelClassName,
  arrayConfig,
}) => {
  const { name, state, handleChange, handleBlur } = fieldApi;
  const value = useMemo(() => (state.value as unknown[]) || [], [state.value]);

  const {
    itemType,
    itemLabel,
    itemPlaceholder,
    minItems = 0,
    maxItems = 10,
    addButtonLabel = "Add Item",
    removeButtonLabel = "Remove",
    itemComponent: CustomItemComponent,
    sortable = false,
    defaultValue = '',
    itemProps = {},
  } = arrayConfig || {};

  // Get the component for rendering items
  const ItemComponent = CustomItemComponent || fieldTypeComponents[itemType || 'text'] || TextField;

  const addItem = useCallback(() => {
    if (value.length >= maxItems) return;

    const newValue = [...value, defaultValue];
    handleChange(newValue);
  }, [value, maxItems, defaultValue, handleChange]);

  const removeItem = useCallback((index: number) => {
    if (value.length <= minItems) return;

    const newValue = value.filter((_, i) => i !== index);
    handleChange(newValue);
    handleBlur();
  }, [value, minItems, handleChange, handleBlur]);

  const updateItem = useCallback((index: number, newItemValue: unknown) => {
    const newValue = [...value];
    newValue[index] = newItemValue;
    handleChange(newValue);
  }, [value, handleChange]);

  const moveItem = useCallback((fromIndex: number, toIndex: number) => {
    if (!sortable) return;
    if (fromIndex === toIndex) return;

    const newValue = [...value];
    const [movedItem] = newValue.splice(fromIndex, 1);
    newValue.splice(toIndex, 0, movedItem);
    handleChange(newValue);
  }, [value, handleChange, sortable]);

  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);



  // Create a mock field API for each item
  const createItemFieldApi = useCallback((index: number) => {
    return {
      name: `${name}[${index}]`,
      state: {
        value: value[index],
        meta: {
          errors: [],
          isTouched: false,
          isValidating: false,
        },
      },
      handleChange: (newValue: unknown) => updateItem(index, newValue),
      handleBlur: () => handleBlur(),
      form: fieldApi.form,
    };
  }, [name, value, updateItem, handleBlur, fieldApi.form]);

  const canAddMore = value.length < maxItems;
  const canRemove = value.length > minItems;

  return (
    <div className={cn("space-y-4", wrapperClassName)}>
      {label && (
        <Label className={cn("text-sm font-medium", labelClassName)}>
          {label}
          {value.length > 0 && (
            <span className="ml-2 text-xs text-muted-foreground">
              ({value.length}{maxItems < Infinity ? `/${maxItems}` : ''} items)
            </span>
          )}
        </Label>
      )}
      {description && <p className="text-xs text-muted-foreground">{description}</p>}

      <div className="space-y-3">
        {value.map((_, index) => (
          <div
            key={index}
            className="flex items-start gap-2 p-3 border rounded-lg bg-card"
            onDragOver={sortable ? (e) => {
              e.preventDefault();
              e.dataTransfer.dropEffect = 'move';
            } : undefined}
            onDrop={sortable ? (e) => {
              e.preventDefault();
              if (draggedIndex !== null && draggedIndex !== index) {
                moveItem(draggedIndex, index);
              }
            } : undefined}
          >
            {sortable && (
              <button
                type="button"
                className="mt-2 p-1 hover:bg-muted rounded cursor-grab active:cursor-grabbing"
                draggable
                onDragStart={(e) => {
                  setDraggedIndex(index);
                  e.dataTransfer.effectAllowed = 'move';
                }}
                onDragEnd={() => {
                  setDraggedIndex(null);
                }}
              >
                <GripVertical className="h-4 w-4 text-muted-foreground" />
              </button>
            )}

            <div className="flex-1">
              <ItemComponent
                fieldApi={createItemFieldApi(index)}
                label={itemLabel ? `${itemLabel} ${index + 1}` : undefined}
                placeholder={itemPlaceholder}
                wrapperClassName="mb-0"
                {...itemProps}
                // Pass specific props based on item type
                {...(itemType === 'select' && { options: itemProps.options || [] })}
                {...(itemType === 'number' && { 
                  min: itemProps.min, 
                  max: itemProps.max, 
                  step: itemProps.step 
                })}
                {...(itemType === 'slider' && { 
                  min: itemProps.min, 
                  max: itemProps.max, 
                  step: itemProps.step 
                })}
                {...(itemType === 'file' && { 
                  accept: itemProps.accept,
                  multiple: itemProps.multiple 
                })}
                {...(itemType === 'date' && typeof itemProps.dateProps === 'object' && itemProps.dateProps ? itemProps.dateProps : {})}
                {...(['text', 'email', 'password', 'url', 'tel'].includes(itemType) && {
                  type: itemType,
                  datalist: itemProps.datalist,
                })}
              />
            </div>

            {canRemove && (
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={() => removeItem(index)}
                className="mt-2 h-8 w-8 p-0 text-destructive hover:text-destructive"
                title={removeButtonLabel}
              >
                <Trash2 className="h-4 w-4" />
              </Button>
            )}
          </div>
        ))}

        {value.length === 0 && (
          <div className="text-center py-8 text-muted-foreground border-2 border-dashed rounded-lg">
            <p className="text-sm">No items added yet</p>
            <p className="text-xs mt-1">Click &quot;{addButtonLabel}&quot; to add your first item</p>
          </div>
        )}
      </div>

      {canAddMore && (
        <Button
          type="button"
          variant="outline"
          onClick={addItem}
          className="w-full"
          disabled={fieldApi.form.state.isSubmitting}
        >
          <Plus className="h-4 w-4 mr-2" />
          {addButtonLabel}
        </Button>
      )}

      {minItems > 0 && value.length < minItems && (
        <p className="text-xs text-muted-foreground">
          Minimum {minItems} item{minItems !== 1 ? 's' : ''} required
        </p>
      )}

      {state.meta.isTouched && state.meta.errors.length > 0 && (
        <div className="text-xs text-destructive pt-1">
          {state.meta.errors.map((err: string | Error, index: number) => (
            <p key={index}>{typeof err === 'string' ? err : (err as Error)?.message || 'Invalid'}</p>
          ))}
        </div>
      )}
    </div>
  );
};

"use client";
import React, { useState, useEffect, useRef } from "react";
import type { BaseFieldProps } from "@/lib/formedible/types";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface AutocompleteOption {
  value: string;
  label: string;
}

interface AutocompleteFieldProps extends BaseFieldProps {
  autocompleteConfig?: {
    options?: string[] | AutocompleteOption[];
    asyncOptions?: (query: string) => Promise<string[] | AutocompleteOption[]>;
    debounceMs?: number;
    minChars?: number;
    maxResults?: number;
    allowCustom?: boolean;
    placeholder?: string;
    noOptionsText?: string;
    loadingText?: string;
  };
}

export const AutocompleteField: React.FC<AutocompleteFieldProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  wrapperClassName,
  labelClassName,
  inputClassName,
  autocompleteConfig = {},
}) => {
  const {
    options = [],
    asyncOptions,
    debounceMs = 300,
    minChars = 1,
    maxResults = 10,
    allowCustom = true,
    noOptionsText = "No options found",
    loadingText = "Loading..."
  } = autocompleteConfig;

  const [inputValue, setInputValue] = useState(fieldApi.state.value || "");
  const [filteredOptions, setFilteredOptions] = useState<AutocompleteOption[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);

  const inputRef = useRef<HTMLInputElement>(null);
  const listRef = useRef<HTMLDivElement>(null);
  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Normalize options to consistent format
  const normalizeOptions = (opts: string[] | AutocompleteOption[]): AutocompleteOption[] => {
    return opts.map(opt => 
      typeof opt === 'string' ? { value: opt, label: opt } : opt
    );
  };

  // Filter static options
  const filterStaticOptions = React.useCallback((query: string): AutocompleteOption[] => {
    if (!query || query.length < minChars) return [];

    const normalizedOptions = normalizeOptions(options);
    return normalizedOptions
      .filter(option => 
        option.label.toLowerCase().includes(query.toLowerCase()) ||
        option.value.toLowerCase().includes(query.toLowerCase())
      )
      .slice(0, maxResults);
  }, [minChars, options, maxResults]);

  // Handle async options
  const fetchAsyncOptions = React.useCallback(async (query: string) => {
    if (!asyncOptions || query.length < minChars) return;

    setIsLoading(true);
    try {
      const results = await asyncOptions(query);
      const normalizedResults = normalizeOptions(results);
      setFilteredOptions(normalizedResults.slice(0, maxResults));
    } catch (error) {
      console.error('Autocomplete async options error:', error);
      setFilteredOptions([]);
    } finally {
      setIsLoading(false);
    }
  }, [asyncOptions, minChars, maxResults]);

  // Debounced search
  useEffect(() => {
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
    }

    debounceRef.current = setTimeout(() => {
      if (asyncOptions) {
        fetchAsyncOptions(inputValue);
      } else {
        setFilteredOptions(filterStaticOptions(inputValue));
      }
    }, debounceMs);

    return () => {
      if (debounceRef.current) {
        clearTimeout(debounceRef.current);
      }
    };
  }, [inputValue, asyncOptions, debounceMs, fetchAsyncOptions, filterStaticOptions]);

  // Handle input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setInputValue(value);
    setIsOpen(true);
    setHighlightedIndex(-1);

    if (allowCustom) {
      fieldApi.handleChange(value);
    }
  };

  // Handle option selection
  const handleOptionSelect = (option: AutocompleteOption) => {
    setInputValue(option.label);
    fieldApi.handleChange(option.value);
    setIsOpen(false);
    setHighlightedIndex(-1);
    inputRef.current?.blur();
  };

  // Handle keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!isOpen) {
      if (e.key === 'ArrowDown' || e.key === 'Enter') {
        setIsOpen(true);
        return;
      }
      return;
    }

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setHighlightedIndex(prev => 
          prev < filteredOptions.length - 1 ? prev + 1 : prev
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setHighlightedIndex(prev => prev > 0 ? prev - 1 : -1);
        break;
      case 'Enter':
        e.preventDefault();
        if (highlightedIndex >= 0 && filteredOptions[highlightedIndex]) {
          handleOptionSelect(filteredOptions[highlightedIndex]);
        } else if (allowCustom && inputValue) {
          fieldApi.handleChange(inputValue);
          setIsOpen(false);
        }
        break;
      case 'Escape':
        setIsOpen(false);
        setHighlightedIndex(-1);
        inputRef.current?.blur();
        break;
    }
  };

  // Handle focus/blur
  const handleFocus = () => {
    if (inputValue.length >= minChars) {
      setIsOpen(true);
    }
  };

  const handleBlur = () => {
    // Delay closing to allow option clicks
    setTimeout(() => {
      if (!listRef.current?.contains(document.activeElement)) {
        setIsOpen(false);
        setHighlightedIndex(-1);
      }
    }, 150);
  };

  // Scroll highlighted option into view
  useEffect(() => {
    if (highlightedIndex >= 0 && listRef.current) {
      const highlightedElement = listRef.current.children[highlightedIndex] as HTMLElement;
      if (highlightedElement) {
        highlightedElement.scrollIntoView({
          block: 'nearest',
          behavior: 'smooth'
        });
      }
    }
  }, [highlightedIndex]);

  const showDropdown = isOpen && (filteredOptions.length > 0 || isLoading || (inputValue.length >= minChars && !isLoading));

  return (
    <div className={cn("space-y-2", wrapperClassName)}>
      {label && (
        <Label htmlFor={fieldApi.name} className={labelClassName}>
          {label}
        </Label>
      )}

      {description && (
        <p className="text-sm text-muted-foreground">{description}</p>
      )}

      <div className="relative">
        <Input
          ref={inputRef}
          id={fieldApi.name}
          name={fieldApi.name}
          value={inputValue}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          onFocus={handleFocus}
          onBlur={handleBlur}
          placeholder={placeholder || autocompleteConfig.placeholder || "Type to search..."}
          className={cn(inputClassName, isOpen && "rounded-b-none")}
          autoComplete="off"
        />

        {showDropdown && (
          <Card className="absolute top-full left-0 right-0 z-50 max-h-60 overflow-y-auto border-t-0 rounded-t-none">
            <div ref={listRef} className="p-1">
              {isLoading && (
                <div className="px-3 py-2 text-sm text-muted-foreground">
                  {loadingText}
                </div>
              )}

              {!isLoading && filteredOptions.length === 0 && inputValue.length >= minChars && (
                <div className="px-3 py-2 text-sm text-muted-foreground">
                  {noOptionsText}
                  {allowCustom && (
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="ml-2 h-auto p-1 text-xs"
                      onClick={() => {
                        fieldApi.handleChange(inputValue);
                        setIsOpen(false);
                      }}
                    >
                      Use "{inputValue}"
                    </Button>
                  )}
                </div>
              )}

              {!isLoading && filteredOptions.map((option, index) => (
                <button
                  key={`${option.value}-${index}`}
                  type="button"
                  className={cn(
                    "w-full text-left px-3 py-2 text-sm rounded-sm transition-colors",
                    "hover:bg-muted focus:bg-muted focus:outline-none",
                    highlightedIndex === index && "bg-muted"
                  )}
                  onClick={() => handleOptionSelect(option)}
                  onMouseEnter={() => setHighlightedIndex(index)}
                >
                  <div className="font-medium">{option.label}</div>
                  {option.value !== option.label && (
                    <div className="text-xs text-muted-foreground">{option.value}</div>
                  )}
                </button>
              ))}
            </div>
          </Card>
        )}
      </div>

      {fieldApi.state.meta.errors && fieldApi.state.meta.errors.length > 0 && (
        <p className="text-sm text-destructive">
          {fieldApi.state.meta.errors[0]}
        </p>
      )}
    </div>
  );
};

import React from 'react';
import { Checkbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { cn } from '@/lib/utils';
import type { BaseFieldProps } from '@/lib/formedible/types';

export const CheckboxField: React.FC<BaseFieldProps> = ({
  fieldApi,
  label,
  description,
  labelClassName,
  wrapperClassName,
}) => {
  return (
    <div className={cn("space-y-1.5", wrapperClassName)}>
      <div className="flex items-center space-x-2">
        <Checkbox
          id={fieldApi.name}
          checked={!!fieldApi.state.value}
          onCheckedChange={(checked) => fieldApi.handleChange(Boolean(checked))}
          onBlur={fieldApi.handleBlur}
          disabled={fieldApi.form.state.isSubmitting}
          aria-describedby={description ? `${fieldApi.name}-description` : undefined}
        />
        {label && (
          <Label htmlFor={fieldApi.name} className={cn("text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70", labelClassName)}>
            {label}
          </Label>
        )}
      </div>
      {description && <p id={`${fieldApi.name}-description`} className="text-xs text-muted-foreground">{description}</p>}
      {fieldApi.state.meta.isTouched && fieldApi.state.meta.errors.length > 0 && (
        <div className="text-xs text-destructive pt-1">
          {fieldApi.state.meta.errors.map((err: string, index: number) => (
            <p key={index}>{String(err)}</p>
          ))}
        </div>
      )}
    </div>
  );
};

'use client';
import React, { useState, useRef, useEffect } from 'react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { Palette, Check } from 'lucide-react';
import type { BaseFieldProps } from '@/lib/formedible/types';

export interface ColorPickerFieldSpecificProps extends BaseFieldProps {
  colorConfig?: {
    format?: 'hex' | 'rgb' | 'hsl';
    showPreview?: boolean;
    presetColors?: string[];
    allowCustom?: boolean;
  };
}

const DEFAULT_PRESETS = [
  '#FF0000', '#FF8000', '#FFFF00', '#80FF00', '#00FF00', '#00FF80',
  '#00FFFF', '#0080FF', '#0000FF', '#8000FF', '#FF00FF', '#FF0080',
  '#000000', '#404040', '#808080', '#C0C0C0', '#FFFFFF', '#8B4513'
];

// Color conversion utilities
const hexToRgb = (hex: string): { r: number; g: number; b: number } | null => {
  const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
  return result ? {
    r: parseInt(result[1], 16),
    g: parseInt(result[2], 16),
    b: parseInt(result[3], 16)
  } : null;
};



const hexToHsl = (hex: string): { h: number; s: number; l: number } | null => {
  const rgb = hexToRgb(hex);
  if (!rgb) return null;

  const { r, g, b } = rgb;
  const rNorm = r / 255;
  const gNorm = g / 255;
  const bNorm = b / 255;

  const max = Math.max(rNorm, gNorm, bNorm);
  const min = Math.min(rNorm, gNorm, bNorm);
  const diff = max - min;

  let h = 0;
  let s = 0;
  const l = (max + min) / 2;

  if (diff !== 0) {
    s = l > 0.5 ? diff / (2 - max - min) : diff / (max + min);

    switch (max) {
      case rNorm:
        h = (gNorm - bNorm) / diff + (gNorm < bNorm ? 6 : 0);
        break;
      case gNorm:
        h = (bNorm - rNorm) / diff + 2;
        break;
      case bNorm:
        h = (rNorm - gNorm) / diff + 4;
        break;
    }
    h /= 6;
  }

  return {
    h: Math.round(h * 360),
    s: Math.round(s * 100),
    l: Math.round(l * 100)
  };
};

const formatColor = (hex: string, format: 'hex' | 'rgb' | 'hsl'): string => {
  switch (format) {
    case 'rgb': {
      const rgb = hexToRgb(hex);
      return rgb ? `rgb(${rgb.r}, ${rgb.g}, ${rgb.b})` : hex;
    }
    case 'hsl': {
      const hsl = hexToHsl(hex);
      return hsl ? `hsl(${hsl.h}, ${hsl.s}%, ${hsl.l}%)` : hex;
    }
    default:
      return hex;
  }
};

export const ColorPickerField: React.FC<ColorPickerFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  colorConfig = {},
  inputClassName,
  labelClassName,
  wrapperClassName,
}) => {
  const {
    format = 'hex',
    showPreview = true,
    presetColors = DEFAULT_PRESETS,
    allowCustom = true,
  } = colorConfig;

  const { state, handleChange, handleBlur } = fieldApi;
  const value = (state.value as string) || '#000000';

  const [isOpen, setIsOpen] = useState(false);
  const [customInput, setCustomInput] = useState(value);
  const containerRef = useRef<HTMLDivElement>(null);
  const colorInputRef = useRef<HTMLInputElement>(null);

  // Ensure value is always a valid hex color
  const normalizedValue = value.startsWith('#') ? value : `#${value}`;
  const displayValue = formatColor(normalizedValue, format);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleColorSelect = (color: string) => {
    const formattedColor = formatColor(color, format);
    handleChange(formattedColor);
    setCustomInput(color);
    setIsOpen(false);
    handleBlur();
  };

  const handleCustomInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    setCustomInput(inputValue);

    // Validate and update if it's a valid color
    if (inputValue.match(/^#[0-9A-Fa-f]{6}$/)) {
      const formattedColor = formatColor(inputValue, format);
      handleChange(formattedColor);
    }
  };

  const handleNativeColorChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const color = e.target.value;
    const formattedColor = formatColor(color, format);
    handleChange(formattedColor);
    setCustomInput(color);
  };

  const isValidColor = (color: string): boolean => {
    return /^#[0-9A-Fa-f]{6}$/.test(color);
  };

  return (
    <div className={cn("relative space-y-2", wrapperClassName)} ref={containerRef}>
      {label && (
        <Label className={cn("text-sm font-medium", labelClassName)}>
          {label}
        </Label>
      )}
      {description && (
        <p className="text-xs text-muted-foreground">{description}</p>
      )}

      <div className="flex gap-2">
        {/* Color preview and trigger */}
        <div className="relative">
          <Button
            type="button"
            variant="outline"
            className={cn(
              "w-12 h-10 p-0 border-2",
              state.meta.errors.length ? "border-destructive" : "",
              inputClassName
            )}
            onClick={() => setIsOpen(!isOpen)}
            disabled={fieldApi.form.state.isSubmitting}
            style={{ backgroundColor: normalizedValue }}
          >
            {!showPreview && <Palette className="h-4 w-4" />}
          </Button>

          {/* Native color input (hidden) */}
          <input
            ref={colorInputRef}
            type="color"
            value={normalizedValue}
            onChange={handleNativeColorChange}
            className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
            disabled={fieldApi.form.state.isSubmitting}
          />
        </div>

        {/* Color value input */}
        <Input
          value={displayValue}
          onChange={(e) => {
            const inputValue = e.target.value;
            handleChange(inputValue);
            // Try to extract hex value for internal use
            if (inputValue.startsWith('#')) {
              setCustomInput(inputValue);
            }
          }}
          onBlur={handleBlur}
          placeholder={format === 'hex' ? '#000000' : format === 'rgb' ? 'rgb(0, 0, 0)' : 'hsl(0, 0%, 0%)'}
          className={cn(
            "flex-1",
            state.meta.errors.length ? "border-destructive" : ""
          )}
          disabled={fieldApi.form.state.isSubmitting}
        />
      </div>

      {/* Color picker dropdown */}
      {isOpen && (
        <div className="absolute z-50 mt-1 p-4 bg-popover border rounded-md shadow-lg w-64">
          {/* Preset colors */}
          <div className="mb-4">
            <h4 className="text-sm font-medium mb-2">Preset Colors</h4>
            <div className="grid grid-cols-6 gap-2">
              {presetColors.map((color, index) => (
                <button
                  key={index}
                  type="button"
                  className={cn(
                    "w-8 h-8 rounded border-2 hover:scale-110 transition-transform",
                    normalizedValue.toLowerCase() === color.toLowerCase() 
                      ? "border-primary ring-2 ring-primary ring-offset-2" 
                      : "border-muted hover:border-primary"
                  )}
                  style={{ backgroundColor: color }}
                  onClick={() => handleColorSelect(color)}
                  title={color}
                >
                  {normalizedValue.toLowerCase() === color.toLowerCase() && (
                    <Check className="h-4 w-4 text-white drop-shadow-lg" />
                  )}
                </button>
              ))}
            </div>
          </div>

          {/* Custom color input */}
          {allowCustom && (
            <div>
              <h4 className="text-sm font-medium mb-2">Custom Color</h4>
              <div className="flex gap-2">
                <Input
                  value={customInput}
                  onChange={handleCustomInputChange}
                  placeholder="#000000"
                  className="flex-1 text-xs"
                />
                <Button
                  type="button"
                  size="sm"
                  onClick={() => handleColorSelect(customInput)}
                  disabled={!isValidColor(customInput)}
                >
                  Apply
                </Button>
              </div>
            </div>
          )}
        </div>
      )}

      {state.meta.isTouched && state.meta.errors.length > 0 && (
        <div className="text-xs text-destructive pt-1">
          {state.meta.errors.map((err: string | Error, index: number) => (
            <p key={index}>{typeof err === 'string' ? err : (err as Error)?.message || 'Invalid'}</p>
          ))}
        </div>
      )}
    </div>
  );
};

