"use client";
import React, { useState, useEffect } from "react";
import type { BaseFieldProps } from "@/lib/formedible/types";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { cn } from "@/lib/utils";

interface DurationValue {
  hours?: number;
  minutes?: number;
  seconds?: number;
  totalSeconds?: number;
}

interface DurationPickerFieldProps extends BaseFieldProps {
  durationConfig?: {
    format?: 'hms' | 'hm' | 'ms' | 'hours' | 'minutes' | 'seconds';
    maxHours?: number;
    maxMinutes?: number;
    maxSeconds?: number;
    showLabels?: boolean;
    allowNegative?: boolean;
  };
}

export const DurationPickerField: React.FC<DurationPickerFieldProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  wrapperClassName,
  labelClassName,
  inputClassName,
  durationConfig = {},
}) => {
  const {
    format = 'hms',
    maxHours = 23,
    maxMinutes = 59,
    maxSeconds = 59,
    showLabels = true,
    allowNegative: _allowNegative = false
  } = durationConfig;

  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  // Initialize from field value
  useEffect(() => {
    const value = fieldApi.state.value;
    if (value) {
      if (typeof value === 'number') {
        // Value is total seconds
        const totalSeconds = Math.abs(value);
        const newHours = Math.min(Math.floor(totalSeconds / 3600), maxHours);
        const newMinutes = Math.min(Math.floor((totalSeconds % 3600) / 60), maxMinutes);
        const newSeconds = Math.min(totalSeconds % 60, maxSeconds);
        setHours(newHours);
        setMinutes(newMinutes);
        setSeconds(newSeconds);
      } else if (typeof value === 'object') {
        // Value is duration object
        setHours(Math.min(value.hours || 0, maxHours));
        setMinutes(Math.min(value.minutes || 0, maxMinutes));
        setSeconds(Math.min(value.seconds || 0, maxSeconds));
      }
    }
  }, [fieldApi.state.value, maxHours, maxMinutes, maxSeconds]);

  // Update field value when duration changes
  useEffect(() => {
    const totalSeconds = hours * 3600 + minutes * 60 + seconds;
    const durationValue: DurationValue = {
      hours,
      minutes,
      seconds,
      totalSeconds
    };

    // Update field based on format
    switch (format) {
      case 'hours':
        fieldApi.handleChange(hours + minutes / 60 + seconds / 3600);
        break;
      case 'minutes':
        fieldApi.handleChange(hours * 60 + minutes + seconds / 60);
        break;
      case 'seconds':
        fieldApi.handleChange(totalSeconds);
        break;
      default:
        fieldApi.handleChange(durationValue);
    }
  }, [hours, minutes, seconds, format, fieldApi]);

  const formatDuration = () => {
    const parts = [];
    if (format.includes('h') && hours > 0) parts.push(`${hours}h`);
    if (format.includes('m') && minutes > 0) parts.push(`${minutes}m`);
    if (format.includes('s') && seconds > 0) parts.push(`${seconds}s`);
    return parts.join(' ') || '0';
  };

  const renderTimeInput = (
    value: number,
    onChange: (value: number) => void,
    max: number,
    unit: string,
    show: boolean
  ) => {
    if (!show) return null;

    return (
      <div className="flex flex-col space-y-1">
        {showLabels && (
          <Label className="text-xs text-muted-foreground capitalize">
            {unit}
          </Label>
        )}
        <Select
          value={value.toString()}
          onValueChange={(val) => onChange(parseInt(val))}
        >
          <SelectTrigger className={cn("w-20", inputClassName)}>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            {Array.from({ length: max + 1 }, (_, i) => (
              <SelectItem key={i} value={i.toString()}>
                {i.toString().padStart(2, '0')}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>
    );
  };

  const renderManualInput = () => {
    return (
      <div className="space-y-2">
        <Input
          id={fieldApi.name}
          value={formatDuration()}
          placeholder={placeholder || "Enter duration (e.g., 1h 30m 45s)"}
          className={inputClassName}
          onChange={(e) => {
            const input = e.target.value;
            // Parse manual input like "1h 30m 45s" or "90m" or "3600s"
            const hourMatch = input.match(/(\d+)h/i);
            const minuteMatch = input.match(/(\d+)m(?!s)/i); // Don't match 'ms'
            const secondMatch = input.match(/(\d+)s/i);

            const newHours = hourMatch ? Math.max(0, parseInt(hourMatch[1], 10)) : 0;
            const newMinutes = minuteMatch ? Math.max(0, parseInt(minuteMatch[1], 10)) : 0;
            const newSeconds = secondMatch ? Math.max(0, parseInt(secondMatch[1], 10)) : 0;

            if (newHours <= maxHours && newMinutes <= maxMinutes && newSeconds <= maxSeconds) {
              setHours(newHours);
              setMinutes(newMinutes);
              setSeconds(newSeconds);
            } else {
              // Provide feedback for invalid input
              console.warn('Duration values exceed maximum limits');
            }
          }}
        />
        <div className="text-xs text-muted-foreground">
          Format: {format === 'hms' ? '1h 30m 45s' : format === 'hm' ? '1h 30m' : format === 'ms' ? '30m 45s' : `${format} only`}
        </div>
      </div>
    );
  };

  return (
    <div className={cn("space-y-2", wrapperClassName)}>
      {label && (
        <Label htmlFor={fieldApi.name} className={labelClassName}>
          {label}
        </Label>
      )}

      {description && (
        <p className="text-sm text-muted-foreground">{description}</p>
      )}

      <div className="space-y-3">
        {/* Dropdown selectors */}
        <div className="flex gap-3">
          {renderTimeInput(
            hours,
            setHours,
            maxHours,
            'hours',
            format.includes('h')
          )}
          {renderTimeInput(
            minutes,
            setMinutes,
            maxMinutes,
            'minutes',
            format.includes('m')
          )}
          {renderTimeInput(
            seconds,
            setSeconds,
            maxSeconds,
            'seconds',
            format.includes('s')
          )}
        </div>

        {/* Manual input alternative */}
        {renderManualInput()}

        {/* Duration display */}
        <div className="text-sm text-muted-foreground">
          Total: {formatDuration()}
          {format !== 'seconds' && ` (${hours * 3600 + minutes * 60 + seconds} seconds)`}
        </div>
      </div>

      {fieldApi.state.meta.errors && fieldApi.state.meta.errors.length > 0 && (
        <p className="text-sm text-destructive">
          {fieldApi.state.meta.errors[0]}
        </p>
      )}
    </div>
  );
};

'use client';
import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { HelpCircle, ExternalLink, Info } from 'lucide-react';

interface FieldHelpProps {
  help?: {
    text?: string;
    tooltip?: string;
    position?: 'top' | 'bottom' | 'left' | 'right';
    link?: { url: string; text: string };
  };
  className?: string;
}

export const FieldHelp: React.FC<FieldHelpProps> = ({
  help,
  className,
}) => {
  const [showTooltip, setShowTooltip] = useState(false);

  if (!help || (!help.text && !help.tooltip && !help.link)) {
    return null;
  }

  const { text, tooltip, position = 'top', link } = help;

  return (
    <div className={cn("space-y-2", className)}>
      {/* Help text */}
      {text && (
        <div className="flex items-start gap-2 text-xs text-muted-foreground">
          <Info className="h-3 w-3 mt-0.5 flex-shrink-0" />
          <p>{text}</p>
        </div>
      )}

      {/* Tooltip trigger */}
      {tooltip && (
        <div className="relative inline-block">
          <Button
            type="button"
            variant="ghost"
            size="sm"
            className="h-6 w-6 p-0 text-muted-foreground hover:text-foreground"
            onMouseEnter={() => setShowTooltip(true)}
            onMouseLeave={() => setShowTooltip(false)}
            onFocus={() => setShowTooltip(true)}
            onBlur={() => setShowTooltip(false)}
          >
            <HelpCircle className="h-3 w-3" />
          </Button>

          {/* Tooltip */}
          {showTooltip && (
            <div
              className={cn(
                "absolute z-50 px-2 py-1 text-xs text-white bg-black rounded shadow-lg whitespace-nowrap",
                "pointer-events-none",
                {
                  'bottom-full left-1/2 -translate-x-1/2 mb-1': position === 'top',
                  'top-full left-1/2 -translate-x-1/2 mt-1': position === 'bottom',
                  'right-full top-1/2 -translate-y-1/2 mr-1': position === 'left',
                  'left-full top-1/2 -translate-y-1/2 ml-1': position === 'right',
                }
              )}
            >
              {tooltip}
              {/* Tooltip arrow */}
              <div
                className={cn(
                  "absolute w-0 h-0 border-2 border-transparent",
                  {
                    'top-full left-1/2 -translate-x-1/2 border-t-black border-b-0': position === 'top',
                    'bottom-full left-1/2 -translate-x-1/2 border-b-black border-t-0': position === 'bottom',
                    'top-1/2 left-full -translate-y-1/2 border-l-black border-r-0': position === 'left',
                    'top-1/2 right-full -translate-y-1/2 border-r-black border-l-0': position === 'right',
                  }
                )}
              />
            </div>
          )}
        </div>
      )}

      {/* Help link */}
      {link && (
        <div className="flex items-center gap-1">
          <Button
            type="button"
            variant="link"
            size="sm"
            className="h-auto p-0 text-xs text-primary hover:text-primary/80"
            onClick={() => window.open(link.url, '_blank', 'noopener,noreferrer')}
          >
            {link.text}
            <ExternalLink className="h-3 w-3 ml-1" />
          </Button>
        </div>
      )}
    </div>
  );
};

import React from "react";
import type { AnyFieldApi } from "@tanstack/react-form";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { cn } from "@/lib/utils";
import { PaperclipIcon, XIcon, UploadCloudIcon } from "lucide-react";
