interface FileUploadFieldProps {
  fieldApi: AnyFieldApi;
  label?: string;
  accept?: string;
  className?: string;
  wrapperClassName?: string;
  description?: string;
}

export const FileUploadField: React.FC<FileUploadFieldProps> = ({
  fieldApi,
  label,
  accept,
  className,
  wrapperClassName,
  description,
}) => {
  const { name, state, handleChange, handleBlur } = fieldApi;
  const file = state.value as File | null;

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files?.[0] ?? null;
    handleChange(selectedFile);
    handleBlur();
  };

  const handleRemoveFile = () => {
    handleChange(null);
    const inputElement = document.getElementById(name) as HTMLInputElement;
    if (inputElement) {
      inputElement.value = "";
    }
    handleBlur();
  };

  const triggerFileInput = () => {
    const inputElement = document.getElementById(name) as HTMLInputElement;
    inputElement?.click();
  };

  return (
    <div
      className={cn("space-y-1.5", wrapperClassName)}
    >
      {label && (
        <Label className="text-sm font-medium cursor-pointer" onClick={triggerFileInput}>
          {label}
        </Label>
      )}
      {description && <p className="text-xs text-muted-foreground">{description}</p>}
      <Input
        id={name}
        name={name}
        type="file"
        accept={accept}
        onChange={handleFileChange}
        className="hidden"
      />
      {file ? (
        <div
          className="flex items-center justify-between p-2.5 border rounded-lg bg-muted/40 shadow-sm hover:shadow-md transition-shadow"
        >
          <div className="flex items-center gap-2 text-sm overflow-hidden">
            <PaperclipIcon className="h-5 w-5 text-primary shrink-0" />
            <span className="truncate" title={file.name}>{file.name}</span>
            <span className="text-xs text-muted-foreground whitespace-nowrap">
              ({(file.size / 1024).toFixed(1)} KB)
            </span>
          </div>
          <Button
            type="button"
            variant="ghost"
            size="icon"
            onClick={handleRemoveFile}
            className="h-7 w-7 text-destructive hover:bg-destructive/10 shrink-0"
            aria-label="Remove file"
          >
            <XIcon className="h-4 w-4" />
          </Button>
        </div>
      ) : (
        <button
          type="button"
          onClick={triggerFileInput}
          className={cn(
            "w-full flex flex-col items-center justify-center p-4 border-2 border-dashed rounded-lg hover:border-primary transition-colors cursor-pointer bg-background hover:bg-muted/50",
            className,
            state.meta.errors.length ? "border-destructive hover:border-destructive" : "border-muted-foreground/50",
          )}
        >
          <UploadCloudIcon className="h-8 w-8 text-muted-foreground mb-2" />
          <span className="text-sm font-medium text-muted-foreground">
            Click or drag and drop a file
          </span>
          {accept && <span className="text-xs text-muted-foreground/80 mt-1">Accepted types: {accept}</span>}
        </button>
      )}
      {state.meta.isTouched && state.meta.errors.length > 0 && (
        <div className="text-xs text-destructive pt-1">
          {state.meta.errors.map((err: string | Error, index: number) => (
            <p key={index}>{typeof err === 'string' ? err : (err as Error)?.message || 'Invalid'}</p>
          ))}
        </div>
      )}
    </div>
  );
};

'use client';
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { cn } from '@/lib/utils';
import { Check, X, Loader2 } from 'lucide-react';
import type { AnyFieldApi } from '@tanstack/react-form';


interface InlineValidationWrapperProps {
  children: React.ReactNode;
  fieldApi: AnyFieldApi;
  inlineValidation?: {
    enabled?: boolean;
    debounceMs?: number;
    showSuccess?: boolean;
    asyncValidator?: (value: unknown) => Promise<string | null>;
  };
  className?: string;
}

export const InlineValidationWrapper: React.FC<InlineValidationWrapperProps> = ({
  children,
  fieldApi,
  inlineValidation = {},
  className,
}) => {
  const {
    enabled = true,
    debounceMs = 300,
    showSuccess = true,
    asyncValidator,
  } = inlineValidation;

  const [validationState, setValidationState] = useState<{
    isValidating: boolean;
    isValid: boolean | null;
    message: string | null;
  }>({
    isValidating: false,
    isValid: null,
    message: null,
  });

  const { state } = fieldApi;
  const value = state.value;
  const hasErrors = state.meta.errors.length > 0;
  const isTouched = state.meta.isTouched;

  // Validation function
  const validateValue = useCallback(async (currentValue: unknown) => {
    if (!enabled || !asyncValidator) return;

    setValidationState(prev => ({ ...prev, isValidating: true }));

    try {
      const result = await asyncValidator(currentValue);

      setValidationState({
        isValidating: false,
        isValid: result === null,
        message: result,
      });
    } catch (error) {
      setValidationState({
        isValidating: false,
        isValid: false,
        message: error instanceof Error ? error.message : 'Validation failed',
      });
    }
  }, [enabled, asyncValidator]);

  // Debounced validation function
  const debouncedValidate = useMemo(
    () => debounce(validateValue, debounceMs),
    [validateValue, debounceMs]
  );

  // Trigger validation when value changes
  useEffect(() => {
    if (enabled && isTouched && value !== undefined && value !== '') {
      debouncedValidate(value);
    } else {
      setValidationState({
        isValidating: false,
        isValid: null,
        message: null,
      });
    }
  }, [value, isTouched, enabled, debouncedValidate]);

  // Reset validation state when field is reset
  useEffect(() => {
    if (!isTouched) {
      setValidationState({
        isValidating: false,
        isValid: null,
        message: null,
      });
    }
  }, [isTouched]);

  const getValidationIcon = () => {
    if (!enabled || !isTouched) return null;

    if (validationState.isValidating) {
      return <Loader2 className="h-4 w-4 animate-spin text-muted-foreground" />;
    }

    if (hasErrors) {
      return <X className="h-4 w-4 text-destructive" />;
    }

    if (validationState.isValid === false) {
      return <X className="h-4 w-4 text-destructive" />;
    }

    if (showSuccess && validationState.isValid === true) {
      return <Check className="h-4 w-4 text-green-500" />;
    }

    if (showSuccess && !hasErrors && isTouched && value) {
      return <Check className="h-4 w-4 text-green-500" />;
    }

    return null;
  };

  const getValidationMessage = () => {
    if (!enabled || !isTouched) return null;

    // Show form validation errors first
    if (hasErrors) {
      return state.meta.errors[0];
    }

    // Show async validation message
    if (validationState.message) {
      return validationState.message;
    }

    return null;
  };

  const validationIcon = getValidationIcon();
  const validationMessage = getValidationMessage();

  return (
    <div className={cn("relative", className)}>
      {/* Field with validation icon */}
      <div className="relative">
        {children}
        {validationIcon && (
          <div className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none">
            {validationIcon}
          </div>
        )}
      </div>

      {/* Validation message */}
      {validationMessage && (
        <div className={cn(
          "text-xs mt-1 flex items-center gap-1",
          hasErrors || validationState.isValid === false
            ? "text-destructive"
            : "text-muted-foreground"
        )}>
          {typeof validationMessage === 'string' 
            ? validationMessage 
            : (validationMessage as Error)?.message || 'Validation error'
          }
        </div>
      )}
    </div>
  );
};

// Debounce utility function
function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeout: ReturnType<typeof setTimeout>;

  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
}

