  const Form: React.FC<FormProps> = ({ 
    className, 
    children, 
    onSubmit,
    // HTML form attributes
    action,
    method,
    encType,
    target,
    autoComplete,
    noValidate,
    acceptCharset,
    // Event handlers
    onReset,
    onInput,
    onInvalid,
    onKeyDown,
    onKeyUp,

    onFocus,
    onBlur,
    // Accessibility
    role,
    'aria-label': ariaLabel,
    'aria-labelledby': ariaLabelledby,
    'aria-describedby': ariaDescribedby,
