    };

    const handleInput = (e: React.FormEvent) => {
      if (onInput) {
        onInput(e);
      }
      if (onFormInput) {
        onFormInput(e, form);
      }
    };

    const handleInvalid = (e: React.FormEvent) => {
      if (onInvalid) {
        onInvalid(e);
      }
      if (onFormInvalid) {
        onFormInvalid(e, form);
      }
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
      if (onKeyDown) {
        onKeyDown(e);
      }
      if (onFormKeyDown) {
        onFormKeyDown(e, form);
      }
    };

    const handleKeyUp = (e: React.KeyboardEvent) => {
      if (onKeyUp) {
        onKeyUp(e);
      }
      if (onFormKeyUp) {
        onFormKeyUp(e, form);
      }
    };

