    });



    const handleFocus = (e: React.FocusEvent) => {
      if (onFocus) {
        onFocus(e);
      }
      if (onFormFocus) {
        onFormFocus(e, form);
      }
    };

    const handleBlur = (e: React.FocusEvent) => {
      if (onBlur) {
        onBlur(e);
      }
      if (onFormBlur) {
        onFormBlur(e, form);
      }
    };

    const formClass = cn("space-y-6", formClassName, className);

    // Helper function to resolve options (static or dynamic)
    const resolveOptions = React.useCallback((options: FieldConfig['options'], currentValues: Record<string, unknown>) => {
      if (typeof options === 'function') {
        return options(currentValues);
      }
      return options;
    }, []);

    const renderField = React.useCallback((fieldConfig: FieldConfig) => {
      const { 
        name, 
        type, 
        label, 
        placeholder, 
        description, 
        options,
        min,
        max,
        step,
        accept,
        multiple,
        component: CustomComponent,
        wrapper: CustomWrapper,
        validation,
        arrayConfig,
        datalist,
        help,
        inlineValidation,

        ratingConfig,
        phoneConfig,
        colorConfig,
        multiSelectConfig,
        locationConfig,
        durationConfig,
        autocompleteConfig,
        maskedInputConfig,
        objectConfig,
        sliderConfig,
        numberConfig,
        dateConfig,
        fileConfig,
        textareaConfig,
        passwordConfig,
        emailConfig
      } = fieldConfig;

      return (
        <form.Field 
          key={name} 
          name={name as keyof TFormValues & string}
          validators={validation ? { 
            onChange: ({ value }) => {
              const result = validation.safeParse(value);
              return result.success ? undefined : result.error.errors[0]?.message || 'Invalid value';
