          } : undefined}
        >
          {(field) => {
            // TanStack Form Best Practice: Use FieldConditionalRenderer to prevent parent re-renders
            return (
              <FieldConditionalRenderer
                form={form}
                fieldConfig={fieldConfig}
              >
                {(shouldRender) => {
                  if (!shouldRender) {
                    return null;
                  }

                  // Subscribe to form values for dynamic options
                  return (
                    <form.Subscribe
                      selector={(state: any) => state.values}
                    >
                      {(currentValues: any) => {
                        // Check for cross-field validation errors
                        const crossFieldError = crossFieldErrors[name];
                        const asyncValidationState = asyncValidationStates[name];

                        // Resolve options (static or dynamic)
                        const resolvedOptions = resolveOptions(options, currentValues);

                        const baseProps = {
                          fieldApi: field,
                          label,
                          placeholder,
                          description,
                          wrapperClassName: fieldClassName,
                          min,
                          max,
                          step,
                          accept,
                          multiple,
                          disabled: disabled || loading || field.form.state.isSubmitting,
                          crossFieldError,
                          asyncValidationState,
                        };

                        // Select the component to use
                        const FieldComponent = CustomComponent || fieldComponents[type] || TextField;

                        // Add type-specific props
                        let props: FieldComponentProps = { ...baseProps };

                        // Normalize options to the expected format
                        const normalizedOptions = resolvedOptions ? resolvedOptions.map(opt => 
                          typeof opt === 'string' ? { value: opt, label: opt } : opt
                        ) : [];

                        if (type === 'select') {
                          props = { ...props, options: normalizedOptions };
                        } else if (type === 'array') {
                          const mappedArrayConfig = arrayConfig ? {
                            itemType: arrayConfig.itemType || 'text',
                            itemLabel: arrayConfig.itemLabel,
                            itemPlaceholder: arrayConfig.itemPlaceholder,
                            minItems: arrayConfig.minItems,
                            maxItems: arrayConfig.maxItems,
                            itemValidation: arrayConfig.itemValidation,
                            itemComponent: arrayConfig.itemComponent as React.ComponentType<BaseFieldProps>,
                            addButtonLabel: arrayConfig.addButtonLabel,
                            removeButtonLabel: arrayConfig.removeButtonLabel,
                            sortable: arrayConfig.sortable,
                            defaultValue: arrayConfig.defaultValue
                          } : undefined;
                          props = { ...props, arrayConfig: mappedArrayConfig };
                        } else if (['text', 'email', 'password', 'url', 'tel'].includes(type)) {
                          props = { ...props, type: type as 'text' | 'email' | 'password' | 'url' | 'tel', datalist: datalist?.options };
                        } else if (type === 'radio') {
                          props = { ...props, options: normalizedOptions };
                        } else if (type === 'multiSelect') {
                          props = { ...props, options: normalizedOptions, multiSelectConfig };
                        } else if (type === 'colorPicker') {
                          props = { ...props, colorConfig };
                        } else if (type === 'rating') {
                          props = { ...props, ratingConfig };
                        } else if (type === 'phone') {
                          props = { ...props, phoneConfig };
                        } else if (type === 'location') {
                          props = { ...props, locationConfig };
                        } else if (type === 'duration') {
                          props = { ...props, durationConfig };
                        } else if (type === 'autocomplete') {
                          // Handle dynamic options for autocomplete
                          const resolvedAutocompleteConfig = autocompleteConfig ? {
                            ...autocompleteConfig,
                            options: resolveOptions(autocompleteConfig.options, currentValues)
                          } : undefined;
                          props = { ...props, autocompleteConfig: resolvedAutocompleteConfig };
                        } else if (type === 'masked') {
                          props = { ...props, maskedInputConfig };
                        } else if (type === 'object') {
                          props = { ...props, objectConfig };
                        } else if (type === 'slider') {
                          props = { ...props, sliderConfig };
                        } else if (type === 'number') {
                          props = { ...props, numberConfig };
                        } else if (type === 'date') {
                          props = { ...props, dateConfig };
                        } else if (type === 'file') {
                          props = { ...props, fileConfig };
                        } else if (type === 'textarea') {
                          props = { ...props, textareaConfig };
                        } else if (type === 'password') {
                          props = { ...props, passwordConfig };
                        } else if (type === 'email') {
                          props = { ...props, emailConfig };
                        }

                        // Render the field component
                        const fieldElement = <FieldComponent {...props} />;

                        // Apply inline validation wrapper if enabled
                        const wrappedFieldElement = inlineValidation?.enabled 
                          ? (
                              <InlineValidationWrapper
                                fieldApi={field}
                                inlineValidation={inlineValidation}
                              >
                                {fieldElement}
                              </InlineValidationWrapper>
                            )
                          : fieldElement;

                        // Add field help if provided
                        const fieldWithHelp = help ? (
                          <div className="space-y-2">
                            {wrappedFieldElement}
                            <FieldHelp help={help} />
                          </div>
                        ) : wrappedFieldElement;

                        // Apply custom wrapper or global wrapper
                        const Wrapper = CustomWrapper || globalWrapper;

                        return Wrapper 
                          ? <Wrapper field={fieldConfig}>{fieldWithHelp}</Wrapper>
                          : fieldWithHelp;
                      }}
                    </form.Subscribe>
                  );
                }}
              </FieldConditionalRenderer>
            );
          }}
        </form.Field>
      );
    }, [resolveOptions]);

    const renderTabContent = React.useCallback((tabFields: FieldConfig[]) => {
      // TanStack Form Best Practice: Use reusable subscription component
      return (
        <ConditionalFieldsSubscription
          form={form}
          fields={tabFields}
          conditionalSections={conditionalSections}
        >
          {(currentValues) => {
            // Filter fields based on conditional sections using subscribed values
            const visibleFields = tabFields.filter(field => {
              const conditionalSection = conditionalSections.find(section => 
                section.fields.includes(field.name)
              );

              if (conditionalSection) {
                return conditionalSection.condition(currentValues as TFormValues);
              }

              return true;
            });

            // Group fields by section and group
            const groupedFields = visibleFields.reduce((acc, field) => {
              const sectionKey = field.section?.title || 'default';
              const groupKey = field.group || 'default';

              if (!acc[sectionKey]) {
                acc[sectionKey] = {
                  section: field.section,
                  groups: {}
                };
              }

              if (!acc[sectionKey].groups[groupKey]) {
                acc[sectionKey].groups[groupKey] = [];
              }

              acc[sectionKey].groups[groupKey].push(field);
              return acc;
            }, {} as Record<string, { section?: { title: string; description?: string; collapsible?: boolean; defaultExpanded?: boolean }; groups: Record<string, FieldConfig[]> }>);

            const renderSection = (sectionKey: string, sectionData: { section?: { title: string; description?: string; collapsible?: boolean; defaultExpanded?: boolean }; groups: Record<string, FieldConfig[]> }) => (
              <SectionRenderer
                key={sectionKey}
                sectionKey={sectionKey}
                sectionData={sectionData}
                renderField={renderField}
              />
            );

            const sectionsToRender = Object.entries(groupedFields);

            return sectionsToRender.length === 1 && sectionsToRender[0][0] === 'default' 
              ? sectionsToRender[0][1].groups.default?.map((field: FieldConfig) => renderField(field))
              : sectionsToRender.map(([sectionKey, sectionData]) => 
                  renderSection(sectionKey, sectionData)
                );
          }}
        </ConditionalFieldsSubscription>
      );
    }, [renderField]);

    const renderPageContent = React.useCallback(() => {
      if (hasTabs) {
        // Render tabs - memoize tab content to prevent rerenders
        const tabsToRender = tabs!.map(tab => ({
          id: tab.id,
          label: tab.label,
          content: renderTabContent(fieldsByTab[tab.id] || [])
        }));

        return (
          <FormTabs
            tabs={tabsToRender}
            activeTab={activeTab}
            onTabChange={setActiveTab}
          />
        );
      }

      // Original page rendering logic with TanStack Form best practices
      const currentFields = getCurrentPageFields();
      const pageConfig = getCurrentPageConfig();

      // For now, subscribe to all form values since we don't have explicit dependencies
      // This could be optimized further by analyzing the condition functions

      // TanStack Form Best Practice: Use targeted selector for minimal re-renders
      return (
        <form.Subscribe
          selector={(state: any) => state.values}
        >
          {(currentValues: any) => {
            // Filter fields based on conditional sections using subscribed values
            const visibleFields = currentFields.filter(field => {
              const conditionalSection = conditionalSections.find(section => 
                section.fields.includes(field.name)
              );

              if (conditionalSection) {
                return conditionalSection.condition(currentValues as TFormValues);
              }

              return true;
            });

            // Group fields by section and group
            const groupedFields = visibleFields.reduce((acc, field) => {
              const sectionKey = field.section?.title || 'default';
              const groupKey = field.group || 'default';

              if (!acc[sectionKey]) {
                acc[sectionKey] = {
                  section: field.section,
                  groups: {}
                };
              }

              if (!acc[sectionKey].groups[groupKey]) {
                acc[sectionKey].groups[groupKey] = [];
              }

              acc[sectionKey].groups[groupKey].push(field);
              return acc;
            }, {} as Record<string, { section?: { title: string; description?: string; collapsible?: boolean; defaultExpanded?: boolean }; groups: Record<string, FieldConfig[]> }>);

            const renderSection = (sectionKey: string, sectionData: { section?: { title: string; description?: string; collapsible?: boolean; defaultExpanded?: boolean }; groups: Record<string, FieldConfig[]> }) => (
              <SectionRenderer
                key={sectionKey}
                sectionKey={sectionKey}
                sectionData={sectionData}
                renderField={renderField}
              />
            );

            const sectionsToRender = Object.entries(groupedFields);

            const PageComponent = pageConfig?.component || DefaultPageComponent;

            return (
              <PageComponent
                title={pageConfig?.title}
                description={pageConfig?.description}
                page={currentPage}
                totalPages={totalPages}
              >
                {sectionsToRender.length === 1 && sectionsToRender[0][0] === 'default' 
                               ? sectionsToRender[0][1].groups.default?.map((field: FieldConfig) => renderField(field))
                  : sectionsToRender.map(([sectionKey, sectionData]) => 
                      renderSection(sectionKey, sectionData)
                    )
                }
              </PageComponent>
            );
          }}
        </form.Subscribe>
      );
    }, [renderTabContent, renderField, activeTab, setActiveTab, hasTabs, tabs]);

    const renderProgress = () => {
      if (!hasPages || !progress) return null;

      const ProgressComponent = progress.component || DefaultProgressComponent;

      return (
        <ProgressComponent
          value={progressValue}
          currentPage={currentPage}
          totalPages={totalPages}
          className={progress.className}
        />
      );
    };

    const renderNavigation = () => {
      if (!showSubmitButton) return null;
      if (!hasPages) {
        return (
          <form.Subscribe
            selector={(state) => ({
              canSubmit: state.canSubmit,
              isSubmitting: state.isSubmitting,
            })}
          >
            {(state) => {
              const { canSubmit, isSubmitting } = state as { canSubmit: boolean; isSubmitting: boolean };
              return (
                <Button
                  type="submit"
                  disabled={!canSubmit || isSubmitting || disabled || loading}
                  className="w-full"
                >
                  {loading ? "Loading..." : isSubmitting ? "Submitting..." : submitLabel}
                </Button>
              );
            }}
          </form.Subscribe>
        );
      }

      return (
        <form.Subscribe
          selector={(state) => ({
            canSubmit: state.canSubmit,
            isSubmitting: state.isSubmitting,
          })}
        >
          {(state) => {
            const { canSubmit, isSubmitting } = state as { canSubmit: boolean; isSubmitting: boolean };
            return (
              <div className="flex justify-between gap-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={goToPreviousPage}
                  disabled={isFirstPage || disabled || loading}
                  className={isFirstPage ? "invisible" : ""}
                >
                  {previousLabel}
                </Button>

                <Button
                  type="submit"
                  disabled={(!canSubmit || isSubmitting || disabled || loading) && isLastPage}
                  className="flex-1 max-w-xs"
                >
                  {loading && isLastPage
                    ? "Loading..."
                    : isSubmitting && isLastPage
                    ? "Submitting..."
                    : isLastPage
                    ? submitLabel
                    : nextLabel}
                </Button>
              </div>
            );
          }}
        </form.Subscribe>
      );
    };

          return (
        <form 
          onSubmit={handleSubmit} 
          className={formClass}
          action={action}
          method={method}
          encType={encType}
          target={target}
          autoComplete={autoComplete}
          noValidate={noValidate}
          acceptCharset={acceptCharset}
          onReset={handleReset}
          onInput={handleInput}
          onInvalid={handleInvalid}
          onKeyDown={handleKeyDown}
          onKeyUp={handleKeyUp}

          onFocus={handleFocus}
          onBlur={handleBlur}
          role={role}
          aria-label={ariaLabel}
          aria-labelledby={ariaLabelledby}
          aria-describedby={ariaDescribedby}
          tabIndex={tabIndex}
        >
        {children || (
          <>
            {renderProgress()}
            {renderPageContent()}
            {renderNavigation()}
          </>
        )}
      </form>
    );
  };

  return {
    form,
    Form,
    currentPage,
    totalPages,
    goToNextPage,
    goToPreviousPage,
    setCurrentPage: setCurrentPageWithValidation,
    isFirstPage,
    isLastPage,
    progressValue,
    // Advanced features
    crossFieldErrors,
    asyncValidationStates,
    validateCrossFields,
    validateFieldAsync,
    saveToStorage,
    loadFromStorage,
    clearStorage,
  };
}

import React from 'react';
import type { AnyFieldApi } from '@tanstack/react-form';
import type { FormApi, ValidationError, FormState } from '@tanstack/form-core';

// Strict type definitions for better type safety
export interface StrictFieldApi<T = unknown> {
  name: string;
  value: T;
  errors: ValidationError[];
  touched: boolean;
  setValue: (value: T) => void;
  setTouched: (touched: boolean) => void;
  validate: () => Promise<ValidationError[]>;
}

export interface TypedFormState<TFormData = Record<string, unknown>> {
  values: TFormData;
  errors: Record<keyof TFormData, ValidationError[]>;
  touched: Record<keyof TFormData, boolean>;
  isSubmitting: boolean;
  isValidating: boolean;
  canSubmit: boolean;
}

export interface TypedFormSubscriptionSelector<TFormData = Record<string, unknown>, TSelected = unknown> {
  (state: FormState<TFormData, undefined, undefined, undefined, undefined, undefined, undefined, undefined, undefined>): TSelected;
}

// Validation error types based on TanStack Form
export type FormedibleValidationError = ValidationError;

// Validation error that can be a string, Error object, or custom validation result
export type FieldValidationError = string | Error | ValidationError;

// Array of validation errors for a field
export type FieldValidationErrors = FieldValidationError[];

// Type alias for our FormApi - use the core FormApi type which is what useForm actually returns
export type FormedibleFormApi<TFormData = Record<string, unknown>> = FormApi<
  TFormData,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined
>;

// Option types for select, radio, and multi-select fields
export type FieldOption = {
  value: string;
  label: string;
  disabled?: boolean;
  description?: string;
};

export type FieldOptions = string[] | FieldOption[];

// Normalize options to consistent format
export type NormalizedFieldOption = FieldOption;

// Props that all basic field components rendered by FormedibleRoot will receive
export interface BaseFieldProps {
  fieldApi: AnyFieldApi;
  label?: string;
  description?: string;
  placeholder?: string;
  inputClassName?: string;   // For the <Input /> component itself
  labelClassName?: string;   // For the <Label /> component
  wrapperClassName?: string; // For the div wrapping label and input
}

// Specific field component prop types
export interface SelectFieldProps extends BaseFieldProps {
  options: FieldOptions;
  placeholder?: string;
}

export interface RadioFieldProps extends BaseFieldProps {
  options: FieldOptions;
}

export interface MultiSelectFieldProps extends BaseFieldProps {
  options: FieldOptions;
  placeholder?: string;
  maxSelections?: number;
}

export interface ArrayFieldProps extends BaseFieldProps {
  arrayConfig: {
    itemType: string;
    itemLabel?: string;
    itemPlaceholder?: string;
    itemValidation?: unknown;
    minItems?: number;
    maxItems?: number;
    addButtonLabel?: string;
    removeButtonLabel?: string;
    sortable?: boolean;
    defaultValue?: unknown;
    itemComponent?: React.ComponentType<BaseFieldProps>;
    itemProps?: Record<string, unknown>;
  };
}

export interface TextFieldProps extends BaseFieldProps {
  type?: 'text' | 'email' | 'password' | 'url' | 'tel';
  datalist?: string[];
  maxLength?: number;
  minLength?: number;
  pattern?: string;
  autoComplete?: string;
}

export interface NumberFieldProps extends BaseFieldProps {
  min?: number;
  max?: number;
  step?: number;
  precision?: number;
}

export interface DateFieldProps extends BaseFieldProps {
  dateConfig?: {
    format?: string;
    minDate?: Date;
    maxDate?: Date;
    disabledDates?: Date[];
    showTime?: boolean;
    timeFormat?: string;
  };
}

export interface SliderFieldProps extends BaseFieldProps {
  sliderConfig?: {
    min?: number;
    max?: number;
    step?: number;
    showValue?: boolean;
    showTicks?: boolean;
    orientation?: 'horizontal' | 'vertical';
  };
}

export interface FileUploadFieldProps extends BaseFieldProps {
  fileConfig?: {
    accept?: string;
    multiple?: boolean;
    maxSize?: number;
    maxFiles?: number;
    allowedTypes?: string[];
    uploadUrl?: string;
    onUpload?: (files: File[]) => Promise<string[]>;
  };
}

export interface LocationPickerFieldProps extends BaseFieldProps {
  locationConfig?: LocationConfig;
}

export interface DurationPickerFieldProps extends BaseFieldProps {
  durationConfig?: DurationConfig;
}

export interface AutocompleteFieldProps extends BaseFieldProps {
  autocompleteConfig?: AutocompleteConfig;
}

export interface MaskedInputFieldProps extends BaseFieldProps {
  maskedConfig?: MaskedInputConfig;
}

export interface ColorPickerFieldProps extends BaseFieldProps {
  colorConfig?: {
    format?: 'hex' | 'rgb' | 'hsl';
    showAlpha?: boolean;
    presetColors?: string[];
    allowCustom?: boolean;
  };
}

export interface RatingFieldProps extends BaseFieldProps {
  ratingConfig?: {
    max?: number;
    allowHalf?: boolean;
    allowClear?: boolean;
    icon?: 'star' | 'heart' | 'thumbs' | React.ComponentType;
    size?: 'sm' | 'md' | 'lg' | 'small' | 'medium' | 'large';
    showValue?: boolean;
  };
}

export interface PhoneFieldProps extends BaseFieldProps {
  phoneConfig?: {
    defaultCountry?: string;
    preferredCountries?: string[];
    onlyCountries?: string[];
    excludeCountries?: string[];
    format?: 'national' | 'international';
  };
}

// Union type for all possible field component props - using intersection for flexibility
export type FieldComponentProps = BaseFieldProps & {
  // Optional props that specific field types might need
  options?: FieldOptions;
  arrayConfig?: ArrayFieldProps['arrayConfig'];
  type?: TextFieldProps['type'];
  datalist?: string[];
  dateConfig?: DateFieldProps['dateConfig'];
  sliderConfig?: SliderFieldProps['sliderConfig'];
  fileConfig?: FileUploadFieldProps['fileConfig'];
  locationConfig?: LocationPickerFieldProps['locationConfig'];
  durationConfig?: DurationPickerFieldProps['durationConfig'];
  autocompleteConfig?: AutocompleteFieldProps['autocompleteConfig'];
  maskedConfig?: MaskedInputFieldProps['maskedConfig'];
  colorConfig?: ColorPickerFieldProps['colorConfig'];
  ratingConfig?: RatingFieldProps['ratingConfig'];
  phoneConfig?: PhoneFieldProps['phoneConfig'];
  // Legacy support for existing configurations
  multiSelectConfig?: {
    maxSelections?: number;
    searchable?: boolean;
    creatable?: boolean;
    placeholder?: string;
    noOptionsText?: string;
    loadingText?: string;
  };
  maskedInputConfig?: MaskedInputConfig;
  // Allow additional props for extensibility
  [key: string]: unknown;
};

// Cross-field validation configuration
export interface CrossFieldValidation<TFormValues = Record<string, unknown>> {
  fields: (keyof TFormValues)[];
  validator: (values: Partial<TFormValues>) => string | null;
  message: string;
}

// Async validation configuration
export interface AsyncValidation {
  validator: (value: unknown) => Promise<string | null>;
  debounceMs?: number;
  loadingMessage?: string;
}

// Form analytics and tracking configuration
export interface FormAnalytics {
  onFieldFocus?: (fieldName: string, timestamp: number) => void;
  onFieldBlur?: (fieldName: string, timeSpent: number) => void;
  onFormAbandon?: (completionPercentage: number) => void;
  onPageChange?: (fromPage: number, toPage: number, timeSpent: number) => void;
  onFieldChange?: (fieldName: string, value: unknown, timestamp: number) => void;
  onFormComplete?: (timeSpent: number, formData: unknown) => void;
}

// Layout configuration for forms
export interface LayoutConfig {
  type: 'grid' | 'flex' | 'tabs' | 'accordion' | 'stepper';
  columns?: number;
  gap?: string;
  responsive?: boolean;
  className?: string;
}

// Conditional sections configuration
export interface ConditionalSection<TFormValues = Record<string, unknown>> {
  condition: (values: TFormValues) => boolean;
  fields: string[];
  layout?: LayoutConfig;
}

// Location picker configuration
export interface LocationConfig {
  apiKey?: string;
  defaultLocation?: { lat: number; lng: number };
  zoom?: number;
  searchPlaceholder?: string;
  enableSearch?: boolean;
  enableGeolocation?: boolean;
  mapProvider?: 'google' | 'openstreetmap';
}

// Duration picker configuration
export interface DurationConfig {
  format?: 'hms' | 'hm' | 'ms' | 'hours' | 'minutes' | 'seconds';
  maxHours?: number;
  maxMinutes?: number;
  maxSeconds?: number;
  showLabels?: boolean;
  allowNegative?: boolean;
}

// Autocomplete configuration
export interface AutocompleteConfig {
  options?: string[] | { value: string; label: string }[];
  asyncOptions?: (query: string) => Promise<string[] | { value: string; label: string }[]>;
  debounceMs?: number;
  minChars?: number;
  maxResults?: number;
  allowCustom?: boolean;
  placeholder?: string;
  noOptionsText?: string;
  loadingText?: string;
}

// Masked input configuration
export interface MaskedInputConfig {
  mask: string | ((value: string) => string);
  placeholder?: string;
  showMask?: boolean;
  guide?: boolean;
  keepCharPositions?: boolean;
  pipe?: (conformedValue: string, config: unknown) => false | string | { value: string; indexesOfPipedChars: number[] };
}

'use client';
import React, { useCallback, useMemo, useState } from 'react';
import { Button } from '@/components/ui/button';

import { Plus, Trash2, GripVertical } from 'lucide-react';
import type { BaseFieldProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';
import { TextField } from './text-field';
import { NumberField } from './number-field';
import { TextareaField } from './textarea-field';
import { SelectField } from './select-field';
import { CheckboxField } from './checkbox-field';
import { SwitchField } from './switch-field';
import { DateField } from './date-field';
import { SliderField } from './slider-field';
import { FileUploadField } from './file-upload-field';

// Map of field types to components
const fieldTypeComponents: Record<string, React.ComponentType<any>> = {
  text: TextField,
  email: TextField,
  password: TextField,
  url: TextField,
  tel: TextField,
  number: NumberField,
  textarea: TextareaField,
  select: SelectField,
  checkbox: CheckboxField,
  switch: SwitchField,
  date: DateField,
  slider: SliderField,
  file: FileUploadField,
};

export interface ArrayFieldSpecificProps extends BaseFieldProps {
  arrayConfig: {
    itemType: string;
    itemLabel?: string;
    itemPlaceholder?: string;
    itemValidation?: unknown;
    minItems?: number;
    maxItems?: number;
    addButtonLabel?: string;
    removeButtonLabel?: string;
    itemComponent?: React.ComponentType<any>;
    sortable?: boolean;
    defaultValue?: unknown;
    // Additional props to pass to item components
    itemProps?: Record<string, unknown>;
  };
}

export const ArrayField: React.FC<ArrayFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
  arrayConfig,
}) => {
  const name = fieldApi.name;
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;

  const value = useMemo(() => (fieldApi.state?.value as unknown[]) || [], [fieldApi.state?.value]);

  const {
    itemType,
    itemLabel,
    itemPlaceholder,
    minItems = 0,
    maxItems = 10,
    addButtonLabel = "Add Item",
    removeButtonLabel = "Remove",
    itemComponent: CustomItemComponent,
    sortable = false,
    defaultValue = '',
    itemProps = {},
  } = arrayConfig || {};

  // Get the component for rendering items
  const ItemComponent = CustomItemComponent || fieldTypeComponents[itemType || 'text'] || TextField;

  const addItem = useCallback(() => {
    if (value.length >= maxItems) return;

    const newValue = [...value, defaultValue];
    fieldApi.handleChange(newValue);
  }, [value, maxItems, defaultValue, fieldApi.handleChange]);

  const removeItem = useCallback((index: number) => {
    if (value.length <= minItems) return;

    const newValue = value.filter((_, i) => i !== index);
    fieldApi.handleChange(newValue);
    fieldApi.handleBlur();
  }, [value, minItems, fieldApi.handleChange, fieldApi.handleBlur]);

  const updateItem = useCallback((index: number, newItemValue: unknown) => {
    const newValue = [...value];
    newValue[index] = newItemValue;
    fieldApi.handleChange(newValue);
  }, [value, fieldApi.handleChange]);

  const moveItem = useCallback((fromIndex: number, toIndex: number) => {
    if (!sortable) return;
    if (fromIndex === toIndex) return;

    const newValue = [...value];
    const [movedItem] = newValue.splice(fromIndex, 1);
    newValue.splice(toIndex, 0, movedItem);
    fieldApi.handleChange(newValue);
  }, [value, fieldApi.handleChange, sortable]);

  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);



  // Create a mock field API for each item
  const createItemFieldApi = useCallback((index: number) => {
    return {
      name: `${name}[${index}]`,
      state: {
        value: value[index],
        meta: {
          errors: [],
          isTouched: false,
          isValidating: false,
        },
      },
      handleChange: (newValue: unknown) => updateItem(index, newValue),
      handleBlur: () => fieldApi.handleBlur(),
      form: fieldApi.form,
    };
  }, [name, value, updateItem, fieldApi.handleBlur, fieldApi.form]);

  const canAddMore = value.length < maxItems;
  const canRemove = value.length > minItems;

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="space-y-4">
        <div className="space-y-3">
          {value.map((_, index) => (
              <div
                key={index}
                className="flex items-start gap-2 p-3 border rounded-lg bg-card"
                onDragOver={sortable ? (e) => {
                  e.preventDefault();
                  e.dataTransfer.dropEffect = 'move';
                } : undefined}
                onDrop={sortable ? (e) => {
                  e.preventDefault();
                  if (draggedIndex !== null && draggedIndex !== index) {
                    moveItem(draggedIndex, index);
                  }
                } : undefined}
              >
                {sortable && (
                  <button
                    type="button"
                    className="mt-2 p-1 hover:bg-muted rounded cursor-grab active:cursor-grabbing"
                    draggable
                    onDragStart={(e) => {
                      setDraggedIndex(index);
                      e.dataTransfer.effectAllowed = 'move';
                    }}
                    onDragEnd={() => {
                      setDraggedIndex(null);
                    }}
                    disabled={isDisabled}
                  >
                    <GripVertical className="h-4 w-4 text-muted-foreground" />
                  </button>
                )}

                <div className="flex-1">
                  <ItemComponent
                    fieldApi={createItemFieldApi(index)}
                    label={itemLabel ? `${itemLabel} ${index + 1}` : undefined}
                    placeholder={itemPlaceholder}
                    wrapperClassName="mb-0"
                    {...itemProps}
                    // Pass specific props based on item type
                    {...(itemType === 'select' && { options: itemProps.options || [] })}
                    {...(itemType === 'number' && { 
                      min: itemProps.min, 
                      max: itemProps.max, 
                      step: itemProps.step 
                    })}
                    {...(itemType === 'slider' && { 
                      min: itemProps.min, 
                      max: itemProps.max, 
                      step: itemProps.step 
                    })}
                    {...(itemType === 'file' && { 
                      accept: itemProps.accept,
                      multiple: itemProps.multiple 
                    })}
                    {...(itemType === 'date' && typeof itemProps.dateProps === 'object' && itemProps.dateProps ? itemProps.dateProps : {})}
                    {...(['text', 'email', 'password', 'url', 'tel'].includes(itemType) && {
                      type: itemType,
                      datalist: itemProps.datalist,
                    })}
                  />
                </div>

                {canRemove && (
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={() => removeItem(index)}
                    className="mt-2 h-8 w-8 p-0 text-destructive hover:text-destructive"
                    title={removeButtonLabel}
                    disabled={isDisabled}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                )}
              </div>
            ))}

            {value.length === 0 && (
              <div className="text-center py-8 text-muted-foreground border-2 border-dashed rounded-lg">
                <p className="text-sm">No items added yet</p>
                <p className="text-xs mt-1">Click &quot;{addButtonLabel}&quot; to add your first item</p>
              </div>
            )}
          </div>

          {canAddMore && (
            <Button
              type="button"
              variant="outline"
              onClick={addItem}
              className="w-full"
              disabled={isDisabled}
            >
              <Plus className="h-4 w-4 mr-2" />
              {addButtonLabel}
            </Button>
          )}

          {minItems > 0 && value.length < minItems && (
            <p className="text-xs text-muted-foreground">
              Minimum {minItems} item{minItems !== 1 ? 's' : ''} required
            </p>
          )}
        </div>
    </FieldWrapper>
  );
};

"use client";
import React, { useState, useEffect, useRef } from "react";
import type { BaseFieldProps } from "@/lib/formedible/types";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { FieldWrapper } from './base-field-wrapper';

interface AutocompleteOption {
  value: string;
  label: string;
}

interface AutocompleteFieldProps extends BaseFieldProps {
  autocompleteConfig?: {
    options?: string[] | AutocompleteOption[];
    asyncOptions?: (query: string) => Promise<string[] | AutocompleteOption[]>;
    debounceMs?: number;
    minChars?: number;
    maxResults?: number;
    allowCustom?: boolean;
    placeholder?: string;
    noOptionsText?: string;
    loadingText?: string;
  };
}

export const AutocompleteField: React.FC<AutocompleteFieldProps> = ({
  fieldApi,
  placeholder,
  inputClassName,
  autocompleteConfig = {},
  ...wrapperProps
}) => {
  const {
    options = [],
    asyncOptions,
    debounceMs = 300,
    minChars = 1,
    maxResults = 10,
    allowCustom = true,
    noOptionsText = "No options found",
    loadingText = "Loading..."
  } = autocompleteConfig;

  const name = fieldApi.name;

  const [inputValue, setInputValue] = useState(fieldApi.state?.value || "");
  const [filteredOptions, setFilteredOptions] = useState<AutocompleteOption[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);

  const inputRef = useRef<HTMLInputElement>(null);
  const listRef = useRef<HTMLDivElement>(null);
  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Normalize options to consistent format
  const normalizeOptions = (opts: string[] | AutocompleteOption[]): AutocompleteOption[] => {
    return opts.map(opt => 
      typeof opt === 'string' ? { value: opt, label: opt } : opt
    );
  };

  // Filter static options
  const filterStaticOptions = React.useCallback((query: string): AutocompleteOption[] => {
    if (!query || query.length < minChars) return [];

    const normalizedOptions = normalizeOptions(options);
    return normalizedOptions
      .filter(option => 
        option.label.toLowerCase().includes(query.toLowerCase()) ||
        option.value.toLowerCase().includes(query.toLowerCase())
      )
      .slice(0, maxResults);
  }, [minChars, options, maxResults]);

  // Handle async options
  const fetchAsyncOptions = React.useCallback(async (query: string) => {
    if (!asyncOptions || query.length < minChars) return;

    setIsLoading(true);
    try {
      const results = await asyncOptions(query);
      const normalizedResults = normalizeOptions(results);
      setFilteredOptions(normalizedResults.slice(0, maxResults));
    } catch (error) {
      console.error('Autocomplete async options error:', error);
      setFilteredOptions([]);
    } finally {
      setIsLoading(false);
    }
  }, [asyncOptions, minChars, maxResults]);

  // Debounced search
  useEffect(() => {
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
    }

    debounceRef.current = setTimeout(() => {
      if (asyncOptions) {
        fetchAsyncOptions(inputValue);
      } else {
        setFilteredOptions(filterStaticOptions(inputValue));
      }
    }, debounceMs);

    return () => {
      if (debounceRef.current) {
        clearTimeout(debounceRef.current);
      }
    };
  }, [inputValue, asyncOptions, debounceMs, fetchAsyncOptions, filterStaticOptions]);

  // Handle input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setInputValue(value);
    setIsOpen(true);
    setHighlightedIndex(-1);

    if (allowCustom) {
      fieldApi.handleChange(value);
    }
  };

  // Handle option selection
  const handleOptionSelect = (option: AutocompleteOption) => {
    setInputValue(option.label);
    fieldApi.handleChange(option.value);
    setIsOpen(false);
    setHighlightedIndex(-1);
    inputRef.current?.blur();
  };

  // Handle keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!isOpen) {
      if (e.key === 'ArrowDown' || e.key === 'Enter') {
        setIsOpen(true);
        return;
      }
      return;
    }

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setHighlightedIndex(prev => 
          prev < filteredOptions.length - 1 ? prev + 1 : prev
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setHighlightedIndex(prev => prev > 0 ? prev - 1 : -1);
        break;
      case 'Enter':
        e.preventDefault();
        if (highlightedIndex >= 0 && filteredOptions[highlightedIndex]) {
          handleOptionSelect(filteredOptions[highlightedIndex]);
        } else if (allowCustom && inputValue) {
          fieldApi.handleChange(inputValue);
          setIsOpen(false);
        }
        break;
      case 'Escape':
        setIsOpen(false);
        setHighlightedIndex(-1);
        inputRef.current?.blur();
        break;
    }
  };

  // Handle focus/blur
  const handleFocus = () => {
    if (inputValue.length >= minChars) {
      setIsOpen(true);
    }
  };

  const handleInputBlur = () => {
    // Delay closing to allow option clicks
    setTimeout(() => {
      if (!listRef.current?.contains(document.activeElement)) {
        setIsOpen(false);
        setHighlightedIndex(-1);
      }
    }, 150);
  };

  // Scroll highlighted option into view
  useEffect(() => {
    if (highlightedIndex >= 0 && listRef.current) {
      const highlightedElement = listRef.current.children[highlightedIndex] as HTMLElement;
      if (highlightedElement) {
        highlightedElement.scrollIntoView({
          block: 'nearest',
          behavior: 'smooth'
        });
      }
    }
  }, [highlightedIndex]);

  const showDropdown = isOpen && (filteredOptions.length > 0 || isLoading || (inputValue.length >= minChars && !isLoading));

  const isDisabled = fieldApi.form.state.isSubmitting;

  return (
    <FieldWrapper fieldApi={fieldApi} {...wrapperProps}>
      <div className="relative">
        <Input
          ref={inputRef}
          id={fieldApi.name}
          name={fieldApi.name}
          value={inputValue}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          onFocus={handleFocus}
          onBlur={(e) => {
            handleInputBlur();
            fieldApi.handleBlur();
          }}
          placeholder={placeholder || autocompleteConfig.placeholder || "Type to search..."}
          className={cn(inputClassName, isOpen && "rounded-b-none")}
          autoComplete="off"
          disabled={isDisabled}
        />

        {showDropdown && (
          <Card className="absolute top-full left-0 right-0 z-50 max-h-60 overflow-y-auto border-t-0 rounded-t-none">
            <div ref={listRef} className="p-1">
              {isLoading && (
                <div className="px-3 py-2 text-sm text-muted-foreground">
                  {loadingText}
                </div>
              )}

              {!isLoading && filteredOptions.length === 0 && inputValue.length >= minChars && (
                <div className="px-3 py-2 text-sm text-muted-foreground">
                  {noOptionsText}
                  {allowCustom && (
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="ml-2 h-auto p-1 text-xs"
                      onClick={() => {
                        fieldApi.handleChange(inputValue);
                        setIsOpen(false);
                      }}
                      disabled={isDisabled}
                    >
                      Use "{inputValue}"
                    </Button>
                  )}
                </div>
              )}

              {!isLoading && filteredOptions.map((option, index) => (
                <button
                  key={`${option.value}-${index}`}
                  type="button"
                  className={cn(
                    "w-full text-left px-3 py-2 text-sm rounded-sm transition-colors",
                    "hover:bg-muted focus:bg-muted focus:outline-none",
                    highlightedIndex === index && "bg-muted"
                  )}
                  onClick={() => handleOptionSelect(option)}
                  onMouseEnter={() => setHighlightedIndex(index)}
                  disabled={isDisabled}
                >
                  <div className="font-medium">{option.label}</div>
                  {option.value !== option.label && (
                    <div className="text-xs text-muted-foreground">{option.value}</div>
                  )}
                </button>
              ))}
            </div>
          </Card>
        )}
      </div>
    </FieldWrapper>
  );
};

