'use client';
import React, { useState, useRef, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';

import { cn } from '@/lib/utils';
import { X, ChevronDown, Check } from 'lucide-react';
import type { BaseFieldProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';

export interface MultiSelectFieldSpecificProps extends BaseFieldProps {
  options: Array<{ value: string; label: string }> | string[];
  multiSelectConfig?: {
    maxSelections?: number;
    searchable?: boolean;
    creatable?: boolean;
    placeholder?: string;
    noOptionsText?: string;
    loadingText?: string;
  };
}

export const MultiSelectField: React.FC<MultiSelectFieldSpecificProps> = ({
  fieldApi,
  options = [],
  multiSelectConfig = {},

  ...wrapperProps
}) => {
  const {
    maxSelections = Infinity,
    searchable = true,
    creatable = false,
    placeholder = 'Select options...',
    noOptionsText = 'No options found',
  } = multiSelectConfig;

  const name = fieldApi.name;
  const selectedValues = Array.isArray(fieldApi.state?.value) ? (fieldApi.state?.value as string[]) : [];

  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const containerRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const normalizedOptions = options.map(option => 
    typeof option === 'string' 
      ? { value: option, label: option }
      : option
  );

  // Filter options based on search query
  const filteredOptions = normalizedOptions.filter(option =>
    option.label.toLowerCase().includes(searchQuery.toLowerCase()) ||
    option.value.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Add create option if enabled and query doesn't match existing options
  const canCreate = creatable && 
    searchQuery.trim() && 
    !normalizedOptions.some(opt => 
      opt.value.toLowerCase() === searchQuery.toLowerCase() ||
      opt.label.toLowerCase() === searchQuery.toLowerCase()
    ) &&
    !selectedValues.includes(searchQuery.trim());

  const displayOptions = [...filteredOptions];
  if (canCreate) {
    displayOptions.unshift({
      value: searchQuery.trim(),
      label: `Create "${searchQuery.trim()}"`,
      isCreateOption: true
    } as { value: string; label: string; isCreateOption: true });
  }

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
        setIsOpen(false);
        setSearchQuery('');
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleSelect = (optionValue: string) => {
    if (selectedValues.includes(optionValue)) {
      // Remove if already selected
      const newValues = selectedValues.filter(v => v !== optionValue);
      fieldApi.handleChange(newValues);
    } else if (selectedValues.length < maxSelections) {
      // Add if not at max selections
      const newValues = [...selectedValues, optionValue];
      fieldApi.handleChange(newValues);
    }

    setSearchQuery('');
    if (!searchable) {
      setIsOpen(false);
    }
    inputRef.current?.focus();
  };

  const handleRemove = (valueToRemove: string) => {
    const newValues = selectedValues.filter(v => v !== valueToRemove);
    fieldApi.handleChange(newValues);
    fieldApi.handleBlur();
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Backspace' && !searchQuery && selectedValues.length > 0) {
      // Remove last selected item on backspace
      handleRemove(selectedValues[selectedValues.length - 1]);
    } else if (e.key === 'Enter' && canCreate) {
      e.preventDefault();
      handleSelect(searchQuery.trim());
    } else if (e.key === 'Escape') {
      setIsOpen(false);
      setSearchQuery('');
    }
  };

  const getSelectedLabels = () => {
    return selectedValues.map(value => {
      const option = normalizedOptions.find(opt => opt.value === value);
      return option ? option.label : value;
    });
  };

  const isDisabled = fieldApi.form.state.isSubmitting;

  return (
    <FieldWrapper fieldApi={fieldApi} {...wrapperProps}>
      <div className="space-y-2" ref={containerRef}>
        {wrapperProps.label && maxSelections < Infinity && (
          <div className="text-sm text-muted-foreground">
            ({selectedValues.length}/{maxSelections})
          </div>
        )}

        <div className="relative">
          {/* Selected items display */}
          <div
            className={cn(
              "min-h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background",
              "focus-within:ring-2 focus-within:ring-ring focus-within:ring-offset-2",
              fieldApi.state?.meta?.errors.length ? "border-destructive" : "",
              isDisabled ? "opacity-50 cursor-not-allowed" : "cursor-text"
            )}
            onClick={() => {
              if (!isDisabled) {
                setIsOpen(true);
                inputRef.current?.focus();
              }
            }}
          >
          <div className="flex flex-wrap gap-1 items-center">
            {/* Selected tags */}
            {selectedValues.map((value, index) => {
              const label = getSelectedLabels()[index];
              return (
                <Badge
                  key={value}
                  variant="secondary"
                  className="text-xs h-6 px-2 gap-1"
                >
                  {label}
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="h-3 w-3 p-0 hover:bg-destructive hover:text-destructive-foreground"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleRemove(value);
                    }}
                    disabled={isDisabled}
                  >
                    <X className="h-2 w-2" />
                  </Button>
                </Badge>
              );
            })}

            {/* Search input */}
            {searchable && (
              <Input
                ref={inputRef}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyDown={handleKeyDown}
                onFocus={() => setIsOpen(true)}
                onBlur={fieldApi.handleBlur}
                placeholder={selectedValues.length === 0 ? placeholder : ''}
                className="border-0 p-0 h-6 focus-visible:ring-0 focus-visible:ring-offset-0 bg-transparent"
                disabled={isDisabled || selectedValues.length >= maxSelections}
              />
            )}

            {/* Dropdown indicator */}
            <ChevronDown 
              className={cn(
                "h-4 w-4 text-muted-foreground transition-transform ml-auto",
                isOpen ? "rotate-180" : ""
              )} 
            />
          </div>
        </div>

        {/* Dropdown */}
        {isOpen && (
          <div className="absolute z-50 w-full mt-1 bg-popover border rounded-md shadow-lg max-h-60 overflow-y-auto">
            {displayOptions.length === 0 ? (
              <div className="p-2 text-sm text-muted-foreground text-center">
                {noOptionsText}
              </div>
            ) : (
              displayOptions.map((option: { value: string; label: string; isCreateOption?: boolean }, index) => {
                const isSelected = selectedValues.includes(option.value);
                const isDisabled = !isSelected && selectedValues.length >= maxSelections;

                return (
                  <button
                    key={`${option.value}-${index}`}
                    type="button"
                    className={cn(
                      "w-full px-3 py-2 text-left text-sm hover:bg-accent hover:text-accent-foreground",
                      "flex items-center justify-between",
                      isSelected ? "bg-accent" : "",
                      isDisabled ? "opacity-50 cursor-not-allowed" : "",
                      option.isCreateOption ? "font-medium text-primary" : ""
                    )}
                    onClick={() => !isDisabled && handleSelect(option.value)}
                    disabled={isDisabled}
                  >
                    <span>{option.label}</span>
                    {isSelected && <Check className="h-4 w-4" />}
                  </button>
                );
              })
            )}
          </div>
        )}
      </div>

      </div>
    </FieldWrapper>
  );
};

'use client';
import React from 'react';
import { Input } from '@/components/ui/input';
import { cn } from '@/lib/utils';
import type { BaseFieldProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';

export interface NumberFieldSpecificProps extends BaseFieldProps {
  min?: number;
  max?: number;
  step?: number;
}

export const NumberField: React.FC<NumberFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  placeholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
  min,
  max,
  step,
}) => {
  const name = fieldApi.name;
  const value = fieldApi.state?.value as number | string | undefined;
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;
  const hasErrors = fieldApi.state?.meta?.isTouched && fieldApi.state?.meta?.errors?.length > 0;

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = e.target.value;
    let parsedValue: number | string | undefined;

    if (val === '') {
      parsedValue = undefined;
    } else {
      const num = parseFloat(val);
      parsedValue = isNaN(num) ? val : num;
    }

    fieldApi.handleChange(parsedValue);
  };

  const onBlur = () => {
    fieldApi.handleBlur();
  };

  let displayValue: string | number = '';
  if (typeof value === 'number') {
    displayValue = value;
  } else if (typeof value === 'string') {
    displayValue = value;
  }

  const computedInputClassName = cn(
    inputClassName,
    hasErrors ? "border-destructive" : ""
  );

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <Input
        id={name}
        name={name}
        type="number"
        value={displayValue}
        onBlur={onBlur}
        onChange={onChange}
        placeholder={placeholder}
        className={computedInputClassName}
        disabled={isDisabled}
        min={min}
        max={max}
        step={step}
      />
    </FieldWrapper>
  );
};

"use client";
import React from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

import type { BaseFieldProps } from "@/lib/formedible/types";
// Note: Import individual field components directly to avoid circular dependency
import { TextField } from "./text-field";
import { NumberField } from "./number-field";
import { SelectField } from "./select-field";
import { FieldWrapper } from './base-field-wrapper';

interface ObjectFieldConfig {
  title?: string;
  description?: string;
  fields: Array<{
    name: string;
    type: string;
    label?: string;
    placeholder?: string;
    description?: string;
    options?: Array<{ value: string; label: string }>;
    min?: number;
    max?: number;
    step?: number;
    [key: string]: unknown;
  }>;
  collapsible?: boolean;
  defaultExpanded?: boolean;
  showCard?: boolean;
  layout?: "vertical" | "horizontal" | "grid";
  columns?: number;
}

interface ObjectFieldProps extends BaseFieldProps {
  objectConfig?: ObjectFieldConfig;
  disabled?: boolean;
}

export const ObjectField: React.FC<ObjectFieldProps> = ({
  fieldApi,
  objectConfig,
  disabled,
  ...wrapperProps
}) => {
  const name = fieldApi.name;
  const [isExpanded, setIsExpanded] = React.useState(
    objectConfig?.defaultExpanded !== false
  );

  // Create a properly typed mockFieldApi that includes the form property
  const createMockFieldApi = (fieldName: string, fieldValue: unknown) => {
    return {
      name: `${fieldApi.name}.${fieldName}`,
      form: fieldApi.form, // Include the form property to fix the bug
      state: {
        ...fieldApi.state,
        value: fieldValue,
        meta: {
          ...fieldApi.state.meta,
          errors: [], // Reset errors for subfield
          isTouched: false, // Reset touched state for subfield
        }
      },
      handleChange: (value: unknown) => {
        const currentValue = fieldApi.state?.value || {};
        fieldApi.handleChange({
          ...currentValue,
          [fieldName]: value
        });
      },
      handleBlur: fieldApi.handleBlur,
    };
  };

  const renderField = (fieldConfig: ObjectFieldConfig['fields'][0]) => {
    // Map field types to components directly to avoid circular dependency
    const fieldComponentMap = {
      text: TextField,
      number: NumberField,
      select: SelectField,
    };

    const FieldComponent = fieldComponentMap[fieldConfig.type as keyof typeof fieldComponentMap];

    if (!FieldComponent) {
      console.warn(`Object field: Unknown field type "${fieldConfig.type}"`);
      return null;
    }

    const fieldValue = fieldApi.state?.value?.[fieldConfig.name] || '';
    const mockFieldApi = createMockFieldApi(fieldConfig.name, fieldValue) as unknown as BaseFieldProps['fieldApi'];

    // Create properly typed field props using the helper
    const baseProps: BaseFieldProps = {
      fieldApi: mockFieldApi,
      label: fieldConfig.label,
      placeholder: fieldConfig.placeholder,
      description: fieldConfig.description,
    };

    const additionalProps: Record<string, unknown> = {
      ...(fieldConfig.min !== undefined && { min: fieldConfig.min }),
      ...(fieldConfig.max !== undefined && { max: fieldConfig.max }),
      ...(fieldConfig.step !== undefined && { step: fieldConfig.step }),
      ...(disabled !== undefined && { disabled }),
    };

    // Handle fields that require options with proper typing
    if (['select', 'radio', 'multiselect'].includes(fieldConfig.type)) {
      additionalProps.options = fieldConfig.options || [];
    }

    const fieldProps: any = {
      ...baseProps,
      ...additionalProps,
    };

    // Ensure required props are provided for each field type
    if (fieldConfig.type === 'select') {
      fieldProps.options = fieldConfig.options || [];
    }

    return (
      <div key={fieldConfig.name}>
        <FieldComponent {...fieldProps} />
      </div>
    );
  };

  const getLayoutClasses = () => {
    const layout = objectConfig?.layout || "vertical";
    const columns = objectConfig?.columns || 2;

    switch (layout) {
      case "horizontal":
        return "flex flex-wrap gap-4";
      case "grid":
        return `grid grid-cols-1 md:grid-cols-${columns} gap-4`;
      default:
        return "space-y-4";
    }
  };

  const content = (
    <FieldWrapper fieldApi={fieldApi} {...wrapperProps}>
      <div className="space-y-4">
        {/* Object title and description */}
        {(objectConfig?.title || objectConfig?.description) && (
          <div className="space-y-1">
            {objectConfig?.title && (
              <div className="flex items-center justify-between">
                <h4 className="text-sm font-medium text-muted-foreground">
                  {objectConfig.title}
                </h4>
                {objectConfig?.collapsible && (
                  <button
                    type="button"
                    onClick={() => setIsExpanded(!isExpanded)}
                    className="text-xs text-muted-foreground hover:text-foreground"
                  >
                    {isExpanded ? "Collapse" : "Expand"}
                  </button>
                )}
              </div>
            )}
            {objectConfig?.description && (
              <p className="text-xs text-muted-foreground">
                {objectConfig.description}
              </p>
            )}
          </div>
        )}

        {/* Fields */}
        {(!objectConfig?.collapsible || isExpanded) && (
          <>
            {objectConfig?.title && <div className="border-t my-4" />}
            <div className={getLayoutClasses()}>
              {objectConfig?.fields?.map(renderField)}
            </div>
          </>
        )}

        {/* Show field errors */}
        {fieldApi.state?.meta?.errors && fieldApi.state?.meta?.errors.length > 0 && (
          <div className="text-sm text-destructive">
            {fieldApi.state?.meta?.errors.join(", ")}
          </div>
        )}
      </div>
    </FieldWrapper>
  );

  // Wrap in card if specified
  if (objectConfig?.showCard) {
    return (
      <Card className="w-full">
        {(objectConfig?.title || objectConfig?.description) && (
          <CardHeader className="pb-3">
            {objectConfig?.title && (
              <div className="flex items-center justify-between">
                <CardTitle className="text-base">{objectConfig.title}</CardTitle>
                {objectConfig?.collapsible && (
                  <button
                    type="button"
                    onClick={() => setIsExpanded(!isExpanded)}
                    className="text-xs text-muted-foreground hover:text-foreground"
                  >
                    {isExpanded ? "Collapse" : "Expand"}
                  </button>
                )}
              </div>
            )}
            {objectConfig?.description && (
              <p className="text-sm text-muted-foreground mt-1">
                {objectConfig.description}
              </p>
            )}
          </CardHeader>
        )}
        <CardContent className="pt-0">
          {(!objectConfig?.collapsible || isExpanded) && (
            <div className={getLayoutClasses()}>
              {objectConfig?.fields?.map(renderField)}
            </div>
          )}

          {/* Show field errors */}
          {fieldApi.state?.meta?.errors && fieldApi.state?.meta?.errors.length > 0 && (
            <div className="text-sm text-destructive mt-4">
              {fieldApi.state?.meta?.errors.join(", ")}
            </div>
          )}
        </CardContent>
      </Card>
    );
  }

  return content;
};

'use client';
import React, { useState, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { ChevronDown, Phone } from 'lucide-react';
import type { BaseFieldProps } from '@/lib/formedible/types';
import { FieldWrapper } from './base-field-wrapper';

export interface PhoneFieldSpecificProps extends BaseFieldProps {
  phoneConfig?: {
    defaultCountry?: string;
    format?: 'national' | 'international';
    allowedCountries?: string[];
    placeholder?: string;
  };
}

// Common country codes and their formatting
const COUNTRY_CODES = {
  US: { code: '+1', name: 'United States', flag: '', format: '(###) ###-####' },
  CA: { code: '+1', name: 'Canada', flag: '', format: '(###) ###-####' },
  GB: { code: '+44', name: 'United Kingdom', flag: '', format: '#### ### ####' },
  FR: { code: '+33', name: 'France', flag: '', format: '## ## ## ## ##' },
  DE: { code: '+49', name: 'Germany', flag: '', format: '### ### ####' },
  IT: { code: '+39', name: 'Italy', flag: '', format: '### ### ####' },
  ES: { code: '+34', name: 'Spain', flag: '', format: '### ### ###' },
  AU: { code: '+61', name: 'Australia', flag: '', format: '#### ### ###' },
  JP: { code: '+81', name: 'Japan', flag: '', format: '##-####-####' },
  CN: { code: '+86', name: 'China', flag: '', format: '### #### ####' },
  IN: { code: '+91', name: 'India', flag: '', format: '##### #####' },
  BR: { code: '+55', name: 'Brazil', flag: '', format: '(##) #####-####' },
  MX: { code: '+52', name: 'Mexico', flag: '', format: '## #### ####' },
  RU: { code: '+7', name: 'Russia', flag: '', format: '### ###-##-##' },
  KR: { code: '+82', name: 'South Korea', flag: '', format: '##-####-####' },
};

const formatPhoneNumber = (value: string, format: string): string => {
  // Remove all non-digits
  const digits = value.replace(/\D/g, '');

  // Apply format pattern
  let formatted = '';
  let digitIndex = 0;

  for (const char of format) {
    if (char === '#' && digitIndex < digits.length) {
      formatted += digits[digitIndex];
      digitIndex++;
    } else if (char !== '#') {
      formatted += char;
    } else {
      break;
    }
  }

  return formatted;
};

const extractDigits = (value: string): string => {
  return value.replace(/\D/g, '');
};

export const PhoneField: React.FC<PhoneFieldSpecificProps> = ({
  fieldApi,
  label,
  description,
  placeholder: fieldPlaceholder,
  inputClassName,
  labelClassName,
  wrapperClassName,
  phoneConfig = {},
}) => {
  const {
    defaultCountry = 'US',
    format = 'national',
    allowedCountries,
    placeholder,
  } = phoneConfig;

  const name = fieldApi.name;
  const value = (fieldApi.state?.value as string) || '';
  const isDisabled = fieldApi.form?.state?.isSubmitting ?? false;
  const hasErrors = fieldApi.state?.meta?.isTouched && fieldApi.state?.meta?.errors?.length > 0;

  const [selectedCountry, setSelectedCountry] = useState(defaultCountry);
  const [isCountryDropdownOpen, setIsCountryDropdownOpen] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const availableCountries = allowedCountries 
    ? Object.entries(COUNTRY_CODES).filter(([code]) => allowedCountries.includes(code))
    : Object.entries(COUNTRY_CODES);

  const currentCountry = COUNTRY_CODES[selectedCountry as keyof typeof COUNTRY_CODES];

  // Parse existing value on mount
  useEffect(() => {
    if (value) {
      // Try to extract country code and phone number
      const digits = extractDigits(value);

      // Find matching country code
      const matchingCountry = Object.entries(COUNTRY_CODES).find(([_, country]) => {
        const countryDigits = extractDigits(country.code);
        return digits.startsWith(countryDigits);
      });

      if (matchingCountry) {
        const [countryCode, countryData] = matchingCountry;
        setSelectedCountry(countryCode);

        const countryCodeDigits = extractDigits(countryData.code);
        const phoneDigits = digits.slice(countryCodeDigits.length);
        setPhoneNumber(formatPhoneNumber(phoneDigits, countryData.format));
      } else {
        setPhoneNumber(value);
      }
    }
  }, [value]);

  const handlePhoneNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    const digits = extractDigits(inputValue);

    // Format the phone number according to country format
    const formatted = formatPhoneNumber(digits, currentCountry.format);
    setPhoneNumber(formatted);

    // Create the final value based on format preference
    const finalValue = format === 'international' 
      ? `${currentCountry.code} ${formatted}`.trim()
      : formatted;

    fieldApi.handleChange(finalValue);
  };

  const handleCountryChange = (countryCode: string) => {
    setSelectedCountry(countryCode);
    setIsCountryDropdownOpen(false);

    // Update the value with new country code
    const newCountry = COUNTRY_CODES[countryCode as keyof typeof COUNTRY_CODES];
    const digits = extractDigits(phoneNumber);
    const formatted = formatPhoneNumber(digits, newCountry.format);

    const finalValue = format === 'international' 
      ? `${newCountry.code} ${formatted}`.trim()
      : formatted;

    fieldApi.handleChange(finalValue);
  };

  const getPlaceholder = (): string => {
    if (placeholder) return placeholder;

    const exampleNumber = formatPhoneNumber('1234567890', currentCountry.format);
    return format === 'international' 
      ? `${currentCountry.code} ${exampleNumber}`
      : exampleNumber;
  };

  return (
    <FieldWrapper
      fieldApi={fieldApi}
      label={label}
      description={description}
      inputClassName={inputClassName}
      labelClassName={labelClassName}
      wrapperClassName={wrapperClassName}
    >
      <div className="space-y-2">
        <div className="flex">
          {/* Country selector */}
          <div className="relative">
            <Button
              type="button"
              variant="outline"
              className={cn(
                "rounded-r-none border-r-0 px-3 h-10 min-w-[80px]",
                hasErrors ? "border-destructive" : ""
              )}
              onClick={() => setIsCountryDropdownOpen(!isCountryDropdownOpen)}
              disabled={isDisabled}
            >
              <span className="flex items-center gap-2">
                <span className="text-base">{currentCountry.flag}</span>
                {format === 'international' && (
                  <span className="text-xs text-muted-foreground">
                    {currentCountry.code}
                  </span>
                )}
                <ChevronDown className="h-3 w-3" />
              </span>
            </Button>

            {/* Country dropdown */}
            {isCountryDropdownOpen && (
              <div className="absolute z-50 top-full left-0 mt-1 bg-popover border rounded-md shadow-lg max-h-60 overflow-y-auto min-w-[200px]">
                {availableCountries.map(([code, country]) => (
                  <button
                    key={code}
                    type="button"
                    className={cn(
                      "w-full px-3 py-2 text-left text-sm hover:bg-accent hover:text-accent-foreground",
                      "flex items-center gap-3",
                      selectedCountry === code ? "bg-accent" : ""
                    )}
                    onClick={() => handleCountryChange(code)}
                  >
                    <span className="text-base">{country.flag}</span>
                    <div className="flex-1">
                      <div className="font-medium">{country.name}</div>
                      <div className="text-xs text-muted-foreground">{country.code}</div>
                    </div>
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Phone number input */}
          <Input
            value={phoneNumber}
            onChange={handlePhoneNumberChange}
            onBlur={() => fieldApi.handleBlur()}
            placeholder={getPlaceholder()}
            className={cn(
              "rounded-l-none flex-1",
              hasErrors ? "border-destructive" : "",
              inputClassName
            )}
            disabled={isDisabled}
          />
        </div>

        {/* Format hint */}
        <div className="flex items-center gap-2 text-xs text-muted-foreground">
          <Phone className="h-3 w-3" />
          <span>
            Format: {currentCountry.format.replace(/#/g, '0')}
            {format === 'international' && ` (${currentCountry.code})`}
          </span>
        </div>
      </div>
    </FieldWrapper>
  );
};

