          {/* Field breakdown */}
          <div className="space-y-2">
            <h4 className="text-sm font-medium text-muted-foreground">Field Types Used</h4>
            <div className="flex flex-wrap gap-2">
              {Array.from(new Set(config.fields.map(f => f.type))).map(type => {
                const count = config.fields.filter(f => f.type === type).length;
                const icons: Record<string, string> = {
                  text: 'üìù', email: 'üìß', password: 'üîí', textarea: 'üìÑ',
                  number: 'üî¢', select: 'üìã', radio: '‚ö™', multiSelect: '‚òëÔ∏è',
                  checkbox: '‚úÖ', switch: 'üîò', date: 'üìÖ', slider: 'üéöÔ∏è',
                  rating: '‚≠ê', colorPicker: 'üé®', phone: 'üìû', file: 'üìé',
                  array: 'üìö'
                };

                return (
