"use client";
import React, { useState, useCallback } from "react";
import { useFormedible } from "@/hooks/use-formedible";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { cn } from "@/lib/utils";
import {
  Plus,
  Eye,
  Code,
  Settings,
  Download,
  Upload,
  Trash2,
  Copy,
  Move,
  Edit,
  FileText,
} from "lucide-react";
import { z } from "zod";
import { CodeBlock } from "@/components/ui/code-block";
import { FieldConfigurator } from "./field-configurator";
import { FormPreview } from "./form-preview";

// Types for the form builder
interface FormField {
  id: string;
  name: string;
  type: string;
  label: string;
  placeholder?: string;
  description?: string;
  required?: boolean;
  options?: Array<{ value: string; label: string }>;
  validation?: any;
  page?: number;
  group?: string;
  section?: {
    title: string;
    description?: string;
    collapsible?: boolean;
    defaultExpanded?: boolean;
  };
  help?: {
    text?: string;
    tooltip?: string;
    position?: "top" | "bottom" | "left" | "right";
    link?: { url: string; text: string };
  };
  inlineValidation?: {
    enabled?: boolean;
    debounceMs?: number;
    showSuccess?: boolean;
  };
  // Field-specific configs
  arrayConfig?: any;
  datalist?: any;
  multiSelectConfig?: any;
  colorConfig?: any;
  ratingConfig?: any;
  phoneConfig?: any;
}

interface FormConfiguration {
  title: string;
  description?: string;
  fields: FormField[];
  pages: Array<{
    page: number;
    title: string;
    description?: string;
  }>;
  settings: {
    submitLabel: string;
    nextLabel: string;
    previousLabel: string;
    showProgress: boolean;
    allowPageNavigation: boolean;
    resetOnSubmit: boolean;
  };
}

const FIELD_TYPES = [
  { value: "text", label: "Text Input", icon: "📝" },
  { value: "email", label: "Email", icon: "📧" },
  { value: "password", label: "Password", icon: "🔒" },
  { value: "textarea", label: "Textarea", icon: "📄" },
  { value: "number", label: "Number", icon: "🔢" },
  { value: "select", label: "Select", icon: "📋" },
  { value: "radio", label: "Radio Group", icon: "⚪" },
  { value: "multiSelect", label: "Multi-Select", icon: "☑️" },
  { value: "checkbox", label: "Checkbox", icon: "✅" },
  { value: "switch", label: "Switch", icon: "🔘" },
  { value: "date", label: "Date Picker", icon: "📅" },
  { value: "slider", label: "Slider", icon: "🎚️" },
  { value: "rating", label: "Rating", icon: "⭐" },
  { value: "colorPicker", label: "Color Picker", icon: "🎨" },
  { value: "phone", label: "Phone Number", icon: "📞" },
  { value: "file", label: "File Upload", icon: "📎" },
  { value: "array", label: "Array Field", icon: "📚" },
];

export const FormBuilder: React.FC = () => {
  const [formConfig, setFormConfig] = useState<FormConfiguration>({
    title: "My Form",
    description: "A form built with Formedible",
    fields: [],
    pages: [{ page: 1, title: "Page 1", description: "First page" }],
    settings: {
      submitLabel: "Submit",
      nextLabel: "Next",
      previousLabel: "Previous",
      showProgress: true,
      allowPageNavigation: false,
      resetOnSubmit: false,
    },
  });

  const [selectedFieldId, setSelectedFieldId] = useState<string | null>(null);
  const [selectedPageId, setSelectedPageId] = useState<number | null>(null);
  const [editingPageId, setEditingPageId] = useState<number | null>(null);
  const [activeTab, setActiveTab] = useState<string>("builder");
  const [previewMode, setPreviewMode] = useState<
    "desktop" | "tablet" | "mobile"
  >("desktop");

  // Form configuration form using formedible
  const configFormSchema = z.object({
    title: z.string().min(1, "Title is required"),
    description: z.string().optional(),
  });

  const { Form: ConfigForm } = useFormedible({
    schema: configFormSchema,
    fields: [
      {
        name: "title",
        type: "text",
        label: "Form Title",
        placeholder: "Enter form title",
      },
      {
        name: "description",
        type: "textarea",
        label: "Description",
        placeholder: "Enter form description",
      },
    ],
    formOptions: {
      defaultValues: {
        title: formConfig.title,
        description: formConfig.description || "",
      },
      onChange: ({ value }) => {
        setFormConfig(prev => ({
          ...prev,
          title: value.title,
          description: value.description,
        }));
      },
    },
    showSubmitButton: false,
  });

  // Page editing form using formedible
  const pageFormSchema = z.object({
    title: z.string().min(1, "Page title is required"),
    description: z.string().optional(),
  });

  const { Form: PageForm } = useFormedible({
    schema: pageFormSchema,
    fields: [
      {
        name: "title",
        type: "text",
        label: "Page Title",
        placeholder: "Enter page title",
      },
      {
        name: "description",
        type: "textarea",
        label: "Description (optional)",
        placeholder: "Enter page description",
      },
    ],
    formOptions: {
      defaultValues: editingPageId ? {
        title: formConfig.pages.find(p => p.page === editingPageId)?.title || "",
        description: formConfig.pages.find(p => p.page === editingPageId)?.description || "",
      } : { title: "", description: "" },
      onChange: ({ value }) => {
        if (editingPageId) {
          setFormConfig(prev => ({
            ...prev,
            pages: prev.pages.map(p =>
              p.page === editingPageId
                ? { ...p, title: value.title, description: value.description }
                : p
            ),
          }));
        }
      },
    },
    showSubmitButton: false,
  });

  // Add a new field
  const addField = useCallback(
    (type: string) => {
      const newField: FormField = {
        id: `field_${Date.now()}`,
        name: `field_${formConfig.fields.length + 1}`,
        type,
        label: `${
          FIELD_TYPES.find((t) => t.value === type)?.label || type
        } Field`,
        required: false,
        page: selectedPageId || 1,
      };

      setFormConfig((prev) => ({
        ...prev,
        fields: [...prev.fields, newField],
      }));

      setSelectedFieldId(newField.id);
    },
    [formConfig.fields.length, selectedPageId]
  );

  // Update a field
  const updateField = useCallback(
    (fieldId: string, updates: Partial<FormField>) => {
      setFormConfig((prev) => ({
        ...prev,
        fields: prev.fields.map((field) =>
          field.id === fieldId ? { ...field, ...updates } : field
        ),
      }));
    },
    []
  );

  // Delete a field
  const deleteField = useCallback((fieldId: string) => {
    setFormConfig((prev) => ({
      ...prev,
      fields: prev.fields.filter((field) => field.id !== fieldId),
    }));
    setSelectedFieldId(null);
  }, []);

  // Duplicate a field
  const duplicateField = useCallback(
    (fieldId: string) => {
      const field = formConfig.fields.find((f) => f.id === fieldId);
      if (field) {
        const newField: FormField = {
          ...field,
          id: `field_${Date.now()}`,
          name: `${field.name}_copy`,
          label: `${field.label} (Copy)`,
        };
        setFormConfig((prev) => ({
          ...prev,
          fields: [...prev.fields, newField],
        }));
        setSelectedFieldId(newField.id);
      }
    },
    [formConfig.fields]
  );

  // Generate formedible configuration
  const generateFormedibleConfig = useCallback(() => {
    const schemaFields: Record<string, any> = {};

    formConfig.fields.forEach((field) => {
      let fieldSchema: any;

      // Create appropriate schema based on field type
      switch (field.type) {
        case "number":
        case "slider":
        case "rating":
          fieldSchema = z.number();
          break;
        case "checkbox":
        case "switch":
          fieldSchema = z.boolean();
          break;
        case "date":
          fieldSchema = z.string(); // or z.date() if you parse the string
          break;
        case "multiSelect":
        case "array":
          fieldSchema = z.array(z.string());
          break;
        default:
          fieldSchema = z.string();
      }

      if (field.required) {
        if (
          field.type === "number" ||
          field.type === "slider" ||
          field.type === "rating"
        ) {
          // For numbers, required means not null/undefined
          fieldSchema = fieldSchema;
        } else if (field.type === "checkbox" || field.type === "switch") {
          fieldSchema = fieldSchema.refine((val: boolean) => val === true, {
            message: `${field.label} is required`,
          });
        } else if (typeof fieldSchema.min === "function") {
          fieldSchema = fieldSchema.min(1, `${field.label} is required`);
        }
      } else {
        fieldSchema = fieldSchema.optional();
      }

      schemaFields[field.name] = fieldSchema;
    });
    return {
      schema: z.object(schemaFields),
      fields: formConfig.fields.map((field) => ({
        name: field.name,
        type: field.type,
        label: field.label,
        placeholder: field.placeholder,
        description: field.description,
        page: field.page || 1,
        group: field.group,
        section: field.section,
        help: field.help,
        inlineValidation: field.inlineValidation,
        ...(field.options && { options: field.options }),
        ...(field.arrayConfig && { arrayConfig: field.arrayConfig }),
        ...(field.datalist && { datalist: field.datalist }),
        ...(field.multiSelectConfig && {
          multiSelectConfig: field.multiSelectConfig,
        }),
        ...(field.colorConfig && { colorConfig: field.colorConfig }),
        ...(field.ratingConfig && { ratingConfig: field.ratingConfig }),
        ...(field.phoneConfig && { phoneConfig: field.phoneConfig }),
      })),
      pages: formConfig.pages,
      submitLabel: formConfig.settings.submitLabel,
      nextLabel: formConfig.settings.nextLabel,
      previousLabel: formConfig.settings.previousLabel,
      progress: formConfig.settings.showProgress
        ? { showSteps: true, showPercentage: true }
        : undefined,
      formOptions: {
        onSubmit: async ({ value }: any) => {
          console.log("Form submitted:", value);
          alert("Form submitted! Check console for values.");
        },
      },
    };
  }, [formConfig]);

  // Export configuration as JSON
  const exportConfig = useCallback(() => {
    const config = generateFormedibleConfig();
    const blob = new Blob([JSON.stringify(config, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${formConfig.title
      .toLowerCase()
      .replace(/\s+/g, "-")}-form.json`;
    a.click();
    URL.revokeObjectURL(url);
  }, [formConfig, generateFormedibleConfig]);

  // Import configuration from JSON
  const importConfig = useCallback(() => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = ".json";
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (!file) return;

      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const config = JSON.parse(e.target?.result as string);

          // Convert imported config back to form configuration
          const importedFields: FormField[] = (config.fields || []).map(
            (field: any, index: number) => ({
              id: `field_${Date.now()}_${index}`,
              name: field.name,
              type: field.type,
              label: field.label,
              placeholder: field.placeholder,
              description: field.description,
              required: false, // Will be determined by schema
              page: field.page || 1,
              group: field.group,
              section: field.section,
              help: field.help,
              inlineValidation: field.inlineValidation,
              options: field.options,
              arrayConfig: field.arrayConfig,
              datalist: field.datalist,
              multiSelectConfig: field.multiSelectConfig,
              colorConfig: field.colorConfig,
              ratingConfig: field.ratingConfig,
              phoneConfig: field.phoneConfig,
            })
          );

          setFormConfig({
            title: "Imported Form",
            description: "Imported from JSON",
            fields: importedFields,
            pages: config.pages || [
              { page: 1, title: "Page 1", description: "" },
            ],
            settings: {
              submitLabel: config.submitLabel || "Submit",
              nextLabel: config.nextLabel || "Next",
              previousLabel: config.previousLabel || "Previous",
              showProgress: !!config.progress,
              allowPageNavigation: false,
              resetOnSubmit: false,
            },
          });
        } catch (error) {
          alert("Error importing configuration. Please check the file format.");
        }
      };
      reader.readAsText(file);
    };
    input.click();
  }, []);

  // Generate code preview
  const generateCode = useCallback(() => {
    const config = generateFormedibleConfig();
    return `import { useFormedible } from 'formedible';
import { z } from 'zod';

const formConfig = ${JSON.stringify(config, null, 2)};

export const MyForm = () => {
  const { Form } = useFormedible(formConfig);

  return <Form />;
};`;
  }, [generateFormedibleConfig]);

  const selectedField = formConfig.fields.find((f) => f.id === selectedFieldId);

  return (
    <div className="w-full min-h-[800px] flex flex-col bg-background">
      {/* Header */}
      <div className="border-b bg-card">
        <div className="flex h-16 items-center px-6">
          <div className="flex items-center space-x-4">
            <h1 className="text-2xl font-bold">Formedible Builder</h1>
            <div className="text-foreground">
              Build forms with forms! 🚀
            </div>
          </div>
          <div className="ml-auto flex items-center space-x-4">
            <Button variant="secondary" onClick={exportConfig}>
              <Download className="h-4 w-4 mr-2" />
              Export
            </Button>
            <Button variant="secondary" onClick={importConfig}>
              <Upload className="h-4 w-4 mr-2" />
              Import
            </Button>
          </div>
        </div>
      </div>

      <div className="flex-1 flex min-h-0">
        {/* Sidebar - Field Types */}
        <div className="w-72 border-r bg-card overflow-y-auto">
          <div className="p-6">
            <h3 className="font-semibold text-lg mb-6">Field Types</h3>
            <div className="grid grid-cols-1 gap-3">
              {FIELD_TYPES.map((fieldType) => (
                <Button
                  key={fieldType.value}
                  variant="outline"
                  size="default"
                  className="justify-start h-auto p-4"
                  onClick={() => addField(fieldType.value)}
                >
                  <span className="mr-3 text-lg">{fieldType.icon}</span>
                  <span>{fieldType.label}</span>
                </Button>
              ))}
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          <Tabs
            value={activeTab}
            onValueChange={setActiveTab}
            className="flex-1 flex flex-col"
          >
            <TabsList className="w-full justify-start border-b rounded-none h-14 bg-card p-0">
              <TabsTrigger
                value="builder"
                className="flex items-center gap-2 h-full px-6"
              >
                <Settings className="h-4 w-4" />
                Builder
              </TabsTrigger>
              <TabsTrigger
                value="preview"
                className="flex items-center gap-2 h-full px-6"
              >
                <Eye className="h-4 w-4" />
                Preview
              </TabsTrigger>
              <TabsTrigger
                value="code"
                className="flex items-center gap-2 h-full px-6"
              >
                <Code className="h-4 w-4" />
                Code
              </TabsTrigger>
            </TabsList>

            <div className="flex-1 min-h-0">
              <TabsContent value="builder" className="h-full m-0">
                <div className="h-full flex min-h-0">
                  {/* Form Structure */}
                  <div className="flex-1 p-4 overflow-y-auto min-h-0">
                    <div className="mx-auto space-y-4">
                      <Card className="hover:shadow-lg transition-shadow py-3 gap-2">
                        <CardHeader>
                          <CardTitle className="text-xl">Form Configuration</CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-2 p-4">
                          <ConfigForm />
                        </CardContent>
                      </Card>

                      {/* Page Management */}
                      <Card className="hover:shadow-lg transition-shadow gap-2">
                        <CardHeader>
                          <CardTitle className="flex items-center justify-between text-xl">
                            Pages ({formConfig.pages.length})
                            {selectedPageId && (
                              <span className="text-sm font-normal text-primary">
                                Page {selectedPageId} selected
                              </span>
                            )}
                            <Button
                              onClick={() => {
                                const newPageNumber =
                                  Math.max(
                                    ...formConfig.pages.map((p) => p.page)
                                  ) + 1;
                                setFormConfig((prev) => ({
                                  ...prev,
                                  pages: [
                                    ...prev.pages,
                                    {
                                      page: newPageNumber,
                                      title: `Page ${newPageNumber}`,
                                      description: "",
                                    },
                                  ],
                                }));
                                setEditingPageId(newPageNumber);
                              }}
                            >
                              <Plus className="h-4 w-4 mr-2" />
                              Add Page
                            </Button>
                          </CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-2 p-4">
                          <div className="space-y-2">
                            {formConfig.pages.map((page, index) => (
                              <div
                                key={page.page}
                                className={cn(
                                  "border rounded-lg transition-all cursor-pointer",
                                  selectedPageId === page.page
                                    ? "border-primary bg-primary/5 shadow-sm"
                                    : "border-border hover:border-primary/50",
                                  editingPageId === page.page &&
                                    "ring-2 ring-primary/20"
                                )}
                                onClick={() => {
                                  if (editingPageId === page.page) return;
                                  setSelectedPageId(
                                    selectedPageId === page.page
                                      ? null
                                      : page.page
                                  );
                                }}
                              >
                                {editingPageId === page.page ? (
                                  <div className="p-8 space-y-6">
                                    <PageForm />
                                    <div className="flex items-center gap-3">
                                      <Button
                                        onClick={() => setEditingPageId(null)}
                                      >
                                        Done
                                      </Button>
                                      <Button
                                        variant="outline"
                                        onClick={() => setEditingPageId(null)}
                                      >
                                        Cancel
                                      </Button>
                                    </div>
                                  </div>
                                ) : (
                                  <div className="flex items-center justify-between p-8">
                                    <div className="flex items-center space-x-4">
                                      <FileText
                                        className={cn(
                                          "h-5 w-5",
                                          selectedPageId === page.page
                                            ? "text-primary"
                                            : "text-muted-foreground"
                                        )}
                                      />
                                      <div>
                                        <div
                                          className={cn(
                                            "font-medium text-lg",
                                            selectedPageId === page.page &&
                                              "text-primary"
                                          )}
                                        >
                                          {page.title}
                                        </div>
                                        {page.description && (
                                          <div className="text-muted-foreground">
                                            {page.description}
                                          </div>
                                        )}
                                        <div className="text-sm text-muted-foreground">
                                          {
                                            formConfig.fields.filter(
                                              (f) => (f.page || 1) === page.page
                                            ).length
                                          }{" "}
                                          fields
                                        </div>
                                      </div>
                                    </div>
                                    <div className="flex items-center space-x-2">
                                      <Button
                                        variant="ghost"
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          setEditingPageId(page.page);
                                        }}
                                      >
                                        <Edit className="h-4 w-4" />
                                      </Button>
                                      {formConfig.pages.length > 1 && (
                                        <Button
                                          variant="ghost"
                                          onClick={(e) => {
                                            e.stopPropagation();
                                            if (
                                              confirm(
                                                `Delete ${page.title}? Fields on this page will be moved to Page 1.`
                                              )
                                            ) {
                                              setFormConfig((prev) => {
                                                const filteredPages =
                                                  prev.pages.filter(
                                                    (p) => p.page !== page.page
                                                  );
                                                const renumberedPages =
                                                  filteredPages.map(
                                                    (p, index) => ({
                                                      ...p,
                                                      page: index + 1,
                                                    })
                                                  );

                                                const updatedFields =
                                                  prev.fields.map((f) => {
                                                    if (
                                                      (f.page || 1) ===
                                                      page.page
                                                    ) {
                                                      return { ...f, page: 1 };
                                                    }
                                                    if (
                                                      (f.page || 1) > page.page
                                                    ) {
                                                      return {
                                                        ...f,
                                                        page: (f.page || 1) - 1,
                                                      };
                                                    }
                                                    return f;
                                                  });

                                                return {
                                                  ...prev,
                                                  pages: renumberedPages,
                                                  fields: updatedFields,
                                                };
                                              });
                                              if (
                                                selectedPageId === page.page
                                              ) {
                                                setSelectedPageId(null);
                                              }
                                            }
                                          }}
                                        >
                                          <Trash2 className="h-4 w-4" />
                                        </Button>
                                      )}
                                    </div>
                                  </div>
                                )}
                              </div>
                            ))}
                          </div>
                        </CardContent>
                      </Card>

                      {/* Fields List */}
                      <Card className="hover:shadow-lg transition-shadow gap-2">
                        <CardHeader>
                          <CardTitle className="flex items-center justify-between text-xl">
                            {selectedPageId ? (
                              <>
                                {
                                  formConfig.pages.find(
                                    (p) => p.page === selectedPageId
                                  )?.title
                                }{" "}
                                Fields
                                <span className="text-sm font-normal text-muted-foreground">
                                  (
                                  {
                                    formConfig.fields.filter(
                                      (f) => (f.page || 1) === selectedPageId
                                    ).length
                                  }{" "}
                                  fields)
                                </span>
                              </>
                            ) : (
                              <>
                                All Fields ({formConfig.fields.length})
                                {formConfig.fields.length === 0 && (
                                  <span className="text-sm font-normal text-muted-foreground">
                                    Add fields from the sidebar →
                                  </span>
                                )}
                              </>
                            )}
                          </CardTitle>
                          {selectedPageId && (
                            <p className="text-muted-foreground">
                              New fields will be added to this page. Click the
                              page again to deselect.
                            </p>
                          )}
                        </CardHeader>
                        <CardContent className="space-y-2 p-4">
                          {(() => {
                            const fieldsToShow = selectedPageId
                              ? formConfig.fields.filter(
                                  (f) => (f.page || 1) === selectedPageId
                                )
                              : formConfig.fields;

                            if (fieldsToShow.length === 0) {
                              return (
                                <div className="text-center py-12 text-muted-foreground">
                                  <Plus className="h-12 w-12 mx-auto mb-4 opacity-50" />
                                  <p className="text-lg">
                                    {selectedPageId
                                      ? `No fields on this page yet. Add some from the sidebar!`
                                      : `No fields yet. Add some from the sidebar!`}
                                  </p>
                                </div>
                              );
                            }

                            if (selectedPageId) {
                              // Show only selected page fields in a simple list
                              return (
                                <div className="space-y-4">
                                  {fieldsToShow.map((field, index) => (
                                    <div
                                      key={field.id}
                                      className={cn(
                                        "flex items-center justify-between p-6 border rounded-lg cursor-pointer transition-colors hover:shadow-md",
                                        selectedFieldId === field.id
                                          ? "border-primary bg-primary/5"
                                          : "border-border hover:border-primary/50"
                                      )}
                                      onClick={() =>
                                        setSelectedFieldId(field.id)
                                      }
                                    >
                                      <div className="flex items-center space-x-4">
                                        <span className="text-2xl">
                                          {FIELD_TYPES.find(
                                            (t) => t.value === field.type
                                          )?.icon || "📝"}
                                        </span>
                                        <div>
                                          <div className="font-medium text-lg">
                                            {field.label}
                                          </div>
                                          <div className="text-muted-foreground">
                                            {field.type} • {field.name}
                                          </div>
                                        </div>
                                      </div>
                                      <div className="flex items-center space-x-2">
                                        <Button
                                          variant="ghost"
                                          onClick={(e) => {
                                            e.stopPropagation();
                                            duplicateField(field.id);
                                          }}
                                        >
                                          <Copy className="h-4 w-4" />
                                        </Button>
                                        <Button
                                          variant="ghost"
                                          onClick={(e) => {
                                            e.stopPropagation();
                                            deleteField(field.id);
                                          }}
                                        >
                                          <Trash2 className="h-4 w-4" />
                                        </Button>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              );
                            } else {
                              // Show all fields grouped by page
                              return (
                                <div className="space-y-8">
                                  {formConfig.pages.map((page) => {
                                    const pageFields = formConfig.fields.filter(
                                      (f) => (f.page || 1) === page.page
                                    );
                                    return (
                                      <div key={page.page}>
                                        <div className="flex items-center gap-3 mb-4">
                                          <FileText className="h-5 w-5 text-muted-foreground" />
                                          <span className="font-medium text-lg">
                                            {page.title}
                                          </span>
                                          <span className="text-sm text-muted-foreground">
                                            ({pageFields.length} fields)
                                          </span>
                                        </div>
                                        <div className="space-y-4 ml-8">
                                          {pageFields.length === 0 ? (
                                            <div className="text-muted-foreground italic">
                                              No fields on this page
                                            </div>
                                          ) : (
                                            pageFields.map((field, index) => (
                                              <div
                                                key={field.id}
                                                className={cn(
                                                  "flex items-center justify-between p-6 border rounded-lg cursor-pointer transition-colors hover:shadow-md",
                                                  selectedFieldId === field.id
                                                    ? "border-primary bg-primary/5"
                                                    : "border-border hover:border-primary/50"
                                                )}
                                                onClick={() =>
                                                  setSelectedFieldId(field.id)
                                                }
                                              >
                                                <div className="flex items-center space-x-4">
                                                  <span className="text-2xl">
                                                    {FIELD_TYPES.find(
                                                      (t) =>
                                                        t.value === field.type
                                                    )?.icon || "📝"}
                                                  </span>
                                                  <div>
                                                    <div className="font-medium text-lg">
                                                      {field.label}
                                                    </div>
                                                    <div className="text-muted-foreground">
                                                      {field.type} •{" "}
                                                      {field.name}
                                                    </div>
                                                  </div>
                                                </div>
                                                <div className="flex items-center space-x-2">
                                                  <Button
                                                    variant="ghost"
                                                    onClick={(e) => {
                                                      e.stopPropagation();
                                                      duplicateField(field.id);
                                                    }}
                                                  >
                                                    <Copy className="h-4 w-4" />
                                                  </Button>
                                                  <Button
                                                    variant="ghost"
                                                    onClick={(e) => {
                                                      e.stopPropagation();
                                                      deleteField(field.id);
                                                    }}
                                                  >
                                                    <Trash2 className="h-4 w-4" />
                                                  </Button>
                                                </div>
                                              </div>
                                            ))
                                          )}
                                        </div>
                                      </div>
                                    );
                                  })}
                                </div>
                              );
                            }
                          })()}
                        </CardContent>
                      </Card>
                    </div>
                  </div>

                  {/* Field Configuration Panel */}
                  {selectedField && (
                    <div className="w-96 border-l bg-card overflow-y-auto min-h-0">
                      <div className="p-6">
                        <h3 className="font-semibold text-lg mb-6">Configure Field</h3>
                        <FieldConfigurator
                          field={selectedField}
                          onUpdate={updateField}
                          availablePages={formConfig.pages.map((p) => p.page)}
                        />
                      </div>
                    </div>
                  )}
                </div>
              </TabsContent>

              <TabsContent
                value="preview"
                className="h-full m-0 p-8 overflow-y-auto min-h-0"
              >
                <div className="max-w-6xl mx-auto">
                  <div className="mb-8 flex items-center justify-between">
                    <h2 className="text-2xl font-bold">Live Preview</h2>
                    <div className="flex items-center space-x-3">
                      {(["desktop", "tablet", "mobile"] as const).map(
                        (mode) => (
                          <Button
                            key={mode}
                            variant={
                              previewMode === mode ? "default" : "outline"
                            }
                            onClick={() => setPreviewMode(mode)}
                          >
                            {mode === "desktop" && "🖥️"}
                            {mode === "tablet" && "📱"}
                            {mode === "mobile" && "📱"}
                            <span className="ml-2 capitalize">{mode}</span>
                          </Button>
                        )
                      )}
                    </div>
                  </div>
                  <div
                    className={cn(
                      "mx-auto transition-all",
                      previewMode === "mobile"
                        ? "max-w-sm"
                        : previewMode === "tablet"
                        ? "max-w-md"
                        : "max-w-4xl"
                    )}
                  >
                    <FormPreview config={formConfig} />
                  </div>
                </div>
              </TabsContent>

              <TabsContent
                value="code"
                className="h-full m-0 p-8 overflow-y-auto min-h-0"
              >
                <div className="max-w-6xl mx-auto">
                  <div className="mb-8">
                    <h2 className="text-2xl font-bold">Generated Code</h2>
                    <p className="text-muted-foreground text-lg">
                      Copy this code to use your form in your application
                    </p>
                  </div>
                  <CodeBlock
                    code={generateCode()}
                    language="tsx"
                    title="MyForm.tsx"
                    showCopyButton={true}
                    showLineNumbers={true}
                  />
                </div>
              </TabsContent>
            </div>
          </Tabs>
        </div>
      </div>
    </div>
  );
};

'use client';
import React, { useCallback, useMemo } from 'react';
import { useFormedible } from '@/hooks/use-formedible';
import { z } from 'zod';

interface FormField {
  id: string;
  name: string;
  type: string;
  label: string;
  placeholder?: string;
  description?: string;
  required?: boolean;
  options?: Array<{ value: string; label: string }>;
  validation?: any;
  page?: number;
  group?: string;
  section?: {
    title: string;
    description?: string;
    collapsible?: boolean;
    defaultExpanded?: boolean;
  };
  help?: {
    text?: string;
    tooltip?: string;
    position?: 'top' | 'bottom' | 'left' | 'right';
    link?: { url: string; text: string };
  };
  inlineValidation?: {
    enabled?: boolean;
    debounceMs?: number;
    showSuccess?: boolean;
  };
  arrayConfig?: any;
  datalist?: any;
  multiSelectConfig?: any;
  colorConfig?: any;
  ratingConfig?: any;
  phoneConfig?: any;
}

interface FieldConfiguratorProps {
  field: FormField;
  onUpdate: (fieldId: string, updates: Partial<FormField>) => void;
  availablePages: number[];
}

export const FieldConfigurator: React.FC<FieldConfiguratorProps> = ({
  field,
  onUpdate,
  availablePages,
}) => {
  const updateField = useCallback((updates: Partial<FormField>) => {
    onUpdate(field.id, updates);
  }, [field.id, onUpdate]);

  // Base configuration fields
  const baseFields = useMemo(() => [
    {
      name: 'name',
      type: 'text',
      label: 'Field Name',
      placeholder: 'e.g., firstName, email',
      validation: z.string().min(1, 'Field name is required'),
      help: {
        text: 'Unique identifier for this field',
        tooltip: 'Used as the key in form data'
      }
    },
    {
      name: 'label',
      type: 'text',
      label: 'Label',
      placeholder: 'e.g., First Name, Email Address',
      validation: z.string().min(1, 'Label is required'),
    },
    {
      name: 'placeholder',
      type: 'text',
      label: 'Placeholder',
      placeholder: 'e.g., Enter your name...',
    },
    {
      name: 'description',
      type: 'textarea',
      label: 'Description',
      placeholder: 'Optional description or help text',
    },
    {
      name: 'required',
      type: 'switch',
      label: 'Required Field',
      description: 'Make this field mandatory',
    },
    {
      name: 'page',
      type: 'select',
      label: 'Page',
      options: availablePages.map(p => ({ value: p.toString(), label: `Page ${p}` })),
      description: 'Which page should this field appear on?',
    },
  ], [availablePages]);

  // Advanced configuration fields
  const advancedFields = useMemo(() => [
    {
      name: 'group',
      type: 'text',
      label: 'Group',
      placeholder: 'e.g., personal, contact, preferences',
      description: 'Group related fields together',
      help: {
        text: 'Fields with the same group will be visually grouped',
        tooltip: 'Use groups to organize related fields'
      }
    },
    {
      name: 'section.title',
      type: 'text',
      label: 'Section Title',
      placeholder: 'e.g., Personal Information',
      description: 'Create a new section with this title',
    },
    {
      name: 'section.description',
      type: 'text',
      label: 'Section Description',
      placeholder: 'Brief description of this section',
    },
    {
      name: 'section.collapsible',
      type: 'switch',
      label: 'Collapsible Section',
      description: 'Allow users to collapse/expand this section',
    },
    {
      name: 'section.defaultExpanded',
      type: 'switch',
      label: 'Default Expanded',
      description: 'Section starts expanded',
      conditional: (values: any) => values['section.collapsible'],
    },
  ], []);

  // Help & validation fields
  const helpFields = useMemo(() => [
    {
      name: 'help.text',
      type: 'text',
      label: 'Help Text',
      placeholder: 'Additional help information',
      description: 'Shown below the field',
    },
    {
      name: 'help.tooltip',
      type: 'text',
      label: 'Tooltip',
      placeholder: 'Tooltip text on hover',
      description: 'Shows on hover/focus',
    },
    {
      name: 'help.position',
      type: 'select',
      label: 'Tooltip Position',
      options: [
        { value: 'top', label: 'Top' },
        { value: 'bottom', label: 'Bottom' },
        { value: 'left', label: 'Left' },
        { value: 'right', label: 'Right' },
      ],
      conditional: (values: any) => values['help.tooltip'],
    },
    {
      name: 'help.link.text',
      type: 'text',
      label: 'Help Link Text',
      placeholder: 'e.g., Learn more',
    },
    {
      name: 'help.link.url',
      type: 'text',
      label: 'Help Link URL',
      placeholder: 'https://example.com/help',
      conditional: (values: any) => values['help.link.text'],
    },
    {
      name: 'inlineValidation.enabled',
      type: 'switch',
      label: 'Enable Inline Validation',
      description: 'Show validation feedback as user types',
    },
    {
      name: 'inlineValidation.showSuccess',
      type: 'switch',
      label: 'Show Success State',
      description: 'Show checkmark when valid',
      conditional: (values: any) => values['inlineValidation.enabled'],
    },
    {
      name: 'inlineValidation.debounceMs',
      type: 'number',
      label: 'Validation Delay (ms)',
      placeholder: '300',
      description: 'Delay before validating',
      conditional: (values: any) => values['inlineValidation.enabled'],
    },
  ], []);

  // Field-specific configuration
  const getFieldSpecificConfig = useCallback(() => {
    switch (field.type) {
      case 'select':
      case 'radio':
        return [
          {
            name: 'options',
            type: 'array',
            label: 'Options',
            arrayConfig: {
              itemType: 'text',
              itemLabel: 'Option',
              itemPlaceholder: 'Option value',
              minItems: 1,
              addButtonLabel: 'Add Option',
              removeButtonLabel: 'Remove',
            },
            validation: z.array(z.string()).min(1, 'At least one option is required'),
          },
        ];

      case 'multiSelect':
        return [
          {
            name: 'options',
            type: 'array',
            label: 'Options',
            arrayConfig: {
              itemType: 'text',
              itemLabel: 'Option',
              minItems: 1,
              addButtonLabel: 'Add Option',
            },
            validation: z.array(z.string()).min(1, 'At least one option is required'),
          },
          {
            name: 'multiSelectConfig.maxSelections',
            type: 'number',
            label: 'Max Selections',
            placeholder: '5',
            description: 'Maximum number of selections allowed',
          },
          {
            name: 'multiSelectConfig.searchable',
            type: 'switch',
            label: 'Searchable',
            description: 'Allow users to search options',
          },
          {
            name: 'multiSelectConfig.creatable',
            type: 'switch',
            label: 'Allow Creating New Options',
            description: 'Users can add custom options',
          },
        ];

      case 'rating':
        return [
          {
            name: 'ratingConfig.max',
            type: 'number',
            label: 'Maximum Rating',
            placeholder: '5',
            description: 'Highest rating value',
          },
          {
            name: 'ratingConfig.allowHalf',
            type: 'switch',
            label: 'Allow Half Ratings',
            description: 'Enable half-star ratings',
          },
          {
            name: 'ratingConfig.icon',
            type: 'select',
            label: 'Icon Type',
            options: [
              { value: 'star', label: 'Star' },
              { value: 'heart', label: 'Heart' },
              { value: 'thumbs', label: 'Thumbs Up' },
            ],
          },
          {
            name: 'ratingConfig.size',
            type: 'select',
            label: 'Icon Size',
            options: [
              { value: 'sm', label: 'Small' },
              { value: 'md', label: 'Medium' },
              { value: 'lg', label: 'Large' },
            ],
          },
          {
            name: 'ratingConfig.showValue',
            type: 'switch',
            label: 'Show Numeric Value',
            description: 'Display the rating number',
          },
        ];

      case 'colorPicker':
        return [
          {
            name: 'colorConfig.format',
            type: 'select',
            label: 'Color Format',
            options: [
              { value: 'hex', label: 'HEX (#FF0000)' },
              { value: 'rgb', label: 'RGB (rgb(255, 0, 0))' },
              { value: 'hsl', label: 'HSL (hsl(0, 100%, 50%))' },
            ],
          },
          {
            name: 'colorConfig.showPreview',
            type: 'switch',
            label: 'Show Color Preview',
            description: 'Display selected color preview',
          },
          {
            name: 'colorConfig.allowCustom',
            type: 'switch',
            label: 'Allow Custom Colors',
            description: 'Users can enter custom color values',
          },
          {
            name: 'colorConfig.presetColors',
            type: 'array',
            label: 'Preset Colors',
            arrayConfig: {
              itemType: 'text',
              itemLabel: 'Color',
              itemPlaceholder: '#FF0000',
              addButtonLabel: 'Add Color',
            },
            description: 'Predefined color options',
          },
        ];

      case 'phone':
        return [
          {
            name: 'phoneConfig.defaultCountry',
            type: 'select',
            label: 'Default Country',
            options: [
              { value: 'US', label: 'United States' },
              { value: 'CA', label: 'Canada' },
              { value: 'GB', label: 'United Kingdom' },
              { value: 'FR', label: 'France' },
              { value: 'DE', label: 'Germany' },
              { value: 'AU', label: 'Australia' },
              { value: 'JP', label: 'Japan' },
            ],
          },
          {
            name: 'phoneConfig.format',
            type: 'select',
            label: 'Phone Format',
            options: [
              { value: 'national', label: 'National ((555) 123-4567)' },
              { value: 'international', label: 'International (+1 555 123 4567)' },
            ],
          },
        ];

      case 'array':
        return [
          {
            name: 'arrayConfig.itemType',
            type: 'select',
            label: 'Item Type',
            options: [
              { value: 'text', label: 'Text' },
              { value: 'email', label: 'Email' },
              { value: 'number', label: 'Number' },
              { value: 'phone', label: 'Phone' },
              { value: 'url', label: 'URL' },
            ],
          },
          {
            name: 'arrayConfig.itemLabel',
            type: 'text',
            label: 'Item Label',
            placeholder: 'e.g., Email Address',
          },
          {
            name: 'arrayConfig.minItems',
            type: 'number',
            label: 'Minimum Items',
            placeholder: '0',
          },
          {
            name: 'arrayConfig.maxItems',
            type: 'number',
            label: 'Maximum Items',
            placeholder: '10',
          },
          {
            name: 'arrayConfig.addButtonLabel',
            type: 'text',
            label: 'Add Button Text',
            placeholder: 'Add Item',
          },
        ];

      case 'text':
      case 'email':
      case 'url':
        return [
          {
            name: 'datalist.options',
            type: 'array',
            label: 'Autocomplete Options',
            arrayConfig: {
              itemType: 'text',
              itemLabel: 'Option',
              addButtonLabel: 'Add Option',
            },
            description: 'Static autocomplete suggestions',
          },
        ];

      case 'number':
      case 'slider':
        return [
          {
            name: 'min',
            type: 'number',
            label: 'Minimum Value',
            placeholder: '0',
          },
          {
            name: 'max',
            type: 'number',
            label: 'Maximum Value',
            placeholder: '100',
          },
          {
            name: 'step',
            type: 'number',
            label: 'Step',
            placeholder: '1',
            description: 'Increment/decrement step',
          },
        ];

      default:
        return [];
    }
  }, [field.type]);

  // Convert nested object paths to flat form values
  const getFormValues = useCallback(() => {
    const values: any = {
      name: field.name,
      label: field.label,
      placeholder: field.placeholder || '',
      description: field.description || '',
      required: field.required || false,
      page: field.page?.toString() || '1',
      group: field.group || '',
    };

    // Handle nested objects
    if (field.section) {
      values['section.title'] = field.section.title || '';
      values['section.description'] = field.section.description || '';
      values['section.collapsible'] = field.section.collapsible || false;
      values['section.defaultExpanded'] = field.section.defaultExpanded || false;
    }

    if (field.help) {
      values['help.text'] = field.help.text || '';
      values['help.tooltip'] = field.help.tooltip || '';
      values['help.position'] = field.help.position || 'top';
      if (field.help.link) {
        values['help.link.text'] = field.help.link.text || '';
        values['help.link.url'] = field.help.link.url || '';
      }
    }

    if (field.inlineValidation) {
      values['inlineValidation.enabled'] = field.inlineValidation.enabled || false;
      values['inlineValidation.showSuccess'] = field.inlineValidation.showSuccess || false;
      values['inlineValidation.debounceMs'] = field.inlineValidation.debounceMs || 300;
    }

    // Handle field-specific configs
    if (field.options) {
      values.options = field.options.map(opt => typeof opt === 'string' ? opt : opt.value);
    }

    // Add field-specific config values
    const configs = ['arrayConfig', 'multiSelectConfig', 'colorConfig', 'ratingConfig', 'phoneConfig', 'datalist'];
    configs.forEach(configKey => {
      const config = (field as any)[configKey];
      if (config) {
        Object.keys(config).forEach(key => {
          values[`${configKey}.${key}`] = config[key];
        });
      }
    });

    return values;
  }, [field]);

  // Convert flat form values back to nested object
  const handleFormChange = useCallback(({ value }: any) => {
    const updates: Partial<FormField> = {
      name: value.name,
      label: value.label,
      placeholder: value.placeholder || undefined,
      description: value.description || undefined,
      required: value.required,
      page: parseInt(value.page) || 1,
      group: value.group || undefined,
    };

    // Handle section
    if (value['section.title']) {
      updates.section = {
        title: value['section.title'],
        description: value['section.description'] || undefined,
        collapsible: value['section.collapsible'] || false,
        defaultExpanded: value['section.defaultExpanded'] || false,
      };
    }

    // Handle help
    if (value['help.text'] || value['help.tooltip'] || value['help.link.text']) {
      updates.help = {
        text: value['help.text'] || undefined,
        tooltip: value['help.tooltip'] || undefined,
        position: value['help.position'] || 'top',
        link: value['help.link.text'] ? {
          text: value['help.link.text'],
          url: value['help.link.url'] || '',
        } : undefined,
      };
    }

    // Handle inline validation
    if (value['inlineValidation.enabled']) {
      updates.inlineValidation = {
        enabled: value['inlineValidation.enabled'],
        showSuccess: value['inlineValidation.showSuccess'],
        debounceMs: value['inlineValidation.debounceMs'] || 300,
      };
    }

    // Handle options
    if (value.options) {
      updates.options = value.options.map((opt: string) => ({ value: opt, label: opt }));
    }

    // Handle field-specific configs
    const configKeys = ['arrayConfig', 'multiSelectConfig', 'colorConfig', 'ratingConfig', 'phoneConfig', 'datalist'];
    configKeys.forEach(configKey => {
      const configValues: any = {};
      let hasValues = false;

      Object.keys(value).forEach(key => {
        if (key.startsWith(`${configKey}.`)) {
          const subKey = key.replace(`${configKey}.`, '');
          configValues[subKey] = value[key];
          hasValues = true;
        }
      });

      if (hasValues) {
        (updates as any)[configKey] = configValues;
      }
    });

    updateField(updates);
  }, [updateField]);

  const allFields = [
    ...baseFields,
    ...getFieldSpecificConfig(),
    ...advancedFields,
    ...helpFields,
  ];

  const { Form } = useFormedible({
    fields: allFields,
    formOptions: {
      defaultValues: getFormValues(),
      onChange: handleFormChange,
    },
  });

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-2 pb-4 border-b">
        <div className="text-lg">
          {field.type === 'text' && '📝'}
          {field.type === 'email' && '📧'}
          {field.type === 'password' && '🔒'}
          {field.type === 'textarea' && '📄'}
          {field.type === 'number' && '🔢'}
          {field.type === 'select' && '📋'}
          {field.type === 'radio' && '⚪'}
          {field.type === 'multiSelect' && '☑️'}
          {field.type === 'checkbox' && '✅'}
          {field.type === 'switch' && '🔘'}
          {field.type === 'date' && '📅'}
          {field.type === 'slider' && '🎚️'}
          {field.type === 'rating' && '⭐'}
          {field.type === 'colorPicker' && '🎨'}
          {field.type === 'phone' && '📞'}
          {field.type === 'file' && '📎'}
          {field.type === 'array' && '📚'}
        </div>
        <div>
          <div className="font-medium">{field.label}</div>
          <div className="text-sm text-muted-foreground">{field.type}</div>
        </div>
      </div>

      <Form />
    </div>
  );
};

'use client';
import React, { useMemo } from 'react';
import { useFormedible } from 'formedible';
import { z } from 'zod';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { AlertCircle } from 'lucide-react';
import { cn } from '@/lib/utils';

interface FormField {
  id: string;
  name: string;
  type: string;
  label: string;
  placeholder?: string;
  description?: string;
  required?: boolean;
  options?: Array<{ value: string; label: string }>;
  validation?: any;
  page?: number;
  group?: string;
  section?: {
    title: string;
    description?: string;
    collapsible?: boolean;
    defaultExpanded?: boolean;
  };
  help?: {
    text?: string;
    tooltip?: string;
    position?: 'top' | 'bottom' | 'left' | 'right';
    link?: { url: string; text: string };
  };
  inlineValidation?: {
    enabled?: boolean;
    debounceMs?: number;
    showSuccess?: boolean;
  };
  arrayConfig?: any;
  datalist?: any;
  multiSelectConfig?: any;
  colorConfig?: any;
  ratingConfig?: any;
  phoneConfig?: any;
}

interface FormConfiguration {
  title: string;
  description?: string;
  fields: FormField[];
  pages: Array<{
    page: number;
    title: string;
    description?: string;
  }>;
  settings: {
    submitLabel: string;
    nextLabel: string;
    previousLabel: string;
    showProgress: boolean;
    allowPageNavigation: boolean;
    resetOnSubmit: boolean;
  };
}

interface FormPreviewProps {
  config: FormConfiguration;
  className?: string;
}

export const FormPreview: React.FC<FormPreviewProps> = ({
  config,
  className,
}) => {
  // Convert form configuration to formedible configuration
  const formedibleConfig = useMemo(() => {
    if (config.fields.length === 0) {
      return {
        fields: [],
        schema: z.object({})
      };
    }

    try {
      return {
        fields: config.fields.map(field => {
          const fieldConfig: any = {
            name: field.name,
            type: field.type,
            label: field.label,
            placeholder: field.placeholder,
            description: field.description,
            page: field.page || 1,
            group: field.group,
            section: field.section,
            help: field.help,
            inlineValidation: field.inlineValidation,
          };

          // Add validation if field is required
          if (field.required) {
            switch (field.type) {
              case 'text':
              case 'email':
              case 'password':
              case 'textarea':
                fieldConfig.validation = z.string().min(1, `${field.label} is required`);
                break;
              case 'number':
              case 'slider':
              case 'rating':
                fieldConfig.validation = z.number().min(0, `${field.label} is required`);
                break;
              case 'select':
              case 'radio':
                fieldConfig.validation = z.string().min(1, `Please select ${field.label}`);
                break;
              case 'multiSelect':
                fieldConfig.validation = z.array(z.string()).min(1, `Please select at least one ${field.label}`);
                break;
              case 'checkbox':
              case 'switch':
                fieldConfig.validation = z.boolean().refine(val => val === true, `${field.label} is required`);
                break;
              case 'date':
                fieldConfig.validation = z.date();
                break;
              case 'array':
                fieldConfig.validation = z.array(z.string()).min(1, `At least one ${field.label} is required`);
                break;
              case 'colorPicker':
                fieldConfig.validation = z.string().regex(/^#[0-9A-Fa-f]{6}$/, 'Please select a color');
                break;
              case 'phone':
                fieldConfig.validation = z.string().min(10, 'Please enter a valid phone number');
                break;
              case 'file':
                fieldConfig.validation = z.any().refine(val => val && val.length > 0, `${field.label} is required`);
                break;
            }
          }

          // Add options for select/radio/multiSelect fields
          if (['select', 'radio', 'multiSelect'].includes(field.type) && field.options) {
            fieldConfig.options = field.options;
          }

          // Add field-specific configurations
          if (field.arrayConfig) fieldConfig.arrayConfig = field.arrayConfig;
          if (field.datalist) fieldConfig.datalist = field.datalist;
          if (field.multiSelectConfig) fieldConfig.multiSelectConfig = field.multiSelectConfig;
          if (field.colorConfig) fieldConfig.colorConfig = field.colorConfig;
          if (field.ratingConfig) fieldConfig.ratingConfig = field.ratingConfig;
          if (field.phoneConfig) fieldConfig.phoneConfig = field.phoneConfig;

          return fieldConfig;
        }),
        pages: config.pages.length > 1 ? config.pages : undefined,
        submitLabel: config.settings.submitLabel,
        nextLabel: config.settings.nextLabel,
        previousLabel: config.settings.previousLabel,
        progress: config.settings.showProgress ? { 
          showSteps: true, 
          showPercentage: true 
        } : undefined,
        formOptions: {
          onSubmit: async ({ value }: any) => {
            console.log('Preview form submitted:', value);
            alert('Form submitted successfully! Check the console for form data.');
          },
        },
      };
    } catch (error) {
      console.error('Error converting form configuration:', error);
      return {
        fields: [],
        schema: z.object({})
      };
    }
  }, [config]);

  // Create the form using formedible - call hook at top level
  const formResult = useFormedible(formedibleConfig);

  // Handle configuration errors or empty fields
  if (!formResult || !formResult.Form || config.fields.length === 0) {
    if (config.fields.length === 0) {
      return (
        <Card className={cn("bg-muted/30", className)}>
          <CardContent className="flex flex-col items-center justify-center py-12 text-center">
            <div className="text-4xl mb-4">📝</div>
            <h3 className="text-lg font-medium mb-2">No Fields Added</h3>
            <p className="text-muted-foreground">
              Add some fields from the sidebar to see your form preview
            </p>
          </CardContent>
        </Card>
      );
    }
    return (
      <Card className={cn("bg-muted/30", className)}>
        <CardContent className="py-6">
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              There was an error generating the form preview. Please check your field configurations.
            </AlertDescription>
          </Alert>
        </CardContent>
      </Card>
    );
  }

  const { Form } = formResult;

  return (
    <Card className={cn("bg-muted/30", className)}>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div>
            <div>{config.title}</div>
            {config.description && (
              <div className="text-sm font-normal text-muted-foreground mt-1">
                {config.description}
              </div>
            )}
          </div>
          <div className="text-sm text-muted-foreground">
            {config.fields.length} field{config.fields.length !== 1 ? 's' : ''}
            {config.pages.length > 1 && ` • ${config.pages.length} pages`}
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {/* Form stats */}
          <div className="grid grid-cols-3 gap-4 p-4 bg-muted/50 border rounded-lg">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{config.fields.length}</div>
              <div className="text-xs text-muted-foreground">Fields</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{config.pages.length}</div>
              <div className="text-xs text-muted-foreground">Pages</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">
                {config.fields.filter(f => f.required).length}
              </div>
              <div className="text-xs text-muted-foreground">Required</div>
            </div>
          </div>

          {/* Live form preview */}
          <div className="border-2 border-dashed border-muted-foreground/20 rounded-lg p-6 bg-background">
            <div className="text-xs text-muted-foreground mb-4 text-center">
              ✨ Live Preview - This form is fully functional!
            </div>
            <Form />
          </div>

          {/* Field breakdown */}
          <div className="space-y-2">
            <h4 className="text-sm font-medium text-muted-foreground">Field Types Used</h4>
            <div className="flex flex-wrap gap-2">
              {Array.from(new Set(config.fields.map(f => f.type))).map(type => {
                const count = config.fields.filter(f => f.type === type).length;
                const icons: Record<string, string> = {
                  text: '📝', email: '📧', password: '🔒', textarea: '📄',
                  number: '🔢', select: '📋', radio: '⚪', multiSelect: '☑️',
                  checkbox: '✅', switch: '🔘', date: '📅', slider: '🎚️',
                  rating: '⭐', colorPicker: '🎨', phone: '📞', file: '📎',
                  array: '📚'
                };

                return (
                  <div
                    key={type}
                    className="inline-flex items-center gap-1 px-2 py-1 bg-muted rounded text-xs"
                  >
                    <span>{icons[type] || '📝'}</span>
                    <span>{type}</span>
                    {count > 1 && (
                      <span className="bg-primary text-primary-foreground rounded-full w-4 h-4 text-xs flex items-center justify-center">
                        {count}
                      </span>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          {/* Advanced features used */}
          {(config.fields.some(f => f.section) || 
            config.fields.some(f => f.group) ||
            config.fields.some(f => f.help) ||
            config.fields.some(f => f.inlineValidation?.enabled)) && (
            <div className="space-y-2">
              <h4 className="text-sm font-medium text-muted-foreground">Advanced Features</h4>
              <div className="flex flex-wrap gap-2">
                {config.fields.some(f => f.section) && (
                  <div className="inline-flex items-center gap-1 px-2 py-1 bg-primary/10 text-primary rounded text-xs">
                    📑 Sections
                  </div>
                )}
                {config.fields.some(f => f.group) && (
                  <div className="inline-flex items-center gap-1 px-2 py-1 bg-accent/10 text-accent rounded text-xs">
                    🏷️ Field Groups
                  </div>
                )}
                {config.fields.some(f => f.help) && (
                  <div className="inline-flex items-center gap-1 px-2 py-1 bg-secondary/50 text-foreground rounded text-xs">
                    ❓ Help & Tooltips
                  </div>
                )}
                {config.fields.some(f => f.inlineValidation?.enabled) && (
                  <div className="inline-flex items-center gap-1 px-2 py-1 bg-muted text-muted-foreground rounded text-xs">
                    ⚡ Inline Validation
                  </div>
                )}
                {config.settings.showProgress && (
                  <div className="inline-flex items-center gap-1 px-2 py-1 bg-primary/20 text-primary rounded text-xs">
                    📊 Progress Indicator
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

"use client";

import React, { useState } from "react";
import { Highlight, themes } from "prism-react-renderer";
import { motion, AnimatePresence } from "motion/react";
import { Copy, Check } from "lucide-react";
import { toast } from "sonner";

/**
 * Unified CodeBlock component with syntax highlighting, copy functionality, and package manager tabs
 * 
 * Features:
 * - Syntax highlighting using Prism.js with oneDark theme
 * - Floating copy button with animation feedback
 * - Optional line numbers
 * - Package manager tabs for installation commands (npx, pnpm, yarn, bunx)
 * - Optional title header
 * - Responsive design with proper overflow handling
 */

interface CodeBlockProps {
  code: string;
  language?: string;
  showLineNumbers?: boolean;
  showCopyButton?: boolean;
  showPackageManagerTabs?: boolean;
  className?: string;
  title?: string;
}

// Copy Button Component
const CopyButton: React.FC<{ text: string; className?: string }> = ({
  text,
  className = "",
}) => {
  const [copied, setCopied] = useState(false);

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      toast.success("Copied to clipboard!");
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      toast.error("Failed to copy");
    }
  };

  return (
    <motion.button
      onClick={copyToClipboard}
      className={`relative inline-flex items-center justify-center p-2 rounded-md bg-muted/80 hover:bg-muted text-muted-foreground hover:text-foreground backdrop-blur-sm transition-colors shadow-lg ${className}`}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
    >
      <AnimatePresence mode="wait">
        {copied ? (
          <motion.div
            key="check"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0 }}
          >
            <Check className="w-4 h-4 text-accent" />
          </motion.div>
        ) : (
          <motion.div
            key="copy"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0 }}
          >
            <Copy className="w-4 h-4" />
          </motion.div>
        )}
      </AnimatePresence>
    </motion.button>
  );
};

export const CodeBlock: React.FC<CodeBlockProps> = ({
  code,
  language = "tsx",
  showLineNumbers = false,
  showCopyButton = true,
  showPackageManagerTabs = false,
  className = "",
  title,
}) => {
  const [selectedPM, setSelectedPM] = useState("npx");

  const packageManagers = {
    npx: code,
    pnpm: code.replace("npx shadcn@latest", "pnpm dlx shadcn@latest"),
    yarn: code.replace("npx shadcn@latest", "yarn dlx shadcn@latest"),
    bunx: code.replace("npx shadcn@latest", "bunx --bun shadcn@latest"),
  };

  const currentCode = showPackageManagerTabs ? packageManagers[selectedPM as keyof typeof packageManagers] : code;

  return (
    <div className={`relative ${className}`}>
      {title && (
        <div className="bg-muted text-muted-foreground px-4 py-2 text-sm font-medium rounded-t-lg border-b">
          {title}
        </div>
      )}

      {showPackageManagerTabs && (
        <div className="flex gap-1 mb-2">
          {Object.keys(packageManagers).map((pm) => (
            <button
              key={pm}
              onClick={() => setSelectedPM(pm)}
              className={`px-3 py-1 text-xs rounded-t-md transition-colors ${
                selectedPM === pm
                  ? "bg-primary text-primary-foreground"
                  : "bg-secondary text-secondary-foreground hover:bg-secondary/80"
              }`}
            >
              {pm}
            </button>
          ))}
        </div>
      )}

      <div className="relative group">
        <Highlight
          theme={themes.oneDark}
          code={currentCode.trim()}
          language={language as any}
        >
          {({ className: highlightClassName, style, tokens, getLineProps, getTokenProps }) => (
            <pre 
              className={`${highlightClassName} p-4 rounded-lg font-mono text-sm overflow-x-auto ${
                showPackageManagerTabs ? 'rounded-tl-none' : ''
              } ${title ? 'rounded-t-none' : ''}`}
              style={style}
            >
              {tokens.map((line, i) => (
                <div key={i} {...getLineProps({ line })} className={showLineNumbers ? "table-row" : ""}>
                  {showLineNumbers && (
                     <span className="table-cell text-right pr-4 text-muted-foreground select-none text-xs">                      {i + 1}
                    </span>
                  )}
                  <span className={showLineNumbers ? "table-cell" : ""}>
                    {line.map((token, key) => (
                      <span key={key} {...getTokenProps({ token })} />
                    ))}
                  </span>
                </div>
              ))}
            </pre>
          )}
        </Highlight>

        {showCopyButton && (
          <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
            <CopyButton text={currentCode} />
          </div>
        )}
      </div>
    </div>
  );
};
