  NumberConfig,
  MultiSelectConfig,
  DateFieldProps,
  SliderFieldProps,
  FileUploadFieldProps,
  LocationConfig,
  DurationConfig,
  AutocompleteConfig,
  MaskedInputConfig,
  ColorPickerFieldProps,
  RatingFieldProps,
  PhoneFieldProps,
  ArrayFieldProps
};

// Use real FieldConfig from main formedible package - no duplicates!
export type ParsedFieldConfig = FieldConfig;


// Parser config - picks only the serializable parts of UseFormedibleOptions
// Excludes functions and React components that can't be parsed from text
export type ParsedFormConfig = Pick<UseFormedibleOptions<Record<string, unknown>>, 
  | 'fields'
  | 'schema' 
  | 'pages'
  | 'tabs'
  | 'layout'
  | 'progress'
  | 'submitLabel'
  | 'nextLabel' 
  | 'previousLabel'
  | 'collapseLabel'
  | 'expandLabel'
  | 'formClassName'
  | 'fieldClassName'
  | 'labelClassName'
  | 'buttonClassName'
  | 'submitButtonClassName'
  | 'autoScroll'
  | 'autoSubmitOnChange'
  | 'autoSubmitDebounceMs'
  | 'disabled'
  | 'loading'
  | 'resetOnSubmitSuccess'
  | 'showSubmitButton'
  | 'crossFieldValidation'
  | 'asyncValidation'
  | 'analytics'
  | 'conditionalSections'
  | 'persistence'
  | 'formOptions'
>;

export interface ParserOptions {
  strictValidation?: boolean;
}

export interface ParserError extends Error {
  code?: string;
  field?: string;
  line?: number;
  column?: number;
}

// Phase 2: Enhanced Parser Types

/**
 * Enhanced parser options for Phase 2 features
 */
export interface EnhancedParserOptions extends ParserOptions {
  baseSchema?: unknown; // z.ZodSchema in actual usage
  mergeStrategy?: 'extend' | 'override' | 'intersect';
  predefinedHandlers?: {
    onSubmit?: (data: unknown) => void;
    specificFields?: Record<string, ParsedFieldConfig>;
    [key: string]: unknown;
  };
}

/**
 * Enhanced error type with AI-friendly suggestions
 */
export interface EnhancedParserError {
  type: 'syntax' | 'validation' | 'field_type' | 'schema';
  message: string;
  suggestion?: string;
  location?: {
    line?: number;
    column?: number;
    field?: string;
  };
  examples?: string[];
}

/**
 * Schema inference configuration options
 */
export interface SchemaInferenceOptions {
  enabled?: boolean;
  fieldTypeMapping?: Record<string, unknown>; // z.ZodType in actual usage
  defaultValidation?: boolean;
  inferFromValues?: boolean;
}

/**
 * Result of schema inference parsing
 */
export interface SchemaInferenceResult {
  config: ParsedFormConfig;
  inferredSchema: unknown; // z.ZodSchema in actual usage
  confidence: number;
}

/**
 * Result of validation with suggestions
 */
export interface ValidationWithSuggestionsResult {
  isValid: boolean;
  errors: EnhancedParserError[];
  suggestions: string[];
}

import React from "react";
import type { AnyFieldApi } from "@tanstack/react-form";
import type {
  FormApi,
  ValidationError,
  FormState,
  AnyFormApi,
} from "@tanstack/form-core";
import { z } from "zod";

// Strict type definitions for better type safety
export interface StrictFieldApi<T = unknown> {
  name: string;
  value: T;
  errors: ValidationError[];
  touched: boolean;
  setValue: (value: T) => void;
  setTouched: (touched: boolean) => void;
  validate: () => Promise<ValidationError[]>;
}

export interface TypedFormState<TFormData = Record<string, unknown>> {
  values: TFormData;
  errors: Record<keyof TFormData, ValidationError[]>;
  touched: Record<keyof TFormData, boolean>;
  isSubmitting: boolean;
  isValidating: boolean;
  canSubmit: boolean;
}

export interface TypedFormSubscriptionSelector<
  TFormData = Record<string, unknown>,
  TSelected = unknown
> {
  (
    state: FormState<
      TFormData,
      undefined,
      undefined,
      undefined,
      undefined,
      undefined,
      undefined,
      undefined,
      undefined,
      undefined,
      undefined
    >
  ): TSelected;
}

// Validation error types based on TanStack Form
export type FormedibleValidationError = ValidationError;

// Validation error that can be a string, Error object, or custom validation result
export type FieldValidationError = string | Error | ValidationError;

// Array of validation errors for a field
export type FieldValidationErrors = FieldValidationError[];

// Dynamic text types for template interpolation
export type DynamicText =
  | string
  | ((values: Record<string, unknown>) => string);
export type OptionalDynamicText = DynamicText | undefined;

// Type alias for our FormApi - use the core FormApi type which is what useForm actually returns
export type FormedibleFormApi<TFormData = Record<string, unknown>> = FormApi<
  TFormData,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  undefined,
  never
>;

// Option types for select, radio, and multi-select fields
export type FieldOption = {
  value: string;
  label: string;
  disabled?: boolean;
  description?: string;
};

export type FieldOptions = string[] | FieldOption[];

// Normalize options to consistent format
export type NormalizedFieldOption = FieldOption;

// Props that all basic field components rendered by FormedibleRoot will receive
export interface BaseFieldProps {
  fieldApi: AnyFieldApi;
  label?: string;
  description?: string;
  placeholder?: string;
