}

// Enhanced form analytics and tracking configuration
export interface FormAnalytics {
  // Field-level analytics
  onFieldFocus?: (fieldName: string, timestamp: number) => void;
  onFieldBlur?: (fieldName: string, timeSpent: number) => void;
  onFieldChange?: (
    fieldName: string,
    value: unknown,
    timestamp: number
  ) => void;
  onFieldComplete?: (
    fieldName: string,
    isValid: boolean,
    timeSpent: number
  ) => void;
  onFieldError?: (
    fieldName: string,
    errors: string[],
    timestamp: number
  ) => void;

  // Form-level analytics
  onFormStart?: (timestamp: number) => void;
  onFormComplete?: (timeSpent: number, formData: unknown) => void;
  onFormAbandon?: (
    completionPercentage: number,
    context?: {
      currentPage?: number;
      currentTab?: string;
      lastActiveField?: string;
    }
  ) => void;
  onFormReset?: (timestamp: number, reason?: string) => void;

  // Page-level analytics (for multi-page forms)
  onPageChange?: (
    fromPage: number,
    toPage: number,
    timeSpent: number,
    pageValidationState?: { hasErrors: boolean; completionPercentage: number }
  ) => void;
  onPageComplete?: (
    pageNumber: number,
    timeSpent: number,
    fieldsCompleted: number,
    totalFields: number
  ) => void;
  onPageAbandon?: (
    pageNumber: number,
    completionPercentage: number,
    timeSpent: number
  ) => void;
  onPageValidationError?: (
    pageNumber: number,
    errors: Record<string, string[]>,
    timestamp: number
  ) => void;

  // Tab-level analytics (for tabbed forms)
  onTabChange?: (
    fromTab: string,
    toTab: string,
    timeSpent: number,
    tabCompletionState?: { completionPercentage: number; hasErrors: boolean }
  ) => void;
  onTabComplete?: (
    tabId: string,
    timeSpent: number,
    fieldsCompleted: number,
    totalFields: number
  ) => void;
  onTabAbandon?: (
    tabId: string,
    completionPercentage: number,
    timeSpent: number
  ) => void;
  onTabValidationError?: (
    tabId: string,
    errors: Record<string, string[]>,
    timestamp: number
  ) => void;
  onTabFirstVisit?: (tabId: string, timestamp: number) => void;

  // Performance analytics
  onRenderPerformance?: (
    componentName: string,
    renderTime: number,
    rerenderCount: number
  ) => void;
  onValidationPerformance?: (
    fieldName: string,
    validationType: "sync" | "async",
    duration: number
  ) => void;
  onSubmissionPerformance?: (
    submissionTime: number,
    validationTime: number,
    processingTime: number
  ) => void;
}

